            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("482b7872-388f-43ab-80b8-f6d29d653ebf"), 3, new DateTime(1988, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pauillac", "Rich and massively concentrated, with layers of plum, anise, black cherry and tobacco, firm acidity and structure and thick, chewy, balanced tannins. Beautiful, elegant and stylish. One of the greatest Lynch Bages ever made; will improve with five or more years in the cellar.  ", 37.0m, "1985", new Guid("b5cf7d7d-16f5-4fce-8098-e781db5e414b") },
                    { new Guid("2e5ffc0c-2f75-4577-98f3-39d626dd1a33"), 6, new DateTime(2009, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Columbia Valley", "Light, refreshing and off-dry, with pretty peach, pineapple and floral aromas and flavors, echoing enticingly. Nicely knit. Drink now through 2011. 3,870 cases made.", 8.0m, "2008", new Guid("f18e65be-1c1a-4a31-b274-8272639cd4dc") },
                    { new Guid("e2154cc4-4637-430b-85ee-0d7d5aa959e8"), 3, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Lucia Highlands Twelve Clones", "Well-crafted, tight and structured, with firm, gripping tannins keeping the zesty, tart wild berry, blackberry and date-nut bread flavors in check. Drink now through 2020. 10,000 cases made.", 32.0m, "2010", new Guid("89aead9a-06a8-4539-a4b4-2c371f8383f8") },
                    { new Guid("fa28b133-4412-432e-9608-ad5481172d75"), 6, new DateTime(2011, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Monterey Metallico Un-Oaked", "Fresh, intense and vibrant, with an aromatic honeysuckle aroma leading to lively, concentrated pear and lemon-lime flavors. Full-bodied, clean and snappy on the long, layered finish. Drink now through 2016. 3,800 cases made.", 20.0m, "2009", new Guid("89aead9a-06a8-4539-a4b4-2c371f8383f8") },
                    { new Guid("5ec4b290-a0c1-4fe0-8a1f-8ea456c9c9d4"), 6, new DateTime(2005, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Albariño Rias Baixas Lagar de Cervera", "This full-bodied white is rich with almond, pear, butter and herb flavors. It's not overtly fruity, but shows complexity, with a firm backbone of acidity for balance. Drink now. 29,000 cases made.", 19.0m, "2004", new Guid("4af5e1b4-6031-4da3-9f53-79350a69ce71") },
                    { new Guid("57109adf-3e11-447c-8db8-705393a319b2"), 3, new DateTime(2005, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Victoria Billi Billi", "Ripe, generous and distinctly peppery around a generous core of ripe plum, cherry and spice flavors, the black pepper character weaving enticingly through the finish. Yummy stuff. Drink now through 2015. 2,000 cases imported.", 15.0m, "2002", new Guid("add4cf71-f17f-46c2-be2a-36505436467c") },
                    { new Guid("3f6f4ccc-2363-4607-a599-5608177d3cf7"), 3, new DateTime(2015, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Hacienda Monasterio", "This rich red delivers blueberry compote, dark chocolate, licorice and smoke flavors in a firm texture, supported by solid tannins. Ample acidity keeps this fresh through the floral, cola-accented finish. Drink now through 2026. 1,000 cases imported.", 51.0m, "2011", new Guid("00a61efe-bde9-4842-89e3-5cc413e2e9a9") },
                    { new Guid("66b090a1-f69c-4400-bf8a-7790e83dbd56"), 3, new DateTime(2005, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Hacienda Monasterio Crianza", "Elegant and polished, with blackberry and black cherry flavors accented by toasty, vanilla-scented oak and backed by focused tannins. Graceful through the long, floral finish. Drink now through 2009. 15,000 cases made.", 39.0m, "2001", new Guid("00a61efe-bde9-4842-89e3-5cc413e2e9a9") },
                    { new Guid("624e0c1a-4bbc-4c5b-a665-6864375d69d8"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon South Eastern Australia", "Soft, supple, ripe and generous with its currant, cherry and wild berry aromas and flavors, lingering nicely on the spicy finish. Immensely appealing stuff. Drink now through 2010. 6,000 cases imported.", 18.0m, "2003", new Guid("6799cb27-4de1-4d4b-83d2-0989300d7faf") },
                    { new Guid("9c94560e-ba04-4b38-a711-861973321582"), 3, new DateTime(2005, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Central Otago", "Lithe and lively. A lovely mouthful of richly textured currant, blackberry and plum flavors that persist into a gorgeously balanced finish. Has enough style and steel to make it last and develop in the bottle. Drink now through 2012. 2,100 cases imported.", 30.0m, "2003", new Guid("2c06d979-b7ef-4c28-8eec-281ffaaafe36") },
                    { new Guid("cf93191f-2e58-4976-9238-af817164d770"), 3, new DateTime(2009, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vinsobres Les Cornuds", "Very pure, with superracy red cherry, raspberry and violet notes that stretch out through the lengthy, graphite-filled finish. There's lovely, integrated acidity. Delicious. Drink now through 2011. 5,000 cases made.", 22.0m, "2007", new Guid("e85e028a-1149-4217-a48b-a9704142da65") },
                    { new Guid("37f7ef77-823f-4538-89ea-314c2ef1f008"), 3, new DateTime(2005, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cahors", "Quite polished and filled with layers of concentrated flavors of blackberry, dark plum, boysenberry, black olive and cassis. Well-integrated tannins, though they need time to settle. Finishes with notes of chocolate-covered strawberry. Best from 2006 through 2010. 9,410 cases made.", 25.0m, "2001", new Guid("386e1791-a2a4-48ad-8c61-c01722d10e43") },
                    { new Guid("2f48e4f6-3865-44c5-adaf-1888ea47e887"), 3, new DateTime(2008, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gigondas La Gille", "Dark and smoky, with alluring cocoa powder, mesquite and bacon fat notes leading the way for rich black currant, melted licorice and espresso. The long, smoldering finish shows a nice wild side. Impressive now, but better with some cellaring. Grenache and Syrah. Drink now through 2014. 3,000 cases made.", 27.0m, "2005", new Guid("e85e028a-1149-4217-a48b-a9704142da65") },
                    { new Guid("6b5ce48c-2297-4d08-a1e7-128babe939cd"), 3, new DateTime(2005, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Les Sinards", "Juicy, modern style, with mocha, blackberry, fig and boysenberry flavors mingling with tar and mineral notes. Firm finish has plenty of pure fruit for balance, and this gains steam in the glass too. Drink now through 2013. 6,700 cases made.", 30.0m, "2003", new Guid("e85e028a-1149-4217-a48b-a9704142da65") },
                    { new Guid("7eaa726c-0b4f-454f-a21c-9bfe07d2d14f"), 6, new DateTime(2005, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Colli Orientali del Friuli Terre Alte", "This shows lots of character but it's reserved and refined. Aromas of toasted oak, apple and peaches. Full-bodied, with white pepper and apple flavors. Long and lively. A fantastic blend—this is now one of the best whites in Italy. Tocai, Sauvignon Blanc and Pinot Bianco. Drink now. 6,000 cases made.", 35.0m, "2002", new Guid("2a6df4bc-981e-4137-b48a-a2adb60d7a97") },
                    { new Guid("f86b874a-d5be-493e-b717-6d5cb868d034"), 6, new DateTime(2019, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Grigio Alto Adige Tradition", "A creamy, light-bodied white, with mouthwatering acidity driving a finely knit range of Honeycrisp apple, Marcona almond and orchard blossom flavors, showing a rich and tangy streak of salinity that lingers on the finish. Drink now through 2023. 9,167 cases made, 8,000 cases imported.", 25.0m, "2018", new Guid("37770a0e-ae52-4f0f-979a-9dc771118912") },
                    { new Guid("6fbad904-5573-4936-913b-757aed0a9289"), 6, new DateTime(2005, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Bianco Alto Adige Classico Terlaner", "Really wonderful, with ultraclean and focused aromas of mineral, chalk, and apple. Full-bodied yet balanced, with good acidity and a long, creamy finish. Drink now. 6,000 cases made.", 18.0m, "2003", new Guid("37770a0e-ae52-4f0f-979a-9dc771118912") },
                    { new Guid("5efad3ca-2b40-44d3-b27e-e5fd14f78435"), 3, new DateTime(2005, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Grande Reserve Terroir Selection", "Structured Malbec, with lots of graphite and loam layered within the blackberry and black currant fruit. Thick, but ripe, with chocolate and espresso notes framing the finish. Nice mineral hint too. Best from 2006 through 2010. 1,500 cases imported.", 19.0m, "2003", new Guid("3fef06ea-100b-4215-8f89-5f8fd1a1bbd2") },
                    { new Guid("16a1b247-9e15-48d7-a6bd-7c8c24fa6f72"), 3, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Saumur-Champigny Domaine des Roches Neuves", "This wine has a beam of pure, lush blackberry and boysenberry fruit, along with suave toast, cocoa, mineral and tobacco notes. Concentrated, but lush and sweet through the finish, this drips with both <i>terroir</i> and fruit. Drink now through 2008. 6,500 cases made.", 16.0m, "2004", new Guid("79c0afd5-502e-4f06-9075-caf749eda82f") },
                    { new Guid("6fb33318-df0a-4548-8dad-e4b333086f2a"), 3, new DateTime(2005, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Minervois La Livinière La Touge", "Very meaty and decadent, this has ripe, full-bodied flavors of cassis, dark plum, chocolate and spice. Reminiscent of a Northern Rhône with its powerful, intense style. Pepper and beef carpaccio on the long finish. Tempting now, but should improve with some cellaring. Best from 2006 through 2010. 4,200 cases made.", 14.0m, "2002", new Guid("69024be1-b1c8-4323-b675-2b673d983816") },
                    { new Guid("98aeded5-6dd4-4801-9aad-61f40180716a"), 3, new DateTime(2005, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Prazo", "Rich and concentrated, with layers of mouthfilling red and black fruit flavors. Full-bodied, with blackberry, dark chocolate and licorice elements all in balance. Pure, minerally finish, with plenty of grip. Tempting now. Tinta Roriz, Touriga Franca, Tinta Barroca and Touriga Nacional. Best from 2007 through 2010. 2,000 cases imported.", 13.0m, "2003", new Guid("bcdef179-4e77-4c6b-8a77-0cf7eadb9ca7") },
                    { new Guid("d365ff02-ce60-4c32-b99a-9fc3a66a6214"), 3, new DateTime(2005, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Anderson Valley Migration", "Notably rich and plush, dark red cherry in color, with a mix of ripe plum, cherry and wild berry fruit flavors that are evenly distributed. It gains complexity and reveals its concentration on a long finish. Shows a dash of heat, but also fine acidity and tannins. Drink now through 2008. 3,061 cases made.", 28.0m, "2003", new Guid("c23fd900-a8f1-4aa2-9a48-fbf45e559b3a") },
                    { new Guid("373b77ff-752b-404e-a1d3-c68b6eef59e7"), 6, new DateTime(2005, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Clare Valley Polish Hill River", "This lively, distinctive Riesling has grapefruit, nectarine and mineral flavors that persist into a juicy, generous, dry finish. A stylish wine with amazing intensity and deftness. Drink now through 2012. 2,000 cases made.", 19.0m, "2003", new Guid("2a0b8075-a2c9-4212-a8f2-fbf960e03895") },
                    { new Guid("eeabee06-4715-4fc3-8339-63668b066126"), 3, new DateTime(2005, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vacqueyras Les Christins", "A gorgeous nose of dark fruit confiture and spice cake flavors is followed by a powerful palate of dark fruit, smoke, tar and minerals. Richly layered and densely structured, but with an elegant side as well. Best from 2006 through 2009. 4,165 cases made.", 19.0m, "2003", new Guid("e85e028a-1149-4217-a48b-a9704142da65") },
                    { new Guid("4cd12a6e-3046-4565-a9a3-3d2274bb112d"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Jumilla Altos de Luzón", "Ripe and luscious. This rich red is bursting with fruit, with flavors of cherry, boysenberry and currant, supported by well-integrated toasty oak, balanced acidity and ripe tannins. Accents of coffee, mineral and briar add allure, and mingle with the fruit on the long finish. Monastrell (Mourvèdre), Cabernet Sauvignon and Tempranillo. Drink now through 2010. 1,500 cases made.", 16.0m, "2003", new Guid("e5f31838-a4e7-4826-8c59-fb5b9b6d530f") },
                    { new Guid("8a390fc5-4803-4519-a240-7000ef74f7d0"), 3, new DateTime(2005, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nero d'Avola Sicilia Una Rosa", "Dark, full-bodied and chewy, with meaty blackberry and mineral character. Medium- to full-bodied, with good, solid tannins and a long finish. Well done. Best after 2005. 1,500 cases made.", 16.0m, "2002", new Guid("7f14da88-27f9-46ac-8090-7c30525e35c5") },
                    { new Guid("a5e7a3e6-78e0-44c5-96a9-4507832a2853"), 3, new DateTime(2006, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Il Blu", "The aromas leave me speechless, with freshly cut roses and lilacs that follow through to crushed blackberry and cherry undertones. Full-bodied but extremely balanced, with velvety yet caressing tannins and a finish that goes on for minutes. Sangiovese, Merlot and Cabernet Sauvignon. Best after 2008. 4,100 cases made.", 70.0m, "2004", new Guid("65b3d9db-5f2f-4147-8a32-a1e927d3b0f0") },
                    { new Guid("85c1ad18-cbea-4636-914b-f8479db8c975"), 3, new DateTime(2007, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Carnival of Love", "A stunning wine of immense depth and integrity. Seamless from start to finish, this starts off strong and keeps building through the long, powerful finish. The flavors center on blueberry, plum, meaty cherry, cedar and cream, with a hint of mint, and the tannins are so finely polished they gleam. Drink now through 2020. 4,738 cases made.", 80.0m, "2006", new Guid("73226680-a8f1-4b02-84a8-64b2239c5880") },
                    { new Guid("ab9ef7fb-ba1a-41d2-9f17-9612af59ecf8"), 3, new DateTime(2006, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale-Langhorne Creek-Padthaway The Boxer", "Dark and dense. A big wine, yet lively with nutmeg- and cinnamon-scented blackberry, cherry and licorice flavors, balanced against firm but well-integrated tannins. Needs some time to soften. Best from 2007 through 2014. 14,194 cases made.", 20.0m, "2005", new Guid("73226680-a8f1-4b02-84a8-64b2239c5880") },
                    { new Guid("e5c7e7d9-a871-4564-b4a2-f85564b079e7"), 3, new DateTime(2006, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Garnacha Campo de Borja Tres Picos", "Coffee and smoky flavors add complexity to the core of plum and raisin fruit in this muscular red, and pretty floral and spice flavors linger on the finish. A bit tannic now, but still expressive. Drink now through 2011. 5,000 cases imported.", 12.0m, "2004", new Guid("5ff650ed-1793-43bf-8f1f-369558ff0acc") },
                    { new Guid("69c98b47-f2af-4beb-81de-7510f0a6a4f4"), 6, new DateTime(2006, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Bianco Alto Adige", "Ultrarich, with layers of opulent, tropical fruit and light mineral undertones. Full-bodied, with a long, long finish. Drink now. 12,500 cases made.", 12.0m, "2005", new Guid("04055df4-c7db-448b-b130-cb8f25848007") },
                    { new Guid("61904e48-ff50-4321-ae2a-a64fc50b598f"), 6, new DateTime(2009, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling South Australia Reserve", "Tart and lively, offering a mouthwatering style that delivers a nice hit of green apple and lime flavor, persisting nicely. Drink now through 2015. 7,000 cases imported.", 13.0m, "2008", new Guid("0ab9a1ab-46e3-44c0-a30c-02b6972654e6") },
                    { new Guid("e8809e1b-ebca-4c67-a6dd-a13fe6d69146"), 3, new DateTime(2006, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Australia Reserve", "Thick, with a layer of black pepper over a mouthfilling gob of black cherry and licorice that cries out for grilled red meat. Drinkable now, better with some age. Best from 2008 through 2013. 61,000 cases imported.", 13.0m, "2003", new Guid("0ab9a1ab-46e3-44c0-a30c-02b6972654e6") },
                    { new Guid("5bc04d99-047c-46f5-b993-cc9538b31587"), 3, new DateTime(2006, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Torrione", "Plenty of blackberry and currant bush aromas. Intense. Full-bodied and very chewy, with ripe tannins and a long, powerful finish. A structured Sangiovese. Like the 2001 or better. Best after 2008. 3,100 cases made.", 38.0m, "2004", new Guid("822d689f-bdcf-4b22-b313-7b076c40541b") },
                    { new Guid("27d3b9ef-6541-463b-bbcf-982ccd271145"), 3, new DateTime(2006, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Lucia Highlands Garys' Vineyard", "Plush and concentrated, with layers of pure Pinot delight built around gorgeous plum, wild berry, blackberry and huckleberry fruit. Elegant and vibrant, it dances on the palate, finishing with fleshy, ripe fruit. Drink now through 2011. 628 cases made.", 45.0m, "2004", new Guid("af805d15-5a73-4563-99ef-8076fb7ebd23") },
                    { new Guid("9a014c28-d9a8-49d2-9b1d-b96e7caba3fe"), 3, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sta. Rita Hills Clos Pepe Vineyard", "Good intensity, richness and concentration, with excellent depth to the earthy, loamy wild berry and blueberry flavors, ending with firm, integrated tannins. Takes some time to open up, but once it does it's compelling. Drink now through 2011. 800 cases made.", 48.0m, "2005", new Guid("c7debf70-d171-468c-a97a-7f58ab918e00") },
                    { new Guid("548b84a8-2d01-436d-993e-093a93a672ee"), 3, new DateTime(2006, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Lucia Highlands Rosella's Vineyard", "Decadent forest floor, chocolate and dense boysenberry provide a captivating core of complex flavors in this tighly wound youngster. Firmly tannic, with subtle nuances of dark berry fruits that emerge on the finish, along with delicate toasty oak, giving an extra layer of complexity. Drink now through 2009. 750 cases made.", 48.0m, "2004", new Guid("c7debf70-d171-468c-a97a-7f58ab918e00") },
                    { new Guid("b9fbeca6-cd4d-436f-bd0b-d352466bacb4"), 3, new DateTime(2005, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Touriga Nacional Alentejo", "Decadent and powerful, with a meaty, gamy aroma backed by deep plum and dark cherry flavors. Mocha and roasted notes fill the long finish, with plenty of licorice. Reminiscent of a Châteauneuf. Drink now. 12,062 cases made.", 20.0m, "2002", new Guid("57c56f22-b865-40b3-b9d0-21a9b5bb2a9c") },
                    { new Guid("a1f82a06-624f-4bf2-b50a-25d4a7d24581"), 3, new DateTime(2006, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Emily's Cuvée", "Fragrant rose petal and crushed raspberry aromas turn elegant and stylish on the palate, with fresh, lively acidity and a sleek, rich aftertaste. Wonderful balance, finesse and delicacy. Finish sails on. Drink now through 2010. 1,595 cases made.", 38.0m, "2003", new Guid("a80dbd3c-1415-4bce-910c-4c94a552e400") },
                    { new Guid("412b333b-07b9-4140-90bd-af51d71f4528"), 3, new DateTime(2015, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Superior", "Well-crafted and suave, with refined flavors of raspberry tart, red plum and cherry pastry, matched to cardamom and savory spice notes. Silky tannins and a plush, creamy finish offer chocolate mousse accents.  Drink now through 2022. 15,166 cases made.", 30.0m, "2012", new Guid("32a7cab5-bc50-4bc7-84de-7807c09068ae") },
                    { new Guid("2418334d-a4d5-4678-b677-0a85f22d680f"), 3, new DateTime(2006, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Reserva Old Vines", "A ripe, full-bodied red packed with dark fruity flavors as well as plenty of paprika. Powerful, with a dense core of spicy elements that follow through to the long, cream-filled finish. Drink now through 2012. 6,000 cases made.", 35.0m, "2004", new Guid("32a7cab5-bc50-4bc7-84de-7807c09068ae") },
                    { new Guid("cb04807c-2a0d-4d76-9429-415c8261d5dd"), 2, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tokaji Aszú 5 Puttonyos Red Label", "Alluring aromas of gingersnap cookies, apricot and orange marmalade. Sweet, yet deftly balanced by lively structure. Fine lingering finish, where the harmony really shows. Drink now through 2020. 13,000 cases made.", 32.0m, "2000", new Guid("2e6ab51a-e4be-4dd1-a3dc-edb059c5ed84") },
                    { new Guid("e1709c53-b5d9-4879-aabf-5a2c59925637"), 3, new DateTime(2006, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grenache-Shiraz-Mourvèdre McLaren Vale Cadenzia", "A vivid red, rich and velvety, brimming with plum, blackberry and cherry flavors, wrapped in a veil of very fine tannins, weaving a dark chocolate note through the long, generous finish. Best from 2008 through 2015. 2,474 cases made.", 25.0m, "2004", new Guid("ed264523-0127-4a71-8cf8-a466e668f85f") },
                    { new Guid("6985a0dd-408a-4426-80bb-a963f3e353a9"), 6, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Russian River Valley", "A brilliant effort. Elegant and graceful, with a broad range of flavors including quince, melon, fragrant Asian pear, citrus, tangerine and herb. Lithe and refreshing, the flavors echo on the long finish. Drink now through 2009. 2,500 cases made.", 27.0m, "2005", new Guid("b56287ee-e4b0-4cfc-819d-67fd03ac2086") },
                    { new Guid("98510453-5812-48c7-a6e9-eb2fafe71904"), 3, new DateTime(2006, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Klopp Ranch Méthode à l'Ancienne", "A classy Pinot Noir that captures the essence of the grape in spades, with vivid, complex wild berry, raspberry, spice, anise and hazelnut shadings that are well-dispersed. Impeccable balance, lively acidity and tremendous fruit complexity. Drink now through 2011. 1,250 cases made.", 48.0m, "2003", new Guid("b56287ee-e4b0-4cfc-819d-67fd03ac2086") },
                    { new Guid("eb31868e-7351-4113-aa11-481e130c92c3"), 3, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley Valley Floor", "For a wine that doesn't feel big or broad, this Shiraz has amazing depth of flavor and focus, delivering a seamless package of blueberry, currant, plum and vanilla-spice flavors that fan out over the palate. Has compelling purity and impressive persistence. Best from 2008 through 2016. 8,000 cases made.", 23.0m, "2004", new Guid("9028c25f-5f04-410f-bd51-a01b52b9a565") },
                    { new Guid("f49bb47c-d4e1-4a59-a27c-319fd9a665ea"), 3, new DateTime(2006, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Shea Vineyard Estate", "Lithe, supple and expressive, offering layers and layers of white pepper-accented currant and cherry, with a mineral note that adds depth and distinction. Broad and open-textured, lasting beautifully on the finish against superfine tannins. Best from 2007 through 2016. 1,200 cases made.", 38.0m, "2004", new Guid("c0fec632-e6dc-4c9d-97f9-d25888eb1c6d") },
                    { new Guid("8c0e2a94-4dad-4a4b-85c5-c9fb0bbb737a"), 3, new DateTime(2015, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Ilatraia", "Dense and tightly wound, with a beam of black cherry and blueberry fruit. Cedar, smoke, tar and tobacco notes add nuance, giving this a complex impression overall. Balanced and long, with a compact finish that should expand as this integrates more fully. Cabernet Sauvignon, Petit Verdot and Cabernet Franc. Best from 2018 through 2032. 3,750 cases imported.", 60.0m, "2012", new Guid("65b3d9db-5f2f-4147-8a32-a1e927d3b0f0") },
                    { new Guid("95263242-9891-4a9f-9470-27e623781765"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Tre", "There's wonderful intensity of fruit in this wine, with crushed raspberry and blackberry and hints of coffee and fresh flowers. Full-bodied, with velvety tannins and a long, soft-textured finish that shows loads of fruit. Sangiovese, Merlot and Cabernet Sauvignon. Best after 2010. 25,000 cases made.", 20.0m, "2007", new Guid("65b3d9db-5f2f-4147-8a32-a1e927d3b0f0") },
                    { new Guid("2ea11170-1b3a-4148-8c01-9e0ddaaaac61"), 3, new DateTime(2006, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Edna Valley Chamisal Vineyards", "Subtle earth and crushed raspberry and wild berry fruit is seductive, with savory herb, mushroom, bay leaf and exotic spice. This is a notably complex and concentrated wine, well-extracted and firm on the finish. Try one now; cellar one for later. Drink now through 2009. 2,144 cases made.", 28.0m, "2004", new Guid("fb3abbef-0eef-4293-9ada-4247fbe27fb0") },
                    { new Guid("9ec5e198-d0ff-4817-b37c-ea790fd7836b"), 3, new DateTime(2013, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Uco Valley Reserve", "Concentrated and cut, displaying layers of macerated blueberry, cassis, black peppercorn and flint on a full frame. Silky tannins and undertones of game and mocha line the savory finish. Drink now through 2020. 6,500 cases made.", 24.0m, "2011", new Guid("923b1819-5ce4-41c8-b65c-0b3dfb1dcfeb") },
                    { new Guid("ea1cea25-d7f1-4880-a3eb-87fe9be7beb4"), 6, new DateTime(2006, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Paretai Estate Series", "Juicy, mouthwatering and vibrant with lemon, quince and mineral aromas and flavors, persisting impressively on the long, expressive finish. Drink now. 4,000 cases imported.", 17.0m, "2005", new Guid("0f7749d9-efea-4b8a-aff0-b9b71f11f937") },
                    { new Guid("59c5d7f2-8f00-469a-8cc8-ad4ebb24c449"), 6, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Paretai", "Packs an enormous amount of zingy, lively character into a beautifully focused, racy frame, offering lime, mineral, passion fruit and a crunch of celery, all mingling with zesty grapefruit as the finish lingers. Drink now. 1,000 cases imported.", 17.0m, "2004", new Guid("0f7749d9-efea-4b8a-aff0-b9b71f11f937") },
                    { new Guid("ac1049b7-d4a5-4ab0-9f74-14abd6471c19"), 3, new DateTime(2004, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Estate", "Dramatically aromatic, brimming with black cherry, hinting at smoke and spices as the flavors play against firm tannins. Needs time to soften, and then fasten your seatbelt. Best from 2006 through 2012. 4,500 cases made.", 22.0m, "2002", new Guid("16d9518a-13fb-40a0-bdbc-369151716d07") },
                    { new Guid("877e334b-d76c-44d1-9488-99630a3182d0"), 3, new DateTime(2005, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Alta", "Deftly toasted, with vanilla and mocha suavely intertwined with lush plum, blackberry and boysenberry fruit. Long, plush and smoky through the finish, with the fruit taking an encore. A beauty. Drink now through 2008. 4,000 cases made.", 45.0m, "2002", new Guid("447b06e1-50e8-48c1-a70a-6fa3fe0346ef") },
                    { new Guid("264c9d36-bb1a-41a4-bf23-9aa1df415cbd"), 3, new DateTime(2004, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza", "Rock solid, with thoroughly ripe blackberry and boysenberry fruit augmented by toast, spice, mineral and loam. Dense and plush, but vivid and defined through the finely stitched finish. Drink now through 2007. 33,000 cases made.", 22.0m, "2002", new Guid("447b06e1-50e8-48c1-a70a-6fa3fe0346ef") },
                    { new Guid("e613e2a3-b792-4a0b-9b09-334efd8d9f73"), 3, new DateTime(2004, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Firehouse Red Columbia Valley", "Polished, focused and impressive for its pure currant and blackberry fruit, nicely shaded with hints of mineral and a whisper of red pepper. The flavors linger enticingly against fine, crisp tannins. Cabernet Sauvignon, Syrah, Merlot and Cabernet Franc. Drink now through 2012. 4,312 cases made.", 18.0m, "2002", new Guid("774c1287-237b-49b6-925d-7088b40c7989") },
                    { new Guid("6a779798-eecd-4153-b4b8-0d92cdc144b4"), 3, new DateTime(2005, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Goats do Roam In Villages Red Coastal Region", "Solid, modern-style wine here, with dark, racy blackberry and boysenberry fruit, ample toast and cocoa notes, and a long, plush, fruit-filled finish. Solid acidity keeps it all honest too. Delicious. Shiraz and Pinotage. Drink now through 2006. 12,000 cases made.", 13.0m, "2003", new Guid("ba107559-64ff-431c-8834-e26049d4ea7e") },
                    { new Guid("93aa7496-3e5b-4ea0-b9dd-98c45429a2d6"), 3, new DateTime(2004, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Goat-Roti Western Cape", "This just oozes fruit--blackberries, plums and currants--but has firm tannins to keep it from getting to gushy. Lots of tar, smoke and dark chocolate here too, with the fruit pumping through the finish. Shiraz with five percent Viognier. Drink now through 2005. 7,000 cases imported.", 18.0m, "2002", new Guid("ba107559-64ff-431c-8834-e26049d4ea7e") },
                    { new Guid("46975a34-1964-4d9a-be9a-208b60f7cfe9"), 3, new DateTime(2004, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Cellole Riserva", "Phenomenal nose, with complex smoky, tarry, licoricelike red fruit aromas. Full-bodied, with added tobacco and vanilla coming through on the palate, with silky tannins and a long, minerally finish. One of the best Chianti Classicos I've ever tasted. Best after 2005. 2,500 cases made.", 45.0m, "2001", new Guid("2e45b313-c9a5-4a99-8507-efe4a592355d") },
                    { new Guid("b1025373-1376-4142-9b74-1b7a8207eddc"), 3, new DateTime(2004, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Reserva", "Terrific core of blackberry, black fig and boysenberry fruit, with lush toast, cocoa powder and loam notes as well. Juicy and long, with a powerful finish. Jump on it. Drink now through 2008. 3,500 cases imported.", 15.0m, "2002", new Guid("27d28ffd-5783-4149-b3a0-a4bdac033bda") },
                    { new Guid("728ba9f7-0b7f-4d25-91fa-4c30b85fde31"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montsant Castell de Falset", "Expressive aromas of black currants and coffee follow through on the muscular palate, amplified by anise, tobacco and mineral notes. When the tannins give way, the finish is clean and long. A full-throttle red that achieves power without ever losing control. Grenache, Cabernet Sauvignon, Carignane and Tempranillo. Best from 2006 through 2014. 1,500 cases imported.", 17.0m, "2000", new Guid("dbb4d9a7-55bd-449a-b215-2f17163fdbe3") },
                    { new Guid("ebdbe52a-7a50-4009-a820-f0e810fadc10"), 3, new DateTime(2004, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Sophia's Cuvee", "Appealing for its exuberant display of ripe, fleshy blackberry and wild berry fruit. It adds a pretty touch of floral and cedar nuances before revealing its depth, richness and concentration on a long, persistent, impressive finish that keeps the flavors lively and flowing. Drink now through 2012. 1,375 cases made.", 27.0m, "2001", new Guid("7ef5a22e-fce7-47ab-b77e-7faabf6d6b62") },
                    { new Guid("390efcf0-4338-4499-bd10-0d05895c355d"), 3, new DateTime(2004, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Three Cape Ladies Simonsberg-Stellenbosch", "Suave wine, with black and red currant fruit offset by tangy mineral and sanguine notes. Plenty of toast and spice as well, but it's all folded together in a rich, creamy package with a nice tar note on the finish. Cabernet Sauvignon, Pinotage and Merlot. Drink now through 2006. 6,000 cases made.", 22.0m, "2001", new Guid("6e51e28f-2d02-4758-bb93-f77c25aa901b") },
                    { new Guid("dd14d76f-21a7-49f6-b936-5ddfef74361c"), 3, new DateTime(2004, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Bricco Fiasco", "The nose of this wine evokes crushed Nebbiolo, with hints of fresh flowers and Indian spices. Full-bodied, with incredibly round, soft tannins. Long, velvety finish. Voluptuous. Azelia has come into its own. Serve this to the nonbelievers. Best after 2008. 1,040 cases made.", 78.0m, "2000", new Guid("3edbcd48-3e75-4e4a-8ac4-6f5b9ccd682a") },
                    { new Guid("5e7e4590-49cf-48f8-86fc-9252bbfcb7b1"), 3, new DateTime(2004, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Struie Barossa Valley", "Ripe, round and decadent, plush in texture, oozing with tarry, chocolaty plum and black cherry flavors that linger beautifully. Picks up all kinds of spicy notes as the finish goes on and on. Should only get deeper with age. Drink now through 2020. 2,500 cases made.", 50.0m, "2002", new Guid("ec508f24-c949-4b48-bc51-dc08c45a3d67") },
                    { new Guid("ab890e6b-a5c4-4136-ac31-7366a7932faa"), 3, new DateTime(2014, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Dominio de Valdepusa", "This dense and harmonious red delivers plum, tobacco, cedar and mineral flavors that mingle over muscular but polished tannins. A bit reticent, but balanced and deep. Drink now through 2025. 3,000 cases made.", 40.0m, "2010", new Guid("b5bacee5-076b-46cb-9f88-32046046505f") },
                    { new Guid("73e62790-8968-4e26-8aab-a95f8a768517"), 3, new DateTime(2004, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Viño de Mesa de Toledo Dominio de Valdepusa", "Powerful aromas of anise, game and tar may be too strong for some, but they morph into ripe blackberry, cassis, espresso, black pepper, licorice and mineral flavors on the palate. The texture is plush, the tannins massive but well-integrated, and the fruit sails on the finish. Ambitious red in the full-blown international style. Drink now through 2010. 4,000 cases made.", 35.0m, "2001", new Guid("b5bacee5-076b-46cb-9f88-32046046505f") },
                    { new Guid("1c4a524f-fe7a-4229-8ce7-c441a505df64"), 6, new DateTime(2018, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sancerre Les Baronnes", "Lively, with bright lemon curd, gooseberry, thyme and lime pith notes. The bright finish has mouthwatering cut thanks to a light chalky echo. Drink now through 2021. 7,000 cases made.", 25.0m, "2017", new Guid("068c59bb-7358-4b13-8678-5df424680f83") },
                    { new Guid("e97113a8-ab8e-4428-8fa6-e8f1c2e97db5"), 6, new DateTime(2004, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fumé La Porte de L'Abbaye", "This offers layers of streamlined flint, lime, lemon curd, smoke, green almond and dried herbs, all running through a long finish that never breaks stride. Drink now through 2008. 12,500 cases made.", 17.0m, "2002", new Guid("068c59bb-7358-4b13-8678-5df424680f83") },
                    { new Guid("3c1c7d44-17a1-4e2f-bb78-a3128bf71d16"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley", "Ripe, beautifully focused and generous, a delicious mouthful of blackberry, cherry, blueberry, plum and exotic spice flavors that just don't quit, hinting at smoke and meat on the lithe, elegant finish. Impressive for its balance of power and grace. Drink now through 2012. 1,360 cases made.", 26.0m, "2002", new Guid("49d9fa95-0728-484d-a4af-ab879b5b7b12") },
                    { new Guid("e5d2e143-0e55-42d9-a2cc-f190c5f2a58a"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Victoria", "Bright, juicy and lively with blackberry, huckleberry, white pepper and a refined floral character that melds beautifully through the finish, and the tremendous aftertaste just doesn't quit. A stylish Shiraz that should keep giving for years. Best from 2006 through 2015. 5,000 cases imported.", 18.0m, "2002", new Guid("f74fc2fb-343c-408a-81ad-801f92a45219") },
                    { new Guid("d91f4498-ffb8-40c2-9f37-a8e5d41ff1b5"), 3, new DateTime(2007, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Meandro", "Powerful aromas and flavors of cassis, mint, dark chocolate and spice are accented by an impressive minerality. Medium-grained tannins and plenty of structure back up the almost Port-like essence of this muscular red. Shows exotic Asian spice notes on the finish. Drink now through 2012. 1,450 cases imported.", 19.0m, "2005", new Guid("bb9a6fdd-4d89-43c4-89e0-741b9d3efdd5") },
                    { new Guid("159841a7-d0ac-4aa4-92a1-d633f78c3da1"), 6, new DateTime(2004, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley", "You won't find many Chardonnays in this class. Ultrarich and multidimensional, with a fabulous array of creamy flavors built around a core of fig, apricot, crème brûlée and roasted marshmallows. Full-bodied, yet elegant and concentrated, it weaves the tapestry of flavors to a long, rich, graceful finish. Drink now through 2009. 2,016 cases made.", 40.0m, "2002", new Guid("e06509c7-f47a-4505-b0f5-cf5a2341d73d") },
                    { new Guid("4aee7701-7d24-41c6-8eb7-e7d046014129"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Rutherford 25th Anniversary Selection", "A pure and seductive style, with enticing aromas of mocha, baking spices, melted chocolate, ripe plum, black cherry and black licorice that are supple, elegant, balanced, rich and layered, ending with an amazingly long and persistent finish. Drink now through 2020. 2,500 cases made.", 175.0m, "2007", new Guid("24ab7649-5c5c-4e92-a364-1723cf2a2ad1") },
                    { new Guid("e4ffbe14-1690-4199-ad14-71a4883809ab"), 3, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley Woodcutter's", "Aromatic and stylish, opening with violet, white pepper and subtle sweet cream notes. The core gushes with huckleberry and wild blueberry flavors, revealing shades of rosemary and apricot. Supple and smooth, with tannins hanging in the background, providing appealing traction. The finish sails on and on. Drink now through 2035. 7,000 cases made, 3,500 cases imported.", 25.0m, "2017", new Guid("ec508f24-c949-4b48-bc51-dc08c45a3d67") },
                    { new Guid("b7c75702-f815-4c4a-aecd-6a2eb99090f0"), 3, new DateTime(2008, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Estate Reserve Simonsberg-Stellenbosch", "Dark and exotic, with notes of fig paste, hoisin sauce, graphite and macerated plum, this stays driven and focused thanks to racy, fine-grained tannins. The lingering finish lets black tea and bittersweet cocoa chime in. A big step up for this wine. Cabernet Sauvignon, Cabernet Franc and Merlot. This wine is labeled as Trilogy outside the U.S. market. Drink now through 2011. 3,500 cases made.", 28.0m, "2005", new Guid("6e51e28f-2d02-4758-bb93-f77c25aa901b") },
                    { new Guid("e44d3dc8-70c2-42fe-885a-a47162dcad7a"), 3, new DateTime(2004, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Cosme Palacio y Hermanos", "Expressive and alluring. This modern red shows a plush texture and ripe fruit flavors of blackberry and plum, with pretty oak accents and well-integrated tannins. It has depth, but remains graceful on the palate. Drink now through 2008. 10,000 cases made.", 12.0m, "2001", new Guid("e9380bf9-9c76-4264-ad05-7861817d0c9d") },
                    { new Guid("0c24199d-67bc-486f-83db-b8a78385b923"), 3, new DateTime(2004, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon San Rafael-Argentina Famiglia", "The lush cassis and raspberry fruit is surrounded by cocoa, spice and toast notes. The fruit really pumps through the finish, offset by a mineral note that adds further dimension. Elegant and well put together. Drink now through 2008. 6,000 cases made.", 18.0m, "2002", new Guid("c688cc32-e7b7-4ef6-bb9d-e0383c61cc0e") },
                    { new Guid("deb76e2c-fc1b-44e3-86e4-6554314ebe62"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Machete California", "Concentrated yet light-weight, fragrant and juicy, with aromas of toasted cumin, gingerbread and Earl Grey tea surrounding the core of wild blueberry and huckleberry flavors. The firm tannins stay in the background, allowing this to finish on the juicy side. Petite Sirah, Syrah and Grenache. Drink now through 2025. 5,000 cases made.", 48.0m, "2012", new Guid("ac3fdeb3-f1f1-42c5-b03e-29d69ee4fe16") },
                    { new Guid("bddb1ccf-b996-4506-975e-b2438d1479e1"), 3, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Abstract California", "Offers wonderful complexity, with peppery wild berry, raspberry, spice, hot brick and sage flavors. Full-bodied, ending with a supple mix of subtle flavors. Grenache, Petite Sirah and Syrah. Drink now through 2018. 8,000 cases made.", 25.0m, "2009", new Guid("ac3fdeb3-f1f1-42c5-b03e-29d69ee4fe16") },
                    { new Guid("439b3173-4092-4696-94d5-3be9051a6ce6"), 3, new DateTime(2010, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel California Saldo", "Well-built, yet rich and stylish, with spicy blackberry aromas and zesty, layered flavors of black cherry and licorice framed by toasty oak. Offers a long, satisfying finish. Drink now through 2015. 14,000 cases made.", 28.0m, "2008", new Guid("ac3fdeb3-f1f1-42c5-b03e-29d69ee4fe16") },
                    { new Guid("f240b57a-13ed-4b3d-a2b5-1e284df09623"), 3, new DateTime(2005, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Prisoner Napa Valley", "Dark, immense and packed with zesty wild berry, spice, floral and currant flavors that are sharply focused and well-defined. The long, persistent aftertaste offers a hint of chocolate and vanilla. Zinfandel, Cabernet Sauvignon, Syrah, Charbono and Petite Sirah. Drink now through 2009. 4,500 cases made.", 25.0m, "2003", new Guid("ac3fdeb3-f1f1-42c5-b03e-29d69ee4fe16") },
                    { new Guid("97ca714d-d382-43db-8034-6d9c7fd01eb6"), 3, new DateTime(2019, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yamhill-Carlton District Shea Vineyard", "Elegant yet dynamic, with violet, raspberry and spiced cinnamon flavors that build richness and structure toward refined tannins. Drink now through 2026. 1,100 cases made.", 72.0m, "2016", new Guid("c952aafb-6968-4ac7-a0ec-9a86fcdc7eaf") },
                    { new Guid("81dc6e35-f642-45f1-ac07-d1dc005b1649"), 3, new DateTime(2013, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Finca Mirador", "This pure, racy red exhibits a dark side, with layers of spice, graphite and maduro tobacco to the crushed raspberry, blackberry and concentrated cassis fruit. Needs time in the cellar. Best from 2015 through 2022. 1,122 cases made.", 125.0m, "2011", new Guid("1170f7c6-9063-4b85-aa68-d771e95524b5") },
                    { new Guid("10d967b0-0b16-4c17-be9d-a64934d1f3cd"), 3, new DateTime(2012, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Finca Bella Vista", "Dark and powerful, yet elegant, with a delicious core of crushed raspberry, blackberry and damson plum notes tightly woven with silky tannins. The minerally finish lingers on with layered flavors of wildflowers, melted licorice and spice box. Drink now through 2020. 1,250 cases imported.", 120.0m, "2010", new Guid("1170f7c6-9063-4b85-aa68-d771e95524b5") },
                    { new Guid("6a893aaf-7ad3-409a-87b6-ffd9b334aecc"), 3, new DateTime(2005, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Finca Altamira Mendoza", "A stunning nose of blackberry and boysenberry confiture that never gets heady leads to a massively endowed palate of fruit, lavender flower, mineral, tar and graphite flavors. Superlong finish, with hints of dark chocolate, shows both power and elegance. An amazing display of fruit and balance thanks to terrific acidity. Drink now through 2015. 715 cases made.", 85.0m, "2003", new Guid("1170f7c6-9063-4b85-aa68-d771e95524b5") },
                    { new Guid("e6e61233-bd29-4b79-b5c0-7dd70f3e8863"), 3, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Maremma Toscana Ilatraia", "Gorgeous aromas of blackberries, raspberries and hints of tar follow through to a full-bodied palate with supersilky tannins and a long, long finish. Classy red from Maremma. Rich yet subtle. Cabernet Sauvignon, Sangiovese and Petit Verdot. Best after 2008. 1,650 cases made.", 56.0m, "2003", new Guid("f75a6a53-6d8f-4a02-9fc0-5db91e93bc9a") },
                    { new Guid("032be4e6-6602-41f3-940f-0ff37f290273"), 3, new DateTime(2005, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Russian River Valley", "Classic Syrah aromas of beef carpaccio, pepper, wild berry, blackberry and plum. This remarkable wine is rich and concentrated, with a tight focus and an intense yet supple and integrated finish, where the flavor themes keep building on each other. Drink now through 2012. 866 cases made.", 50.0m, "2003", new Guid("f2eefa26-5ebd-4263-bbb0-bc7601ff127e") },
                    { new Guid("6c0dd491-b1ce-4432-a424-dc6d72a4623d"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Domaine Grand Veneur Les Origines", "Jaw-dropping raspberry ganache and plum fruit, along with cocoa, tar, hoisin sauce and mineral notes, are flamboyantly displayed, while an intense tarry, minerally drive extends the finish. Best from 2007 through 2025. 1,000 cases imported.", 40.0m, "2003", new Guid("44bf6392-7b20-4780-835e-3fed2789cd62") },
                    { new Guid("90015d72-b7de-46a5-b5ab-85ad362ff669"), 3, new DateTime(2005, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Sixtine Réserve", "A superb core of blackberry, boysenberry and black currant fruit stands amidst cocoa, mineral, licorice and tar flavors. Densely structured but perfectly ripe, with a long finish that cascades with both fruit and <i>terroir</i>. Awesome for its power and balance. Best from 2007 through 2025. 1,800 cases made.", 55.0m, "2003", new Guid("aa90d2f6-b3ad-4c50-bac8-bef574a0240a") },
                    { new Guid("66d920ec-c13b-406c-87e6-8bc1e7aff082"), 3, new DateTime(2009, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa James Goddard", "Ripe and rich, this is dense with flavor but transparent in texture. A Shiraz of power and elegance as it plays out its cherry, black tea and spice flavors. Approachable now, but this has miles to go as it develops. Drink now through 2020. 1,000 cases made.", 33.0m, "2006", new Guid("ac9a696f-4dfa-4dfd-9a94-74fb6dada31e") },
                    { new Guid("fb762e4e-e02e-44de-9778-882891527523"), 3, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Shotfire", "Aims for refinement and achieves a supple balance of ripe plum and currant fruit against a range of sweet spices and fine tannins. Lingers gently and expressively. Production was split between cork and screw-cap closures. Tasted from cork. Drink now through 2016. 15,000 cases made.", 22.0m, "2006", new Guid("ac9a696f-4dfa-4dfd-9a94-74fb6dada31e") },
                    { new Guid("ebde4cb9-acd2-4d5f-b33b-7bd1d55c7c89"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Shotfire Ridge", "Big, rich and concentrated, a huge mouthful of peppery black cherry, plum and exotic spice flavors pulling themselves together into a supple wine that actually strives for elegance. It's all in balance, which lets the rich flavors ride on a sleek track and linger beautifully. Best from 2008 through 2020. 12,000 cases made.", 20.0m, "2003", new Guid("ac9a696f-4dfa-4dfd-9a94-74fb6dada31e") },
                    { new Guid("cf8aab26-2e1b-4f6c-96d9-28adce0ecb2b"), 3, new DateTime(2005, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast Maritime Vineyard", "A gorgeous wine, rich, smooth and seamless, with layers of complex creamy cherry, wild berry and plum shaded by mocha-laced oak that is neatly integrated. The tannins are ripe and supple, the aftertaste fascinating. Drink now through 2010. 545 cases made.", 45.0m, "2003", new Guid("82bdae09-77dd-40c3-9e4a-53616de07597") },
                    { new Guid("1c87262a-2a50-4099-9106-533e203e1a51"), 3, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Cortona Il Bosco", "Powerful aromas of crushed black fruits, pepper and wet earth; just a hint of roasted meat. Full-bodied, chewy and rich, with a long, long finish. This is a fantastic Syrah. Like a great Hermitage. Best after 2008. 3,000 cases made.", 50.0m, "2003", new Guid("8eac80be-bb7c-439e-8cef-562d7464d80e") },
                    { new Guid("3eede3cc-ba9d-4551-9574-994256a3a57a"), 3, new DateTime(2007, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast Kanzler Vineyard", "The initial dusty berry and lead pencil shavings are far richer and fruitier on the palate, while still being very taut, backward, intense and concentrated, with potent wild berry and black cherry fruit that's deep and expansive. Needs time. Best from 2008 through 2014. 500 cases made.", 62.0m, "2005", new Guid("e3a7feda-449c-4646-a461-25210d0ae535") },
                    { new Guid("51824e4d-b077-4046-84a7-75645cf6e559"), 6, new DateTime(2004, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Insolia-Chardonnay Sicilia Angimbé", "Fantastic white, with lively pineapple, honey and apricot aromas and flavors. Full-bodied, with bright acidity and a long finish. Excellent value. Drink now. 8,500 cases made.", 13.0m, "2002", new Guid("aa11a9de-c2fa-48b4-bd90-145c21df9e7a") },
                    { new Guid("fdd5e2c5-9e10-4a7a-9710-27e39481315b"), 6, new DateTime(2004, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Soave Classico", "Beautiful aromas of mineral, apple and light honey with just hints of lemon rind. Full-bodied, with lively fruit and a long, long finish. A beauty. Drink now. 13,000 cases made.", 13.0m, "2002", new Guid("711437d2-6e3f-4a2c-98ff-be156270e37a") },
                    { new Guid("4de2d044-57c8-4adc-8a71-93944a702779"), 3, new DateTime(2004, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montepulciano d'Abruzzo", "Plenty of soft and spicy character with hints of black pepper, even eucalyptus. Medium-bodied, with round and velvety tannins and a long finish. Very well done. Tuscan enologist Luca d'Attoma has turned this place around. Drink now through 2008. 11,600 cases made.", 12.0m, "2002", new Guid("f573a3de-b3d3-4e83-b972-f6635208de00") },
                    { new Guid("a88aa38c-cc71-41b2-a7d3-7a2910a26ea6"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Cartoixa Reserva", "Alluring spice notes of cinnamon and clove add complexity to the ripe plum and sweet vanilla flavors in this rich red. It's balanced and expressive, not overly powerful, but brings you back for another sip. Grenache, Cabernet Sauvignon and Syrah. Drink now through 2012. 1,000 cases imported.", 26.0m, "2000", new Guid("4033b626-e239-4c25-8c12-fd1e139e5280") },
                    { new Guid("4a4c3b3c-1d36-4074-9416-b5be72dba11e"), 6, new DateTime(2004, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Rheingau Rüdesheimer Magdalenenkreuz", "A tantalizing mix of apricot, orange, rose and mineral meshed in a creamy texture, all backed by vibrant acidity. Concentrated and long, with a crispness at the end that will become integrated with time. Best from 2006 through 2015. 1,000 cases made.", 22.0m, "2002", new Guid("1cb48f74-2069-4e7d-afa1-05410d05b143") },
                    { new Guid("ba84fdd4-1481-46ec-8ec7-e2d434e02637"), 3, new DateTime(2013, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Two Left Feet McLaren Vale", "Polished, ripe and generous with its blackberry, mocha and sage flavors, brilliantly focused and elegantly balanced, lingering effortlessly on the seamless finish. Complex and inviting, this should develop beautifully with cellaring. Shiraz, Cabernet Sauvignon and Merlot. Drink now through 2020. 7,800 cases made.", 25.0m, "2011", new Guid("73226680-a8f1-4b02-84a8-64b2239c5880") },
                    { new Guid("4515936b-1a7b-4200-89db-3072446e0c60"), 3, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Rutherford 20th Anniversary", "Another tremendous effort from this producer, supple and harmonious, with rich, fleshy blackberry, black cherry, plum and currant fruit that's tight and concentrated. A hint of mocha, herb and cedar add a wonderful dimension and the structure is excellent, with lively acidity and firm tannins. Best from 2006 through 2012. 2,500 cases made.", 125.0m, "2002", new Guid("24ab7649-5c5c-4e92-a364-1723cf2a2ad1") },
                    { new Guid("38aea980-d84e-4035-94ca-dbab1111943a"), 3, new DateTime(2006, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Churchill Estates", "Very pure-tasting, with an impressive array of mineral and dark fruit flavors. Blackberry and raspberry dominate, with dark plum and boysenberry. Long, fresh finish, with touches of smoke. Drink now through 2012. 19,200 cases made.", 15.0m, "2004", new Guid("20115489-2932-47bb-a2ef-ca3a248a476e") },
                    { new Guid("f73649b4-026e-40a6-9aca-1aae6ea76597"), 3, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Thea's Selection", "Rich and aromatic. Not heavy, but packed with huckleberry, plum and an array of sweet spices that keep sailing through the long, beautifully formed finish. A complete wine that has style and grace. Best from 2007 through 2012. 3,386 cases made.", 32.0m, "2004", new Guid("b2174c79-a0e4-4bc6-9fb0-db41de686e02") },
                    { new Guid("383d2927-cd27-4a2d-815b-a14adb01ecb6"), 3, new DateTime(2008, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Beckstoffer To Kalon Vineyard T6", "Powerful, dark, superrich and deeply concentrated. Massive yet elegant, offering a dense core of mocha-laced currant, blackberry, wild berry and toasty, cedary oak, with an intense, persistent finish. Best from 2009 through 2016. 250 cases made.", 95.0m, "2005", new Guid("173e6cf9-d981-4eee-9b70-59fe8b53af5c") },
                    { new Guid("489a8b08-77a8-4799-8e3c-3973e33741ec"), 3, new DateTime(2008, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Vigneto La Villa", "What a beautiful nose, with flowers, licorice, ripe fruit and fresh leather. Full-bodied, with velvety tannins and a wonderful ripeness of fruit, yet this is balanced and structured. Very smooth. The best wine I have ever had from this producer. Best after 2012. 1,700 cases made.", 65.0m, "2004", new Guid("f291ecd4-b457-492b-b53d-6a3d10a119bf") },
                    { new Guid("3a9392d8-da7b-4de0-abe2-b17aee6d9f26"), 3, new DateTime(2007, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Dundee Hills Estate Selection", "Smooth, round and generous with its red berry-scented cherry and spice flavors, hinting at cinnamon and gravelly notes as the finish lingers effortlessly. Complex and seamless. Best from 2008 through 2015. 6,871 cases made.", 30.0m, "2005", new Guid("eaaa4fc2-ed43-42fb-9f65-17fba766c2aa") },
                    { new Guid("ea4efb92-032f-4a47-962e-bc1c4b5d3714"), 6, new DateTime(2008, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Montmains", "Shows classic Chablis character delivering plenty of mineral, seashore and lemon aromas and flavors, with a little melon and plum. Firm and steely, this is a bit severe now, but balanced. Should develop nicely. Best from 2010 through 2018. 600 cases imported.", 42.0m, "2005", new Guid("d744ff11-08a1-43b1-adb0-35acbd075ff5") },
                    { new Guid("5841d952-f34e-45f2-bd4c-4ec9de21d3b9"), 6, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Domaine", "An apple-scented white, with melon, green plum and mineral flavors. Bright, with a lively structure and a very long finish. Drink now through 2012. 1,000 cases imported.", 25.0m, "2004", new Guid("d744ff11-08a1-43b1-adb0-35acbd075ff5") },
                    { new Guid("771f0e07-e6c3-4de7-beaa-4c1f76d19795"), 3, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bierzo Pétalos", "Thick, fruity and sweet, this international-style red offers plenty of vanilla and toasty oak flavors, with blackberry, cola and licorice notes, supported by plush tannins. Drink now through 2011. 15,000 cases made.", 20.0m, "2005", new Guid("36ef2bed-eb61-4b35-957a-3c3ff859c0cb") },
                    { new Guid("142e50fe-0331-453f-b4f7-f320c3aadb1e"), 6, new DateTime(2015, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valdeorras Viña Godeval Cepas Vellas", "This alluring white delivers a broad range of flavors in a pillowy texture, while crisp, well-integrated acidity maintains the focus. Melon, coconut, spice and smoke flavors mingle harmoniously on the plush palate. The mineral element is fresh and long. Godello. Drink now through 2018. 1,800 cases imported.", 20.0m, "2013", new Guid("26e7670d-9d3a-415a-ab19-0d9366422577") },
                    { new Guid("f49cf4f8-701e-41d7-8987-dd1560f5e367"), 6, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valdeorras Viña Godeval", "Pear, pine and anise flavors are focused and clean in this firm white. Mineral-tinged acidity provides real backbone, with citrus and floral notes lingering on the finish. Drink now through 2011. 1,500 cases imported.", 17.0m, "2006", new Guid("26e7670d-9d3a-415a-ab19-0d9366422577") },
                    { new Guid("ab4444a0-77c6-439a-8bd8-42377270a51f"), 6, new DateTime(2011, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Assyrtiko Santorini", "Juicy and rich, with concentrated pear, apple and white fruit flavors that are backed up by plenty of fresh acidity. The intense finish features mineral notes, joined by sea salt and white pepper. Should turn creamy with time in the cellar. Drink now through 2020. 7,000 cases made.", 24.0m, "2010", new Guid("c995c297-1aba-4b54-a138-9419d6d9a956") },
                    { new Guid("fad5a9c0-06e5-416a-81b7-2dc4a9c9eb81"), 6, new DateTime(2007, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Asirtiko-Athiri Santorini", "A very lush and complex white, with mouthfilling flavors of salted almond, ripe pear, peach and spice. The minerally finish features piecrust notes. Drink now. 2,000 cases imported.", 16.0m, "2005", new Guid("c995c297-1aba-4b54-a138-9419d6d9a956") },
                    { new Guid("ddf211f2-6460-4e32-8786-69df0a486a99"), 3, new DateTime(2010, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley CCS Beckstoffer To Kalon Vineyard", "Offering riveting aromas of red and black cherry, red and black licorice, plum, spice and cedar, this is firm, full-bodied, deep, dense and concentrated on the palate, not wanting to bend much now, but with a muscular thrust of flavors and backbone that bodes well for cellaring. Best from 2012 through 2025. 250 cases made.", 150.0m, "2007", new Guid("173e6cf9-d981-4eee-9b70-59fe8b53af5c") },
                    { new Guid("4f3e17ad-ef59-4027-8a43-5de2b89d7f93"), 6, new DateTime(2007, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Athiri Rhodes Rhodos", "Offers a ripe, spicy aroma, with lush flavors of Fuji apple, pear, hazelnut and custard. There are also some pineapple and tropical fruit flavors in the mix. The fresh, creamy finish echoes the spice. Drink now through 2011. 1,500 cases imported.", 11.0m, "2005", new Guid("cb989909-9216-4a79-99ee-8633b24ac36f") },
                    { new Guid("3fabd96f-9934-4e4d-b0bd-c98861e05210"), 6, new DateTime(2017, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "'T Voetpad Swartland", "Dried lemon peel, fennel seed and honeysuckle notes give this a high-pitched entry, but the core offers up Jonagold apple and melon fruit while the finish pulls in fresh salted butter and verbena details, imparting remarkable range and length. Chenin Blanc, Sémillon, Palomino and Muscat. Drink now through 2024. 280 cases made.", 67.0m, "2015", new Guid("848f87c7-84fb-48d5-9c2d-f78f4068128d") },
                    { new Guid("df79758d-2b3d-4c68-a7ba-39416c574269"), 6, new DateTime(2015, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Palladius Swartland", "Intense, with ripe, lush layers of creamed Jonagold apple, Cavaillon melon and glazed peach fruit, enlivened by dried chamomile, quinine and bitter citrus oil notes. The long finish flows beautifully, with the fruit and floral elements draped over a well-embedded mineral edge. This has depth and energy, and should cellar well. Chenin Blanc, Roussanne, Grenache Blanc, Sémillon, Sémillon Gris, Palomino, Viognier, Clairette Blanche and Verdelho. Drink now through 2022. 750 cases made.", 113.0m, "2012", new Guid("848f87c7-84fb-48d5-9c2d-f78f4068128d") },
                    { new Guid("3c4407a6-6794-4e8c-8606-100b0fb1c2e3"), 3, new DateTime(2007, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Columella Swartland", "Offers a stunning array of blue and black fruit--blackberry, boysenberry, blueberry and currant--that is pure, driven and refined, with licorice, sweet spice, vanilla, mineral and sanguine notes flittering throughout. Despite its concentration, this is remarkably elegant, with superb length and a seamless finish. Welcome to the majors, South Africa. Syrah and Mourvèdre. Drink now through 2013. 670 cases made.", 78.0m, "2005", new Guid("848f87c7-84fb-48d5-9c2d-f78f4068128d") },
                    { new Guid("60759dfb-57e1-4cb8-8f52-fa12cff653ab"), 3, new DateTime(2007, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Roussillon", "Rich and intense, with a meaty aroma and layers of red plum, raspberry, dark cherry and dark chocolate flavors. There's also a pure, minerally spiciness, with an enveloping finish of clove, kirsch and mocha. Carignane, Syrah and Grenache. Drink now through 2015. 400 cases made.", 39.0m, "2003", new Guid("ac2e320a-92e8-48c9-a282-2712ccd2be9b") },
                    { new Guid("15d46742-9488-41ca-a014-73e56ff007f4"), 3, new DateTime(2007, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cornas Les Vieilles Vignes", "Really zesty, with lots of red currant and bramble notes bouncing ahead of dark olive and sage flavors. The structure is briary on the finish, with an encore of red fruit and mouthwatering acidity. Drink now through 2017. 1,415 cases made.", 50.0m, "2004", new Guid("a80fb5aa-5d36-4373-9da7-40119881b817") },
                    { new Guid("c41323b5-16aa-4db5-a9b7-8302251b74a1"), 5, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Blue Top", "Elegant and expressive, this delivers floral, pear and toffee flavors matched to a creamy texture. Beautifully integrated, this lingers nicely on the mineral finish. Drink now through 2010. 35,000 cases imported.", 35.0m, "NV", new Guid("1d68a14d-748c-407f-8a08-0367b3f2c1de") },
                    { new Guid("8cac2f9b-d30b-4bfc-9333-6c392e7ea87c"), 6, new DateTime(2017, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Federspiel Trocken Wachau Steinriegl", "A fruity style, boasting apricot, ripe apple, white pepper, mineral and floral notes, this is harmonious and fresh, leaving a sapid, mineral aftertaste.  Drink now. 2,083 cases made.", 35.0m, "2015", new Guid("50594422-c85f-491a-9be6-e29b917d2649") },
                    { new Guid("68542952-0cf9-4f43-a715-b98899b99c59"), 6, new DateTime(2007, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Federspiel Trocken Wachau Weissenkirchen Steinriegl", "Ripe, full-bodied and succulent, with loads of peach, cherry and berry flavors. Plenty of ripe apple and citrus. The flavors linger with mineral and smoke on the finish. Drink now through 2015. 2,083 cases made.", 28.0m, "2005", new Guid("50594422-c85f-491a-9be6-e29b917d2649") },
                    { new Guid("a748c220-00ff-443d-b671-51d55fe966c2"), 5, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Extra Dry Prosecco di Conegliano-Valdobbiadene", "A delicate, well-balanced Prosecco, showing a good blend of baked apple, sour lemon and grapefruit, with a lightly creamy mineral texture on the palate. Medium froth, with some body and a pleasant lemon and mineral finish. Drink now. 50,000 cases made.", 12.0m, "NV", new Guid("b355273a-c0e2-4c54-a362-59ba5f3998e3") },
                    { new Guid("accf0e6d-212c-49d6-8414-3fc3d8553ad2"), 3, new DateTime(2013, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo La Rosa", "Fine harmony and sweet cherry and berry flavors are the hallmarks of this rich Barolo. Licorice, spice and tobacco flavors add depth as it stays focused and persistent through the finish. The tannins are ripe and well-integrated. Best from 2015 through 2033. 1,500 cases made.", 96.0m, "2008", new Guid("ee9dccee-c623-4430-b04e-759be7fc5e7d") },
                    { new Guid("1a6854ba-2cd3-4e43-90e0-8b5a9a9b2a21"), 6, new DateTime(2018, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Skerpioen Swartland", "Intense and still youthfully tight, with verbena and white peach notes leading the way for yellow apple, chamomile, white ginger and jasmine flavors. A lovely quinine streak is well-embedded, giving this tension and vibrancy through the finish. Chenin Blanc and Palomino. Drink now through 2024. 406 cases made.", 60.0m, "2016", new Guid("848f87c7-84fb-48d5-9c2d-f78f4068128d") },
                    { new Guid("7fb3df46-46c1-4ae2-a4b2-415c6ed71dec"), 3, new DateTime(2007, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera Piemonte Briccotondo", "Very plummy and grapey on the nose, with hints of chocolate. Full-bodied, soft and succulent, with lots of flavor and a long, long finish. Delicious. Drink now. 50,000 cases made.", 13.0m, "2006", new Guid("ee9dccee-c623-4430-b04e-759be7fc5e7d") },
                    { new Guid("14c338c3-24f7-4d37-bacd-ac3eb1c29e4b"), 3, new DateTime(2008, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana", "Rich, almost decadent aromas of crushed berry, meat and mole. Turns floral. Full-bodied, with velvety tannins and a long, caressing finish. A beauty. Best after 2011. 7,900 cases made.", 28.0m, "2006", new Guid("8c153499-7fa5-44fc-a074-d0eea93479f6") },
                    { new Guid("2ce17775-b9e6-4882-ad07-07a81d56218e"), 3, new DateTime(2008, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes de Francs", "Shows lovely black currant, floral and black licorice notes. Full-bodied, with velvety tannins and a tight bead of fruit and mineral throughout. Has a long finish. Best after 2009. 10,830 cases made.", 18.0m, "2005", new Guid("050cbac8-f1ad-4cc4-911a-9492f93a289c") },
                    { new Guid("7da1b69b-19c4-401b-a282-c3b754ad7c35"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Maremma Toscana Mongrana", "This ripe red boasts flavors of cherry, cedar and herb matched to a medium-bodied frame. The acidity and tannins are in the correct proportions, offering a lingering finish. Sangiovese, Cabernet Sauvignon and Merlot. Best from 2015 through 2024. 10,800 cases made.", 22.0m, "2010", new Guid("d6b50de9-039d-49d5-9d9a-fbe72ce606c5") },
                    { new Guid("9d682c52-42af-418d-9573-c9bf4262a6c1"), 3, new DateTime(2008, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Clos des Soutyères", "A muscular style, offering a concentrated, intense mix of crushed red fruit and bittersweet chocolate flavors, with big structure and big tannins. Mint, tannins and spice fill the long, intense finish. Tempting now. Best from 2009 through 2014. 1,000 cases made.", 33.0m, "2006", new Guid("8fb536e0-d257-4e46-ad6c-0b995d149ca0") },
                    { new Guid("a3f4ebec-9477-4e8a-a05f-f366ed65a79a"), 3, new DateTime(2008, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Uncut", "Polished to a gleam, this fleshy wine shows an expressive array of red cherry, raspberry, Santa Rosa plum and sweet spices, mingling effortlessly through the plush finish. For all that, it actually feels like there's some restraint along the way. Drink now through 2016. 4,000 cases made.", 24.0m, "2006", new Guid("52d0ddc7-3408-48d2-8e00-9296dbc34be2") },
                    { new Guid("bdb0ab11-8d9f-4687-b79b-05a5afed15e3"), 3, new DateTime(2008, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Crianza", "Plum, chocolate, tobacco and leaf flavors mingle in this round red. Shows good ripeness and concentration, but maintains a traditional style. A bit austere, but balanced. Drink now through 2012. 3,000 cases imported.", 20.0m, "2004", new Guid("2e0b7c85-b109-46e4-935d-9bcc663acf0e") },
                    { new Guid("400b2481-b631-4446-bee9-b3aa3e7f194f"), 6, new DateTime(2010, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Grigio Venezia-Giulia", "Offers pineapple pie, vanilla cream and honey on the nose. Full-bodied, with a very ripe, almost decadent tropical fruit character and lots of rich fruit. Drink now. 3,500 cases made.", 19.0m, "2008", new Guid("224ebd32-cfde-43e7-8733-2862d441d41d") },
                    { new Guid("ebf941c5-66ef-4fcb-ba99-6f38a161031e"), 6, new DateTime(2008, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Grigio Collio", "A full-bodied style with lots of character, from peach tart to lemon seed to mineral. Crisp and lively. Drink now. 12,900 cases made.", 19.0m, "2007", new Guid("224ebd32-cfde-43e7-8733-2862d441d41d") },
                    { new Guid("99543252-a903-4db7-bf94-c1f5f781dd14"), 3, new DateTime(2008, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Rutherford", "Intense and concentrated, big and expressive, with layers of ripe currant, plum, sage, pencil lead and black cherry, all flowing rather seamlessly. Tightly focused, this is rich without being heavy, ending with a long, persistent finish. Drink now through 2014. 4,750 cases made.", 85.0m, "2005", new Guid("b31b0a7e-89a1-4fbb-ad23-ac507f34eb8f") },
                    { new Guid("ae74a2b1-354b-4451-81c5-2679994985f8"), 6, new DateTime(2016, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Dundee Hills Evenstad Reserve", "Focused and expressive, this is layered with wet stone, tangy citrus, pear and green guava flavors that swirl through an expansive finish, gaining momentum and extra dimensions with each sip. Drink now through 2024. 2,000 cases made.", 55.0m, "2014", new Guid("e96f83e9-fe8c-4b66-b3f6-cf52ef3bd9ec") },
                    { new Guid("e5fd1687-c816-4189-92e4-97873cba5338"), 3, new DateTime(2011, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Dundee Hills Grace Vineyard", "A velvety, focused and extraordinarily expressive, offering a lovely mouthful of plum, blueberry and mineral flavors that gain momentum on the medium-weight finish, persisting enticingly. The structure seems to melt away, and the flavors just hang in there. Drink now through 2018. 250 cases made.", 125.0m, "2008", new Guid("e96f83e9-fe8c-4b66-b3f6-cf52ef3bd9ec") },
                    { new Guid("8b23ba4b-f987-46fb-9b9c-7f7be1e1c3de"), 3, new DateTime(2008, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Evenstad Reserve", "Lithe in texture, with a distinctive range of red cherry, black cherry, cinnamon and mocha flavors that ride smoothly over a graceful frame, lingering nicely on the clear, expressive finish. Drink now through 2015. 6,500 cases made.", 58.0m, "2005", new Guid("e96f83e9-fe8c-4b66-b3f6-cf52ef3bd9ec") },
                    { new Guid("ce4da795-98b1-440e-8261-d9e24a8b030d"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Giorgio Primo", "Dark and brooding, with intense aromas of lead pencil, blackberry, black cherry and licorice. Full-bodied, with velvety, polished tannins, followed by vanilla, berry and currant on the finish. Powerful and complex. Built like a brick house. Merlot, Cabernet Sauvignon and Petit Verdot. Best after 2013. 1,750 cases made.", 65.0m, "2007", new Guid("8c153499-7fa5-44fc-a074-d0eea93479f6") },
                    { new Guid("0ded057f-6d3c-47c6-bb80-cddf08f0e16c"), 3, new DateTime(2008, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Howell Mountain", "Dense and powerful, yet wonderfully rich, supple and well-structured at the core. Offers aromas of blackberry, fresh loamy earth and smoky toast, with ripe yet focused wild berry, mocha-espresso and white pepper flavors. Drink now through 2012. 950 cases made.", 45.0m, "2005", new Guid("59b77a64-21d0-438c-ad3b-7e4cd96942e7") },
                    { new Guid("c3d427eb-7946-49b5-b2f7-c59f3612c412"), 3, new DateTime(2008, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Sonoma Coast Old Lakeville Road", "Ripe and grapey, with rich, elegant spice, pepper and juicy, dusty wild berry flavors that are sleek and focused, ending with a long, persistent finish. Drink now through 2012. 1,155 cases made.", 30.0m, "2006", new Guid("9b645f7c-7369-44ee-a16c-0d230ea8d5e6") },
                    { new Guid("75b520ed-c2be-4bee-a79c-acd1d13a4c00"), 3, new DateTime(2008, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Les Ruffes", "This rich, refined red is very pure-tasting, showing mineral, smoke, dried cherry and sandalwood flavors, with powerful undercurrents of currant and French roast. The long finish echoes spice and roasted meat. Best from 2009 through 2014. 1,000 cases imported.", 16.0m, "2006", new Guid("6e537c52-952c-400e-b380-bd8e7af37ecd") },
                    { new Guid("c47be48e-b793-4766-972e-a2581634d1a2"), 3, new DateTime(2008, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Dão Callabriga", "A ripe, rich and powerful red, with concentrated flavors of blackberry, dark plum, smoke and spice. The long finish has blueberry and dark chocolate. Drink now through 2012. 10,000 cases made.", 16.0m, "2005", new Guid("29fdc151-3bbc-45e8-ac5f-b24e2d994237") },
                    { new Guid("801ab05a-dc5f-45dd-ad64-cf624017f9f5"), 3, new DateTime(2008, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cahors Croix du Mayne", "There's a rich, spicy aroma, and this is dripping with concentrated flavors of kirsch, dark plum and dark cherry. Shows plenty of dark chocolate and raspberry ganache on the tannic finish. Drink now through 2013. 12,000 cases made.", 18.0m, "2005", new Guid("a95b9942-f8ae-495e-a378-438e638bc53e") },
                    { new Guid("b5c068b1-354d-4c05-83a0-2f414a05794d"), 3, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sicilia Santagostino Baglio Soria Red", "Aromas and flavors of bacon fat and mesquite smoke mix with ripe black cherry, licorice snap, mocha and mint in this expressive red, with fine-grained tannins and juicy acidity. Elegant through to the aromatic, coffee-tinged finish. Nero d'Avola and Syrah. Drink now through 2024. 70,000 cases made.", 23.0m, "2011", new Guid("6d371831-e36d-49f2-abe6-ebd4ebb16ebb") },
                    { new Guid("ede2051b-363f-4c7c-b27b-c97d65d1f8e0"), 3, new DateTime(2008, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nero d'Avola-Syrah Sicilia Santagostino Baglio Soria", "Shows focused aromas of boysenberry, blackberry and light vanilla. Full-bodied, with soft, round tannins and a long, flavorful finish. Beautiful and juicy. Drink now. 45,830 cases made.", 28.0m, "2006", new Guid("6d371831-e36d-49f2-abe6-ebd4ebb16ebb") },
                    { new Guid("f3843733-be7c-4624-9aae-b21b329a8f3b"), 3, new DateTime(2008, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Aix Columbia Valley", "Ripe, focused and beguiling for the espresso and caramel notes that swirl through the dark plum and currant fruit, remaining focused and vibrant through the long finish. Time in the cellar should polish the tannins. Cabernet Sauvignon, Syrah and Mourvèdre. Best from 2009 through 2015. 1,900 cases made.", 37.0m, "2005", new Guid("682074d4-7be0-4a84-a5a2-cb2b9d474d04") },
                    { new Guid("3b7eb849-d126-4363-ab12-8dc41e64bc0f"), 6, new DateTime(2008, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Venezia-Giulia Vintage Tunina", "Really complex, with apple tart, apricot, lavender, hints of cream and toasty oak. Full-bodied, with loads of fruit and character. There's masses of spicy mineral and fruity character, yet this is refined and balanced. Gorgeous. Sauvignon, Chardonnay, Ribolla Gialla, Malvasia and Picolit. Drink now. 5,800 cases made.", 80.0m, "2006", new Guid("27b62897-cf65-41e5-87c6-b58c33efe8b0") },
                    { new Guid("8924788b-4838-482e-80b1-d52d137e9550"), 3, new DateTime(2008, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toro San Román", "Alluring plum, blackberry, cola and spice cake flavors are rich and balanced in this expressive Spanish red. There's plenty of structure, but it doesn't get in the way of the flavors, and the spicy, floral finish is long and fresh. Best after 2009. 6,100 cases made.", 62.0m, "2004", new Guid("23cb7e45-ce31-4d96-b35c-095b1c54bee9") },
                    { new Guid("a372cc8f-35b9-41ba-a41a-89b8dcaaf712"), 6, new DateTime(2008, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kremstal Edition Chremisa", "A ripe, full-bore style, with golden raisin, baked peach, cream and spice. Shows lots of tropical fruit notes as well, including mango and guava. The rich finish features nutmeg and crème fraîche. Drink now through 2015. 1,250 cases made.", 18.0m, "2006", new Guid("76b749f3-7434-40a8-8999-e7a24a9f487a") },
                    { new Guid("c48cf6ea-b5e5-48ce-b7a5-5c5c86becef2"), 3, new DateTime(2008, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec La Consulta Reserva", "Rich and dark, but pure, with nice drive to the beam of raspberry, plum sauce and macerated black currant fruit. Creamy tannins and a long, well-integrated finish just sail through, with fine buried minerality. Drink now through 2014. 1,400 cases made.", 30.0m, "2006", new Guid("61ad6bbb-196f-49ec-b14d-655afeb4a074") },
                    { new Guid("92af3590-4d17-4f66-abf3-af8c472399b4"), 3, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Eastern Australia The Reserve", "Bright, lively, harmonious and generous. A sleek mouthful of blueberry, currant and plum flavors, neatly packaged with hints of cream and spice as the finish sails on. Drink now through 2015. 50,000 cases made.", 11.0m, "2005", new Guid("7ebb8337-026e-4f2d-971b-b3b518f3541b") },
                    { new Guid("27af2b7a-8a6e-4ba0-8ecc-ab054f37c77d"), 3, new DateTime(2018, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Yakima Valley Signature", "Polished and gracefully complex, with refined blackberry, espresso and smoked pepper flavors that glide along toward refined tannins. Drink now through 2025. 700 cases made.", 46.0m, "2015", new Guid("013149c5-2243-4e77-8373-55675f5eb6b2") },
                    { new Guid("ac72f39b-6752-49bc-85e4-55395704eaf7"), 3, new DateTime(2007, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "D2 Columbia Valley", "Ripe, firm and beautifully focused, a complex mouthful of currant, plum, cedar and autumn leaf flavors that glow enticingly through the long, long finish. Cabernet Sauvignon, Merlot, Cabernet Franc and Petit Verdot. Drink now through 2012. 2,600 cases made.", 36.0m, "2004", new Guid("013149c5-2243-4e77-8373-55675f5eb6b2") },
                    { new Guid("08f23077-7780-460c-9a20-3d75aa34ae79"), 6, new DateTime(2006, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kamptal Kamptaler Terrassen", "Rich and focused aromas and flavors of ripe apple, peach, gooseberry and ruby red grapefruit give this plenty of punch and power. Long finish of spice, pepper and vanilla. Built to age. Drink now through 2015. 2,500 cases made.", 21.0m, "2004", new Guid("8df5391a-5efd-4477-a5cd-71a5b250b3c3") },
                    { new Guid("8cea87f6-7a20-4aef-bbde-18e820c7a1b9"), 3, new DateTime(2017, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Salta", "Refined and rich-tasting, with effusive minerality to the red berry and cherry flavors, lathered with plenty of creamy accents. Pepper and cigar box notes show on the long and complex finish. Drink now through 2022. 27,300 cases made.", 25.0m, "2015", new Guid("8c944b36-96aa-4423-9253-9cc34a25563b") },
                    { new Guid("007a9f74-2727-4b2c-a581-3d38c60ea0c1"), 3, new DateTime(2016, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Salta Auténtico", "Fresh, rich and juicy, with lively raspberry, cherry and red plum flavors backed by fresh, crunchy acidity. Lengthens out nicely midpalate, showing mineral and pepper accents. Hints of slate and tangerine emerge on the complex finish. Drink now through 2020. 3,500 cases made.", 25.0m, "2014", new Guid("8c944b36-96aa-4423-9253-9cc34a25563b") },
                    { new Guid("016ddb1e-59dd-4538-b6ab-42bd6cb26d1b"), 3, new DateTime(2008, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Calchaquí Valley", "Very inviting, with crushed plum and boysenberry aromas that lead to a lush-textured palate of warm figs and currant paste backed by mouthwatering hints of mesquite, mineral and Turkish coffee. The long finish really holds your interest. Drink now through 2011. 14,250 cases made.", 25.0m, "2006", new Guid("8c944b36-96aa-4423-9253-9cc34a25563b") },
                    { new Guid("75841c34-b953-4da7-b341-3f8094e1dae1"), 3, new DateTime(2006, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Calchaquí Valley", "Exotic aromas of fig and hoisin sauce give way to a rich, creamy-textured palate full of dark plum, blackberry, coffee and chocolate ganache. Has solid grip on the finish, with a loamy hint in the background. Malbec, Cabernet Sauvignon and Tannat. Drink now through 2008. 3,000 cases made.", 25.0m, "2004", new Guid("8c944b36-96aa-4423-9253-9cc34a25563b") },
                    { new Guid("ea48c6ae-b08f-44de-b9df-dd826867a79f"), 3, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Punto Final Reserva", "Offers lovely notes of freshly crushed blueberry and boysenberry fruit, along with integrated mocha, mineral and floral notes. Creamy mouthfeel and a long, fruit-stained finish. Drink now through 2008. 3,000 cases made.", 18.0m, "2004", new Guid("ea3a7cae-f3ee-4767-a86d-9d877482e577") },
                    { new Guid("1f4a5294-c4f2-4f3e-b0fc-93ba6973c953"), 3, new DateTime(2006, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Premières Côtes de Blaye Cuvée Viva", "Wow. Just as I remember it from barrel. This is really beautiful on the nose, with plum, blackberry and spice. Medium- to full-bodied, with a solid core of silky tannins and a medium-to-long finish. All in finesse. A real beauty. Delicious. In harmony. Best after 2007. 4,000 cases made.", 14.0m, "2003", new Guid("077b6641-5c23-4f44-84c9-caf461a191a9") },
                    { new Guid("da0f7218-ae06-46dd-976c-d23c8c8ec60d"), 3, new DateTime(2006, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Stellenbosch", "Exotic and thoroughly ripe, this offers a gorgeous display of blackberry, blueberry, fig and currant fruit backed by cocoa, graphite and mineral flavors. Powerful and rich but never heady, the pure fruit just sails on through the finish. Drink now through 2009. 212 cases made.", 65.0m, "2003", new Guid("275afde4-a371-4a33-9f56-cf7280334d2c") },
                    { new Guid("19437ce8-5139-43a0-ab63-bcbbf79f2657"), 3, new DateTime(2006, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montsant", "Pretty raspberry and strawberry flavors gain depth from floral, mineral and herbal accents in this plush, well-structured red. Balanced and deceptively powerful. Garnacha, Merlot, Syrah and Cabernet Sauvignon. Drink now through 2014. 5,000 cases made.", 25.0m, "2003", new Guid("81edbb12-1c1d-46e1-8a14-1451a6022416") },
                    { new Guid("328d061c-8eee-48ca-9485-cec783527367"), 3, new DateTime(2006, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos des Lambrays", "An opulent, exotic red. Packed with Asian spices, sweet blackberry and raspberry ganache notes, a swath of dense tannins and new oak for support. Vibrant, and sings now, but needs time to fulfill its potential. Great length. Best from 2009 through 2025. 400 cases imported.", 115.0m, "2003", new Guid("f3764e13-df71-4a12-aaab-0ad84202b2f6") },
                    { new Guid("7c03aa16-8d43-44b4-a35d-959b23a85de1"), 6, new DateTime(2009, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kamptal Langenloiser Berg-Vogelsang", "A powerful and racy style, with notes of anise, citrus, glazed apricot and spice. Very stony and minerally, with notes of chamomile, on the taut finish. Needs some time in the cellar. Best from 2010 through 2015. 2,667 cases made.", 38.0m, "2007", new Guid("8df5391a-5efd-4477-a5cd-71a5b250b3c3") },
                    { new Guid("390502de-6176-4a2a-8b47-8086231286dd"), 3, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Uco Valley Alfa Crux", "Plush and rich, with concentrated flavors of dark plum, porcino, baker's chocolate and blackberry. This has a core of dried beef and Asian spice notes that linger on the powerfully creamy finish. Drink now through 2018. 1,250 cases made.", 54.0m, "2010", new Guid("367b51f5-3762-4f4c-a733-77493bf55199") },
                    { new Guid("6c1b52bb-83d3-4f1d-82c0-6375b9507871"), 3, new DateTime(2006, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz-Viognier Victoria", "A wine of remarkable elegance, aromatic, with spicy, floral overtones to the pure berry and plum flavors, lingering with hints of black pepper and licorice against refreshing acidity on the finish. Best from 2007 through 2014. 1,745 cases imported.", 19.0m, "2004", new Guid("ecdc8a54-6d77-435a-baf8-4d23c49ff5cf") },
                    { new Guid("15594d2c-e47c-46ff-aefb-56ab4b7c11df"), 3, new DateTime(2006, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos de Tart", "Aromas of tar and smoke, with very pure, concentrated blackberry and spice notes underneath mark this exotic, seductive red. Silky and complex, it caresses the palate. It needs a little time to absorb the oak, but this is long and has great potential. Best from 2009 through 2030. 1,250 cases made.", 200.0m, "2003", new Guid("bbb5a9bf-e213-4edd-90e5-d7aecc23f5c1") },
                    { new Guid("89894c86-a897-48b7-9038-20329e2e7f42"), 6, new DateTime(2006, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Rheingau Johannisberg V", "Everything comes together here--the site, the grape, the grower--to create something special. Alluring aromas and flavors of pear, peach and a heady blossom essence mark this Riesling. It's balanced, seamless and very, very long on the finish. Drink now through 2018. 1,000 cases made.", 17.0m, "2004", new Guid("572fd3df-10fe-4559-bbe3-09d7f89a6794") },
                    { new Guid("e83a7b4c-9e07-421b-a28e-98db7e1e2905"), 6, new DateTime(2006, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Gris Willamette Valley", "Lithe, supple and immensely appealing for its creamy texture and almond-scented pear and peach flavors. It all lingers beautifully, too. Drink now. 11,100 cases made.", 18.0m, "2005", new Guid("846143f2-8418-487a-a31b-71e89d7cea42") },
                    { new Guid("66b92291-bf2f-4eb2-a687-257934f07523"), 6, new DateTime(2007, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Greco di Tufo Loggia della Serra", "Shows piecrust, apricot and apple, with hints of mineral. Very aromatic. Full-bodied, with lots of fruit and a long, flavorful finish. A bright, fruity white. Drink now. 15,000 cases made.", 22.0m, "2005", new Guid("cb3d1a16-52ae-452a-ae8e-e40921f32aa2") },
                    { new Guid("0f2edb59-f38d-417b-b686-6757bdb681f2"), 6, new DateTime(2006, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Falanghina Irpinia", "Peaches and apples follow through to a full-bodied palate, with lemon, honey and fruit flavors. Long. Drink now. 15,000 cases made.", 15.0m, "2004", new Guid("cb3d1a16-52ae-452a-ae8e-e40921f32aa2") },
                    { new Guid("7084fc53-a824-4804-9afb-89ac92a6a274"), 3, new DateTime(2006, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône Mon Coeur", "Dark coffee and game aromas herald this burly red, with dark plum, hoisin sauce, chestnut, tar and <i>garrigue</i> flavors that pump through the structured finish. A very big Côtes du Rhône. Best from 2007 through 2009. 4,000 cases made.", 20.0m, "2004", new Guid("6c76a1e6-32d0-4151-860c-53e9341c3e65") },
                    { new Guid("f5fde60f-0f6a-4d2e-8a27-ac3488996d1a"), 3, new DateTime(2006, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley Coach House Block Rare Single Vineyard", "This ultrarich and decadent Shiraz comes from old vines in Greenock, a prime growing area in the Barossa. They make heady wines that don't weigh you down like some big Shiraz can. This one has zippy acidity to balance the generous layers of black cherry, boysenberry and black plum flavors, shaded with bittersweet chocolate and smoke notes as the finish becomes increasingly plush. Best from 2009 through 2020. 1,050 cases made.", 100.0m, "2004", new Guid("7d91c35a-2074-4b3c-a493-399bbd89f70b") },
                    { new Guid("a6747b9a-0dd6-455a-a77d-7ff238a01ee5"), 6, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Gamble Ranch Vineyard", "This big, rich, flamboyant Chardonnay delivers a wallop of flavor, with tiers of smoky, toasty oak, buttery pear and fig notes and hints of citrus and fresh earth. Yet for all its size and dimension, there's a measure of elegance. The finish sails on, revealing extra flavor nuances. An excellent combination of a great Chardonnay vineyard and a winemaker who knows the site. Drink now through 2010. 1,275 cases made.", 40.0m, "2004", new Guid("4694d2ee-aa83-47e5-879a-f5338021585d") },
                    { new Guid("def36851-e60e-43fa-8f96-8f8aa9deb3ed"), 6, new DateTime(2006, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Dry Creek Valley Home Ranch", "A bold, opulent style, uncharacteristically complex and distinctive for this appellation. Layers of rich fig, apricot, pear and melon are deep and perfumed, finishing with a long, detailed aftertaste that showcases the wine's depth of flavor while maintaining a graceful mouthfeel. Drink now. 1,300 cases made.", 25.0m, "2004", new Guid("4694d2ee-aa83-47e5-879a-f5338021585d") },
                    { new Guid("b3e2bc50-56f3-47e5-b8bc-dfa8ff4e20ea"), 3, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alfa Crux Uco Valley", "A very flashy, modern-style wine, with layers of mocha and spice-infused toast leading the way for boysenberry, blackberry and fig fruit. Shows additional tar, mineral, graphite and even violet notes through the rich but racy finish. Tempranillo, Malbec and Merlot. Drink now through 2010. 3,300 cases made.", 42.0m, "2002", new Guid("367b51f5-3762-4f4c-a733-77493bf55199") },
                    { new Guid("45cd6dca-692d-4bb4-bab7-391335396073"), 3, new DateTime(2006, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Martinborough Te Muna Road Vineyard", "Lithe and distinctive, with a definite minerality and a touch of loamy earth to the ripe cherry and currant flavors, lingering beautifully on the generous finish. Tannins are well-submerged and its sense of delicacy is welcome. Drink now through 2011. 1,500 cases made.", 40.0m, "2004", new Guid("3e9f4c8b-56bb-4cad-bd01-f254160769f3") },
                    { new Guid("374e2bb1-4a48-473e-80a8-894ff83479f0"), 3, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Altano Reserva", "An intense, powerful red, yet lush and juicy, with deliciously pure flavors of red plum, smoke and cherry. Silky tannins, with a long finish of cream and spice. Touriga Franca and Touriga Nacional. Drink now through 2012. 2,300 cases made.", 18.0m, "2003", new Guid("892b1ac0-8ddd-43f1-a4c0-a8c3ddc2dd78") },
                    { new Guid("804bf452-3768-44e2-b758-7c7678d992ff"), 6, new DateTime(2006, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montlouis Sur Loire Les Choisilles", "Very precise, with a beam of green almond, fig, pear tartine and green tea flavors that races to a long, perfumy finish. Concentrated but elegant, with impressive length. Singing today, but fine for cellaring as well. Drink now through 2011. 1,330 cases made.", 30.0m, "2004", new Guid("fa3d391a-42bd-4ae1-b6bf-448d1658a89f") },
                    { new Guid("e2549f98-9af2-4bf9-8828-7d0f48fce164"), 3, new DateTime(2010, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Yakima Valley Red Willow Vineyard Chapel Block", "Warm, inviting and impressive for the purity of its plum, blackberry and currant fruit, sailing easily over a bed of fine tannins and finishing silky and seamless, with hints of espresso and violet. This has extraordinary balance and finesse without sacrificing an ounce of power. Drink now through 2016. 359 cases made.", 45.0m, "2008", new Guid("7bf63eed-8f7a-4a8e-82ae-4927c623f3a2") },
                    { new Guid("bfa2f500-f8f8-4755-8f47-3eb3ba63b9d1"), 3, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Columbia Valley Ex Umbris", "Supple, generous and beautifully focused to display its cherry, blackberry and dusky spice flavors. Not a big, blatant red, but a wine that shows finesse and refinement without losing any of its ripe flavors. Drink now through 2015. 3,100 cases made.", 24.0m, "2005", new Guid("7bf63eed-8f7a-4a8e-82ae-4927c623f3a2") },
                    { new Guid("5bab3ba2-b2d7-4339-b7f5-995b55469519"), 3, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bierzo El Castro de Valtuille", "A juicy, balanced red, with alluring aromas of wild berry and wet stone that carry through on the palate. Has intensity, yet remains graceful, with spicy and floral accents to the lively fruit. Drink now through 2012. 25,000 cases made.", 25.0m, "2004", new Guid("ba89a443-f4be-45c3-a023-6d55ac94d4ef") },
                    { new Guid("02fc4601-972c-4ad7-8dd3-a03bb500dc72"), 5, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Blanc de Noirs Napa-Mendocino-Sonoma Counties", "A rich, vibrant mouthful of a bubbly, with bold, complex aromas of cinnamon, yeasty vanilla, apple and cherry, with subtle spicy flavors that build to a fresh hint of minerality on the finish. Drink now through 2010. 8,000 cases made.", 35.0m, "2003", new Guid("d9a1aa62-f74f-4260-859d-e5d23697c8f6") },
                    { new Guid("053977c5-8cb8-4765-9c34-df0c072ac032"), 3, new DateTime(2007, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Moreccio", "A perfumed red, with currant and Indian spices on the nose. Full-bodied, with firm tannins and a tight structure. Long and caressing. Sangiovese, Merlot and Cabernet Sauvignon. Best after 2010. 3,000 cases made.", 19.0m, "2005", new Guid("46fc859b-d06e-495c-9339-2088909fd98e") },
                    { new Guid("cbc0dfa8-1ad2-41f3-85d1-142460229f09"), 6, new DateTime(2007, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Western Cape", "A real live-wire, with crackling gooseberry, lemon zest, sea salt and floral notes followed by a long, tangy finish. Superdelicious. Drink now. 2,750 cases made.", 18.0m, "2006", new Guid("cceab2b6-5792-4c15-a891-e41f7da477b3") },
                    { new Guid("11c79438-e8b8-4cf2-9bb6-dd5e2b21cc35"), 3, new DateTime(2007, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Impostor California", "As exotic as it is potent, with deep blackberry liqueur, sandalwood and wild game aromas and rich, ripe cassis, blueberry and licorice flavors that power on toward the bold, spicy finish. Zinfandel, Mourvèdre, Petite Sirah and Syrah. Drink now through 2012. 1,143 cases made.", 32.0m, "2005", new Guid("69a80f4d-cbec-4d2a-9951-45f6c62194d8") },
                    { new Guid("7340ce3d-0144-4efd-812c-f4be5b8ac570"), 3, new DateTime(2013, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Triple C Maipo Valley", "A powerful red, with a surplus of dark plum, blackberry and dark currant flavors that are pure and focused, wrapped in layers of vanilla and cream. The long finish is filled with dark chocolate and wild herb notes. Cabernet Franc, Cabernet Sauvignon and Carmenère. Drink now through 2020. 3,100 cases made.", 40.0m, "2008", new Guid("62d6d1bd-ab44-4ad3-81cb-edb00d240f1e") },
                    { new Guid("d0cd9950-f0f2-40e3-9169-924e92070f30"), 3, new DateTime(2007, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley Medalla Real Special Reserve", "Densely packed, with fig, currant and blackberry flavors, all wrapped up with coffee, bittersweet cocoa powder and loam notes. Long, ripe, muscular finish lets the notes of dark fruit, earth and grilled sage linger. Drink now through 2011. 22,435 cases made.", 19.0m, "2004", new Guid("62d6d1bd-ab44-4ad3-81cb-edb00d240f1e") },
                    { new Guid("0e17c2ed-a372-4580-97f1-8b17a604273a"), 6, new DateTime(2007, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Clare Valley Southern Sisters Reserve", "Balances explosive apple, peach and mineral flavors on a razor's edge of lacy acidity, yet remains remarkably open in texture. Tempting already, but has miles to go. Drink now through 2016. 5,000 cases imported.", 16.0m, "2006", new Guid("6dca5933-db73-4249-b36f-f5630f29b1dc") },
                    { new Guid("392f8dda-e80e-41c3-9caa-834efc831543"), 6, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vouvray Demi-Sec Le Mont", "This tilts toward the flattering side of off-dry, showing a plump feel to the core of creamed pear, white peach and yellow apple fruit. Lovely verbena, honeysuckle and mineral accents keep this balanced and driven, with a long finish that lets the fruit echo. Drink now through 2032. 2,000 cases made.", 44.0m, "2016", new Guid("b2361895-aff9-4af7-b5d2-de03e84309a8") },
                    { new Guid("1291212a-f76f-4cff-9b52-896bbe5ec6b3"), 2, new DateTime(2011, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vouvray Moelleux Clos du Bourg Première Trie", "This is gorgeous, with notes of ginger, orange, clementine, white peach and green fig all bouncing off one another, but working harmoniously through the richly detailed finish. A lingering green tea note hangs on for added length as this puts on weight with air. Drink now through 2025. 760 cases made.", 69.0m, "2009", new Guid("b2361895-aff9-4af7-b5d2-de03e84309a8") },
                    { new Guid("ebc1f2ed-bd92-46a8-9087-24d573ea0b8b"), 6, new DateTime(2007, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vouvray Demi-Sec Clos du Bourg", "Shows very flattering ginger and peach aromas, but tightens up quickly on the palate thanks to its nervy acidity, which keeps the quince, persimmon and marzipan notes in reserve. Superlong finish. Best from 2008 through 2018. 1,740 cases made.", 35.0m, "2005", new Guid("b2361895-aff9-4af7-b5d2-de03e84309a8") },
                    { new Guid("01138ca8-2a60-4988-aafe-97c4ff1671f7"), 6, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Godello Valdeorras Louro", "Peach, quince and lime flavors mingle with notes of lemon cream, heather and grapefruit in this expressive white. The texture is broad, but crisp acidity keeps this focused. Drink now. 1,098 cases imported.", 24.0m, "2017", new Guid("8c7d2353-a8ba-4c9e-a607-5219100d1c3f") },
                    { new Guid("d12f67fd-9dfb-4995-b533-0a31380755a0"), 6, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valdeorras As Sortes Val do Bibei", "Distinctive and ambitious. Toasty, smoky aromas and flavors overlay pear and mineral notes in this muscular, austere white. Gives up an easy fruitiness for concentration and intensity, yet remains balanced and rooted in place. Drink now through 2010. 1,000 cases imported.", 30.0m, "2005", new Guid("8c7d2353-a8ba-4c9e-a607-5219100d1c3f") },
                    { new Guid("9505322f-6526-4525-94c2-3c9985db29bf"), 3, new DateTime(2007, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Rutherford Edizione Pennino", "Rich and loamy, with savory wild berry and brambleberry fruit that's shaded by pretty toasty oak. Yet the fruit holds center stage, and its depth and concentration are apparent. A hint of pepper sneaks in on the finish. Drink now through 2011. 2,499 cases made.", 40.0m, "2004", new Guid("2130685f-8bb0-43d1-9b06-5b6339a3f3ea") },
                    { new Guid("ef3152e7-a828-4e73-a212-1e1a8de9872a"), 3, new DateTime(2009, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Biker", "Ripe and gutsy, but with enough finesse and personality to make this a complete package. Aromas of wild berry and white pepper lead to briary but complex plum, anise and toasty sage flavors that finish with fine, loamy tannins. Best from 2011 through 2015. 4,091 cases made.", 25.0m, "2007", new Guid("66636bb4-9806-483a-9726-b12184cd1775") },
                    { new Guid("90045d21-fcfc-4617-a62d-b5d76bd6b5b9"), 3, new DateTime(2007, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Petite Sirah Paso Robles The Heretic", "Delightfully rich and deep, ripe and fleshy, with a complex mix of currant, wild berry and blackberry fruit that's sharply focused. For all its size and range of flavors, it's lively and vibrant. Drink now through 2011. 885 cases made.", 30.0m, "2004", new Guid("66636bb4-9806-483a-9726-b12184cd1775") },
                    { new Guid("d30f9f3c-1e2a-49b4-9591-f73d357750c5"), 6, new DateTime(2007, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Condrieu DePoncins", "Broad and powerful, with layers of dried apricot, fig, quince and anise offset by peach pit and bitter almond. The concentrated finish is tightly wound and almost tannic in its intensity. This is a rare Condrieu that requires cellaring. Best from 2008 through 2013. 900 cases made.", 55.0m, "2005", new Guid("9e5e0aac-9fb9-471c-ba0c-2cc2d6a59ef9") },
                    { new Guid("f5e7650d-ed1c-4223-9bf5-366ffc6c5950"), 3, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Plexus Red Barossa Valley", "A sophisticated, spicy mix of date bread, cherry compote and clove flavors. Plump and velvety, with persistence and harmony on the finish. Shiraz, Grenache and Mourvèdre. Drink now through 2026. 1,000 cases imported.", 40.0m, "2014", new Guid("1044ce16-ed2d-4ca8-bf87-8b23c349a8d2") },
                    { new Guid("692b7faa-0f8f-4559-b5f3-deccd3d9a915"), 3, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Entity Barossa Valley", "A beautiful evocation of plum and blackberry balanced against a veil of creamy oak--but only a veil. It's the fruit that distinguishes the long, expressive finish. Shiraz. Best from 2009 through 2017. 1,000 cases imported.", 40.0m, "2005", new Guid("1044ce16-ed2d-4ca8-bf87-8b23c349a8d2") },
                    { new Guid("128fe04d-20ca-4192-9cf0-10889d2e97ea"), 3, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Madonna del Piano Riserva", "Mind-blowing aromas of crushed berries, meat, blackberry and violet. Full-bodied, with layers of incredibly velvety tannins and a long, long finish. This is so complex and long, and the mouthfeel is spellbinding. Develops wonderfully in the glass, becoming more and more rich with air. One of the greatest Brunellos I have ever tasted. Best after 2012. 2,000 cases made.", 175.0m, "2001", new Guid("7170267b-f875-4afb-8020-d3e545badab3") },
                    { new Guid("0d4b5d56-8705-4e4d-8f4c-99389b0b668e"), 3, new DateTime(2006, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah San Antonio EQ", "Offers lots of plum, fig and black currant fruit, along with briar, mineral and tar flavors. Bacony toast and racy acidity wraps it up into a powerful and focused presentation. Drink now through 2009. 800 cases made.", 25.0m, "2004", new Guid("3b38c492-6507-40f3-a969-cf587b6f51e7") },
                    { new Guid("6ba1220b-a0e7-42ea-9890-eaf03719bdf3"), 3, new DateTime(2009, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Touriga Nacional Douro", "Rich, ripe and spicy, with luscious flavors of dark plum, red cherry, cedar and sandalwood. The finish of chocolate mousse and café au lait is long and mouthfilling. Drink now through 2015. 50,000 cases made.", 28.0m, "2007", new Guid("20115489-2932-47bb-a2ef-ca3a248a476e") },
                    { new Guid("6638deab-c53e-47f4-bcfa-5148e5f5e26f"), 3, new DateTime(2008, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Uco Valley", "Really goes for the gusto, with dark fig sauce, melted purple licorice, mocha, fruitcake and crushed boysenberry and blueberry fruit, all gliding along rich, velvety tannins. Long, mouthfilling finish. Drink now through 2012. 3,500 cases imported.", 35.0m, "2007", new Guid("ed2737bc-ad12-41c0-b06c-98f468afaa70") },
                    { new Guid("1953cc46-1368-435b-ba95-ec1d1ede5593"), 6, new DateTime(2004, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Rutherford", "Brilliant yellow-gold color, superrich and multifaceted, with layers of rich, creamy fig, citrus, pear and honeysuckle. This deliciously full-blown wine is deeply concentrated, finishing with a long, persistent aftertaste, picking up a cedar, smoke and toasty oak nuance that adds another dimension. Drink now through 2009. 960 cases made.", 50.0m, "2002", new Guid("24ab7649-5c5c-4e92-a364-1723cf2a2ad1") },
                    { new Guid("e98b83a1-25c4-4ad9-977f-1577bd4b4539"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Vigneto Marcarini", "A big wine, with fascinating aromas of black licorice, berry and toasted oak. Full-bodied, with loads of ripe fruit and velvety, fruit-coated tannins. Long finish. Easily the best wine from this producer this year due to the richness and balance. Best after 2008. 1,500 cases made.", 48.0m, "2000", new Guid("3f55bbe3-14d4-4311-b922-860b604b11c4") },
                    { new Guid("4e41cb6b-3eef-4637-81cb-7670444fa40b"), 2, new DateTime(2000, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vintage Port Vau", "Dark purple in color, with wonderful violet, berry and cherry aromas. Full-bodied, medium-sweet, with a solid core of fruit and tannins. Long finish. Subtle. Best Sandeman since the 1960s. Best after 2008. 25,200 cases made.", 42.0m, "1997", new Guid("eca6c9cf-303e-4e02-b7b5-13f5184a3aca") },
                    { new Guid("f2a823ab-eeb6-4391-8df1-3b40b6d37952"), 3, new DateTime(2006, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Edición Limitada", "Powerful yet graceful, this rich, ripe red delivers blackberry and currant fruit framed by coffee and toasty oak notes. Though thick on the palate, it remains fresh and focused, and the finish is clean and sweet, with floral and mineral grace notes. Drink now through 2012. 1,500 cases made.", 45.0m, "2003", new Guid("2a6d8a55-be4e-46f2-894c-a31756a84c7c") },
                    { new Guid("6e74bd9b-bbe3-4e48-929b-5ef1a77addd3"), 3, new DateTime(2000, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Viña Lanciano Reserva", "Elegant. This polished Spanish red is bursting with cherry, berry, spice and vanilla flavors that float over fine-grained tannins and linger, fresh and clean, on the long finish. A beautiful example of the traditional style. Drink now through 2006. 13,600 cases made.", 35.0m, "1995", new Guid("2a6d8a55-be4e-46f2-894c-a31756a84c7c") },
                    { new Guid("f71f6faf-74db-4e10-a794-51375dcfec4e"), 3, new DateTime(2010, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gigondas Terrasse du Diable", "Polished and pure, with delicious layers of plum sauce, braised fig and melted licorice that glide through the dense but silky-textured finish. Hints of graphite and incense lurk in the background. Long, with buried minerality. Should age nicely. Grenache, Mourvèdre and Clairette. Drink now through 2015. 3,830 cases made.", 28.0m, "2007", new Guid("eead94a7-3b18-4aaf-a303-62229897aaeb") },
                    { new Guid("cc60a352-3e51-480b-a5f4-83f39af6cc1c"), 3, new DateTime(2000, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Finca La Planeta", "Intense. First, ripe fruit coats the palate, then darker flavors of coffee, anise and minerals emerge and linger. Firm tannins and high alcohol build an impressive structure. A big wine that needs time to unwind. Best after 2004. 1,000 cases made.", 39.0m, "1997", new Guid("01507188-6401-4833-b64b-6a18883cdf7b") },
                    { new Guid("6542aab0-c44f-4112-ae65-38d70117a43e"), 3, new DateTime(2000, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Cuvée Réservée", "Superopulent. Full-bodied and thick, with rich, ripe fruit flavors that taste sweet midpalate. Wonderful, supple, velvety finish. Delicious. Drink now through 2015. 6,250 cases made.", 45.0m, "1998", new Guid("0beae6fc-e080-4d9c-b7bb-d05afad26231") },
                    { new Guid("15c40e32-65dd-4977-9a09-25510c57418a"), 3, new DateTime(2000, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Tenuta Nuova", "This is a blockbuster Brunello--the real thing. Tastes very ripe and earthy, with lots of wonderful fruit and wet leaf character. Full-bodied, with velvety tannins and a long finish. Drink now through 2008. 3,945 cases made.", 49.0m, "1995", new Guid("2668b681-24e4-4b31-a2d5-a249797689de") },
                    { new Guid("c6b47c84-bf2d-4b9f-851a-8ffddad03bb6"), 3, new DateTime(2000, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape La Crau", "Reserved aromatically, but the wonderful balance is evident as this young Rhône red reveals its mineral, wet earth, delicate plum, mocha, coffee and blackberry character and subtle, ripe tannins. The wet earth and mineral really shine through on the lingering finish. Drink now through 2010. 16,665 cases made.", 37.0m, "1998", new Guid("0c67554d-b758-4564-81c9-d57dfdb6609b") },
                    { new Guid("98ca13e9-59fb-4e75-9d5b-74c11da122a9"), 3, new DateTime(2010, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône", "A lovely floral violet aroma leads the way, followed by good dark berry, tobacco and charcoal notes, with a dash of tarry grip on the finish. Pure and solid. Drink now through 2011. 3,000 cases imported.", 18.0m, "2009", new Guid("7b9e2f25-491d-41b2-be75-5ab3358cb7e8") },
                    { new Guid("102f4c98-c5b1-41fb-9ef0-d9227d35963b"), 6, new DateTime(2000, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Altenberg de Bergheim", "Terrific. Beautiful aromas of flowers, pine forests, peaches and citrus. Rich, creamy and just off-dry midpalate, with firm underlying structure, yet the finish is dry. Still needs time. Drink now through 2005. 3,000 cases made.", 27.0m, "1995", new Guid("86269ce6-2fd6-4dbf-ac3b-055a122904d7") },
                    { new Guid("058ee6ac-5a4d-4953-b8ad-15ee5f1afa86"), 5, new DateTime(2000, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Cuvée Sir Winston Churchill", "Power and intensity in a glass. Bold and full of bread dough and apple, with a marmalade note, married to a big, firm structure, it still seems youthful. A brilliant Champagne that should develop beautifully. Best from 2001 through 2010. 6,250 cases made.", 155.0m, "1990", new Guid("dac2e2dc-2ec9-467f-8efe-1186661abf21") },
                    { new Guid("526e8eac-f5be-41bd-86e0-27916775e12b"), 3, new DateTime(2008, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ode To Lorraine Barossa", "Rich, focused and superbly concentrated to show off its intense, rosemary-accented black cherry, plum and blueberry flavors, riding smoothly on a dense, generous frame that feels plush and seductive, yet the flavors come through transparently. Cabernet Sauvignon, Shiraz and Merlot. Drink now through 2017. 400 cases imported.", 53.0m, "2005", new Guid("e78676f1-8e31-4dfc-b67c-e23dd1c58bf1") },
                    { new Guid("ac17ed03-d759-457e-a2af-4f719efa06e9"), 3, new DateTime(2007, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Torre Muga", "Plush and harmonious, this red is alluring yet subtle, with intense flavors backed by an impressive structure. Look for black currant, cocoa, cigar box and mineral notes now, and expect further complexity as this evolves. Tempranillo, Mazuelo and Graciano. Drink now through 2020. 2,000 cases imported.", 88.0m, "2004", new Guid("2e2d01f7-a358-4098-b7ac-4583a47e6bcb") },
                    { new Guid("0276a7e1-21af-44eb-886e-cabfd8f7f342"), 3, new DateTime(2000, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Torre Muga Reserva", "Beautifully aromatic, with notes of cassis,lilacs, coffee and spices, this sinuous red ispolished and elegant, yet dense with fruit, oakand earthy flavors that linger and expand on thelong finish. An extraordinary marriage of typicityand sophistication. Drink now through 2010. 1,000 cases made.", 55.0m, "1995", new Guid("2e2d01f7-a358-4098-b7ac-4583a47e6bcb") },
                    { new Guid("819e70c2-f7b3-4934-8b06-11a42ad42d35"), 3, new DateTime(2006, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Arcus Estate", "Dense and dramatic, with layers and layers of raspberry, red plum and red cherry flavors that form a tight ball and whoosh through the finish, hardly slowed by mouthcoating tannins. This has tremendous style and depth. Best from 2008 through 2015. 1,522 cases made.", 75.0m, "2003", new Guid("07ef95a3-be13-4c6d-ba76-1397e0f0f7b3") },
                    { new Guid("17b1fb30-029b-4322-981c-6ef4ad24f524"), 3, new DateTime(2000, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Oregon Premier Cuvée", "Ripe, round and distinctive, with juicy, spicy, floral notes around currant and plum flavors that linger long. This Pinot has all its elements packed in tightly and harmoniously. Very classy. Drink now through 2010. 2,809 cases made.", 39.0m, "1998", new Guid("07ef95a3-be13-4c6d-ba76-1397e0f0f7b3") },
                    { new Guid("ec834b52-29c0-4c07-8ab3-96ad0dd2ef07"), 6, new DateTime(2000, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Central Coast", "This wine is opulent, rich and creamy, with a core of nectarine, fig, citrus, pineapple and spice supported by firm, lively acidity, finishing long and full. Lovely Chardonnay from a growing California appellation. Drink now through 2005. 9,000 cases made.", 40.0m, "1997", new Guid("4d4c1c5a-dd4f-40c2-ad61-02707de39e4e") },
                    { new Guid("c020dd35-6811-4758-b27f-457e11b52504"), 3, new DateTime(2006, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Boisrenard", "Loaded with rich, dark layers of black currant, blackberry, truffle, tar, mocha and bittersweet cocoa, this pumps out both fruit and <i>terroir</i> on the gripping finish. Immense in scale and depth, this is hard to lay off now, but should be even more impressive when it drops its muscle to show more elegance (that will take a while, though). Best from 2008 through 2030. 1,665 cases made.", 75.0m, "2003", new Guid("3309566b-82d4-489d-a0db-e809a9f4b81c") },
                    { new Guid("31f1ddac-07c8-42d7-9cd3-1c451625353b"), 3, new DateTime(2001, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône-Villages Rasteau", "Traditional-style red that's worth hunting down. Digs the earth. Medium-bodied, it kicks up mineral and <i>terroir</i>, while also delivering a mouthful of beautifully ripe, black fruit and lots of firm tannins. Honest wine, without fancy oak, that will improve and soften with age. Drink now through 2010. 5,000 cases made.", 15.0m, "1999", new Guid("3309566b-82d4-489d-a0db-e809a9f4b81c") },
                    { new Guid("fc3774cc-960f-43ba-9f9b-08678b182594"), 3, new DateTime(2001, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Castello di Brolio", "Dark ruby color, with lots of chocolate, berry and vanilla aromas, this is full-bodied and velvety, with lots of fruit and a long, succulent finish. A beauty from a great vintage. Best from 2001 through 2006. 17,000 cases made.", 45.0m, "1997", new Guid("b6a28332-bb62-4798-8e0d-47d72982cb6d") },
                    { new Guid("91bbeabe-3923-45b1-8ec8-16d0549c827a"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Casalferro", "A monster Italian red. Aromas of coffee, roastedmeat and ripe berries precede a full-bodied winewith mouthpuckering tannins and an enormousconcentration of fruit. What it lacks in eleganceand sophistication it gives in concentration andpower. Best after 2003. 8,750 cases made.", 38.0m, "1997", new Guid("b6a28332-bb62-4798-8e0d-47d72982cb6d") },
                    { new Guid("28379290-2bf1-47ae-a199-296680655d2f"), 3, new DateTime(2000, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Sorì Burdin", "Kaleidoscopic character. This Piedmont red offers deep and rich aromas of ripe plum, meat and dried fruit and is full-bodied, with wonderfully polished tannins and a finish of red fruit and spice that goes on for minutes. Best after 2004. 1,000 cases made.", 48.0m, "1997", new Guid("b19f3fc4-bc20-42e1-8db7-f5524c107b9b") },
                    { new Guid("518369eb-5d7a-4cf4-b539-f48e03c633c4"), 3, new DateTime(2000, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Command", "A superrich and jammy Aussie red sporting a wonderful array of black cherry, wild berry, seductive toasty oak and spice nuances. The texture is silky-smooth, the finish a mile-long. Drink now through 2008. 1,800 cases made.", 60.0m, "1995", new Guid("e78676f1-8e31-4dfc-b67c-e23dd1c58bf1") },
                    { new Guid("2027c22b-c334-434a-80b4-5fbc9d1cb736"), 3, new DateTime(2000, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Cuvée des Cadettes", "Seductive and sexy, this world-class red is rich, opulent and ripe, with layers of superb mocha, toast and spice. Loaded with blackberry and cassis, its fabulous balance leads to a long fruit-and-oak finish. Very tempting now. Best from 2005 through 2030. 2,200 cases made.", 75.0m, "1998", new Guid("dd22632c-8e6a-4b63-aaa4-1d4680483522") },
                    { new Guid("0fc9f7ac-702c-4830-9735-df14b2a3d6db"), 6, new DateTime(2000, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Premium Selection", "Crisp and fragrant, this Kiwi white delivers a riot of floral and tropical fruit flavors, favoring passion fruit, lime and spicy rose petal notes. Delicious, and affordable, too. Drink now. 8,000 cases made.", 13.0m, "1999", new Guid("f2f76a59-c377-4e0b-8598-8e843b7ccfd3") },
                    { new Guid("6d3d3cda-46fc-4ca4-ab12-6136f7359fb4"), 3, new DateTime(2000, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Les Closiers", "Seductive because of the rich fruit and suppletannins, but also the fresh, clean and crispcharacter that cleanses the palate and gives thisfull-bodied, ripe red a sense of elegance andvibrancy. Has raisiny and plummy aromas, withroasted game, grilled lamb chops and cassisjam. Drink now through 2010. 10,000 cases made.", 24.0m, "1998", new Guid("64d9d4f6-a099-44b9-a83f-b8a74b9fa37f") },
                    { new Guid("cb29a899-4114-4c17-b601-1155c3106422"), 3, new DateTime(2001, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône-Villages", "Delicious and stylish young red, balancing fresh, focused and ripe fruit, clean and pure, with mineral, spice, mocha and coffee notes. Dark-colored and medium-bodied, its tannins are sweet, and there's pure fruit on the lively finish. Drink now through 2007. 40,000 cases made.", 11.0m, "2000", new Guid("ce1e7f2b-d798-450e-8152-54735d1da58f") },
                    { new Guid("a3bfe315-9b33-46b6-9e5e-52b26bfe0e6d"), 3, new DateTime(2001, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vino Nobile di Montepulciano Riserva", "Ripe and powerful aromas of berries and smoke, with hints of raisins. Full-bodied and massive in the mouth, with big, velvety tannins and a long, long finish. A blockbuster riserva. Best after 2004. 7,100 cases made.", 31.0m, "1997", new Guid("8bb3667c-ebb8-464f-8efb-3027b6b83dfc") },
                    { new Guid("a3820a54-7107-4216-a9e2-ffda71b24e5f"), 3, new DateTime(2001, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Bronzinelle", "A powerhouse red from the south of France. Rich and complex, with vivid and well-defined flavors of dark plum, meat and spice. Deeply colored with a meaty aroma, this has the stuffing to age. Peppery and brickish notes linger on the finish. Drink now through 2005. 1,000 cases imported.", 13.0m, "1999", new Guid("79dac6d8-fcbd-4e65-b4e6-040d7f6ac763") },
                    { new Guid("c6defa25-338a-4ca7-bd46-e398e210cc56"), 5, new DateTime(2002, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Grande Cuvée", "One whiff and you know this is serious stuff. The aromas of baking brioche, coconut, candied citrus and leather pick up roasted coffee and grilled nuts on the palate, permeating the senses. Profound depth and complexity, offering a unique Champagne experience. Drink now through 2006. 40,000 cases made.", 140.0m, "NV", new Guid("a3d67c33-9dfb-4c48-81a9-418a465e8482") },
                    { new Guid("a5293c04-79f2-4cd9-861d-1577e8886e79"), 5, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Blanc de Blancs Champagne Clos du Mesnil", "A tightly wound thoroughbred, with complex nut and biscuit notes playing off a floral freshness. Driving it all is a racy acidity matched by a seamless elegance and innate power that lingers through the persistent ginger-tinged finish. Will age beautifully. Drink now through 2008. 1,200 cases made.", 370.0m, "1988", new Guid("a3d67c33-9dfb-4c48-81a9-418a465e8482") },
                    { new Guid("976ae0ee-e1f5-4213-bc7f-d87351448250"), 3, new DateTime(2008, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vacqueyras Le Clos ", "This Rhône red is dark and really packed, but also seamless, with a round, juicy texture carrying the fig sauce, black licorice, Turkish coffee and iron notes. The long finish is filled with blackberry and mineral. Best from 2009 through 2013. 2,500 cases made.", 30.0m, "2005", new Guid("177d4c29-aaa4-4041-98a4-a32737c4cedb") },
                    { new Guid("f449f048-5936-4cdf-b52e-f68821c3cf71"), 3, new DateTime(2001, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vallagarina", "Black ruby in color, intense aromas of blackberries, licorice, red berries and green tobacco, this is a full-bodied red with loads of silky tannins and a long, long aftertaste of tobacco, sage and berry. A huge core of fruit in this wine. Needs time. An Italian interpretation of great Bordeaux from bygone days. Best after 2006. 7,450 cases made.", 50.0m, "1997", new Guid("4b043a90-9ad4-4a02-956d-df24f1662b21") },
                    { new Guid("2d98af42-4cfb-4e89-a976-f5ee2af92549"), 6, new DateTime(2007, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Federspiel Trocken Wachau Terrassen Domäne Wachau", "Racy and powerful, with plenty of green peach, lychee and citrus flavors. Vibrant mineral notes run throughout, with an ironclad finish of graphite and hints of pineapple. Drink now through 2014. 7,750 cases made.", 14.0m, "2005", new Guid("a08fb67a-92ee-4c60-b829-4294bf03b897") },
                    { new Guid("f4bfc6a8-b3ee-4869-a27c-11e003740a27"), 6, new DateTime(2001, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Smaragd Trocken Wachau Loibner Loibenberg", "Brilliant effort. Amply proportioned and spicy, this '99 Riesling combines fascinating smoke, quince and peach flavors with a vibrant structure that drives the flavors. Explodes on the finish with a potpourri of mineral and spice. Drink now through 2006. 1,200 cases made.", 29.0m, "1999", new Guid("a08fb67a-92ee-4c60-b829-4294bf03b897") },
                    { new Guid("142ee457-7323-4747-a404-7705d26ed65a"), 6, new DateTime(2000, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Smaragd Trocken Wachau Spitzer Singerriedel", "Multidimensional. Intense aromas of licorice and spice combine with a broad structure and thick texture that feels more like that of a red wine. Closed now, so be patient. Best from 2001 through 2006. 625 cases made.", 29.0m, "1998", new Guid("a08fb67a-92ee-4c60-b829-4294bf03b897") },
                    { new Guid("085baeb9-c14e-439d-99aa-e5cfc7ee70a8"), 6, new DateTime(2000, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Columbia Valley Eroica", "Offers a bouquet of wonderful flavors, pouring outits pear, apricot, mineral and honey notes.Off-dry but balanced, with enough of a steelybackbone to make the flavors sing. A collaborativeeffort of Chateau Ste. Michelle and Mosel's Dr.Loosen. Wonderful now. Best from 2002 through 2009. 2,600 cases made.", 20.0m, "1999", new Guid("5ddee7bf-1bd4-49ed-8775-1ecadb6a04a1") },
                    { new Guid("7d5d07ca-e957-4eb4-aabc-0a347a8fda6e"), 3, new DateTime(2019, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cerasuolo di Vittoria", "This medium-bodied red is balanced and fresh, with light, taut tannins and a well-knit mix of wild strawberry and raspberry, dried thyme, singed orange peel and spicy mineral flavors. Drink now through 2023. 8,000 cases made, 4,000 cases imported.", 24.0m, "2017", new Guid("92346a7b-bef1-4aa6-a33e-92dcf6a6afe1") },
                    { new Guid("d7106682-48c6-48d3-baec-64f041da5d66"), 3, new DateTime(2000, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Morellino di Scansano Riserva", "This Tuscan red is gorgeous. Emitting beautiful aromas of blackberry, wet earth and toasted oak, it's full-bodied and very polished, with fine tannins and a fresh, delicious finish. Needs time. Best after 2000. 2,590 cases made.", 19.0m, "1997", new Guid("21a6a3f8-a763-4030-8e12-e5e26a4b4604") },
                    { new Guid("787a03e0-d9eb-4cbe-b6d0-4ef864f1f65b"), 3, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Stellenbosch", "This is well-rendered, with a core of bramble-edged boysenberry, blackberry and raspberry fruit laced with mouthwatering savory and anise notes. Shows judicious toast through the finish, as roasted apple wood details dance through, letting the fruit win out in the end. Drink now through 2024. 3,540 cases made.", 29.0m, "2014", new Guid("e1e3e236-d972-4452-a4c7-138c96746a12") },
                    { new Guid("05e461c2-0e04-442a-8967-4d65b840b3fb"), 3, new DateTime(2000, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Stellenbosch", "This red makes a statement. Fresh, bold and extravagantly oaked, exuding vanilla, coffee and toast elements, backed by a core of rich, concentrated plum and black currant. Lovely acidity keeps it all lively, and the long finish bodes well for the future. Cabernet Sauvignon, Merlot and Shiraz. Best from 2001 through 2008. 3,000 cases made.", 32.0m, "1996", new Guid("e1e3e236-d972-4452-a4c7-138c96746a12") },
                    { new Guid("b82d7902-4625-4815-86fa-1e6b81924de9"), 6, new DateTime(2004, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Falanghina Sannio", "Aromas of lemons and rhubarb turn to minerals, melons, and honey. Medium- to full-bodied, with lovely acidity and a long, ripe fruit aftertaste. Delicious. One of the best values in Italian white. Drink now. 70,000 cases made.", 17.0m, "2003", new Guid("0dd6e1e3-77d4-41a2-bc17-4e25f23bf78c") },
                    { new Guid("5b7b7252-9b7b-4396-9491-eaf978abf2c4"), 6, new DateTime(2002, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Greco di Tufo", "An outstanding modern-style wine from an ancient Italian grape variety. Wonderful aromas of blanched almonds, dried peach, pineapple and minerals. Medium- to full-bodied, with lovely fresh fruit, flinty character and a long, long finish. Excellent. Drink now. 66,650 cases made.", 18.0m, "2001", new Guid("0dd6e1e3-77d4-41a2-bc17-4e25f23bf78c") },
                    { new Guid("8b0b5f08-2d79-48e1-988c-835ff46a96bc"), 6, new DateTime(2000, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Fiano di Avellino", "An impressive Italian white with layers of tropical fruit and citrus character. It's full-bodied, with wonderful concentration, good acidity and a long, long flavorful finish. Drink now through 2004. 21,000 cases made.", 18.0m, "1999", new Guid("0dd6e1e3-77d4-41a2-bc17-4e25f23bf78c") },
                    { new Guid("474b2174-09b5-43b2-a3d7-4badc1cb5e0e"), 6, new DateTime(2001, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Reserve", "Supple in texture and lively in flavor, this elegant New Zealand Sauvignon shows minty lime, apple and passion fruit flavors that linger through the finish. Drink now. 12,000 cases made.", 17.0m, "2000", new Guid("d42a93fe-16e7-4054-9bc1-ac0dc7e73632") },
                    { new Guid("c6349a30-16d5-4bf7-9dfd-184cae5fb102"), 3, new DateTime(2000, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône-Villages Sablet", "Seductive red for a Valentine's date. A bit earthy--but what amazing texture and ripeness in the midpalate. It just massages the taste buds,offering a hedonistic tasting experience that makes you dream. Mineral from the wet earth, blackberry and massive, sweet tannins. Very <i>terroir</i>-driven. Drink now through 2008. 5,000 cases made.", 17.0m, "1998", new Guid("ff144de3-266d-4538-837a-b6d98af41173") },
                    { new Guid("8f81dbad-4d5f-4ae7-b8f4-643fb0ae9a04"), 6, new DateTime(2008, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Mosel Ürzig Würzgarten", "Peach and Golden Delicious apple notes combine with the slate edge in this sleek, well-defined kabinett. Brilliant and succulent, with a lime and mineral aftertaste. Drink now through 2022. 6,000 cases made.", 22.0m, "2007", new Guid("b742ddcc-8b0e-482f-9958-689e228b4977") },
                    { new Guid("a4d32251-b3ca-4225-afe3-9825e4c1c36b"), 6, new DateTime(2007, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Mosel-Saar-Ruwer Ürziger Würzgarten", "A beauty, with both intensity and cut. More unevolved than others, this offers grapefruit, peach and mineral flavors matched to a keen acidity. Good lingering finish. Drink now through 2012. 1,000 cases imported.", 20.0m, "2005", new Guid("b742ddcc-8b0e-482f-9958-689e228b4977") },
                    { new Guid("4fb3f314-0ba1-47d9-b50b-df8ecc9c7c84"), 6, new DateTime(2000, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sicilia", "Here's an Italian white that tastes like a villageMeursault from Lafon; very impressive. Golden incolor, with very ripe pineapple and apricot aromasplus hints of vanilla and dough. Full in body,with lots of unctuous fruit and a long, longfinish. Drink now through 2003. 5,000 cases made.", 30.0m, "1998", new Guid("92346a7b-bef1-4aa6-a33e-92dcf6a6afe1") },
                    { new Guid("17a225db-c276-4539-af47-b581835c680a"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Justification Paso Robles", "A ripe, intense, fruit-driven style, with racy, jammy black cherry and blackberry fruit that's well-focused, picking up pretty toasty oak, tar and leather shadings. Holds its focus nicely. Cabernet Franc and Merlot. Drink now through 2009. 2,000 cases made.", 29.0m, "1999", new Guid("39788d9a-12d4-4d69-a6a8-83b1bc418567") },
                    { new Guid("7cff6f96-557f-4da9-85f8-b7c7fd4d22d0"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Isosceles Paso Robles", "Absolutely delicious, this blend of Cabernet Sauvignon (mostly), Cabernet Franc and Merlot is brimming with ripe and juicy currant, blackcherry, wild berry, plum and anise flavors that are complex, deep and polished. Finishes long and flavorful. Best from 2002 through 2012. 8,000 cases made.", 40.0m, "1997", new Guid("39788d9a-12d4-4d69-a6a8-83b1bc418567") }
                });

            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("6637d83a-8552-4dc2-8ca8-51fbb2699d3f"), 3, new DateTime(1999, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Dolcetto d'Alba Vigna del Mandorlo", "A Dolcetto of the highest level. Full-bodied, coating the palate with well-integrated tannins as well as mocha, chocolate and mostly black cherry and tar. For this varietal, there's amazing richness on the opulent finish. Tastes unfiltered. A perfect forebear of what the great '98 vintage might bring us. Drink now through 2001. 1,084 cases made.", 17.0m, "1998", new Guid("a67a7cfe-09ee-4fb9-a1e7-6eb237b7960e") },
                    { new Guid("d2360745-ee44-4900-b1a3-be4f48868ad8"), 6, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Adelaide Hills", "A glorious white wine, graceful and harmonious as it layers its vibrant apple, pear, tropical fruit, spice and vanilla flavors and hints at raisin and lemon on the long, long finish. Best from 2000 through 2005. 3,000 cases made.", 20.0m, "1997", new Guid("8738b787-b832-4c03-8065-83608a09ece2") },
                    { new Guid("a0fc68e7-38f0-44e6-9137-06d33fc962ff"), 6, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mercurey White", "A Mercurey that tastes like a little Montrachet. With its beautiful blend of tropical and toasty aromas, this full-bodied white is generous and silky, coating every taste bud with its luscious, thick-as-oil liquid. Remarkable concentration of refined flavors--dried apricot, honey, lemon and mango. Drink now. 4,967 cases made.", 17.0m, "1997", new Guid("65b55701-5de0-416c-89f4-8d774154d82c") },
                    { new Guid("21f75834-417a-466a-92e0-1a5a227b99ff"), 3, new DateTime(2000, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley Ebenezer", "Firm in texture, with juicy plum, blackberry and rose petal flavors, the fruit echoing enticingly amid the fine-grained tannins on the finish. Appealing already, but it's destined for a long life. Drink now through 2012. 3,500 cases made.", 29.0m, "1997", new Guid("ada6a899-0ed7-49ea-b6ca-c23b756f9a96") },
                    { new Guid("16bcb8be-4529-4b59-8635-fd0060b0c741"), 3, new DateTime(1999, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley E&E Black Pepper", "A profound wine, This Australian Shiraz burrows deep into its plush texture to reveal gorgeous flavors of ripe plum, berry and exotic spice, all balanced impressively on an elegant frame. The tannins are refined and well integrated. Approachable already. Best after 2001. 4,000 cases made.", 60.0m, "1996", new Guid("ada6a899-0ed7-49ea-b6ca-c23b756f9a96") },
                    { new Guid("1154bd68-a2e4-44d6-a421-8da6ca11206c"), 6, new DateTime(2002, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Carneros Dijon Clones", "A bold mouthful of Chardonnay that packs in lots of rich, complex flavors that are concentrated and deep. Smoky toasty oak, pear, fig, sage and anise turn thick and buttery. Drink now through 2006. 15,000 cases made.", 25.0m, "2000", new Guid("b004b473-6030-43e3-8067-a46fc471515b") },
                    { new Guid("68cb0996-0db5-4cac-beda-2d17b07446e6"), 3, new DateTime(1998, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley Crimson Creek", "Smooth and supple, with ripe, earthy plum, wild berry and black cherry flavors that fold together nicely. Finishes with a complex interplay of flavors and mild, well-integrated tannins. Drink now through 2004. 10,500 cases made.", 25.0m, "1996", new Guid("b004b473-6030-43e3-8067-a46fc471515b") },
                    { new Guid("e6c3c532-c3a8-48ef-9f58-c66fa1f9b7ad"), 3, new DateTime(1998, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Santa Barbara County Bien Nacido Reserve", "A real beauty, ripe, supple and complex, with a fleshy array of meaty plum, spice, oak and cedar. Holds its focus on the finish, where the flavors are vibrant, fan out and linger. Drink now through 2004. 2,100 cases made.", 25.0m, "1996", new Guid("599c6da7-82dd-4393-adb5-d3f4ae1a628f") },
                    { new Guid("2fd02dc1-f62a-4b10-b672-7e14cb86c186"), 3, new DateTime(1998, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Viña Mayor Reserva", "This strong, balanced red offers clean flavors of black cherry and plum, with notes of coffee and licorice, backed by firm tannins in a balanced, graceful package. This Spanish wine is food-friendly now, should become more expressive with age. Drink now through 2004. 25,000 cases made.", 13.0m, "1994", new Guid("5096a307-1fc8-4aae-9de4-a1a756e6869e") },
                    { new Guid("9bae5b9b-0592-4b07-b656-54b29f800569"), 3, new DateTime(1998, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc", "Beautifully complex, with incredibly concentrated and well-focused flavors of blackberry, currant and ripe plum. Has a fragrant aroma and a lingering finish accented by leather and chocolate notes. Despite its impressive power, it comes off balanced and classy. Drink now through 2001. 2,800 cases made.", 12.0m, "1995", new Guid("a0526ccf-31e8-4235-885e-c56df3dd02ab") },
                    { new Guid("3694601d-385d-4516-8381-dd079b19e100"), 3, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Lucente", "Big, rich aromas of crushed blackberries, licorice and minerals with just a hint of lemongrass. Full-bodied with thick, luscious tannins and a long, smoky tar and berry finish. A full-throttle wine. This is really excellent for the money. Sangiovese, Merlot and Cabernet Sauvignon. Best after 2009. 11,300 cases made.", 25.0m, "2003", new Guid("865f7b43-e3db-4977-bc13-4cee467579f9") },
                    { new Guid("0bfc48f1-3379-4071-8e99-96f6d1f13275"), 3, new DateTime(1999, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pommard Clos des Epeneaux", "A deep wine--one where you have to put your nose far into the glass to capture the subtleties. The black fruit, game, mineral and currant notes fill the palate, proving how dense and thick this '96 red Burgundy is. A brilliant Pommard. Best from 2006 through 2020. 1,665 cases made.", 65.0m, "1996", new Guid("05e7ed83-e61d-4670-a440-50f488091581") },
                    { new Guid("56b826ea-bb1f-4a05-8c9f-7929dfb3db77"), 3, new DateTime(1998, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Luce", "Loads of crushed berry and milk chocolate aromas. Medium-bodied, very polished, with ultrafine tannins and a long, long finish. A slinky, sexy red, like a supermodel strolling down the catwalk. Best Luce to date. Best after 1999. 7,500 cases made.", 63.0m, "1995", new Guid("865f7b43-e3db-4977-bc13-4cee467579f9") },
                    { new Guid("3f440ec4-69c6-45b5-ab66-4810b3b6c454"), 6, new DateTime(1998, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Padthaway St. Hilary", "Here's an aromatic, spicy and beautifully focused white, almost resinous in flavor as it spreads its oak-scented pineapple, pear and honey tones over the finish. Nicely done, good price, too. Drink now through 2000. 10,000 cases made.", 16.0m, "1996", new Guid("539ff07f-dccc-477a-9bb0-771418537eda") },
                    { new Guid("fb531a7c-9472-460d-8398-d2f6f1e0b6ab"), 3, new DateTime(1998, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Napa Valley Edizione Pennino", "Elegant and refined, ripe, smooth and polished, with just the right proportion of black cherry, plum and blackberry flavors and spicy, minty nuances. Finishes with a burst of fruit and supple tannins. Drink now through 2002. 2,800 cases made.", 24.0m, "1995", new Guid("b5b35604-f3e3-411b-85a2-161c5ae65ab6") },
                    { new Guid("e09a8ee5-7670-46cc-9db1-e2823f100661"), 3, new DateTime(1998, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera d'Asti Ai Suma", "Incredible. Purple-black in color, full-bodied, hedonistically rich and so ripe it tastes almost like a late-harvest with residual sugar. Bursts with clean, pure and vibrant cassis flavors accented by deftly dosed toasted oak. Very youthful on the superharmonious, smooth and fruity finish. The quality matches the reputation of this cult wine. Bravo! Drink now through 2000. 2,160 cases made.", 40.0m, "1996", new Guid("13efa153-bef7-4c11-8a79-f410a000b19a") },
                    { new Guid("0253c115-8a59-4dc5-81a5-6a0d52f067af"), 3, new DateTime(1998, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Coteaux du Languedoc Clos Léone", "A big, deep wine with a rich meaty aroma and layers of ripe plum, dark cherry and bacony flavors. Has all the stuffing and the trimmings besides, from the thick texture to the sophisticated tannins and almost decadent flavors. An amazingly concentrated wine that's full of character. Best from 2000 through 2005. 1,800 cases made.", 33.0m, "1994", new Guid("0dbbe3df-b123-42f9-9c5f-a8289801d65d") },
                    { new Guid("c5b98ce1-f083-43ca-bd25-c1dc70c40b07"), 3, new DateTime(2001, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gigondas Le Grand Montmirail", "Fantastic. Dark and deep, with lots of black fruit, this is as silky a red as you can imagine—all cream and smoothness and seduction. Full-bodied, it kicks in with a touch of fresh fruit character on the long, juicy finish. Massive, sweet tannins lurk in the background, so it should age. Drink now through 2010. 3,750 cases made.", 20.0m, "2000", new Guid("59fa06a0-00cf-4bd8-b310-5efa4c6ad86f") },
                    { new Guid("f406ddec-19a3-4fe2-a7d6-7f541fa03f4b"), 3, new DateTime(1998, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gigondas Les Hauts de Montmirail", "Outstanding. Yes, it's oaky, but this exciting Southern Rhône red is also packed with freshly crushed black currant, black cherry, cranberry and blueberry flavors that burst from the glass. Thick, dense and seamless in its smooth texture, it's inky black in color, full in body and layered with violet, spice and mocha notes. The finish is silky-smooth. Drink now through 2007. 1,915 cases made.", 32.0m, "1996", new Guid("59fa06a0-00cf-4bd8-b310-5efa4c6ad86f") },
                    { new Guid("2ef2de0a-235c-4241-ae5a-ee5d09f157d6"), 6, new DateTime(1998, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fuissé Château de Beauregard", "Exotic, with a lovely balance of oak and fruit. It smells of apple blossoms and tastes of kiwi, lime, pineapple and juicy ripe pear. Crisp yet also very smooth in texture, this medium-bodied Pouilly turns smoky and toasted on the lingering finish. Hunt for it. Drink now through 2000. 10,000 cases made.", 21.0m, "1996", new Guid("c6d17e7e-78e6-4bc3-9d17-7fcc485251a9") },
                    { new Guid("abca4649-d3bb-4540-b225-8a21dd253cd1"), 6, new DateTime(1998, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Hermitage White Les Nobles Rives", "Very lush and ripe, honeyed as can be, showing a supple texture that's to die for. Offers layers of honey, peach, pineapple and dried figs in an almost ethereal package. Full-bodied, with a long, harmonious finish. 1,330 cases made.", 29.0m, "1996", new Guid("e0c63b08-5947-4f10-abb8-76f04d86ef31") },
                    { new Guid("9890ac06-04e0-4089-ba68-e4c15db092b6"), 6, new DateTime(2006, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Adelaide Hills M3 Vineyard", "A seamless wine that has real presence and delivers plenty of personality. Lithe and refined, with lovely peach, orange and spice aromas and flavors. Harmonious, many-layered and very, very long. Drink now through 2014. 3,000 cases made.", 29.0m, "2004", new Guid("71e67dfa-6ba8-4107-ab1e-5bf22e730d0c") },
                    { new Guid("d707d765-7e4d-4eb2-a374-f369d4a0ce43"), 6, new DateTime(1998, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc South Australia", "Bright and fresh, appealing for its zingy passion fruit and nectarine flavors that linger beautifully on the disarmingly pure finish. Drink now. 9,000 cases made.", 14.0m, "1996", new Guid("71e67dfa-6ba8-4107-ab1e-5bf22e730d0c") },
                    { new Guid("632565cb-a695-4e7a-ba9f-9570fe3cf73e"), 3, new DateTime(1998, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Toscana Farnito", "Fabulous aromas of blackberry, black currant and mint. Full-bodied, full of tannins, yet polished and reserved. Slightly closed on the finish but should give more fruit with time. A top-notch Cabernet nearly every vintage. Best after 2000. 6,500 cases made.", 25.0m, "1995", new Guid("7e1b9260-5434-4a4d-ab05-6af95885ac30") },
                    { new Guid("aab98feb-4b40-4b8e-9686-b5ffc28b815d"), 6, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Wehlener Sonnenuhr", "Fabulous. More like an auslese, this '97 spätlese is rich and zingy yet packed into a lean framework. Pinpoint delineation of lime, grapefruit and slate, intensely displayed on bracing acidity, is followed by a long, long finish. Best from 2002 through 2008. 1,000 cases made.", 21.0m, "1997", new Guid("3bd8e27c-7342-4514-993c-66cb50101103") },
                    { new Guid("dd626d74-d066-484f-b1d8-b28551603ce5"), 6, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Les Vaillons Domaine Long-Depaquit", "Absolutely gorgeous. Ripe, yet focused on the minerally soil of Chablis, this is creamy and refreshingly fruity, with masses of tropical, apricot, fig and pear character, with cream to boot on the buttery, caressing finish. Great winemaking. Best from 2000 through 2006. 3,033 cases made.", 30.0m, "1997", new Guid("6fa5f78c-2901-4509-9f32-7e49c12eb64c") },
                    { new Guid("164fbfb5-d5b0-4610-8abb-bdc329159318"), 6, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault La Barre Dessus Clos Marguerite", "Full-bodied yet incredibly elegant. So refined it smells of little but a vague mineraliness, and it's the same story on the palate--a great concentration of <i>terroir</i>, cream, compact tropical fruit and stone--with good acidity and subtle toastiness. Best from 2006 through 2015. 300 cases made.", 32.0m, "1996", new Guid("57482e24-82b8-4cdf-ab40-12d85537922f") },
                    { new Guid("15dd5922-8315-4301-8ae1-c9414abc1c1b"), 6, new DateTime(1999, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Cuvée des Seigneurs", "Aromas and flavors of forest floor, mineral and lanolin are allied to a sleek, steel-plated structure in this impressive Alsace Riesling. Dry, rich and intense, the wine combines freshness and some mature elements that should develop nicely. Drink now through 2005. 4,000 cases made.", 20.0m, "1995", new Guid("2aff18b3-2686-4880-8803-4835be28c495") },
                    { new Guid("8fdc4fea-dbcf-4be5-aa17-285d6463d47f"), 3, new DateTime(1999, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet-Merlot Hawkes Bay", "Ripe, lively and remarkably elegant and refined, this has distinctive herb- and cedar-scented currant and raspberry flavors that keep spilling over on the long finish. Drink now through 2010. 5,000 cases made.", 20.0m, "1998", new Guid("7b57bf16-67d5-4864-95aa-e54e808f6abe") },
                    { new Guid("9b5a5c71-4bbf-444b-a75c-4951080804db"), 6, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Rheingau Hattenheimer Schützenhaus", "Authoritative, with a hint of a swagger, but this never loses focus. Shows peach aromas and flavors, but with more smoke and mineral, married to a rich texture and a firm backbone. Fine length of flavor. Drink now through 2007. 3,000 cases made.", 10.0m, "1997", new Guid("00c9ba05-7864-4a96-85ed-3a199f67ae3c") },
                    { new Guid("07c3221d-ec08-4227-90b6-f18069671752"), 3, new DateTime(1999, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Erta e China", "A pretty, balanced Sangiovese and Cabernet Sauvignon blend. Intriguing aromas of plum skin and fresh mushroom have a hint of flowers. Medium- to full-bodied, with velvety tannins and a fruity, minty aftertaste. Best after 2000. 3,500 cases made.", 9.0m, "1997", new Guid("9a217455-71a9-412f-afcc-080893b3e329") },
                    { new Guid("2249afd3-8013-4ebe-bad6-23c07b471bf6"), 3, new DateTime(1999, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Filsell", "Ripe and spicy, here's a generous and focused red from Australia, dripping with blackberry, black cherry, anise and leather flavors that keep pumping through the long finish. Its fine tannins can use cellaring. Best after 2001. 5,000 cases made.", 15.0m, "1996", new Guid("9f7b77a9-de34-4f20-b091-4958ee9041ed") },
                    { new Guid("a7437aea-2551-4b2c-9843-b054653bcf94"), 6, new DateTime(1999, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Gisborne", "Smooth, polished and refreshing for its juicy pineapple, citrus and vanilla-almond flavors that wrap up neatly on the silky finish. Drink now through 2004. 8,000 cases made.", 12.0m, "1998", new Guid("6a1a6d29-bf80-4003-9c0e-b0a496c413f1") },
                    { new Guid("bdec5202-d253-4039-91a9-01abdfa2e284"), 6, new DateTime(2009, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Gris Oregon Signature Collection", "Bright and lively, brimming with lemon meringue, golden raisin and green tea character. Finishes with zip. Drink now. 71,000 cases made.", 17.0m, "2008", new Guid("7e499b73-0d76-4c99-beb7-7db4c4a501cd") },
                    { new Guid("4c50fc7e-c9d0-4c18-af4d-cc35078e8617"), 6, new DateTime(1999, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Gris Oregon Reserve", "A lovely white, smooth and round on the palate, delivering a generous portion of melon, spicy pear and caramel flavors that linger beautifully, drawing you back for another sip. Drink now. 4,000 cases made.", 18.0m, "1997", new Guid("7e499b73-0d76-4c99-beb7-7db4c4a501cd") },
                    { new Guid("527f6695-a26c-4926-96da-6d4688092659"), 3, new DateTime(1999, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Haut-Médoc", "The best Cantemerle in years, beautiful and rich, with a wonderful core of fruit. Pretty plum and berry aromas, with hints of tobacco and chocolate, lead to a full-bodied wine with loads of chocolate and berry character. Long, long aftertaste. Best after 2005. 22,000 cases made.", 23.0m, "1996", new Guid("1b42ab0d-8a0b-42bf-960c-ff415850dddb") },
                    { new Guid("2e1e18c8-644d-4735-a03c-8daa11cc742f"), 3, new DateTime(1999, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja", "This ambitious red from Spain has a deep, opaque color, deep, almost charred aromas of coffee and toast and is firmly tannic on the palate, with ripe flavors of coffee, plum, prune and licorice. The wood notes are dominant now, but it has the concentration to improve with age. Best after 2002. 23,000 cases made.", 20.0m, "1996", new Guid("22c81168-05b7-463d-98b1-3f6ebe24b263") },
                    { new Guid("4b26b6f3-c7fc-485a-ac7c-d97c86192a6c"), 2, new DateTime(1999, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Gris Crimea 2 Year Old", "Delicious sweet wine. Amber-rust colored. Fruit cake with lots of dried fruits on the nose. Medium-bodied and lightly sweet, with a wonderful intensity of caramel and dried fruit character. Fresh finish. Drink now. 35,000 cases made.", 23.0m, "NV", new Guid("2f94edf1-f2da-4219-9bb8-3e991351d87c") },
                    { new Guid("86ef7975-eae4-420b-a5f7-9c744664ab50"), 3, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc La Copa Santa", "Quite a mouthful. Dense and incredibly concentrated, with layers of complex yet well-defined flavors of bacon, dark chocolate, ripe plum and raspberry, finishing with mineral and coffeelike notes. Rather plush despite its power, but this French red will need time in the cellar to integrate even further. Best from 2002 through 2008. 3,167 cases made.", 20.0m, "1996", new Guid("5ad12343-53f4-45b1-a19f-9fb5771d1af4") },
                    { new Guid("c74ffb41-f163-4d67-9c7c-659da83fc426"), 6, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis", "Remarkably intense and rich for a village Chablis, this outstanding white kicks with life, boasting a round mouthfeel and a lovely combination of honey, pear, spice and toast flavors of good complexity and, above all, great length. Drink now. 10,000 cases made.", 15.0m, "1997", new Guid("866afe0d-b3c2-4d6c-af1b-635a27d025cc") },
                    { new Guid("61bf48ea-494e-4180-af37-cdb9d496fe30"), 3, new DateTime(2006, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet-Merlot Limestone Coast", "Lively, juicy and graceful. A smooth red with currant and plum flavors wrapped harmoniously around the finish, with overtones of toast and cedar. Drink now through 2012. 90,000 cases imported.", 15.0m, "2003", new Guid("edff174c-818f-4e6c-9741-f9c1544f7a3f") },
                    { new Guid("7dfb478d-3c71-409e-a019-9718378a44ab"), 3, new DateTime(2005, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Limestone Coast", "Bright, lively and generous with its distinctively blueberry, plum and smoky aromas and flavors. Lingers with unexpected elegance and hints of sweet spice on the finish. Drink now through 2012. 45,000 cases imported.", 16.0m, "2002", new Guid("edff174c-818f-4e6c-9741-f9c1544f7a3f") },
                    { new Guid("1277032a-6370-402c-82b0-59c25b1af5fe"), 3, new DateTime(2004, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Eastern Australia Reserve", "Dark, deep and powerful, an explosive wine, positively vibrating with ultraripe raspberry, blackberry, licorice, sweet peppers and spices, all balanced with juicy acidity and finishing with remarkable harmony and tremendous length. A real knockout. Best after 2006. 800 cases made.", 40.0m, "1999", new Guid("edff174c-818f-4e6c-9741-f9c1544f7a3f") },
                    { new Guid("916d0867-ba5c-45c8-9ac8-c4f15ee3911f"), 3, new DateTime(1999, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet-Merlot Coonawarra", "Ripe and supple, with a chocolate and tobacco edge to the lovely berry, currant and herb flavors that linger through the silky finish. Has power and grace. A terrific wine, period, and even better for the price. Drink now through 2008. 10,000 cases made.", 15.0m, "1996", new Guid("edff174c-818f-4e6c-9741-f9c1544f7a3f") },
                    { new Guid("64224ec0-f716-4364-a744-4ed60b52f9b0"), 3, new DateTime(1999, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tempranillo Rioja", "Ambitious. This deeply colored, highly extracted red has a muscular structure, with mouthfilling tannins that dominate coffee and smoke flavors, which in turn dominate the core of ripe plum, tobacco, licorice and prune flavors. Give it time and watch it soar. Best after 2002. 2,500 cases made.", 28.0m, "1996", new Guid("2866b670-342d-4f92-966c-82d787c57e7c") },
                    { new Guid("20838fa5-5399-4128-973a-7b5688e82b4c"), 6, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Victoria Long Paddock", "What a find at this price and score. A lively white, crisp and juicy, with layers of vibrant apple, nectarine, passion fruit and floral flavors that last on the gentle finish. Wonderful now for its freshness. Drink now through 2001. 30,000 cases made.", 9.0m, "1997", new Guid("77d58946-16b8-4241-810d-21e3f58ee47d") },
                    { new Guid("3d689add-f5db-4380-8242-1e356f940cb3"), 3, new DateTime(1999, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Colli della Toscana Centrale Anagallis", "This is an ultrafine Sangiovese. Dark ruby in color, with bright aromas of raspberry, cherry, violet and rose. Full-bodied, with refined tannins and a superlong aftertaste. Best after 2000. 1,665 cases made.", 34.0m, "1997", new Guid("efdaae34-ab4d-46f4-8966-df7c31cbb0a5") },
                    { new Guid("be8de356-55a9-4bcf-b03a-61a324edadaa"), 3, new DateTime(2010, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Sonoma-Napa Counties", "Lush and rich, yet impeccably structured, with bold red currant and toasty mocha aromas and layered flavors of ripe plum, loamy earth and spice, finishing with big but ripe tannins. Drink now through 2015. 4,503 cases made.", 56.0m, "2007", new Guid("589e991f-f704-41d7-90c7-37e7d0b3b4b4") },
                    { new Guid("45736734-3d19-4807-b065-9c254ae89b44"), 3, new DateTime(2008, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma-Napa Counties Reserve", "Rich and complex, brimming with dense, opulent yet elegant layers of mocha, black cherry, blackberry and wild berry fruit that's pure, focused, deep and persistent, ending with mouthcoating flavors and ripe, fleshy tannins. Drink now through 2015. 1,291 cases made.", 125.0m, "2005", new Guid("589e991f-f704-41d7-90c7-37e7d0b3b4b4") },
                    { new Guid("4180e879-c77f-48c4-858a-7951cdc66e63"), 3, new DateTime(2000, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa-Sonoma Counties", "Impressive. Espresso-style, deeply concentrated Merlot, boasting a core of rich currant, coffee, black cherry and anise. Firms up on the finish, where a touch of bay leaf adds complexity to the flavor profile. Drink now through 2006. 4,137 cases made.", 38.0m, "1998", new Guid("589e991f-f704-41d7-90c7-37e7d0b3b4b4") },
                    { new Guid("3a9c4d41-068a-4e91-a23d-655fe1562a0a"), 6, new DateTime(2001, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling South Australia Gold Label", "Wonderfully aromatic Riesling, its glorious noseful of apricot and floral notes segueing into off-dry flavors that center around lime and apple, with mineral and peppery-floral overtones. Tempting now, but should develop more depth and texture with age. Best from 2003 through 2010. 7,000 cases made.", 12.0m, "2000", new Guid("33588dbb-b568-48f4-a6fb-e23e99008946") },
                    { new Guid("540921b7-7110-421e-8e99-e01503d16f98"), 3, new DateTime(2003, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Mount Veeder", "Tightly wound and firmly concentrated, it opens up to unveil a wealth of rich, exotic spices, with beefy bay leaf, earth and mineral scents to a dense, chewy core of beefy currant, blackberry and new leather, finishing with espresso, mocha and vanilla flavors that make it quite enticing. It should only get better in the next year. Best from 2005 through 2012. 430 cases made.", 50.0m, "2001", new Guid("c492a638-4f89-4255-9f16-74bed7f1f041") },
                    { new Guid("c3a35336-2ba5-4488-b427-ac174b787b72"), 3, new DateTime(2004, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Gold Label", "Round, velvety and generous with its smoky blackberry and tar flavors, gently balanced with tangy acidity, but the emphasis is on the berry and spice flavors that linger on the finish. Not a typical Barossa Shiraz, but it's a honey. Drink now through 2010. 4,000 cases imported.", 24.0m, "2002", new Guid("33588dbb-b568-48f4-a6fb-e23e99008946") },
                    { new Guid("e525a74d-0216-4428-8f13-80ee8acf375f"), 3, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Viña Cumbrero Crianza", "The polished texture carries crisp flavors of tart cherry, fresh herb, anise and mineral, supported by light, firm tannins and lively acidity. Graceful, with balanced fruity and savory notes that give way to a spicy, floral finish. Drink now through 2022. 103,700 cases made.", 10.0m, "2010", new Guid("63955801-0b77-4b02-8958-b0747a1005b9") },
                    { new Guid("c7b264f4-e142-43c2-b1cc-a6b41c4cce8a"), 0, new DateTime(2010, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bandol Rosé", "Very rich and complex, featuring flavors of dried cherry, plum, raspberry, with hints of melon. Chocolate and spice linger on the ripe finish, with a hint of meatiness. Drink now through 2013. 4,150 cases made.", 35.0m, "2009", new Guid("ebe78ab2-c2d2-4eb9-816e-4d3bd8582721") },
                    { new Guid("19449b8d-8647-4867-8633-5220214f5e8d"), 3, new DateTime(2007, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bandol La Migoua", "An intense, mineral-laden red, with concentrated layers of dark plum, raspberry, smoke and spice flavors. Chiseled and powerful, with fine-grained tannins and a luxurious cream- and cocoa-filled finish. Drink now through 2020. 288 cases imported.", 47.0m, "2004", new Guid("ebe78ab2-c2d2-4eb9-816e-4d3bd8582721") },
                    { new Guid("04844d58-c03b-4ee3-9536-0a2bb227d669"), 3, new DateTime(2003, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bandol", "Rich, bacony aromas and flavors mix with alluring dark plum and red cherry flavors in this broad-tasting, full-bodied red. Intense blood, iron and mineral notes linger with spicy elements on the powerful finish.  Drink now through 2007. 1,500 cases imported.", 27.0m, "2000", new Guid("ebe78ab2-c2d2-4eb9-816e-4d3bd8582721") },
                    { new Guid("5107592f-1b4c-437e-9c23-017a76ed1652"), 3, new DateTime(2003, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Ugolaia", "Fabulous bottle. Very, very classy aromas of freshly cut flowers, crushed berries, with hints of cedar. Full-bodied, yet extremely reserved, with fine tannins and impressive fruit. However, it's all in balance. It goes on and on. Best after 2005. 833 cases made.", 99.0m, "1997", new Guid("608c80bb-e639-4d2e-9206-6be8084c743f") },
                    { new Guid("b0924adb-52fb-4161-a321-31adaf1b31fe"), 6, new DateTime(2003, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscadet de Sèvre et Maine Sur Lie Château de Chasseloir ", "This Muscadet offers rare explosiveness, with concentrated lemon peel, grapefruit, honeysuckle, mineral and briny notes. The racy acidity carries it along effortlessly, while a second wave of fruit emerges on the finish. Hard for Muscadet to be better than this. Drink now through 2004. 15,000 cases made.", 9.0m, "2002", new Guid("62cc2b71-2755-47a2-8584-2387ba473ef7") },
                    { new Guid("63b5c6ff-8a82-45fd-9182-fe0ef270fcd0"), 3, new DateTime(2003, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toro Termes", "Blackberry fruit claims center stage in this powerful red, where it mingles with tobacco and coffee notes and plenty of smoky oak. Massive tannins give structure, but the fruit reemerges on the finish.  Best after 2004. 1,000 cases imported.", 21.0m, "1999", new Guid("3b3b9bd1-3eae-435d-b3f7-38d84ea2a1d9") },
                    { new Guid("1c683505-6e80-49ff-ae32-7c342648350a"), 3, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Appellation Series", "Rich and plump, a flood of fruit, with powerful blackberry, plum and a wee touch of peppermint, all echoing through the long finish. A serious McLaren Vale Shiraz, built to last. Drink now through 2012. 5,000 cases made.", 17.0m, "2001", new Guid("f8e1fcc6-993f-40e2-8967-daafe84c3840") },
                    { new Guid("078b5f30-f420-4283-a327-48cf718ff195"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Vigneto Brich Ronchi", "Very attractive aromas of ripe plum, berry and grilled meat. Full-bodied, with lovely fruit and big, silky tannins. The finish is fantastic, with very ripe fruit, yet refined and caressing in texture. Best after 2008. 2,080 cases made.", 65.0m, "2000", new Guid("6fc39bf3-840e-467d-937e-84887e1dce36") },
                    { new Guid("8d8df7c8-e478-42a4-a792-64733677aa36"), 3, new DateTime(2011, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Bric Balin", "Superfresh and focused, delivering pure flavors of blackberry, black currant, violet and mineral, with a hint of tar. It's all backed by refined tannins and bright acidity, providing structure that should allow this to develop over the next 20 years. Best from 2014 through 2030. 1,916 cases made.", 53.0m, "2007", new Guid("b50f26df-b376-42b3-9884-0f36473384a2") },
                    { new Guid("23df78d6-76b8-4be4-9ca6-6fbbb278c412"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Basarin", "Incredibly rich, with masses of ripe fruit on the nose, from plum to blackberry, showing smoky oak undertones. Full-bodied, with layers of silky, ripe tannins and a long, fruity, almost jammy finish. A real triumph. Moccagatta is a leader in Barbaresco right now, and this is one of the greatest wines ever from this producer. Best after 2010. 550 cases made.", 60.0m, "2000", new Guid("b50f26df-b376-42b3-9884-0f36473384a2") },
                    { new Guid("a524692b-5017-4991-901f-5d78ff35aab6"), 6, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Groenekloof", "Really fine Sauvignon here--juicy and vibrant, with lemon, chive, chalk, nettle and mineral notes, and a long, lively finish. Strong grassy edge to the finish adds dimension. Drink now through 2004. 11,000 cases made.", 14.0m, "2002", new Guid("d1568a64-4b3e-4d4b-9750-9486b78bf665") },
                    { new Guid("b262d60f-dd72-423d-8e2f-3ac4c59b733d"), 3, new DateTime(2003, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc La Méjanelle Cuvée Sommelière", "Full-bore, decadent red, with dark plum and blackberry flavors. Bacon fat notes mixed in. Rich, deep and lush, with dark chocolate and espresso notes on the finish. Powerful. Drink now through 2005. 6,000 cases made.", 13.0m, "2000", new Guid("3929cb38-ce2b-4b86-b32c-9602617e1671") },
                    { new Guid("156f071d-7ca8-447c-9a63-8f6eed30309d"), 3, new DateTime(2017, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Edna Valley Reva Alban Estate", "Refined yet dynamic, with a structured and balanced core of acidity and tannins framed by formidable black raspberry, smoky meat, licorice and orange zest flavors that build momentum toward polished tannins. Drink now through 2026. 2,000 cases made.", 98.0m, "2013", new Guid("8f588805-1e7f-46a4-b93a-261e3bf8ae44") },
                    { new Guid("2fb1a151-8b0e-4a42-a044-693e8b93d749"), 3, new DateTime(2012, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Edna Valley Patrina Alban Estate", "Both rich and powerful, with a measure of finesse and restraint, mingling dark berry, boysenberry and huckleberry flavors, with notes of spice, cedar, tobacco and licorice. Maintains its focus and persistence on the finish. A new Syrah from Alban, coming from a younger vineyard. Drink now through 2023. 1,200 cases made.", 48.0m, "2009", new Guid("8f588805-1e7f-46a4-b93a-261e3bf8ae44") },
                    { new Guid("9071a5c7-c35b-493c-bd5b-3dd81abc55d3"), 3, new DateTime(2007, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Edna Valley Alban Estate Reva", "Rich and beefy, with plush smoke, blueberry, blackberry and spicy, leathery scents. Fans out and gains depth and complexity, picking up exotic spice notes. Best from 2008 through 2014. 2,500 cases made.", 74.0m, "2005", new Guid("8f588805-1e7f-46a4-b93a-261e3bf8ae44") },
                    { new Guid("70c81301-306c-43ab-b9f3-abd15ce3ceda"), 3, new DateTime(2003, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Edna Valley Reva", "Deep purple, with loads of ripe plum, black cherry and blackberry flavors, scents of violets, dark fruits, new leather, currants and minerals. A real mouthful that manages to show elegance and a measure of finesse. Long, gushing aftertaste. Drink now through 2012. 850 cases made.", 44.0m, "2000", new Guid("8f588805-1e7f-46a4-b93a-261e3bf8ae44") },
                    { new Guid("3676eae0-8436-4d33-8d3a-db023cefdee7"), 6, new DateTime(2003, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Nahe Niederhäuser Hermannshöhle", "A beautifully understated spätlese, with subtle power, density and length. The passion fruit, sweet corn, berry, fennel and mineral notes are layered and just keep coming at you. Superbly balanced, it seems effortless and ethereal. The finish just melts away. Best from 2005 through 2020. 250 cases made.", 48.0m, "2001", new Guid("bfb95b02-20f7-4c80-80b1-6d5a247fa689") },
                    { new Guid("39661edf-23e0-4e6f-b46a-8231f1d86c05"), 3, new DateTime(2019, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast UV-SL Vineyard", "Rich and expressive, featuring concentrated boysenberry, plum tart and dark cherry flavors that are flush with crunchy acidity. Minerally midpalate, with a long, plush finish that offers flinty and meaty overtones. Drink now through 2024. 791 cases made.", 85.0m, "2017", new Guid("baf4a3f2-61ad-414d-9844-56cd805bed8d") },
                    { new Guid("a4ca3055-88a6-45a2-bddf-e277c9ada42f"), 6, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Carneros Larry Hyde & Sons", "Voluptuous and creamy, with densely packed flavors of apple pastry, pear tart and peach and hints of mango that are long and rich. The plush, complex finish is filled with notes of spice and dried herbs. Drink now through 2026. 960 cases made.", 85.0m, "2016", new Guid("baf4a3f2-61ad-414d-9844-56cd805bed8d") },
                    { new Guid("148c8303-69de-427e-9e20-b35dd817956c"), 6, new DateTime(2014, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Eastside", "Delicious from start to lingering finish, this is rich, broad and exotic, with tiers of complexity built around smoke, fig, apricot, citrus peel and bubble gum flavors. Features a silky smooth texture and just the right amount of tannins. Drink now. 825 cases made.", 80.0m, "2012", new Guid("baf4a3f2-61ad-414d-9844-56cd805bed8d") },
                    { new Guid("3b60a40a-c51e-48ce-9178-6857cb010c49"), 6, new DateTime(2006, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Coast Lauren", "A stunning wine, with amazing richness, depth, purity of flavor and concentration, with layers of ripe pear, crème brûlée, date, apricot and hazelnut flavors that glide along on a silky smooth texture, with a finish that keeps revealing extra flavor nuances. Drink now through 2011. 900 cases made.", 70.0m, "2004", new Guid("baf4a3f2-61ad-414d-9844-56cd805bed8d") },
                    { new Guid("47f0ca15-226f-4820-a3ed-46f646059886"), 6, new DateTime(2003, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Coast Ritchie Vineyard", "Wonderful aromas of flowers, honeysuckle, fig, butter and hazelnut. Rich, silky flavors that are elegant and refined, with a creamy, citrus-laced finish. Vibrant acidity gives it backbone and sustains the flavors on a long, minerally aftertaste. Drink now through 2007. 702 cases made.", 50.0m, "2001", new Guid("baf4a3f2-61ad-414d-9844-56cd805bed8d") },
                    { new Guid("7c9056b9-a6de-4ea2-a68e-fde87de57f93"), 3, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Chryseia", "Essence of blackberry, dark cherry and smoke make this an alluring and distinctive red. Intense concentration and a firm structure provide loads of power. Dark chocolate notes dominate the finish. Still quite youthful, but should develop nicely in the years ahead. Best from 2004 through 2008. 3,000 cases made.", 40.0m, "2001", new Guid("a740c4ac-7ef3-4d3d-b109-23206d661307") },
                    { new Guid("fe995757-23ff-44c5-9663-628e64534134"), 3, new DateTime(2016, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Lily's Garden", "Notes of espresso and toasted hazelnut are rich and thick, with mocha-accented tannins that lend a velvety backdrop to the polished blackberry, licorice and black cherry flavors, lingering generously on the long finish. Drink now through 2030. 2,470 cases made.", 69.0m, "2014", new Guid("38a46712-d2ad-443b-8998-91e0e3010e75") },
                    { new Guid("a4274aa5-41f7-47de-9819-2bb02bc951e1"), 6, new DateTime(2003, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Pfalz Bird Label", "Brilliance and clarity highlight this Riesling, whose peach, grapefruit and spice notes are brought into focus against the vivid structure. Subtle, lingering finish. Drink now through 2006. 5,000 cases made.", 13.0m, "2001", new Guid("015e08b0-06c7-4550-8fba-d0ecb6db0ec1") },
                    { new Guid("19cb7bad-d845-4afa-a133-13af2161af1d"), 6, new DateTime(2007, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viognier-Marsanne McLaren Vale The Hermit Crab", "Soft and charming. A beguiling mouthful of spicy pear and grapefruit flavors that linger with focus on the open-textured finish. Not as rich as pure Viognier, but better balanced. Drink now through 2010. 3,500 cases imported.", 16.0m, "2006", new Guid("7a3a97fe-de63-40d3-8928-bd4987d272da") },
                    { new Guid("cc94ee0b-0b14-4244-acf9-19736c0f0a6f"), 3, new DateTime(2003, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah California Roll Ranch Vineyard", "Simply delicious, overflowing with flavor. Inky dark, ripe, rich and plush, with a load of jammy plum, blackberry, wild berry and spicy aromatics. Finishes with a long, complex aftertaste and a gush of fruit. Tannins are well-integrated. Drink now through 2010. 600 cases made.", 45.0m, "2000", new Guid("32d2b167-16df-40ba-871e-d76c0ec378ab") },
                    { new Guid("3baf6dc2-160a-4514-b30d-b64f2dd275be"), 6, new DateTime(2003, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Les Pierrets", "Etched in stone. Dry and tightly wound, this conjures up a limestone cliff after a thunderstorm. Quince and peach also mingle in its rocky depths. Firm and concentrated, this is for rock hounds. Best from 2005 through 2012. 1,000 cases made.", 47.0m, "2001", new Guid("1c52ad01-e7b9-4f98-aecb-40f38b4190ba") },
                    { new Guid("afb40894-4b05-4d44-9925-0f34e3a065f0"), 3, new DateTime(2017, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tempranillo Ribera del Duero", "Black cherry, red plum and currant flavors are fresh and lively in this firm red, backed by dark chocolate, licorice and toasty notes. Light, firm tannins and citrusy acidity keep this focused. Drink now through 2023. 58,333 cases made.", 24.0m, "2015", new Guid("7d226d6f-4a21-4ae9-9fb4-d1321cc8ec78") },
                    { new Guid("b2d679c9-ea6e-42e3-8eaa-c4df59837a46"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero", "Beautifully polished, complex but balanced, this impressive red shows ripe fruit, judicious oak and a vibrant sense of place. Not overly muscular, but finely proportioned and deeply flavored. Drink now through 2010. 1,000 cases imported.", 28.0m, "2000", new Guid("7d226d6f-4a21-4ae9-9fb4-d1321cc8ec78") },
                    { new Guid("95cf0d23-f8ad-4c5c-89db-c2ddbf58ff8c"), 6, new DateTime(2018, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Niederösterreich Freiheit", "Grass and pine aromas and flavors predominate, with peach, grapefruit and floral notes underneath. Fine balance and persistence here, ending with mouthwatering notes of peach, flowers and mineral. Drink now through 2024. 1,000 cases imported.", 20.0m, "2016", new Guid("2ef9871f-9065-4dfe-938a-487897dcacb9") },
                    { new Guid("1dd9e516-e8ee-4b36-8b71-ff3c51635482"), 6, new DateTime(2003, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kremstal", "A floral white, delicate and graceful, this combines apple, citrus and mineral flavors with a slim yet medium-bodied frame. Its silky texture and seamlessness reveal its pedigree. Drink now through 2010. 1,000 cases made.", 18.0m, "2002", new Guid("2ef9871f-9065-4dfe-938a-487897dcacb9") },
                    { new Guid("a1b32cca-537d-4447-9b89-2b4f6456856f"), 3, new DateTime(2005, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toro Campo Eliseo", "This elegant red shines in the international style, with toasty oak, polished texture and clean, ripe fruit. Blackberry, espresso and mineral notes mingle over well-integrated tannins and emerge haloed by spices on the toasty finish. Drink now through 2012. 1,250 cases made.", 50.0m, "2002", new Guid("f0355eed-7af0-4ade-b5af-fd4600075c35") },
                    { new Guid("65559600-12b9-4761-9b4a-5012ab579018"), 3, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toro El Albar Excelencia", "A beauty, in the international style. Dark color, lavish oak, ripe fruit and muscular structure mark this red. Flavors range from cassis to chocolate to mineral. Balanced and deep.  Best from 2005 through 2010. 1,500 cases made.", 25.0m, "1999", new Guid("f0355eed-7af0-4ade-b5af-fd4600075c35") },
                    { new Guid("b779e894-461a-403f-a84c-882ac0d8e615"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Solanes", "A luscious, velvety texture carries plum, toast, spice and licorice flavors in this generous red. Tannins are big, ripe and round, the finish sweet and spicy. Drink now through 2012. 4,700 cases made.", 25.0m, "2001", new Guid("1eb91c75-20b2-47b7-a9b5-a9ec97059ef4") },
                    { new Guid("d81bb6e7-c51a-4a33-b5be-4a8927677a0d"), 3, new DateTime(2004, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Rita Hills Southing", "Ultraripe, rich and plush, with concentrated, detailed black cherry, wild berry and plummy flavors framed by firm toasty, cedary oak, and finishing with a long, intense, persistent aftertaste offering a wealth of flavors and plenty of rich minerally tannins. Drink now through 2009. 1,000 cases made.", 45.0m, "2002", new Guid("c74ac5a3-4feb-4ccb-9646-44bb0d9c1b7f") },
                    { new Guid("e860e29a-9f20-4f2c-9f6e-6a4aa7202efc"), 3, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale The Footbolt Old Vine", "Bright and jazzy, with delicious blackberry and cherry and subtle touches of anise and pepper weaving through the lively, long finish. Drink now through 2010. 5,000 cases imported.", 18.0m, "2001", new Guid("7a3a97fe-de63-40d3-8928-bd4987d272da") },
                    { new Guid("685cab02-dda0-482c-9fae-9cee77bc887d"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Rita Hills Botella", "Dark, firm, rich and intense, with lots of concentration to the black cherry, blackberry, fresh earth and blueberry jam flavors. Well-focused, with mouthcoating, integrated tannins and a long, pure, rich aftertaste. Drink now through 2008. 536 cases made.", 25.0m, "2001", new Guid("c74ac5a3-4feb-4ccb-9646-44bb0d9c1b7f") },
                    { new Guid("a6729563-7e1a-436e-861f-d60d9892cacf"), 3, new DateTime(2015, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Ribbon Ridge Le Pré Du Col Vineyard", "Supple, seductive and distinctive, with pure blueberry fruit, shaded with white pepper, pear skin and floral character. The flavors dance merrily into the long, deftly balanced finish. Offers presence and intensity without extra weight. Drink now through 2023. 1,500 cases made.", 60.0m, "2013", new Guid("25d92a94-56a2-4259-89b5-2fa24d75f7f5") },
                    { new Guid("1e6baec9-b7e8-4dff-b90d-9ca5057fc1ae"), 3, new DateTime(2019, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alpha M Colchagua Valley", "Rich, robust and lusciously spiced, with concentrated dark cherry, plum and boysenberry flavors supported by fresh acidity. Intense midpalate minerality is followed by a finish loaded with chocolate and cream notes. Cabernet Sauvignon, Cabernet Franc and Petit Verdot. Drink now through 2024. 4,500 cases made, 500 cases imported.", 100.0m, "2015", new Guid("8986292f-2dcf-4334-a888-3db845ee591d") },
                    { new Guid("692f9363-8903-4211-ae04-6fb543f5cec5"), 3, new DateTime(2016, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Colchagua Valley Classic Series", "There's minerally freshness to the ripe dark plum, blackberry and cherry flavors, with peppery hints. Well-structured and crisp midpalate, featuring Asian spice and black fig notes on the finish. Drink now through 2020. 50,000 cases imported.", 13.0m, "2014", new Guid("8986292f-2dcf-4334-a888-3db845ee591d") },
                    { new Guid("69740917-d22f-42ee-a719-bed4dabdfa6e"), 3, new DateTime(2015, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Colchagua Valley Alpha", "Deeply colored and voluptuous, with a lush, spicy aroma and rich flavors of blackberry, blueberry and dark plum. Concentrated chocolate mousse notes emerge midpalate, leading to a cream-filled finish that ends with plenty of mocha accents. Drink now through 2021. 2,000 cases imported.", 22.0m, "2012", new Guid("8986292f-2dcf-4334-a888-3db845ee591d") },
                    { new Guid("0540c0f1-df81-4b3d-8c7f-1e1ae59ee3a9"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Colchagua Valley Alpha Apalta Vineyard", "Suave, stylish wine, with aromatic black cherry, blackberry, vanilla, violet and mineral notes. Shows darkly roasted coffee bean and tar notes on the finish, but relies more on guile and finesse overall. Drink now through 2007. 18,000 cases made.", 23.0m, "2003", new Guid("8986292f-2dcf-4334-a888-3db845ee591d") },
                    { new Guid("6762bd38-c24d-4818-aa86-23506f88ed8d"), 3, new DateTime(2003, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Santa Cruz Alpha Apalta Vineyard", "Lovely fruit here--cassis, red currant and blackberry--with supple texture. Yet there's serious grip, with mineral, cocoa and toast notes as well, all framed by firm yet well-integrated tannins. This should develop nicely. Drink now through 2006. 20,150 cases made.", 20.0m, "2001", new Guid("8986292f-2dcf-4334-a888-3db845ee591d") },
                    { new Guid("e7f59dad-5389-461e-9575-0694eebc54fd"), 3, new DateTime(2015, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mencía Bierzo Cepas Viejas", "The plush texture delivers rich flavors of plum, blackberry and boysenberry, with accents of coffee, mountain herb, cedar and mineral. Fine-grained tannins give this structure, showing enough acidity to stay fresh. A modern-style red. Drink now through 2025. 10,000 cases made.", 30.0m, "2011", new Guid("d9cefee4-7fa4-4aa7-9882-17d57b8fc942") },
                    { new Guid("3758e1b7-7797-4bda-901e-75713383f7f0"), 3, new DateTime(2003, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mencía Bierzo", "This dark red impresses with its solid structure and good balance. The flavors are muted now, but the concentration is evident, and a core of wild plum and toast notes will open with time. Best from 2004 through 2009. 10,000 cases made.", 16.0m, "2001", new Guid("d9cefee4-7fa4-4aa7-9882-17d57b8fc942") },
                    { new Guid("434ea669-4f7a-4a6c-9001-96645bab39c7"), 3, new DateTime(2010, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Stump Jump Red South Australia", "Light in texture and fragrant with floral, berry and beet aromas, this plays its fresh flavors against refined tannins, lingering effortlessly. Grenache, Shiraz and Mourvèdre. Drink now through 2013. 5,000 cases imported.", 11.0m, "2008", new Guid("7a3a97fe-de63-40d3-8928-bd4987d272da") },
                    { new Guid("92685fe1-806b-46e0-b059-d55bbc60eba4"), 3, new DateTime(2009, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale The Stump Jump", "Ripe and open-textured, this is impressive for the way it packs in the cherry, blackberry and raspberry flavors and keeps its foot on the gas pedal through the mineral-inflected finish. Drink now through 2018. 5,000 cases imported.", 11.0m, "2008", new Guid("7a3a97fe-de63-40d3-8928-bd4987d272da") },
                    { new Guid("4021c241-2592-4520-af8a-be88f15f73a8"), 3, new DateTime(2003, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Viña Hormigas Reserva", "Serious juice here, with deep, vivid purple color, loads of plum, boysenberry and blackberry fruit, extra layers of meat, mineral and toast, and a terrific velvety texture despite the dense, muscular tannins. Drink now through 2007. 5,000 cases made.", 24.0m, "2001", new Guid("16ef3f74-447c-4ec7-a253-7e110bff97c5") },
                    { new Guid("09c4eaae-1fb3-4857-a84b-1c6ad371bd9c"), 3, new DateTime(2008, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Angels Share", "Big, bold and expressive, with some unexpected transparency to the black cherry, raspberry, red plum and Asian spice aromas and flavors, lingering on the open-textured finish. The aftertaste just keeps coming. Drink now through 2015. 6,300 cases made.", 35.0m, "2007", new Guid("38a46712-d2ad-443b-8998-91e0e3010e75") },
                    { new Guid("6a24aab6-1e8b-4aea-ba9f-3e9746d0ec10"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Angel's Share", "Not a huge mouthful, but it unfolds its flavors and builds up intensity as the blackberry, plum, cedar and floral pepper flavors persist onto the long, generous finish. Best from 2007 through 2015. 2,200 cases imported.", 24.0m, "2003", new Guid("38a46712-d2ad-443b-8998-91e0e3010e75") },
                    { new Guid("1177a5ce-f435-4ac2-af82-f90323e5731a"), 3, new DateTime(2003, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley Bella's Garden", "Rich, dense and distinctive for its pepper and exotic spice notes weaving through the chewy cherry pie, dried blueberry and coffee flavors, echoing forever on the long, long finish. Drink now through 2020. 400 cases imported.", 40.0m, "2002", new Guid("38a46712-d2ad-443b-8998-91e0e3010e75") },
                    { new Guid("25e3bdc3-db33-460e-80d1-9b4e184a2105"), 3, new DateTime(2003, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley Seven Hills Vineyard", "A racy, wild, generous Syrah, juicy with lively acidity balancing the raucous blackberry, plum and a whiff of mineral and gamy character that wafts through the long, aromatic finish. Tannins are subdued, and it all holds together nicely. Drink now through 2010. 960 cases made.", 35.0m, "2001", new Guid("c349f8f5-4019-4971-85d8-f25c5d0c9d3f") },
                    { new Guid("e324780a-eadb-4802-97b7-cc71cb0a47bf"), 3, new DateTime(2001, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Lagrein Alto Adige Steinraffler", "A blockbuster red. Esoteric. Black-ruby color. Intense aromas of cooked fruits and grilled meat. Full-bodied and very chewy, with a long, long finish. Like a top Syrah. Best after 2003. 1,500 cases made.", 38.0m, "1997", new Guid("371bd17f-3e38-4cd1-8256-620b45c6118d") },
                    { new Guid("8aaedeb8-57ea-4164-9a25-60282ef6efe3"), 3, new DateTime(2001, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône-Villages Cairanne Cuvée Prestige", "Beautiful. Rich, ripe, thick and flavorful. The sweet-tasting fruit and lush tannins make for an opulent red with smoke, mineral, grilled meat, plum and blackberry character. Balanced. Drink now through 2015. 1,500 cases made.", 23.0m, "1999", new Guid("7d988cbb-8a25-4d78-8361-2d6688080762") },
                    { new Guid("b761e576-a757-4e5f-a01a-72a7fb98e5eb"), 6, new DateTime(2002, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Soave Classico Superiore", "Fantastic white. Lovely aromas of fresh green apples, grapefruit and peaches. Full-bodied and very rich, with intense fruit and a long, long finish. A super Soave. Drink now through 2005. 20,000 cases made.", 13.0m, "2000", new Guid("09daf951-2950-413a-9513-7d8161dd1e02") },
                    { new Guid("42586928-467c-4061-9f4e-f82a20590f1a"), 6, new DateTime(2001, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Soave Classico Superiore La Rocca", "Hard to believe it's a Soave. Super, with wonderful late-harvest aromas of honey, light butterscotch and straw, this wine is full-bodied, with lots of ripe fruit and a long, long creamy, fruity aftertaste. Drink now. 3,330 cases made.", 21.0m, "1998", new Guid("09daf951-2950-413a-9513-7d8161dd1e02") },
                    { new Guid("02fd59d2-e011-4678-a734-c7a913e7e28f"), 2, new DateTime(2001, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Late Bottled Port", "An outstanding LBV. Black in color, with wonderful aromas of blackberries, cherries and licorice, it's full-bodied and medium sweet, with soft tannins and a long finish. Delicious now and will improve with age. Great value considering the quality. Better than previously reviewed. Drink now through 2008. 10,000 cases made.", 21.0m, "1994", new Guid("db3a9280-ae10-45e7-8cea-07fda819bd05") },
                    { new Guid("73b40ed3-cba3-4a8e-b3b6-9027ade9e5f2"), 3, new DateTime(2001, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puente Alto", "This has a laser beam of currant and cassis running through its densely textured palate. Extra notes of cocoa powder, mineral and smoke come in on the finish. Densely structured, but the tannins are sweet and finely grained. A serious wine for the cellar. Cabernet Sauvignon, Carmenère and Cabernet Franc. Best from 2002 through 2006. 12,000 cases made.", 91.0m, "1999", new Guid("e74c5852-59a2-412f-a46b-4431dfd56c82") },
                    { new Guid("8e2d1476-3218-4a53-9f27-4e2bb85c3135"), 3, new DateTime(2003, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Pic St.-Loup Cupa Numismae", "Pure, intense red, with delicious red plum, berry, game and spice flavors on a muscular frame. Pepper and leathery notes linger appealingly on the finish. Almost Hermitage-like in its structure.  Drink now through 2006. 4,700 cases made.", 18.0m, "2000", new Guid("b8f68009-3672-4b49-bc4d-1d2ff5b510f3") },
                    { new Guid("23d89dc9-06f3-43f5-b023-9d7b151befc8"), 3, new DateTime(2001, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Pic St.-Loup", "An elegant, well-balanced red, with a chocolaty aroma and pure flavors of red plum and berry. Finishes with brickish and mineral notes.  Drink now through 2003. 4,300 cases made.", 13.0m, "1999", new Guid("b8f68009-3672-4b49-bc4d-1d2ff5b510f3") },
                    { new Guid("d22c9110-1f32-4be1-885b-d99906866751"), 6, new DateTime(2015, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chenin Blanc Stellenbosch W Block", "This distinctive white shows a wide range of yellow apple, white ginger and quince notes, with intriguing blanched almond, Brazil nut and dried citrus peel flavors slightly reminiscent of Sherry. Very fresh and energetic, showing no oxidation. A lovely combination of ripeness and cut. Only available in 3-pack with other Single Vineyard Series bottlings. Drink now through 2020.  ", 33.0m, "2013", new Guid("839453f3-d3de-41cd-b990-69603f0541f4") },
                    { new Guid("7fe6900b-6cbd-4ada-87c9-ec0ef3d09c3a"), 3, new DateTime(2001, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Pic St.-Loup Grande Cuvée", "A full-bore, meaty French red, showing ripe flavors and aromas of dark plum, cassis and blackberry with exotic spicy and mineral elements. Dark chocolate, roasted plum and espresso notes make for an intense and long-lasting finish. Best from 2002 through 2007. 1,500 cases made.", 25.0m, "1998", new Guid("552fe6e3-6eef-46e3-ad5a-c7bb61e951fe") },
                    { new Guid("ca053893-10a8-4f55-ad9b-7ea1a4e61e0e"), 3, new DateTime(2014, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Faithful Hound Stellenbosch", "Dark and muscular, with layers of fig, blackberry and black currant paste offset by espresso, bittersweet cocoa and smoldering tobacco notes. Fleshy and dense on the finish, but well-defined, with a backdrop of singed cedar and bay leaf. Cabernet Franc, Cabernet Sauvignon, Merlot, Malbec and Petit Verdot. Best from 2015 through 2020. Tasted twice, with consistent notes. 3,000 cases imported.", 25.0m, "2011", new Guid("839453f3-d3de-41cd-b990-69603f0541f4") },
                    { new Guid("c4f7f2ac-c0aa-4b8e-bff0-ef02c520591c"), 6, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Stellenbosch", "Lots of shirt-collar-grabbing verve in this serious South African Sauvignon, a vibrant mix of lime, cut grass, sweet pea and chalk, with good, medium-weight texture and a long, juicy finish. Drink now through 2003. 11,000 cases made.", 22.0m, "2001", new Guid("839453f3-d3de-41cd-b990-69603f0541f4") },
                    { new Guid("497d5fb2-ac9b-4637-bf2c-cc1f0d145e7b"), 3, new DateTime(2004, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Chalone", "Delicious for its core of fleshy roasted plum, wild berry and black cherry, this wine holds its focus while picking up a nice touch of beefy, peppery flavors, only to return to its core of pure Syrah, ending with a nice lift in flavor and ripe, integrated tannins. Drink now through 2009. 2,310 cases made.", 25.0m, "2002", new Guid("9b16ac40-7c6b-4f77-9701-8fa13e9c9581") },
                    { new Guid("ae9556e2-5a99-4b1f-8870-a485e318af79"), 6, new DateTime(2001, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Chalone", "A ripe, rich, complex and concentrated California Chardonnay, with layers of ripe pear, fig, citrus and nectarine. Turns creamy and buttery in texture, with a long, full aftertaste. Drink now through 2006. 24,310 cases made.", 31.0m, "1999", new Guid("9b16ac40-7c6b-4f77-9701-8fa13e9c9581") },
                    { new Guid("89080a8d-51a7-4b3d-93e0-2d4647a1eb56"), 6, new DateTime(2014, 1, 1, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Mosel Old Vines", "This version is richly spiced and filled with luscious flavors of glazed apricot, ripe melon and peach cobbler. Shows minerally notes, offering a finish of golden raisin and nutmeg. Drink now through 2016. 10,000 cases made.", 16.0m, "2012", new Guid("bed978a1-a1e2-47bc-bc91-ef496a68de5f") },
                    { new Guid("077b8e65-f399-45db-961b-ec65aecf98d5"), 6, new DateTime(2010, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Mosel Ockfen Bockstein", "Offers enticing peach, apricot, lime, and slate aromas and flavors, with a touch of vanilla cream. Creamy, yet bright and pure, with persistence and a complex finish. Drink now through 2025. 1,800 cases made.", 19.0m, "2009", new Guid("bed978a1-a1e2-47bc-bc91-ef496a68de5f") },
                    { new Guid("fb677f2a-d8af-425b-b197-eacf8f730984"), 6, new DateTime(2008, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Mosel", "Needs a little air to reveal its floral, apricot and slate aromas and flavors. Beautifully integrated, with a lingering, stony, savory finish. Drink now through 2018. 10,000 cases made.", 15.0m, "2007", new Guid("bed978a1-a1e2-47bc-bc91-ef496a68de5f") },
                    { new Guid("2f3fad3d-fcfb-4b0f-9000-c1ce75d2f2a1"), 6, new DateTime(2001, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Mosel-Saar-Ruwer", "Very tightly wound at this stage, here's a racy, intense Riesling full of finely etched lime, rhubarb and slate flavors. Be patient, it has all the elements. Best from 2002 through 2008. 4,000 cases made.", 10.0m, "1999", new Guid("bed978a1-a1e2-47bc-bc91-ef496a68de5f") },
                    { new Guid("68da46ce-25a8-40f0-9e07-67993a5b11d4"), 3, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gattinara", "A poor man's Barolo, with mocha, smoky tea and dried fruit on the nose, and superripe, thick and rich fruit, verging on raisins. Full-bodied, with soft, silky tannins and a juicy, pruny finish. Hard not to drink it now. Best after 2005. 17,000 cases made.", 30.0m, "1997", new Guid("f45abe02-dd09-4868-8a06-c6088e796355") },
                    { new Guid("221e261e-d4b0-4cee-b2ae-b52aa8cd60ac"), 3, new DateTime(2001, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Campaccio", "A lovely, juicy wine, with very soft and polished tannins, ripe and smoky fruit and a long and beautiful finish. This is gorgeous. The best Terrabianca in years. Sangiovese and Cabernet Sauvignon. Best after 2003. 7,915 cases made.", 31.0m, "1997", new Guid("12e4159f-c5f7-4d55-a014-178227786e96") },
                    { new Guid("b844f7fe-6bd1-4b99-a516-fe3c241e7f5f"), 3, new DateTime(2001, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Crozes-Hermitage Les Pierrelles", "Ultrathick and concentrated Crozes; the liquid hangs on to the inside of the glass as you swirl it. The wine has mineral, iron, wet earth and ripe, clean fruit character that seduces, thanks to the sweetness of the whole package. Full-bodied, tough and long, this needs time in the cellar to smooth out. Best from 2005 through 2012. 5,415 cases made.", 19.0m, "1999", new Guid("f499460c-11fb-46bc-ab11-7b76a127fb13") },
                    { new Guid("665f8c3b-a2f1-487d-b234-c09853916113"), 6, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chenin Blanc Stellenbosch", "Superfresh and racy, with a great underlying minerality driving the chamomile, white peach and honeysuckle flavors, followed by a long, bright finish. Really delicious. Drink now through 2008. 7,265 cases made.", 14.0m, "2006", new Guid("839453f3-d3de-41cd-b990-69603f0541f4") },
                    { new Guid("1acffc86-7a9b-47ad-86d8-5fcbd627938c"), 3, new DateTime(2005, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc Pic St.-Loup Vieilles Vignes", "Lovely aromas of violet, dark cherry and spice, with flavors to match. Ripe and full-bodied, wth plenty of character and power. Smoke and cocoa notes with mineral shadings fill the finish.  Drink now through 2007. 5,500 cases made.", 18.0m, "2001", new Guid("552fe6e3-6eef-46e3-ad5a-c7bb61e951fe") },
                    { new Guid("5f8cc0ba-b5a1-4c16-bcd2-e89c7542aa22"), 3, new DateTime(2001, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Zonchera", "Intense aromas of rosebushes and fruit. Full-bodied, with plenty of juicy fruit and chewy tannins. Needs time to open. Best after 2005. 6,300 cases made.", 46.0m, "1997", new Guid("05831874-f88f-4978-bdff-954981dea351") },
                    { new Guid("42d5276d-9d0b-4813-bb12-cffb378836e8"), 3, new DateTime(2002, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Uriah Walla Walla Valley", "Ultrasmooth and gently spicy, a gorgeous rainbow of plum, cherry, cedar, chocolate and exotic spice flavors that play across a supple frame, polished to a deep sheen and echoing forever. Drink now through 2010. 1,200 cases made.", 40.0m, "2000", new Guid("01cb05ed-8cd5-47d0-803e-5cd66e5d4042") },
                    { new Guid("73b98588-49e5-4575-9457-c1ec612ec62b"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Crognolo", "Beautiful aromas of dried cherry, plum and blackberry, with hints of dark chocolate. Full-bodied, with soft, silky tannins and a fruity finish. Balanced and delicious. Sangiovese, Merlot and Cabernet Sauvignon. Best after 2011. 7,500 cases made.", 35.0m, "2007", new Guid("80750668-7fb3-4e7c-817b-2dda98be9230") },
                    { new Guid("12336e5b-e790-41e2-a639-3dabef8e8b94"), 3, new DateTime(2003, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Oreno", "Dark, with fantastic aromas of currant, blackberry, spice and lightly toasty oak. Full-bodied, with very well-integrated tannins and a texture as fine as the best cashmere from Scotland. Superb. Needs time in the bottle to show its full potential. One of the new hot wines of Tuscany. Sangiovese, Cabernet Sauvignon and Merlot. Best after 2008. 3,330 cases made.", 80.0m, "2001", new Guid("80750668-7fb3-4e7c-817b-2dda98be9230") },
                    { new Guid("a0c074cf-11f1-4a21-a632-2e3757e19b86"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat", "Suave, sophisticated, rich and concentrated, but remains balanced and polished, with bright blueberry and blackberry flavors supported by toasty, vanilla-scented oak. A modern wine with great structure and harmony. Best after 2005. 1,800 cases made.", 60.0m, "2001", new Guid("d0dbde50-0be1-40e5-86de-6a5012f75adf") },
                    { new Guid("72f8e5f9-ebd4-4706-b03b-50ec86b445f9"), 3, new DateTime(2003, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Spring Mountain District", "A magnificent Merlot, with gorgeous fruit that's ripe, rich and distinctive. The bounty of complex flavors includes enticing black cherry, currant, blackberry and spice, with a smooth chocolaty aftertaste. Drink now through 2012. 2,510 cases made.", 45.0m, "2001", new Guid("ac8d5085-e082-4e3b-8043-05ee52dc090b") },
                    { new Guid("f18e66f6-51a5-4739-bbb6-213983bbbef6"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viño de la Tierra de Castilla y León", "Harmony and finesse mark this elegant red. It offers black cherry, toast and spice flavors over firm tannins, with a grace that almost hides its intensity. A lovely wine in the modern style. Drink now through 2009. 13,000 cases made.", 32.0m, "1999", new Guid("bebc4a68-1eaa-40ce-bebe-c86cb020aa87") },
                    { new Guid("427b7f6d-f2f1-438e-970f-fcd847bfa2dd"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montsant Laurona", "Powerful oaky flavors dominate this red now, but they wrap around a core of luscious raspberry and blackberry fruit flavors, and the texture is smooth and opulent. An ambitious wine in the modern style. Carignane, Grenache, Syrah, Merlot and Cabernet Sauvignon. Best from 2004 through 2012. 4,300 cases made.", 21.0m, "2000", new Guid("cf3ade31-d016-48ea-a361-992cdb72c4e1") },
                    { new Guid("92b745f7-b455-475c-95b7-e82791f78c84"), 6, new DateTime(2002, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Barbara-Monterey Castello", "A richly flavored yet elegantly crafted wine that's sleek and polished, with ripe pear, fig, honeysuckle and honeydew flavors that turn smooth and creamy, picking up subtle nutmeg and hazelnut nuances on the finish. Drink now through 2006. 2,466 cases made.", 26.0m, "2000", new Guid("061bd335-7f21-4fba-ab03-6727b4a4e78f") },
                    { new Guid("0173426e-5bdd-4f03-b405-eea6a114fa57"), 3, new DateTime(2002, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Langhorne Creek", "Rich and delicious, a lip-smacking mouthful of blackberry, currant, blueberry and plum flavors wrapped in a blanket of exotic spices, with hints of coffee and chocolate. Tannins are fine and chewy. Drink now through 2015. 1,000 cases imported.", 34.0m, "2000", new Guid("014e3ca2-7d44-4bfd-a158-e09343629413") },
                    { new Guid("6599fcf4-97db-46e4-b6bb-18535034a551"), 3, new DateTime(2005, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Reserva", "Seductive and refined, with a beautiful aroma of Asian spice, cassis and chocolate with flavors to match. Broad and lush, with plenty of structure and a caressing finish of cream, black truffle and mocha that goes on and on. Tempting now. Best from 2006 through 2010. 2,000 cases made.", 36.0m, "2003", new Guid("e3987049-e056-4342-a291-783baae1b6fa") },
                    { new Guid("67db8958-9e66-4f00-8fc2-69578515e4c5"), 3, new DateTime(2002, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro", "This Portuguese red bursts in the mouth with intense flavors of cassis, berry and red cherry that are powered by a strong backbone of acidity. Finish of game and bittersweet chocolate. Almost Burgundian in its elegance and balance. Best from 2003 through 2008. 5,000 cases made.", 17.0m, "1999", new Guid("e3987049-e056-4342-a291-783baae1b6fa") },
                    { new Guid("2c445a77-8624-4754-a068-241e7d0a2459"), 6, new DateTime(2004, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Appellation Selection", "Impeccable balance, richness and complexity, with tiers of spicy pear, floral, honey, vanilla and toasty oak and a long, persistent finish that echoes hazelnut and spice. Drink now through 2009. 26,500 cases made.", 13.0m, "2001", new Guid("94b47d8f-7525-4e9c-9d85-076a04a53d9b") },
                    { new Guid("1adf61e7-d578-4a53-a902-aca6dbfd429e"), 3, new DateTime(2002, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley Appellation Selection", "Wonderful richness, depth and purity of flavor. Shows layers of black cherry, ripe plum, raspberry and currant, as well as touches of anise and herb. Vanilla and cedar-scented oak linger in the background. Ripe tannins emerge on the long, complex, elegant finish. Best from 2003 through 2010. 2,500 cases made.", 24.0m, "1999", new Guid("94b47d8f-7525-4e9c-9d85-076a04a53d9b") },
                    { new Guid("1f538b0e-1711-4038-87f5-0248e52a9c73"), 6, new DateTime(2002, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Arroyo Grande Valley", "A wine of finesse and elegance, with intense, vibrant fruit that's ripe and rich, with layers of smoky toasty oak, honeysuckle, fig and melon, turning subtle and polished. Drink now through 2005. 9,630 cases made.", 24.0m, "2000", new Guid("3f3a2301-c364-4d4a-b774-fb93093726d2") },
                    { new Guid("3ba5885a-a233-4b72-a02c-4050ab797dbc"), 3, new DateTime(2002, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Claret Napa Valley", "A supple, elegant style, with a range of herb, currant and green olive that offers complexity, picking up anise, earth and mineral flavors. Pleasantly balanced on the finish. A wine of grace and delicacy. Drink now through 2009. 9,800 cases made.", 22.0m, "1999", new Guid("3a9baf7f-0dbe-4ca3-ac66-d54812cd3fe4") },
                    { new Guid("4173844d-9ca4-4045-97db-8bfdd44830e3"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Dry Creek Valley Cortina", "Dark and intense, yet well-polished, with floral blackberry and pepper aromas and concentrated black cherry, bitter chocolate and black olive flavors that finish with muscular but ripe tannins. Needs time. Best from 2015 through 2022. 2,200 cases made.", 38.0m, "2010", new Guid("71cfa912-e1d7-4876-ab39-681da1980df1") },
                    { new Guid("1315408e-9f12-4ae5-ba13-d2f28d934718"), 3, new DateTime(2011, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Alexander Valley Home Ranch", "A Zinfandel that has it all—rich, ripe flavors, good structure and acidity, and lots of personality. Offers aromas of cherry pie and licorice that lead to complex flavors of huckleberry, toasty vanilla and smoky cracked pepper. Drink now through 2017. 2,500 cases made.", 38.0m, "2009", new Guid("71cfa912-e1d7-4876-ab39-681da1980df1") },
                    { new Guid("b70f00bb-1dbb-433a-91b1-8ae27fcbb4ae"), 3, new DateTime(2004, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Sonoma", "Wonderful balance and sense of elegance, it offers vivid ripe fruit flavors that are rich and complex, with spicy black cherry, blackberry, pomegranate, herb and sage notes that add flavor dimensions and complexity. Firm, but ripe tannins add structure, allowing the fruit to push through. Drink now through 2009. 48,000 cases made.", 17.0m, "2002", new Guid("71cfa912-e1d7-4876-ab39-681da1980df1") },
                    { new Guid("0c7c6d36-0017-4e69-ae1b-1d65a003d2a8"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Old Vine", "A big, ripe, expressive style that packs in lots of spicy blackberry and black cherry fruit, and manages a measure of elegance and finesse given its size and intensity. Finishes with hints of herb and tea. Drink now through 2009. 4,000 cases made.", 28.0m, "2000", new Guid("71cfa912-e1d7-4876-ab39-681da1980df1") },
                    { new Guid("98923bd7-0518-4874-b08e-48ab19fc0f07"), 3, new DateTime(2002, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Australia Individual Vineyard", "Smooth and generous, an inviting red that's plump with black cherry, blackberry and exotic spice flavors, hinting at dark chocolate as the flavors persist on the fine-grained finish. Outstanding. Drink now through 2004. 20,000 cases made.", 10.0m, "2001", new Guid("5a7ef59b-3a4f-43da-be04-66dfad1aacf4") },
                    { new Guid("a898d664-b5e3-4f9d-a947-d81ab55d01d6"), 6, new DateTime(2002, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Margaret River Art Series", "Brilliant, juicy and multilayered, brimming with apple, pear, tropical fruit, almond, mineral and spice flavors that swirl together on a muscular but lithe and seamless frame. The flavors last and last. Has depth, length and power without being at all bulky. Drink now through 2011. 3,000 cases made.", 65.0m, "1999", new Guid("a6ba2820-622e-4c12-b6d9-c812086250c7") },
                    { new Guid("baacf459-2531-489f-819c-9c25ceed7c8d"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Stags Leap District Estate", "Absolutely gorgeous, beautifully defined fruit, with uncommon richness and purity of flavor, gushing with ripe currant, blackberry and wild berry fruit, revealing extra facets and dimensions, with a long, intricate, detailed finish. Firmly tannic, but a wealth of fruit pushes through. Best from 2003 through 2012. 1,300 cases made.", 100.0m, "1999", new Guid("32848921-ceb6-4a05-8ef2-0af047c9d294") },
                    { new Guid("45b4a464-afa6-4694-a801-794015f1b764"), 3, new DateTime(2002, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Vigna di Pianrosso", "Beautiful, with blackberry, perfumed violet and milk chocolate aromas. Full-bodied, with loads of jammy, chocolaty fruit and big, velvety tannins. Lasts for minutes on the palate. Glorious now, but will age for a very long time. Best after 2004. 2,500 cases made.", 60.0m, "1997", new Guid("8b96830a-683b-4042-8706-54ebd86a748c") },
                    { new Guid("3cebe6f8-b462-4d14-98d9-9073332e4183"), 3, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Frederick Walla Walla Valley", "Refined and impeccably structured, with floral black cherry and dusty dried spice aromas, and complex plum, olive and mocha flavors that glide toward polished tannins. Cabernet Sauvignon, Cabernet Franc, Merlot, Petit Verdot and Malbec. Drink now through 2023. 2,966 cases made.", 50.0m, "2014", new Guid("01cb05ed-8cd5-47d0-803e-5cd66e5d4042") },
                    { new Guid("ab9a1f8e-0e68-4f41-8dfe-8ff9b834f083"), 3, new DateTime(2001, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Petite Sirah Sonoma County", "A dazzling red, dark, rich and exuberant, this wine reveals layers of peppery plum, black cherry and blueberry fruit that turn lush, with smooth tannins. Great example of its type. Drink now through 2008. 8,000 cases made.", 20.0m, "1998", new Guid("0778849c-122a-483b-b4a1-3d1d2fa086df") },
                    { new Guid("58c84325-c0a8-440e-bffd-02c084f0f273"), 6, new DateTime(2008, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Albariño Rias Baixas Legado del Conde", "A smoky gunflint note adds interest to this firm white. Ripe peach and light herbal flavors pick up floral and citrus zest notes on the finish. Shows balance, depth and verve. Drink now through 2009. 5,000 cases made.", 16.0m, "2007", new Guid("69696ab1-565f-48db-902f-91585366f0a1") },
                    { new Guid("60f6908d-4037-4d1e-8c3b-334f03205ff4"), 3, new DateTime(2011, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gigondas Valbelle", "A gorgeous wine, with an interplay of ripe, dripping blackberry, cassis and plum sauce flavors providing the flesh, offset by smoldering charcoal, tobacco and bittersweet cocoa notes, which provide the angles. The long finish has nice intensity. Best from 2012 through 2022. 1,175 cases made.", 58.0m, "2009", new Guid("3993e9c1-c7a5-4030-b11e-680e6a2ea567") },
                    { new Guid("c27c2f55-1cd1-4240-bd63-3985a96e6184"), 6, new DateTime(2008, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kremstal Sandgrube", "Intense, with concentrated flavors of ripe pear, baked apple, apricot and spice. The long finish features lemon curd, spice and meringue.  Drink now through 2015. 2,917 cases made.", 18.0m, "2006", new Guid("7ebd26c7-6f51-466b-9413-88381fd988e2") },
                    { new Guid("a041f41d-3df1-4484-8b81-fa77bfaa614c"), 3, new DateTime(2016, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Le Cupole", "Alluring scents of ripe cherry, mulberry and fresh herbs complement concentrated flavors of cherry and sweet spice in this red. Offers a backbone of mouthcoating tannins and remains integrated as the finish lingers. Merlot, Cabernet Franc and Cabernet Sauvignon. Best from 2018 through 2027. 4,600 cases made.", 27.0m, "2014", new Guid("cd10751b-6c09-443f-b786-1c85fa0f3d9d") },
                    { new Guid("aeaa1c46-739a-4e33-8e03-9fd9eff18272"), 5, new DateTime(2016, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Carneros Ultra", "Captivating aromas of Asian pear, mature apple and fresh ginger open to complex and polished flavors of brioche, spicy hazelnut and crème brûlée. Drink now through 2018. 2,700 cases made.", 42.0m, "2011", new Guid("4f0ba539-52c3-4b1e-b8d7-e9ea60cef6ee") },
                    { new Guid("17e74419-9ede-481d-aefc-507fa2ec9e15"), 3, new DateTime(2016, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Claret Columbia Valley", "Firm and focused, with crinkly tannins around a sleek core of blackberry, currant and plum flavors that keep singing on the expressive finish. This has room to grow. Cabernet Sauvignon, Cabernet Franc, Merlot, Malbec and Petit Verdot. Drink now through 2022. 2,129 cases made.", 40.0m, "2013", new Guid("7c7114bb-6154-4241-b481-79cdf35ed857") },
                    { new Guid("b4dcdb75-3e13-45f2-bb62-74ee08572437"), 3, new DateTime(2016, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Borgoforte", "Enticing aromas of violet, black currant and rosemary are accented by licorice and tobacco flavors in this fluid and harmonious red, with fine tannins gracing the long finish. Sangiovese, Cabernet Sauvignon and Merlot. Drink now through 2022. 12,500 cases made.", 12.0m, "2014", new Guid("ca1e3d4c-f07b-425b-b6af-6f48f3edbeb3") },
                    { new Guid("0c136c2d-70ac-448d-8a1c-9e0a0693b51d"), 3, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley Holler", "Dense and focused, with a tangy edge to the dark plum and currant flavors, hinting at stony mineral and dusky spice flavors as the finish persists impressively against fine tannins. Best from 2018 through 2023. 912 cases made.", 32.0m, "2013", new Guid("12984758-0b43-4de4-9884-3c2e5617342a") },
                    { new Guid("2925e4b0-0a68-4887-bb84-1d17f44d5f18"), 3, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Bricco delle Viole", "Shows fine integration between fruit and wood, with sweet cherry and strawberry flavors matched to spice, floral and briar accents. Fresh, vibrant and firmly structured, this is long on the fruit-, spice- and mineral-tinged finish. Shows fine balance and expression. Best from 2018 through 2032. 333 cases made.", 53.0m, "2012", new Guid("fb9c8d84-2dfd-4b7b-bf8e-ee848de523f6") },
                    { new Guid("00b2cdef-764e-4b7b-8c37-894c846f9024"), 3, new DateTime(2016, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Russian River Valley Old Vine", "Plump and fleshy, with a dense core of focused tannins and lively acidity. Aromas of ripe black raspberry, anise and licorice open to layered, slightly jammy flavors of black cherry, blueberry pie and smoky cracked pepper. Drink now through 2024. 2,200 cases made.", 38.0m, "2014", new Guid("aa712796-9c73-4425-a47f-0e56c702447e") },
                    { new Guid("d4ea9874-336b-4093-ad40-d5b1fd8f7b05"), 6, new DateTime(2015, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Jura Les Chamois du Paradis", "This is lovely, with verbena, citrus oil, white peach and Jonagold apple fruit flavors seamlessly layered, offset with a lovely quinine hint and carrying through a long, floral finish. Still a bit tightly coiled, this should unfurl nicely with a bit more cellaring. Chardonnay. Best from 2017 through 2023. 167 cases made.", 53.0m, "2011", new Guid("6644aacf-0707-498a-94f8-611349da23da") },
                    { new Guid("d718191e-0184-4186-8c1e-a690ab542c9d"), 6, new DateTime(2015, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Clare Valley Springvale", "Lithe, vibrant and light-footed, but explosive, with flavors of pear, honeysuckle, apple and cinnamon, glowing brightly on the long and expressive finish. Drink now through 2024. 2,800 cases made.", 42.0m, "2014", new Guid("17de8c37-691f-4d32-b251-17ccfb52d67f") },
                    { new Guid("20562cd8-f30d-496e-9293-de7fc864f200"), 3, new DateTime(2015, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nardò Nerìo Riserva", "A silky red, with sweet, ripe flavors of sun-dried black cherry and date, underscored by rich hints of wood smoke and mineral. This is well-knit, featuring sinewy tannins and accents of &lt;i&gt;garrigue&lt;/i&gt; and graphite. Drink now through 2025. 3,200 cases made.", 22.0m, "2012", new Guid("6fb74c1f-4711-422f-9617-dd7f89020bdc") },
                    { new Guid("bef0cc8a-ef80-43b1-a65a-9a5230dceab8"), 3, new DateTime(2016, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast Gap's Crown Vineyard", "Lightly aromatic, but rich and layered, this offers excellent density and depth, with fine-grained tannic grip and a long, persistent finish that keeps flashing the core flavors. Drink now through 2020. 1,350 cases made.", 59.0m, "2014", new Guid("e55fd862-b229-4e9d-ad90-684015b03458") },
                    { new Guid("8aded657-2b03-45b5-8f75-c1c474859caa"), 3, new DateTime(2015, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Omnia", "Beguiling wood spice, Lapsang souchong tea and warmed raspberry and blackberry coulis aromas and flavors glide along in this gorgeous red, enhanced by a shiso leaf note and backed by a silky, iron-edged finish that is deceptively long. A very distinctive style. Best from 2017 through 2030. 1,000 cases made.", 116.0m, "2012", new Guid("165cb601-b6dc-45af-9386-5a7eadd4735e") },
                    { new Guid("deefdfd6-94d6-4153-a99a-d7aafc67137f"), 3, new DateTime(2015, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sicilia", "A lovely, medium-bodied red, with a pure beam of ripe cherry and fresh acidity layered with supple tannins and accents of star anise, dried thyme, loamy earth and mocha. Drink now through 2023. 10,000 cases made.", 26.0m, "2013", new Guid("7c34f2fa-f08a-4b1f-a830-7a59ebed0f5d") },
                    { new Guid("0c3e5761-57c7-45a2-8a19-1d09a91a04dd"), 6, new DateTime(2015, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Grigio Friuli Colli Orientali", "This creamy, medium-bodied version is framed by finely cut acidity that focuses the rich minerality and flavors of guava, star fruit, pickled ginger and oak spice. Lingering finish. Drink now through 2022. 3,500 cases made.", 20.0m, "2014", new Guid("9b0664b3-2719-4c89-aafd-20fb846d7035") },
                    { new Guid("69a122a6-dcd9-4c29-8852-611c5fe553db"), 3, new DateTime(2015, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Luján de Cuyo Single Vineyard", "Decadently spiced, with intense dark chocolate and roasted plum flavors that feature notes of paprika. Black pepper accents show on the lush, mineral-filled finish. Needs time to open up. Best from 2017 through 2021. 3,800 cases imported.", 27.0m, "2013", new Guid("445a9ad8-c4f2-4121-be59-b5e5362b060c") },
                    { new Guid("c8100ec2-876f-4be4-ba81-55d0b36fd464"), 3, new DateTime(2015, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Aglianico del Vulture", "The black cherry and fresh earth flavors are sweet and focused in this harmonious, medium-bodied red, accented by black olive, fig bread and dried herb notes, all framed by fine-grained tannins that firm up the finish. Drink now through 2027. 15,000 cases made.", 20.0m, "2012", new Guid("caade443-4cff-47bd-9a7f-7b01155ade3d") },
                    { new Guid("7f3e5a50-20b2-4e59-963b-0976b390f1b1"), 6, new DateTime(2015, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malagouzia Florina Turtles Vineyard", "Rich and flush with fresh-cut green apple, honeydew melon and grapefruit flavors that feature a vibrant minerality. Delivers hints of savory herbs, leading to a lush finish of cream and spice. Drink now through 2018. 8,500 cases made.", 18.0m, "2014", new Guid("137297a1-3894-474c-bec6-41f5456cc0db") },
                    { new Guid("6a8979f0-39df-4ff8-ab97-909843a2a007"), 3, new DateTime(2015, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Long Island", "A polished texture carries balanced flavors of tart cherry, pomegranate, toasted hazelnut and espresso in this expressive red. Features firm, well-integrated tannins and lively acidity. Elegant. Drink now through 2022. 2,592 cases made.", 44.0m, "2010", new Guid("fe662473-fa48-4fbd-ac3c-2f7d4b947532") },
                    { new Guid("fd0f0023-e674-417d-8469-5b0489f50669"), 3, new DateTime(2015, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grenache Clarendon Romas", "This has terrific presence, with dark berry, cherry and floral flavors on a taut frame, expanding on the finish against nubby tannins that linger expressively and intensely. Best from 2017 through 2021. 500 cases imported.", 75.0m, "2009", new Guid("2df3bd83-78d3-47f4-9812-8bd8684dbf1b") },
                    { new Guid("68a8d21c-21df-431f-9f3a-d1ed372f2cfc"), 3, new DateTime(2015, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Finca San Martín Crianza", "This red is focused and polished, delivering harmonious flavors of black cherry, olive, smoke and mineral. The tannins are well-integrated, the acidity fresh. A bit reserved, but has depth. Drink now through 2022. 10,500 cases made.", 14.0m, "2012", new Guid("b445f2ae-50b5-4876-b6af-074566d5fe76") },
                    { new Guid("729de573-a0f4-48a5-86c6-e6676d362d7a"), 3, new DateTime(2015, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Il Fauno", "A round version, featuring rich cherry fruit mingling with an earthy element. Turns firm and dry on the finish, with gumcoating tannins, yet this remains persistent and long, revealing a tobacco accent. Merlot, Cabernet Franc, Cabernet Sauvignon and Petit Verdot. Best from 2017 through 2026. 5,200 cases made.", 30.0m, "2010", new Guid("d2235124-3642-4960-a436-6f00b0a684e2") },
                    { new Guid("ea6a33aa-e43b-47bf-8604-c6e58a7ccf5f"), 3, new DateTime(2015, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Madiran Château Bouscassé", "Bright, tangy acidity and firm tannins provide solid structure to the dried raspberry, red currant and black tea flavors. Layers of new leather and tobacco leaf echo through the mouthwatering finish of sweet spice and mineral. Drink now. 20,000 cases made.", 20.0m, "2009", new Guid("85017d81-2a46-4c09-8d0d-9bc52334dd52") },
                    { new Guid("0bfc1255-019c-407d-b0ea-5cbf2d9fc219"), 3, new DateTime(2015, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chinon", "Black cherry and violet notes course along, flecked with olive and white pepper hints that drive through the pure finish. Shows ample minerality, but the fruit takes center stage. Drink now through 2017. 2,300 cases made.", 23.0m, "2014", new Guid("6d5865d8-6dbd-46af-9c42-de4021cfdcfc") },
                    { new Guid("31e3fd13-9583-4a3b-9ab0-daa63d469b4e"), 3, new DateTime(2015, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Central Otago Bannockburn", "Supple wild berry, cherry and plum flavors are both powerful and delicate, with details of Earl Grey tea, white pepper, anise and crushed rock. Expansive and expressive, the finish sails on and on, with satiny tannins that never get in the way. Drink now through 2030. 5,174 cases made.", 65.0m, "2013", new Guid("0720c73b-d79e-4a60-9078-feb952550fcf") },
                    { new Guid("e47a96b3-ead2-45e8-973d-61178cd41822"), 6, new DateTime(2016, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sancerre Les Belles Vignes", "Very pure and chiseled, with gooseberry, straw and flint flavors tightly coiled, driven by a mouthwatering fleur de sel note through the long, bone-dry finish, which is built on minerality. Drink now through 2021. 7,000 cases imported.", 26.0m, "2015", new Guid("e2d4911e-f1b5-4197-b63d-7a274203cc7e") },
                    { new Guid("063ea690-18bc-4110-b7c3-5b6200b55d53"), 3, new DateTime(2017, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja La Vendimia", "This red shows good density, with black cherry, plum, espresso and black olive flavors mingling over firm tannins and balsamic acidity. Stays balanced and focused through the smoky finish. Drink now through 2027. 3,552 cases imported.", 17.0m, "2015", new Guid("f99514a4-8367-442c-902d-ee476246234b") },
                    { new Guid("18d0d167-be84-4d0c-99bd-111ef1d664f6"), 3, new DateTime(2017, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alicante Tarima Hill Old Vines", "This red shows grace and depth. Black cherry, plum, mineral and smoke flavors mingle harmoniously over well-integrated tannins, while lively acidity keeps this focused. Not showy, but all the pieces fit together. Balanced, in the modern style. Drink now through 2025. 10,000 cases made.", 17.0m, "2015", new Guid("ef918163-2a63-4603-865e-06c7dfefc280") },
                    { new Guid("f0836e57-825b-4d1f-851c-57e073895dcf"), 6, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Washington Uncovered", "Dynamic, yet sleek and refined, driven by a core of minerally acidity that meshes with the delicately complex apple, Meyer lemon and spicy yeast accents that linger long on the finish. Drink now through 2021. 1,835 cases made.", 35.0m, "2014", new Guid("02f3fa2a-4c79-4356-9311-a69ebad7dc93") },
                    { new Guid("bbfb81ba-28f8-4377-9757-18488aa8fa29"), 3, new DateTime(2016, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Treehouse", "Combining power and finesse, this is rich and extracted, with a supple yet gutsy core of mineral-laced tannins that weave into a mix of gravelly, loamy earth, juicy blackberry and wild berry. Drink now through 2022. 1,748 cases made.", 125.0m, "2013", new Guid("a4d02acf-f7a3-4962-a372-4d5403cd7c95") },
                    { new Guid("1645ced2-3a0d-461a-b027-4a19dfde787d"), 3, new DateTime(2016, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Napa Valley", "Well-built and richly structured, with bold aromas of blackberry, mocha and licorice, showing layered flavors of smoky raspberry, grilled herb and pepper. The tannins are big, but ripe and balanced. Drink now through 2028. 1,750 cases made.", 56.0m, "2013", new Guid("acd95417-84b5-4fa7-906f-619dac26058c") },
                    { new Guid("82b827c9-64a6-4263-94a2-263a5b0e07f5"), 3, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Yiron Galilee", "A rich and plush red, with a deep well of blackberry pie, dark plum and hot stone flavors that fold together seamlessly. Creamy midpalate, presenting a finish that oozes with chocolate mousse and Asian spice details. Unctuous. Cabernet Sauvignon, Merlot, Syrah and Petit Verdot. Kosher. Drink now through 2020. 10,800 cases made.", 32.0m, "2013", new Guid("593f2f66-264a-4126-b2f6-64696bda95d7") },
                    { new Guid("d6dc6fa1-1d57-4828-a33c-eba5559d89d0"), 3, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Isola dei Nuraghi Montessu", "An elegant version, with Asian five-spice powder and cherry blossom notes wafting through flavors of black raspberry preserves, bresaola and cured tobacco. Long and supple, presenting a mineral-tinged finish. Carignano, Syrah, Cabernet Sauvignon, Cabernet Franc and Merlot. Drink now through 2021. 12,500 cases made.", 30.0m, "2014", new Guid("8210ed30-87f3-4e6d-99a3-054b0cb95c35") },
                    { new Guid("805d6602-7e76-4632-a4a0-f734330b1e4f"), 0, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bandol Rosé Moulin des Costes", "Offers a lovely combination of zip and richness, with creamy edges around a core of delicious white cherry and white peach flavors, all offset by a pure beam of minerality that pierces through from start to finish. Drink now through 2017. 4,000 cases made.", 30.0m, "2015", new Guid("4b7298b6-a2f2-4ea5-a573-67b1c51303cb") },
                    { new Guid("f627b82e-020b-4256-8ddf-5f85c513a539"), 6, new DateTime(2016, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grechetto Colli Martani Grecante", "Thai basil and spice notes lace the creamy mix of Asian pear, white peach, stone and lemon zest in this well-knit white. Light- to medium-bodied and fresh, with a lingering, savory finish. Drink now through 2020. 10,800 cases made.", 20.0m, "2015", new Guid("4e62f0cb-1859-4cb0-aefc-73449862f831") },
                    { new Guid("9d4c1b5a-87ab-4e5c-b45b-4d01243d710f"), 3, new DateTime(2016, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Aglianico del Vulture Piano del Cerro", "A dense and tarry red that glides on the silky palate, with mouthwatering acidity framing flavors of blueberry puree, chocolate-covered cherry, vanilla, dried thyme and oak spice. An expressive version in a modern style, yet refined and harmonious overall, with the grippy finish echoing the ripe fruit and tarry mineral character. Drink now through 2027. 5,000 cases made.", 36.0m, "2012", new Guid("da6264fc-54b3-4e0e-9372-3bc5e85730ba") },
                    { new Guid("2ed516a2-401b-4e06-b142-9314629f90fe"), 6, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Mendoza Felino", "A richly spiced white, with focused, well-structured flavors of dried apple, ripe citrus and guava. Brioche notes show on the firm finish. Drink now through 2019. 4,500 cases imported.", 21.0m, "2015", new Guid("f3afac63-b111-45be-8dfb-89cfe02718e4") },
                    { new Guid("fc142415-a217-491c-bcf7-cb441d5bf629"), 3, new DateTime(2016, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja La Montesa Crianza", "A thick texture carries plum tart, orange peel, toasted vanilla and anise flavors in this ripe version, balanced by juicy acidity. The spicy, berry-scented finish is long and pure. Drink now through 2023. 10,800 cases imported.", 20.0m, "2013", new Guid("f99514a4-8367-442c-902d-ee476246234b") },
                    { new Guid("c34551df-58b3-45d5-ab4c-ec1ff0d26962"), 3, new DateTime(2016, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Quinta Vale D. Maria", "Ripe, rich and lushly spiced, this muscular red features dark cherry and plum compote flavors that are accented by light pepper notes. Dark chocolate and cardamom accents show on the creamy finish. Drink now through 2022. 1,800 cases made.", 68.0m, "2013", new Guid("a7da3038-0808-4ddb-921a-399a28fed393") },
                    { new Guid("5e0ca83c-e39e-4bfc-91ca-ebc2b733d986"), 6, new DateTime(2018, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Manzanilla Sanlúcar de Barrameda La Gitana", "A bright, high-pitched style, showing tangy quinine and chamomile notes laced with talc and fleur de sel flavors. Very dry, brisk, stony-edged finish. An absolute delight. Drink now. 62,000 cases made.", 26.0m, "NV", new Guid("fcdf376b-57fb-41a6-8a7c-4c25269956ae") },
                    { new Guid("eebd60b4-d0fa-4f31-99ea-9541ee300d34"), 6, new DateTime(2016, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Oloroso Jerez Faraon", "Intense, with roasted almond, Brazil nut and walnut notes lending this a slightly burly aspect, while gunpowder tea, bitter orange and almond accents add a taut feel through the finish. This is a distinctive, edgy version that Sherry geeks will dig. Drink now. 1,500 cases made.", 27.0m, "NV", new Guid("fcdf376b-57fb-41a6-8a7c-4c25269956ae") },
                    { new Guid("eef6c3e5-d8dc-4541-93cb-409be245ed6d"), 5, new DateTime(2016, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut California Organic Grapes", "Crisp and luscious, this is a winning bubbly for a big party. Aromas of cinnamon, ginger and citrus lead to creamy flavors of pear and blanched almond. Drink now. 15,000 cases made.", 14.0m, "NV", new Guid("d04d2a75-d87c-41a7-945a-29cd9b9117a3") },
                    { new Guid("2a04b733-e58b-4ff8-8bca-435a14b127ac"), 3, new DateTime(2016, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Lucia Highlands SLH", "The texture is plush, with lots of complex flavors, ranging from plum to anise to dusty, cedary oak. Satisfying from start to finish, ending with dusty, nutmeg-scented tannins. Drink now. 26,500 cases made.", 30.0m, "2014", new Guid("98a28548-5d17-4e1d-8bef-03bed83f3f5d") },
                    { new Guid("e5d7b0a8-c0c6-44d4-8bbe-8d69927e9218"), 3, new DateTime(2016, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Primitivo di Manduria Antica Masseria del Sigillo", "There's a rich, mouthfilling quality to this medium-bodied red, offering a velvety mix of spiced cherry, mulberry and herbed olive, with accents of mocha and graphite. Plush tannins show on the juicy finish. Drink now through 2021. 2,500 cases made.", 17.0m, "2014", new Guid("d2ef0327-e2d6-4b70-bf73-7a3f8057c18b") },
                    { new Guid("db31e207-4839-468b-89c6-c87bdc775ce0"), 6, new DateTime(2016, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viura Rioja Vetiver", "This bold white marries expressive flavors to a muscular texture. Pear and quince notes mingle with brioche, chamomile and dried pineapple details, with juicy acidity and light tannins lending support. Lively now, this should harmonize with time. Drink now through 2024. 2,000 cases made.", 15.0m, "2014", new Guid("86a6c36f-0c81-4fb4-a9d7-e4e284f54b24") },
                    { new Guid("e2c8577c-d244-44d1-9513-5450fa248abf"), 3, new DateTime(2016, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley 1865 Single Vineyard", "Rich and refined, with good cut to the red plum, crushed berry and raspberry flavors matched to notes of sandalwood and cardamom. Creamy midpalate, with a finish that's filled with chocolate and dark currant details. Drink now through 2021. 50,000 cases made.", 18.0m, "2014", new Guid("a0a3c6fa-51a7-4a2c-b3d2-0a48e6c18c0c") },
                    { new Guid("4515b132-fe41-4d64-b8be-f16b474d8323"), 3, new DateTime(2016, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Chinian-Roquebrun La Grange des Combes", "A muscular but polished red, with brooding blackberry pie, dark cherry and roasted plum notes complemented by iron, peppercorn and licorice details. Full but not overpowering tannins linger on the savory, herb-tinged finish. Syrah, Grenache and Mourvèdre. Drink now through 2022. 12,000 cases made.", 20.0m, "2015", new Guid("f8fae364-6e07-492a-98ac-34b8f8684329") },
                    { new Guid("6e19d91e-e297-423f-a8a0-be0616e6ae83"), 6, new DateTime(2016, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Rheinhessen Fritz's", "Redolent of white raspberry jam and mint, this white is lush and creamy midpalate, with crackling acidity. The off-dry finish delivers honey and spice details. Drink now through 2018. 20,000 cases made.", 14.0m, "2014", new Guid("f6a46925-4f86-44e7-b9c1-0e23f3e79247") },
                    { new Guid("fc613025-8642-4eab-9f89-4c31fec40375"), 3, new DateTime(2016, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Uco Valley Q", "A refined and savory red, featuring notes of white pepper to the cherry and crushed red plum flavors. Lengthens out on the palate, with medium-grained tannins. Silky finish. Drink now through 2020. 7,500 cases made.", 20.0m, "2013", new Guid("85e8c77d-655b-41d6-b0c4-8b2b9df83585") },
                    { new Guid("7f8b6302-05e7-4730-8ccf-eade805946ec"), 6, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Judean Hills White", "Offers a spicy aroma, with pure, mineral-infused flavors of Granny Smith apple, lemon curd and pineapple, accented by notes of toasted coconut. The finish has crème brûlée hints that linger with plenty of richness. Chardonnay and Sauvignon Blanc. Kosher. Drink now through 2020. 2,000 cases made.", 30.0m, "2014", new Guid("03102bf2-0edc-417b-ac72-0722c3426b46") },
                    { new Guid("633205db-299d-4fd9-b7ea-c9914268a27b"), 3, new DateTime(2017, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Chehalem Mountains Dopp Creek", "Firm and focused, with cinnamon notes floating around red berry and floral flavors, finishing with gentle persistence against lively acidity and fine tannins. Best from 2018 through 2022. 3,100 cases made.", 26.0m, "2014", new Guid("2095963c-5351-450a-addb-5ce1ee800e9b") },
                    { new Guid("53727e5d-8be0-4966-bd93-400ef578201e"), 3, new DateTime(2015, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Chehalem Mountains Margo", "Lithe and expressive, shading black cherry fruit with glints of mint, loamy earth and walnut. Fine tannins underline a long, dancing finish. Best from 2016 through 2022. 1,711 cases made.", 36.0m, "2012", new Guid("2095963c-5351-450a-addb-5ce1ee800e9b") },
                    { new Guid("ab6256df-9ed3-4590-8595-150f1821cea9"), 3, new DateTime(2015, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Luján de Cuyo Premium Reserve", "Redolent of blueberry and dark currant, this fresh, pure style is matched with plenty of minerally components. The complex finish offers white pepper and bittersweet chocolate details. Drink now through 2019. 13,395 cases made.", 17.0m, "2013", new Guid("b9833fd2-827c-4012-8447-7d45dca3f745") },
                    { new Guid("2683ebae-4556-4919-a0c5-648d9868ca17"), 6, new DateTime(2019, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coquille Blanc Paso Robles", "Fresh and spicy pear, lime and green apple flavors show details of matcha, peach blossom and honeysuckle in this white, which offers nice complexity and balance, with plenty of fresh acidity. Grenache Blanc, Roussanne, Viognier and Marsanne. Drink now through 2024. 1,203 cases made.", 24.0m, "2017", new Guid("0a0332f1-489c-4d61-bd94-252025999c9f") },
                    { new Guid("37f0fa3a-5c3c-42d3-bcb9-aa19fadbe786"), 0, new DateTime(2018, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rosé Wild Thing Rendezvous Mendocino County", "A spirited California rosé, with lively raspberry, cherry and spice flavors that dance along the finish. Carignane, Zinfandel and Cabernet Sauvignon. Drink now. 1,616 cases made.", 15.0m, "2017", new Guid("0a0332f1-489c-4d61-bd94-252025999c9f") },
                    { new Guid("91fc4e7f-d9de-4037-ad44-86a75f69e17c"), 3, new DateTime(2014, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Mendocino County Wild Thing Old Vine", "Vibrant and zesty, offering smoky cherry aromas and complex briary raspberry, underbrush and cracked pepper flavors. Drink now through 2017. 3,500 cases made.", 19.0m, "2011", new Guid("0a0332f1-489c-4d61-bd94-252025999c9f") },
                    { new Guid("02ee7028-1246-4330-b98a-f83370ec78e1"), 3, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast Riviera", "Juicy and generous, featuring a rich, smooth core of plum and raspberry flavors, highlighted by zesty floral scents that accentuate the aroma and fruitiness. Agreeably supple, with easygoing tannins. Drink now through 2022. 3,200 cases made.", 22.0m, "2015", new Guid("47bb29ea-20ba-42f2-a467-156e12a504bd") },
                    { new Guid("f8ef6529-96c3-43a7-bd1b-119c64c2e2d0"), 3, new DateTime(2014, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Barbara County Huntington", "Notably floral and spicy, with firm tannins amid the dark berry, mocha, roasted herb and fresh-turned earth flavors. This is complex, dense and persistent, gaining depth and length. Drink now through 2022. 9,218 cases made.", 23.0m, "2012", new Guid("47bb29ea-20ba-42f2-a467-156e12a504bd") },
                    { new Guid("327a04fe-92da-436f-a1d6-dfdb3fb6e4b6"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montepulciano d'Abruzzo Marina Cveti  S. Martino Rosso", "A fresh, harmonious red, offering a subtle range of flavors, with rich notes of smoky mineral, crushed blackberry, olive tapenade, grilled herb and ground anise. Fine-grained tannins emerge on the finish. Drink now through 2024. 2,500 cases imported.", 25.0m, "2010", new Guid("ebfdbc69-d5eb-4813-b1e7-68f8e9187c95") },
                    { new Guid("fb0841d1-faf7-4f0e-9963-1c3e42af8762"), 3, new DateTime(2014, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vin de Pays du Var St.-Auguste", "A dark, smoky version, with mulled plum and blackberry paste notes liberally lined with charcoal, dark olive and bay leaf flavors. Features a rugged feel through the toasty finish. Syrah, Cabernet Sauvignon and Merlot. Drink now through 2016. 1,125 cases made.", 20.0m, "2010", new Guid("b2f2eb09-b87e-495d-b5f9-0fc1602dbb81") },
                    { new Guid("c4c8504f-adef-4112-9e3f-7e0739a7d845"), 3, new DateTime(2014, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valtellina Superiore Quadrio", "Rich, with a base note of tarry smoke, this remains elegant and medium-bodied overall, showing mouthwatering acidity and fine integration. Dried mint and thyme aromatics lace the dried strawberry and cherry fruit, offset with star anise and beef bouillon details. Offers a lightly chewy finish. Drink now through 2024. 3,300 cases imported.", 20.0m, "2010", new Guid("206d1c65-f70b-4d89-86da-1e82c2ade191") },
                    { new Guid("b6672a9d-72ab-474f-906d-a4d93228317e"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera d'Asti Tre Vigne", "A bright, flashy version, featuring black currant and blackberry fruit that shows notes of vanilla and spice. The oak adds tannins, lending this a tight feel on the otherwise fine finish. Needs time. Best from 2016 through 2020. 5,000 cases made.", 17.0m, "2012", new Guid("1486d00c-3265-4191-9a86-614a57c86a67") },
                    { new Guid("22e0b10e-bb28-4cab-88c9-4beb53b1b55e"), 3, new DateTime(2014, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Molise Ramitello", "The up-front mulberry and plum fruit is ripe and fleshy in this balanced red, with polished tannins. Notes of licorice string, graphite, tarry smoke and grilled mushroom push through midpalate and on the finish. Drink now through 2021. 8,500 cases made.", 18.0m, "2011", new Guid("a8b30c5c-24c7-4cf7-8549-faf2a356f1b9") },
                    { new Guid("f2408438-be67-4139-a603-772c1233a5e2"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Pure", "Concentrated but remarkably refined, this features a large core of raspberry, red currant and black cherry fruit that is pure, silky and beautifully defined. Hints of shiso leaf, iron and black tea line the finish, which has serious length thanks to finely beaded acidity. A deft marriage of power and grace. Best from 2015 through 2030. 1,900 cases made.", 78.0m, "2012", new Guid("a96bafe7-302b-4d2a-9fb8-5b864dce4f4c") },
                    { new Guid("463834ab-5123-4664-99ac-e5bd9fb86865"), 3, new DateTime(2019, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Languedoc Art de Vivre", "Polished, supple tannins support the black cherry, red plum and wild raspberry notes of this juicy red, with savory touches of mineral and herb on the clean finish. Grenache, Syrah and Mourvèdre. Drink now through 2022. 35,000 cases made, 10,000 cases imported.", 20.0m, "2015", new Guid("4a93022c-a99b-4585-899a-1aadecf123ba") },
                    { new Guid("8ba6e892-4ce3-4f9a-aaf6-09b86de40deb"), 3, new DateTime(2014, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah-Grenache Languedoc", "A ripe aroma leads to rich, concentrated dark plum, kirsch and dried blueberry flavors, with a succulent spiciness. The finish shows touches of smoke, alongside notes of chocolate. Drink now through 2017. 18,000 cases made.", 19.0m, "2011", new Guid("4a93022c-a99b-4585-899a-1aadecf123ba") },
                    { new Guid("7ae58b86-be39-4f8b-aa61-6d45a133ee35"), 3, new DateTime(2019, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Lucia Highlands Sierra Mar Vineyard", "Rich and plush, revealing a sinewy power to the dried cherry, macerated plum and red currant flavors. Luxuriantly spiced, with a long, creamy finish of cardamom, dried ginger and chocolate hints. Well-crafted and elegantly styled. Drink now through 2025. 686 cases made.", 58.0m, "2017", new Guid("cb8afdcc-f2d0-4495-97dc-769ef333694e") },
                    { new Guid("fdfed065-c39e-467d-a7cc-b1a3a245483e"), 6, new DateTime(2014, 1, 1, 0, 0, 0, 0, DateTimeKind.Unspecified), "Assyrtiko Santorini Greek Wine Cellars", "A broad and lush-tasting white from Greece, with plenty of rich, spicy notes to the pear tart, melon and baked apple flavors, gliding smoothly across the palate. The long finish echoes with mango, ripe citrus and buttery details. Drink now through 2016. Tasted twice, with consistent notes. 10,000 cases made.", 13.0m, "2012", new Guid("7de4d549-4298-4ee1-b51b-81b3a7dba3ee") },
                    { new Guid("faa65d41-bc04-4035-82d7-5a22f36945ae"), 3, new DateTime(2014, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Primitivo di Manduria LXXIV", "A burly, brambly red, underscored by tarry smoke and underbrush notes, offering flavors of blackberry coulis, herb-marinated black olive, grilled mushroom and ground spice. This shows muscle that recommends it to short-term cellaring, made accessible by integration and balance. Drink now through 2024. Tasted twice, with consistent notes. 8,000 cases made.", 22.0m, "2010", new Guid("c21b3bc0-0653-4c55-81f7-517a6b4271dd") },
                    { new Guid("b9ba3299-5075-4ee9-a661-45345d580a0d"), 3, new DateTime(2014, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Victoria Are You Game?", "Vivid, peppery and appealing, with cherry and blueberry fruit, revealing black pepper overtones and a polished texture. Lingers easily on the refined finish. Lively and graceful. Drink now through 2018. 5,000 cases made.", 17.0m, "2012", new Guid("dfc4aed1-8e3e-4268-8a52-f3c5623d4e8e") },
                    { new Guid("f878cfe2-19e0-43b4-aa6f-a7bd84ac4005"), 3, new DateTime(2014, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Superiore", "A dark and inky red, featuring violet, black currant, cedar, sandalwood and iron aromas and flavors. Nonetheless, this comes across as elegant, even as the tannins prove formidable on the finish. A terrific young wine in the making. Cabernet Sauvignon, Merlot, Cabernet Franc and Petit Verdot. Best from 2017 through 2035. 2,200 cases imported.", 240.0m, "2011", new Guid("ab935e06-cb25-4b45-9eb2-9b101ac012c4") },
                    { new Guid("2ff69e44-d5eb-462b-83cd-7c53259e5920"), 6, new DateTime(2014, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Constantia Bayten", "Very solid, with a crunchy feel along the edges and pure white peach, flint and honeysuckle notes racing along. Drink now. 4,000 cases imported.", 15.0m, "2013", new Guid("4cace791-799b-4cff-82dc-e1a3ecbede61") },
                    { new Guid("76773c27-c9db-4004-9d23-7e7c20b86f28"), 3, new DateTime(2014, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Colchagua Valley La Joya Gran Reserva", "Open-knit, creamy and lush-tasting, with spiced cherry, dark plum and boysenberry flavors that ooze with rich, spicy notes. Fig and chocolate mousse details flood the finish. Drink now through 2016. 10,000 cases imported.", 13.0m, "2012", new Guid("588eac1d-7f1d-4875-8441-b36258812444") },
                    { new Guid("762efe3f-deec-41a5-a152-0e884939679b"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alentejo Ramos Reserva", "Black olive, dark currant and dried plum flavors give this red plenty of power and lift. Quite minerally as well, featuring an intense and balanced finish that lingers with fig paste and raspberry ganache elements. Drink now through 2020. 41,800 cases made.", 13.0m, "2012", new Guid("42c2b303-8177-44b4-95c2-f37ae2824308") },
                    { new Guid("3d023685-f3ab-4657-aaff-e00276906c14"), 3, new DateTime(2014, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grenache California On the Road", "A robust style, brimming with rich, juicy blackberry, wild berry and boysenberry flavors. This is intense and vibrant, featuring a dense core of fruit complemented by black licorice, cedar and spice notes. Drink now through 2022. 682 cases made.", 42.0m, "2011", new Guid("5b4d4689-d3dc-4ab4-9876-df346615fb7c") },
                    { new Guid("ec370282-0b3e-4478-a44b-03ac96b7cbf6"), 3, new DateTime(2016, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grenache-Syrah-Mourvèdre Corbières", "Full-bodied and powerful, with decadent layers of raspberry compote, kirsch and red plum notes that are met with savory details of herb and cured meat. Offers a tangy, hot stone–accented finish. Drink now through 2025. 18,000 cases made.", 19.0m, "2014", new Guid("4a93022c-a99b-4585-899a-1aadecf123ba") },
                    { new Guid("14ad1ff1-71ed-4231-a6ce-003906c85f61"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Galilee", "A rich red, showing good power to the mineral-infused dried blackberry, dark plum and currant flavors. Engaging dried herbal notes emerge on the focused finish. Kosher. Drink now through 2017. 14,000 cases made.", 17.0m, "2012", new Guid("a2da1fd2-934d-4c2f-b1fb-e3d3b0815bd5") },
                    { new Guid("e0de63f4-e095-4709-aa1d-0eb29a9df1f3"), 6, new DateTime(2014, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Manzanilla Sanlúcar de Barrameda Solear", "Very high-pitched, with bright chalk, chamomile, salt and lemon seed notes. The vigorous finish has serious cut. Drink now. 10,000 cases made.", 15.0m, "NV", new Guid("1708f649-296a-4bbd-a1b9-a26ba702659e") },
                    { new Guid("395faca3-65b5-4540-9803-2e02ffbfb5b1"), 6, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Epanomi White", "A vibrant white, with piercing acidity to the crunchy apple, pear and quince flavors, featuring loads of scallion and green herbal notes. Dried tarragon and creamy accents linger on the complex finish. Malagousia and Assyrtiko. Drink now through 2018. 8,333 cases made.", 22.0m, "2013", new Guid("58a57919-a708-4c60-9419-c22bcca7e6ed") },
                    { new Guid("cd461be3-0d2d-47f0-a429-58b34fecd7e6"), 3, new DateTime(2015, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "SUMŌ  Amador County", "Shows excellent purity and intensity to the huckleberry and blueberry coulis flavors, with supple, velvety tannins and an aromatic lift from violet and white pepper details. Notes of sandalwood, black walnut, Earl Grey tea and nutmeg gain momentum on the tremendous finish, where the details and elegance persist. Petite Sirah, Syrah and Viognier. Drink now through 2030. 220 cases made.", 70.0m, "2013", new Guid("c88007c5-97df-47ed-ad91-0b1fae07eb92") },
                    { new Guid("a4cc585a-46d1-4f1d-991a-94a53744c170"), 6, new DateTime(2015, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc California", "Graceful, with fragrant orange blossom, ruby grapefruit and white peach flavors that are fresh and lively, set on a light, lithe body. Appealing lime zest and passion fruit accents show on the long finish. Drink now. 38,000 cases made.", 12.0m, "2014", new Guid("af888e99-b01b-4957-8a4c-2122fccf1d4d") },
                    { new Guid("0cb28daa-0378-48fe-a0e7-abc2ba789950"), 3, new DateTime(2016, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Evel Red", "A lithe red, with good cut to the red berry and plum tart flavors, supported by fresh acidity, showing notes of pepper and hot stone on the minerally finish. Offers impressive purity to the fruit components. Drink now through 2020. 60,000 cases made.", 13.0m, "2014", new Guid("f5620f5f-9059-4868-8b70-af783063efb9") },
                    { new Guid("ab79502f-7e91-45b0-a4aa-86aae6a64d9a"), 3, new DateTime(2015, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Porca de Murça Red", "A taut, sinewy style, with notes of slate and white pepper to the roasted plum and dried red berry flavors. Medium-grained tannins emerge on the snappy finish, showing ferrous accents. Drink now through 2020. 250,000 cases made.", 10.0m, "2013", new Guid("f5620f5f-9059-4868-8b70-af783063efb9") },
                    { new Guid("848068f7-c590-4af5-a100-a610b8e3d5b6"), 3, new DateTime(2015, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Grande Cuvée", "Sleek, lithe and inviting, with juicy cherry, black cherry and gentle spice flavors that glide quickly over polished tannins into a long and vivid finish. This has presence, harmony, depth and persistence. Best from 2016 through 2022. 7,000 cases made.", 25.0m, "2012", new Guid("5b7c9134-7315-4016-a1a0-292cbf7f436f") },
                    { new Guid("2f61e179-5d3f-4d09-94f7-56c66ce26d30"), 3, new DateTime(2015, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Columbia Valley The Pundit", "This shows nice tension between the ripe, dark flavors and crisp, lively structure, featuring a sleek core of plum and smoky tea flavors that carry easily into the long and deftly balanced finish. A little cellaring should soften the tannins. Best from 2017 through 2020. 8,000 cases made.", 25.0m, "2013", new Guid("e9b88a4d-8a24-496b-9103-2bdfefe82d77") },
                    { new Guid("21ed8b8e-3931-4051-8cc8-e9fe5a9545d5"), 3, new DateTime(2015, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley Alto Gran Reserva", "A rich, well-spiced red, with luscious flavors of tar, licorice and dark plum, supported by ample acidity and firm tannins. Offers a lip-smacking finish of dark chocolate and pepper. Drink now through 2019. 20,000 cases made.", 15.0m, "2012", new Guid("20327f5d-1c7b-4e5f-bcf9-ee7124973a13") },
                    { new Guid("a5e3823e-3bf2-4122-9553-cb45534a6e5d"), 3, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley Powerline Estate", "A knockout Syrah, precise and impeccably built but explosive with personality. Smoky roasted meat and floral blackberry aromas combine with bold, supple flavors of dark plum, pepper and licorice. The tannins are big but polished. Drink now through 2024. 1,319 cases made.", 45.0m, "2014", new Guid("135a54d9-cfdb-4bf8-844b-b24e0783c979") },
                    { new Guid("472ae29f-2f2c-413b-a923-cf72f325646a"), 3, new DateTime(2015, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Creator Walla Walla Valley", "Supple, expressive, polished and generous, with layers of dark berry, cherry, black olive and spice flavors that merge harmoniously on the long and refined finish. Cabernet Sauvignon and Syrah. Drink now through 2022. 1,301 cases made.", 55.0m, "2012", new Guid("135a54d9-cfdb-4bf8-844b-b24e0783c979") },
                    { new Guid("6774bd19-6307-4a66-a6ee-98fb896c5f4c"), 6, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Central Coast", "This expressive white is aromatic and juicy, featuring a rich, ripe, fleshy core of apricot and peach flavors, with accents of vanilla bean, honeysuckle and melon. A spicy note lingers on the long finish. Viognier, Roussanne, Grenache Blanc and Chardonnay. Drink now. 5,100 cases made.", 20.0m, "2015", new Guid("c7b85a23-dc83-4972-a8bb-e2e5823d8d93") },
                    { new Guid("9028e5f0-4d9e-4a94-b6dc-8515b80582a1"), 6, new DateTime(2015, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Santa Barbara County", "This is lush and powerful, offering fleshy peach and apricot flavors at the core, with plenty of aromatic highlights, including orange blossom and honeysuckle. The texture is appealingly smooth and mouthwatering, with the complex details gaining momentum on the finish, where this reveals hints of dried spice and matcha green tea. Viognier, Roussanne, Grenache Blanc and Chardonnay. Drink now through 2020. 3,500 cases made.", 20.0m, "2014", new Guid("c7b85a23-dc83-4972-a8bb-e2e5823d8d93") },
                    { new Guid("dede8d39-125e-4168-81d8-8f4e23fa5e39"), 2, new DateTime(2015, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bual Madeira", "Deep, rich and exotic-tasting, with plenty of smoky accents to the dried mango, apricot and roasted pineapple flavors, featuring notes of crème brûlée. Very fresh midpalate, presenting an unctuous finish of ginger and cream. Drink now through 2050. 500 cases made.", 50.0m, "2002", new Guid("7f6a7660-b07a-4123-9597-b0b07f3cef41") },
                    { new Guid("4e5bef95-8a54-4a57-9a4c-a04d976462cc"), 3, new DateTime(2015, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley The Deuce", "Supple, ripe and provocative, with mineral, licorice and tapenade flavors running through a plush, generous core of dark plum and black cherry. Shows presence and polish. Drink now through 2022. 583 cases made.", 52.0m, "2012", new Guid("69d4e6cb-3277-4ce1-864f-80172610a0f1") },
                    { new Guid("e439f3bc-176d-48d8-8099-4f45ba3f2fb0"), 3, new DateTime(2015, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Monterey-Sonoma-Santa Barbara Counties", "Rich yet medium-weight, offering an exciting interplay of berry, oak, earth and spice notes, with subtle edges to the blueberry, raspberry, mocha and fresh-turned earth flavors. Ends with an aftertaste of melted black licorice. Drink now through 2020. 317,592 cases made.", 22.0m, "2013", new Guid("11317035-fcad-444a-8d57-cc71becbb7df") },
                    { new Guid("5550cce0-4803-423b-8964-2121d6665323"), 3, new DateTime(2015, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Russian River Valley", "A knockout Zinfandel, balancing an opulent texture with great focus and depth. Aromas of rose petal and raspberry lead to rich and layered flavors of cherry, herbes de Provence and cracked white pepper. Finishes with a bounce of acidity and ripe, lively tannins. Drink now through 2020. 1,440 cases made.", 32.0m, "2012", new Guid("ff96ae59-0c2f-4361-9e3d-5c905dd0f94d") },
                    { new Guid("7658d83e-00e5-4d4a-a32d-8333a763c1f7"), 2, new DateTime(2015, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vin de Constance Constantia", "Rich and unctuous, yet superbly racy, this offers a gorgeous panoply of dried apricot, fig, mango and quince flavors, underscored by a lively blood orange note and backed by a long, ginger- and green tea-filled finish. Youthfully tight, this should cruise for some time in the cellar. Muscat de Frontignan. Best from 2016 through 2030. 2,500 cases made.", 80.0m, "2009", new Guid("75d94e65-728d-48a3-82ee-5db76e213291") },
                    { new Guid("07ff6476-0c77-4d31-abc3-f61b123bd030"), 3, new DateTime(2015, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Amarone della Valpolicella Classico Serègo Alighieri Vaio Armaron", "An elegant, impeccably balanced red, layering sculpted tannins with finely knit flavors of plumped black cherry, plum sauce, cured tobacco, dark chocolate and woodsy spice. A rich vein of smoky minerality sings on the lasting finish. Drink now through 2035. 3,750 cases made.", 85.0m, "2008", new Guid("a7da0882-ed55-4b6b-aa1d-181006d43ae3") },
                    { new Guid("b90cf960-320f-4e25-a64c-23eb57d4f52b"), 3, new DateTime(2015, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Martinborough Kupe Single Vineyard", "Complex, concentrated and intriguing, with fleshy blackberry and plum flavors at the core, showing highlights of black walnut, sandalwood, dried pine needle, black pepper and musky Darjeeling tea. The tannins are refined and plush, and the finish is epic. Drink now through 2030. 500 cases imported.", 69.0m, "2013", new Guid("a58a7060-dbb8-4e2a-97b0-3f3a89cc0821") },
                    { new Guid("4473453e-7958-42df-8248-aace0e815bc2"), 3, new DateTime(2014, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Marlborough Pioneer Block 16 Awatere", "This offers a good mix of fresh, ripe raspberry and cherry flavors, with details of forest floor, black tea, cigar box and nutmeg, finishing with nuances of warm baking spices and a touch of firmness. Drink now through 2025. 1,000 cases made.", 31.0m, "2012", new Guid("b81d4d83-094d-4207-b0f6-542bf37823c5") },
                    { new Guid("84f57e64-301b-44d3-a00e-a86183eb24d2"), 6, new DateTime(2014, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kamptal", "A bright, crisp white, with savory hints to the laserlike flavors of Fuji apple, Meyer lemon and melon. Long and richly spiced midpalate, presenting hints of ginger and kiwifruit on the finish. Drink now through 2019. 5,416 cases made.", 22.0m, "2012", new Guid("105a0447-5f83-4a76-89b4-0f4e8292ffd1") },
                    { new Guid("f91ea456-861e-4628-8f18-74d3c47c4365"), 3, new DateTime(2018, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Etna San Lorenzo", "Ripe black cherry and raspberry fruit meets tarry mineral and accents of licorice, spiced orange peel and herb in this chewy, medium-bodied red. The bold flavor and structure are deftly knit into a graceful and harmonious package, with the spice and mineral character lingering on the finish. Best from 2020 through 2030. 750 cases made.", 60.0m, "2016", new Guid("2ab6332a-127f-42da-bb87-b69b3cea383c") },
                    { new Guid("fe8919a9-bf77-4210-ba21-0c24938a8004"), 3, new DateTime(2014, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Etna", "Fresh and elegant, with a core of ripe crushed cherry and wild strawberry fruit. The chewy tannins impart supple structure and weight, while hints of ground anise, dried thyme and smoky mineral add complexity and linger on the finish. Drink now through 2022. 7,500 cases made.", 22.0m, "2012", new Guid("2ab6332a-127f-42da-bb87-b69b3cea383c") },
                    { new Guid("ecb48cfa-b94d-4513-83e0-ba316316beab"), 6, new DateTime(2018, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malagousia Epanomi Single Vineyard", "A lovely nose of floral and citrus notes leads to expressive flavors of ruby red grapefruit granita and ripe yellow peach in this well-cut, light- to medium-bodied white. Lithe and lingering on the minerally finish. Drink now through 2023. 6,700 cases made.", 23.0m, "2016", new Guid("58a57919-a708-4c60-9419-c22bcca7e6ed") },
                    { new Guid("5b7a4f8a-d083-4d54-b405-0152fed3d2bc"), 3, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Maremma Toscana Tenuta Belguardo", "A red of great purity and polish, offering black cherry, blackberry, spice and herb flavors, this evokes tobacco, graphite and leather nuances as it builds nicely on the palate. Finishes with grip, harmony and complexity, as well as terrific length. Cabernet Sauvignon and Cabernet Franc. Drink now through 2028. 1,083 cases made.", 55.0m, "2013", new Guid("3bdb48b5-0fcf-4c92-933a-4875b8da245e") },
                    { new Guid("b791e55c-4ac6-4a38-a654-88be2ddd1e87"), 3, new DateTime(2016, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viño de la Tierra de Castilla y León Selección Especial Sardon de Duero", "A polished texture carries harmonious flavors of black cherry, cola, licorice and mineral in this focused red. Well-integrated tannins and balsamic acidity lend this a balanced structure. Reserved but deep. Drink now through 2022. 31,150 cases made.", 28.0m, "2012", new Guid("7b628c54-e1f1-43ee-8c8c-15ce7e0148b4") }
                });

            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("367be49c-9696-4aae-b350-2013e8ea4bcb"), 3, new DateTime(2017, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley Reserva", "Dark plum and dried red berry flavors dominate this muscular red, with plenty of minty and savory accents. Medium-grained tannins support chocolate and cream details on the finish. Drink now through 2021. 25,000 cases made.", 15.0m, "2015", new Guid("1652c7e3-5862-49e7-a12d-e6245b458d4c") },
                    { new Guid("d6ef2074-d055-41a2-a644-411229631492"), 6, new DateTime(2017, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Finger Lakes Classique", "Weighty for a Riesling, with a rounded feel and layers of creamed yellow apple, white peach and persimmon flavors. Delivers energy and cut throughout, with lemon pith, citrus oil and chamomile accents filling in. Shows length and range through the finish. Drink now through 2021. 1,800 cases made.", 19.0m, "2015", new Guid("fdd3c2df-a312-40ba-92ab-3d8bb2eb7877") },
                    { new Guid("3d24aada-bb23-4a4e-82f0-01811361a4d5"), 6, new DateTime(2019, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Dry Creek Valley Alder Grove Vineyard", "A complex note of dried honeysuckle and a whiff of smoke add intrigue and depth to the core of succulent peach, melon and mango flavors, set on a medium body, with a fresh frame. Shows wonderful purity of fruit and nuances that linger effortlessly. Drink now. 1,370 cases made.", 24.0m, "2017", new Guid("343d0fbf-fdff-4b7f-a127-4b4954d554b0") },
                    { new Guid("11917df8-b91e-40d5-b819-1ad3edae7991"), 3, new DateTime(2019, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Zingari", "A well of dark cherry, black currant and blackberry fruit makes this red sumptuous and inviting. Fresh earth, thyme and rosemary accents add depth. A mineral element emerges on the long finish. Shows fine balance overall. Merlot, Sangiovese, Petit Verdot and Syrah. Drink now through 2032. 7,000 cases made, 1,500 cases imported.", 15.0m, "2017", new Guid("7357bbcf-2e8f-4cb7-88d1-6f25977c9807") },
                    { new Guid("347da22f-5641-430c-a60b-a7d99365df32"), 3, new DateTime(2019, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Toscana Legit", "This is broad and incisive, delivering black currant, plum, leather, earth and wild herb flavors. Dense and beefy, yet with ample fruit for balance, driven by vibrant acidity. This is starting to mellow but still has years of life ahead. Drink now through 2036. 15,000 cases made, 3,000 cases imported.", 45.0m, "2013", new Guid("4a2b3006-ed53-49ce-b7d3-e784d1cbc888") },
                    { new Guid("e0a6503a-c6a4-4ff5-af1b-d2a972b90fc8"), 3, new DateTime(2019, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat La Vilella Alta Black Slate", "This generous red features a round, soft texture that carries ripe black fruit flavors, laced with licorice, toast and loamy earth notes. Offers well-integrated tannins and gentle acidity. Pretty floral and cinnamon details emerge on the finish. Drink now through 2028. 1,800 cases imported.", 23.0m, "2016", new Guid("3c10cb01-8f84-4fa0-8f7c-90928b233809") },
                    { new Guid("e0afebf9-1e3d-4181-98a4-05b73c840129"), 3, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Orben", "This big red has a muscular structure, but the flavors are finely detailed, with black cherry, leafy, licorice and smoke notes mingling over sturdy tannins. Harmonious, showing depth and elegance. Best from 2020 through 2030. 2,486 cases made, 500 cases imported.", 32.0m, "2016", new Guid("b338466d-998f-40bc-bb46-a46c96cd12a0") },
                    { new Guid("13235f12-9913-4511-a9c4-e634c3eebfb9"), 3, new DateTime(2019, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Mount Veeder", "Youthfully compact, with tightly wound black currant, bitter plum and dark blueberry notes, this needs time to meld with the charcoal, bay leaf and tar streaks, but the mouthwatering cut on the finish suggests that won't be a problem. Offers a long echo of juniper at the end. Best from 2023 through 2040. 2,250 cases made.", 125.0m, "2015", new Guid("dd784ecd-b99f-4f10-83f8-3e08151cbc46") },
                    { new Guid("70aded06-612c-4d8b-b333-3b59002f7983"), 3, new DateTime(2018, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meritage Rutherford", "Bordeaux-like, offering an expansive mix of currant, cedar, cigar box, riverbed and tobacco leaf flavors. Cabernet Sauvignon, Merlot, Petit Verdot, Malbec and Cabernet Franc. Best from 2020 through 2032. 1,429 cases made.", 45.0m, "2014", new Guid("bbe2e5a6-2bb0-415f-bcf3-1417cda08c08") },
                    { new Guid("e8672d92-2d6c-471a-b211-e1fede79b8fe"), 5, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Lambrusco di Sorbara del Fondatore", "A well-knit rosé Lambrusco, featuring a fine, lively mousse and a bright mix of crushed raspberry, crunchy peach, dried thyme and rose petal notes, with a tangy finish of citrus zest and spice. Drink now through 2020. 5,000 cases made.", 17.0m, "2016", new Guid("27126b06-d6f8-401a-aa69-a6ceba90cc16") },
                    { new Guid("706e390f-7c86-4423-8c08-799f7a4029bc"), 6, new DateTime(2018, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Eola-Amity Hills", "Polished and refined, with expressive apple, ripe cantaloupe and spice flavors that take on richness on the long finish. Drink now through 2020. 2,866 cases made.", 35.0m, "2016", new Guid("d8265d75-8f38-4486-8827-a6174105ad46") },
                    { new Guid("fb2fff6e-7f78-43ce-bf49-3fddb13724a5"), 3, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montefalco Sagrantino Colle alle Macchie", "There's a pleasing creaminess to the intense tannins of this harmonious, full-bodied red, offering crushed cassis and black raspberry fruit that shows accents of lavender and rosemary, with licorice, espresso and loamy earth notes. An iron fist in a velvet glove, this should unfurl beautifully with age. Best from 2020 through 2035. 175 cases made.", 75.0m, "2013", new Guid("760bb254-003f-4613-81b9-91ba3246ec07") },
                    { new Guid("4b52277e-2c73-46bb-9e6f-0cda05b72afb"), 6, new DateTime(2019, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Niederösterreich Am Berg", "Lovely aromas of lily, rose, vanilla and coconut accent the peach and pineapple flavors in this white. A firm structure and dense texture make this complete. Multidimensional, with enough acidity for balance, and the body helps out. Persistent saline finish. Grüner Veltliner. Drink now through 2027. 1,320 cases imported.", 21.0m, "2017", new Guid("aa954f81-d6ca-4ce6-842c-7d2ea520900c") },
                    { new Guid("006f3dad-322e-44a7-886e-63ca2b0e6a48"), 3, new DateTime(2018, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yolo-Sonoma-Monterey Counties Winemaker's Blend", "Complex and spicy, offering wild plum, blackberry, spice and tea flavors that show subtle herb shadings, ending with dusty, loamy tannins and good length. Drink now through 2023. 13,000 cases made.", 23.0m, "2016", new Guid("4fc1b753-ee1b-4100-99fe-bf35cf4d89d3") },
                    { new Guid("26700e21-90a3-4f17-9259-e90e5c96f0c4"), 3, new DateTime(2018, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Lucia Highlands Doctor's Vineyard", "Rich and layered, with plump, juicy wild berry, raspberry, blackberry jam and cedar flavors. A full and complete style that touches all the bases. Drink now through 2025. 777 cases made.", 50.0m, "2016", new Guid("d28ccae6-c7ae-4f6f-b95c-45c1af3095eb") },
                    { new Guid("de6a6fd5-4353-470a-bdf0-8b782f75829c"), 6, new DateTime(2018, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Annia Napa Valley", "A complex and inviting white, showing intricate nuances of roasted almond, matcha, honeysuckle and orange zest, mingling with supple, juicy Asian pear, tangerine and ripe melon notes. The smooth texture and a thread of acidity add to the appeal. Ribolla Gialla, Tocai Friulano and Chardonnay. Drink now. 1,128 cases made.", 30.0m, "2017", new Guid("281a1db5-84f9-480a-a54b-1768ded19e97") },
                    { new Guid("bafa7bdd-9873-4d77-a779-517a6c686783"), 3, new DateTime(2018, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Columbia Valley Levitation", "Polished and expressive, with blackberry, smoky sweet anise and cracked pepper flavors that take on richness toward refined tannins. Drink now through 2024. 750 cases made.", 45.0m, "2015", new Guid("ad03e90e-b39d-42db-8153-642140102f03") },
                    { new Guid("e2001c8c-0028-4da9-968d-12ed19e2cb74"), 0, new DateTime(2018, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Languedoc Rosé Diving Into Hampton Water", "A nicely crafted rosé, with a hint of creamy richness lining the strawberry, melon and white cherry flavors. A dash of minerality imparts depth through the long, mouthwatering finish. A crowd-pleaser. Grenache, Cinsault, Mourvèdre and Syrah. Drink now. 35,000 cases made.", 25.0m, "2017", new Guid("ad053e6a-91c8-4de8-86c1-d11fc9e27bfd") },
                    { new Guid("d143db88-5f47-48dc-ba6d-12e8add7ed71"), 6, new DateTime(2018, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Val di Neto White Critone", "Fresh and floral, with lime blossom, kiwifruit, peach and blood orange sorbet flavors, this medium-bodied white is backed by vibrant acidity and lingers with spice and graphite accents. Chardonnay with Sauvignon Blanc. Drink now through 2021. 30,000 cases made.", 15.0m, "2017", new Guid("22f89b8c-05b5-4522-84af-111a4085a009") },
                    { new Guid("3a0bd6b3-49b6-48e9-a3ab-50ee371b780e"), 3, new DateTime(2018, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Clos des Porrets St.-Georges", "This red combines flavors of cherry, currant, spice and mineral with a core of sweet fruit, a silky texture and firm, chalky tannins. Finishes on the chewy side, with mouthcoating but not astringent tannins. Best from 2023 through 2040. 400 cases imported.", 94.0m, "2015", new Guid("7dba242b-de17-4f65-801d-e5d5a71d6245") },
                    { new Guid("c9a7c621-b656-4c11-89f1-0d8ce054abda"), 3, new DateTime(2018, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Emilia Trebbiolo", "A pure beam of juicy black currant is underscored by woodsy spice, licorice and tea rose notes in this bright and appealing, medium-bodied red, with light tannins. Barbera and Bonarda. Drink now through 2023. 2,216 cases made.", 22.0m, "2014", new Guid("c6bd0f2c-a377-4e4f-bed1-f31adfb49420") },
                    { new Guid("dc96dfd8-b03d-4f0d-b4cc-8fadb66c4da2"), 6, new DateTime(2018, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Kamptal", "An aromatic style, boasting lilac and freesia accents. Apple and grapefruit notes prevail as this elegant white winds down on the graceful finish. Drink now through 2023. 3,700 cases imported.", 20.0m, "2016", new Guid("47aee860-6c2e-4188-befd-702430b23be5") },
                    { new Guid("8e0c0e46-592c-4442-a452-a41d627cc64a"), 6, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Soave Classico Otto", "A lively, lithe white, with lovely lime blossom, pink grapefruit sorbet, stone and spice notes that linger on the mouthwatering finish. Drink now through 2022. 22,500 cases made.", 20.0m, "2017", new Guid("d1397678-9380-422d-af3d-3eb6fd2b0058") },
                    { new Guid("45914e94-54fd-4f8f-a787-1fba94fcf064"), 6, new DateTime(2018, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro White Papa Figos", "Aromatic, with notes of beeswax and almond flanking the lemon curd, pureed mango and peach flavors of this medium- to full-bodied white. Spice, herb and mineral details cascade into the long, creamy finish. Drink now through 2019. 20,000 cases made.", 18.0m, "2016", new Guid("f13129cd-7a15-453f-978a-12d120327ea1") },
                    { new Guid("e14f45f0-a446-40a1-ac48-64d99893b056"), 3, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Luján de Cuyo Finca Perdriel", "Big, rich and full-bodied, with inky accents to the ripe dark plum, cherry and blackberry flavors. Black licorice notes show midpalate, with mocha and dark chocolate details on the dense finish. Drink now through 2022. 336 cases made.", 90.0m, "2015", new Guid("9b3e55c4-bfd0-4268-93ec-a224a3fb8b69") },
                    { new Guid("1a2db99f-cd33-452c-b7bd-f107df5f3f51"), 3, new DateTime(2018, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley Readers", "Polished yet appealingly brawny, with floral black cherry, toasted spice and bittersweet chocolate flavors that take on momentum toward refined tannins. Drink now through 2026. 3,405 cases made.", 28.0m, "2015", new Guid("96769591-bb80-4e28-97a6-4ab04fd88eb2") },
                    { new Guid("4801c838-2dc3-4fe9-b63d-b9c00d1aa03d"), 3, new DateTime(2019, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Autinbej", "A briny mineral note in the aroma signals this high-tension red. There is ample fruit and flesh on the palate, with cherry, strawberry, stone and white pepper flavors. Tightens up again on the finish. Best from 2021 through 2038. 1,830 cases made, 1,200 cases imported.", 28.0m, "2015", new Guid("3c3883dd-cc98-43c6-a954-847242566394") },
                    { new Guid("f0d1b31e-c5ab-4a3d-9ea0-e3854bbfee4f"), 3, new DateTime(2019, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Ribbon Ridge Les Dijonnais", "Sleek and harmonious, with effortlessly complex violet, raspberry and river stone accents that take on richness toward a lingering finish. Drink now through 2025. 640 cases made.", 52.0m, "2016", new Guid("d6d5d0aa-5169-44bd-ac70-94cee277bbca") },
                    { new Guid("e93474a2-2897-4aaf-9be4-78bb4d5f101e"), 3, new DateTime(2019, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Brothers Vineyard", "Offers a tasty core of plum and blackberry fruit, backed by lively anise and apple wood notes. Restrained overall, with an alluring freshness and a subtle charcoal hint lingering deftly at the end. Drink now through 2032. 639 cases made.", 90.0m, "2016", new Guid("df5324ed-06f5-4b27-8d70-514211fd37dd") },
                    { new Guid("43ab42e5-d75c-4dee-bc78-e61e232007b1"), 6, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Franken Iphöfer Kronsberg Alte Reben", "Clean and airy, with good cut to the apple, green peach and gooseberry flavors, featuring notes of dried mint. The spicy finish lingers, revealing accents of savory mineral and white pepper. Best from 2020 through 2027. 1,000 cases made, 650 cases imported.", 19.0m, "2017", new Guid("01eab22d-bb68-44ca-b06c-a62ed99587bc") },
                    { new Guid("2a0703f9-1749-48b9-aa12-9e4e821802f7"), 6, new DateTime(2019, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rueda Campo Elíseo Cuvée Alegre", "This rich white shows lively flavors of pineapple and quince, backed by tangerine acidity and deepened by notes of vanilla and spice. A fruit-centered style that remains bright and balanced. Drink now. 3,750 cases produced, 200 cases imported.", 15.0m, "2018", new Guid("bf653470-29b6-4a3e-8ad1-7fc871d3fe41") },
                    { new Guid("f9bccd63-7d2b-461b-bf0a-b0f4fd8e048b"), 0, new DateTime(2019, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bandol Rosé Réserve", "Supple white raspberry and tangerine notes are supported by steely acidity, with heather, wild herb and mineral notes carrying through the spicy, savory-driven finish. Drink now. 1,200 cases made, 1,200 cases imported.", 22.0m, "2018", new Guid("82e06d66-49b5-4e2e-9c6d-3bda90c85d8c") },
                    { new Guid("aec9bba3-bfe2-4553-8391-206ad2ddc61b"), 3, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Aglianico del Vulture Arberesko", "A fresh and elegant red, medium- to full-bodied, featuring supple tannins finely meshed with flavors of crushed black cherry fruit and a core of smoke-tinged mineral, with delicate tea leaf and star anise accents. Long and racy on the finish. Drink now through 2029. 1,000 cases made, 200 cases imported.", 24.0m, "2015", new Guid("5c371860-5266-4ff5-9e79-d6fc30f086c7") },
                    { new Guid("c58e6e55-514c-4982-a4b6-3cb19756154f"), 6, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Blanc Pfalz Becker Family", "A harmonious white, lush and creamy in texture, with acidity imparting freshness and focus. Offers a pleasing mix of Bosc pear, pickled ginger, apple blossom and stony mineral notes. Spicy finish. Drink now through 2025. 600 cases imported.", 18.0m, "2017", new Guid("2968407a-e256-4ee8-a0fa-1a7497e2503a") },
                    { new Guid("6e3c6a95-8cb9-4146-b631-1d3683db9c07"), 5, new DateTime(2019, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Crémant de Bourgogne Grande Cuvée", "A toasty version, laced with vanilla, pastry cream and spice notes, this is enlivened by racy acidity that creates a mouthwatering frame for the poached quince, apricot and candied ginger flavors. Drink now through 2022. 100,000 cases made, 5,000 cases imported.", 20.0m, "NV", new Guid("2c95ab82-366d-4c25-a51d-f316ff83ea26") },
                    { new Guid("0d88689a-499b-4eaf-9cde-dbf153b3ad75"), 6, new DateTime(2019, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cirò White Mare Chiaro", "An aromatic, light-bodied white, with lip-smacking acidity and a subtly chalky texture, this offers flavors of melon, jasmine and pink grapefruit pith. Minerally finish. Drink now through 2022. 9,000 cases made, 2,000 cases imported.", 19.0m, "2018", new Guid("999633fc-9cfb-463f-8c45-3db31e361f76") },
                    { new Guid("a711c358-5181-4f46-95ab-da2e25a4c091"), 3, new DateTime(2019, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Los Chacayes Gran Reserva Revana Vineyard", "Elegant and powerful, featuring concentrated kirsch, blueberry and dark cherry flavors that are supported by fresh acidity and zesty tannins. Shows intense minerality midpalate, with a long and generously spiced finish, filled with accents of flint and clove. Complex. Drink now through 2025. 200 cases made, 150 cases imported.", 75.0m, "2016", new Guid("16800174-8f7b-46b1-a3e9-9b220796fc4e") },
                    { new Guid("dc23fc73-7bfb-4f5b-877b-95604d747bd1"), 3, new DateTime(2019, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Mt. Jefferson Cuvée", "Expressive strawberry blossom and raspberry aromas open to elegantly structured cherry and spicy cinnamon flavors that build richness toward refined tannins. Drink now through 2025. 9,835 cases made.", 35.0m, "2017", new Guid("70329906-cafc-4091-bd83-5fa1a8c64953") },
                    { new Guid("242735f6-cb5c-4bed-97d9-f11ddb2218c8"), 3, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Dissident Columbia Valley", "Expressive and multilayered, with lively rose petal and black cherry aromas and sleekly complex flavors that take on richness toward polished tannins. Cabernet Sauvignon, Merlot, Cabernet Franc and Petit Verdot. Drink now through 2029. 3,900 cases made.", 38.0m, "2016", new Guid("d59698bc-bb9b-4465-aa70-a46b4d16e323") },
                    { new Guid("56b67c39-8e7a-47e7-aadf-da99bf126235"), 6, new DateTime(2019, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Verdicchio dei Castelli di Jesi Classico Superiore Luzano", "Light herb and citrus zest notes accent the apricot, wet stone, almond blossom and pear flavors in sleek and silky, light- to medium-bodied white. Fresh and lingering, with a minerally finish. Drink now through 2024. 1,500 cases imported.", 17.0m, "2017", new Guid("3f0acc46-62ad-46aa-803a-f1c79b7c3771") },
                    { new Guid("ce1b3ad5-b4bd-456d-a3f5-4e2d65cdb702"), 3, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Coombsville", "Dark and winey, with steeped black currant, fig and blackberry fruit flavors, inlaid liberally with smoldering charcoal and sweet tobacco notes. Anise and tar details score the finish, adding length and range to the depth and energy. Best from 2021 through 2035. Tasted twice, with consistent notes. 257 cases made.", 65.0m, "2015", new Guid("b1552dee-cdc2-4731-b997-4bfa50e8ceed") },
                    { new Guid("168e6aae-0018-44c4-ab6f-f22f6c7f5a3f"), 5, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Rosé Franciacorta", "An expressive rosé, with mouthwatering acidity and a satiny mousse, this offers flavors of ripe nectarine, candied ginger, pomegranate and star anise, underscored by chalky mineral notes. Drink now through 2024. 6,000 cases made, 600 cases imported.", 29.0m, "NV", new Guid("5f9f1c7e-3113-432b-be46-4faa39e81b4e") },
                    { new Guid("7dae968a-cab3-48bb-b022-07d021f559f6"), 3, new DateTime(2019, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast 116", "This offers creamy raspberry, currant and wild plum flavors, supported by medium-grained tannins. The spicy finish lingers with chocolate notes and hints of mint. Drink now through 2023. 5,024 cases made.", 23.0m, "2017", new Guid("c9efae63-a913-4b84-9c23-7d530b0343af") },
                    { new Guid("33eb8ec7-3900-42db-822e-9c0c16356bd0"), 6, new DateTime(2019, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Terre Brûlée Le Blanc Swartland", "Elegant, showing a gentle texture, with intense acidity giving energy to the flavors of yellow apple, lime and honeysuckle. Salty mineral elements emerge midpalate, leaving a savory aftertaste. Best from 2021 through 2028. 2,200 cases imported.", 16.0m, "2018", new Guid("0d52fe30-da63-48b6-9250-956926846f5e") },
                    { new Guid("a0b5a99d-11a1-4a88-83d3-ed0a3883bd10"), 6, new DateTime(2019, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Costières de Nîmes White Nostre Païs", "Fresh, featuring pear, yellow apple, melon and jasmine notes streaming through together, with a flash of lemon shortbread on the enticing finish. Grenache Blanc, Roussanne, Clairette, Viognier and Bourboulenc. Drink now. 3,000 cases made, 1,200 cases imported.", 22.0m, "2017", new Guid("b8c0a799-b532-4856-84c6-f618731c0f76") },
                    { new Guid("6b4c29e3-98c7-4a46-bfec-442463afbce2"), 3, new DateTime(2019, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley Hussonet Gran Reserva", "Big and fresh-tasting, with concentrated dark plum, dark currant and black olive flavors. Asian spice and dark chocolate notes on the muscular finish. Drink now through 2024. 2,500 cases imported.", 15.0m, "2015", new Guid("834f00b6-c5f1-41bb-a5eb-538fb3dd8eb5") },
                    { new Guid("df984044-e623-4e5e-aa6c-650f8c4c4e98"), 6, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Maria Valley Katherine's Vineyard", "Well-structured, with a smoky richness to the mineral-infused apple and pear tart flavors. The fresh, spicy finish lingers with a creamy texture. Drink now through 2022. 40,000 cases made.", 22.0m, "2017", new Guid("1967b2a7-45e5-4ac4-871f-87976cd7d529") },
                    { new Guid("58351fa2-4044-4c9f-b9c5-8c0a49e79f14"), 6, new DateTime(2019, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc North Canterbury", "Offers an appealing core of intense, lush flavors of ripe melon and peach, with details of white pepper, honeysuckle and ruby grapefruit lingering on the finish. Impressive for the intensity and focus. Drink now. 14,800 cases made, 10,000 cases imported.", 17.0m, "2018", new Guid("9cf232ad-1261-4e4a-b809-7e6b6e36d6de") },
                    { new Guid("fece1140-b400-4302-9722-36179e7e5f3a"), 5, new DateTime(2019, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Rosé Cava Reserva", "The flavors of nectarine and white cherry are fresh and pure in this elegant sparkling rosé, with pretty accents of ground ginger, chalk and pink grapefruit sorbet riding the fine bead and lingering on the zesty finish. Drink now through 2022. 5,000 cases made, 1,000 cases imported.", 22.0m, "2016", new Guid("c0e584ae-968c-4416-a131-72861f6c7908") },
                    { new Guid("eccf1e88-ad5f-4850-a9b8-bbf1e5c6f09b"), 3, new DateTime(2019, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Washington Cs Substance", "Precise and vibrantly structured, with black cherry, espresso and spice flavors that take on richness toward polished tannins. Drink now through 2024. 162,605 cases made.", 17.0m, "2017", new Guid("59c55b11-6b9c-45ca-9137-f868537656d5") },
                    { new Guid("8ee76cf7-cdb4-48d7-bed3-9fd3b1ad0611"), 6, new DateTime(2019, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscadet de Sèvre et Maine Sur Lie Clos des Allées", "Very pure, showing a bit of richness, with a silky texture and notes of lemon verbena, lime zest, chamomile and fleur de sel that persist on the long, savory and dry finish. Drink now through 2026. 600 cases imported.", 21.0m, "2018", new Guid("8608a322-4e27-4096-ab4e-b7312689d2d2") },
                    { new Guid("f24f95e4-e7b8-4148-a3a6-b6395bcf9974"), 6, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Grigio Vigneti delle Dolomiti", "This balanced, light-bodied white offers a lively mix of peach, passion fruit, floral and pink grapefruit zest notes, with tangy acidity and a floral finish. Drink now through 2021. 25,800 cases made.", 16.0m, "2017", new Guid("8b9a960d-26f5-42a3-90a3-69b8724be2d5") },
                    { new Guid("6566703c-5f3b-40cf-ac2d-e63f3825b15c"), 6, new DateTime(2018, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Qualitätswein Feinherb Mosel Barrel X", "A lithe white, showing fine clarity and focus, with orange blossom aromas followed by kumquat and nectarine notes on the palate. Off-dry, offering ample acidity for balance, with a mineral-infused finish. Drink now through 2020. 1,000 cases imported.", 22.0m, "2016", new Guid("349b4246-1de0-476a-85c0-180c2932a808") },
                    { new Guid("7e7e8e42-caf6-45ef-9cf1-60a0cab3383c"), 3, new DateTime(2018, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Calchaquí Valley Don David Reserve", "Dried red fruit flavors are lively, offering a surplus of rich, dried savory herb notes. Accents of hot stone and white pepper flood the finish. Drink now through 2023. 110,000 cases made.", 16.0m, "2017", new Guid("b4a01975-aeb5-460d-9b7d-a9219f80b26e") },
                    { new Guid("d4b44739-9bee-43dc-ade5-7d0c466ffe0f"), 3, new DateTime(2017, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Eola-Amity Hills Tongue 'N Cheek", "Lithe and delicately complex, offering rose petal and tart blueberry aromas, with effortlessly balanced raspberry, river rock and spice flavors that finish with refined tannins. Drink now through 2022. 594 cases made.", 60.0m, "2015", new Guid("0eeb8804-211d-497a-9086-999e9390df27") },
                    { new Guid("9588860c-f09a-4f2b-86e6-5597384dd704"), 3, new DateTime(2017, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zachary's Ladder Red Mountain", "Rich yet densely structured, with expressive black cherry and blueberry fruit, accented by smoky licorice and finishing with big but refined tannins. Cabernet Sauvignon, Syrah and Petit Verdot. Best from 2019 through 2025. 600 cases made.", 30.0m, "2014", new Guid("a5ee09b3-8cf1-4f2b-97c4-7b80977b7538") },
                    { new Guid("ff312969-713e-4fd6-b060-24be0e271936"), 3, new DateTime(2017, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montefalco Sagrantino", "This finely balanced Sagrantino layers creamy tannins with a pretty array of raspberry ganache, fig cake and dried sage flavors, accented by aromatic hints of rose petal and spice. Elegant and lightly juicy, with a subtle mineral streak that gains momentum on the finish. Drink now through 2027. 4,000 cases made.", 40.0m, "2011", new Guid("f7d1be8e-be80-40a0-9b28-5bceae53b982") },
                    { new Guid("078e8b91-7492-4131-8b24-d34a0418119f"), 3, new DateTime(2017, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cornas Terres Brûlées", "Plush in feel, with layers of dark fig, blueberry and blackberry reduction leading the way, inlaid with light charcoal and bay leaf accents that emerge steadily through the finish. Ends with a tug of tar. Best from 2020 through 2035. 1,300 cases made.", 58.0m, "2015", new Guid("b8de89af-a61d-485a-8e97-b674a00d0018") },
                    { new Guid("05456c8c-02f0-42ae-931f-b9a9a0c0f20c"), 3, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Dundee Hills Latchkey Vineyard", "Silky and harmonious, with an elegantly precise structure and black cherry, fresh violet and spice notes that glide expressively toward the long finish. Drink now through 2022. 600 cases made.", 50.0m, "2015", new Guid("f476f3e6-353a-4bb1-93f2-dd3ec9e0026a") },
                    { new Guid("87456866-32cc-406c-8584-f7de32dd7f10"), 3, new DateTime(2017, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Ovello", "This pretty red offers delicate flavors and subtle power, evoking flowers, red berry, mineral, tobacco and tar on the nose and palate. Firmly structured, this remains harmonious and long on the mineral- and tobacco-tinged finish.  Best from 2020 through 2036. 500 cases made.", 62.0m, "2013", new Guid("63e7a33e-ed36-41ec-a22b-61befb6925b5") },
                    { new Guid("895986df-b22a-405a-860b-643b5ec80a06"), 3, new DateTime(2017, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Lisboa Colossal Reserva", "Plum and boysenberry notes weave together with dried herb and smoky mineral accents in this sleek red. Savory spice details chime in on the finish. Offers moderate, grippy tannins. Drink now through 2020. 55,000 cases made.", 25.0m, "2015", new Guid("89c3e925-8b21-4fbc-ada9-15968d40f216") },
                    { new Guid("569e0a35-f2f4-4e10-aacf-497c17b7dc42"), 6, new DateTime(2017, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Napa Valley Yount Mill Vineyard", "Clean, smooth and crisp, offering a lip-smacking mix of pear, melon and Key lime flavors, with plenty of intensity on the finish. Drink now. 4,900 cases made.", 19.0m, "2016", new Guid("81e36b07-6efc-4931-9a0c-f24306274e68") },
                    { new Guid("44fc02e2-9040-4f40-9f13-b09a6aeae078"), 3, new DateTime(2019, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Morgon Côte du Py", "A vivid minerally undertone marks the pure raspberry and ripe cherry flavors, accented by licorice and floral details that give way to white pepper and wild herb elements on the long, lightly tannic finish. Drink now through 2024. 3,000 cases made, 200 cases imported.", 28.0m, "2017", new Guid("8a59fff1-74fe-4f56-8db6-9d00e6fee02e") },
                    { new Guid("95dc6248-5990-409b-a94f-d726232a6143"), 3, new DateTime(2017, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Morgon La Chanaise", "This ripe, light- to medium-bodied red sports layers of pure blackberry, cassis and damson plum, edged with licorice, floral and apricot details. Tangy, mouthwatering acidity highlights the mineral and spice details on the long, lightly grippy finish. Drink now through 2020. 13,000 cases made.", 20.0m, "2015", new Guid("8a59fff1-74fe-4f56-8db6-9d00e6fee02e") },
                    { new Guid("a1b22d6a-6292-485f-bc43-94d04567c1b6"), 6, new DateTime(2018, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Willamette Valley Bunker Hill", "Sleek and seamlessly complex, featuring expressive floral lemongrass and steely mineral aromas and plush yet refined green apple and lemon flavors accented by spicy lees. Drink now through 2022. 692 cases made.", 50.0m, "2016", new Guid("0eeb8804-211d-497a-9086-999e9390df27") },
                    { new Guid("b365733a-2126-4385-8993-aef914142b5b"), 3, new DateTime(2017, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yarra Valley", "Plush and elegant, with raspberry, cherry and fresh sage notes that are harmonious, juicy and intense on the finish. Drink now through 2027. 1,900 cases imported.", 20.0m, "2016", new Guid("f0ed18ce-6770-4b96-b9c5-ce3c01fc3eb7") },
                    { new Guid("72d793f5-70f8-4a72-8b26-40975d53f1a4"), 3, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mencía Bierzo Ultreia St.-Jacques", "Smoke, forest floor and mineral notes mingle in this savory red. Lively acidity and light, firm tannins give grip to the polished texture. Cherry and floral accents sing through the finish. Energetic. Drink now through 2023. 1,200 cases imported.", 20.0m, "2015", new Guid("07555456-3f6c-4d2c-b0be-656848ee1abd") },
                    { new Guid("ae4f7257-e7ce-4f96-88d5-442248522799"), 3, new DateTime(2017, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Coonawarra Phoenix", "Dense tannins and flavors are packed into this wine, with briny Kalamata olive and black licorice nuances to the core of plum, cassis, sage, tobacco and cedar flavors, which fan out on the finish. Drink now through 2030. 4,300 cases imported.", 20.0m, "2015", new Guid("3ad529f0-4830-4057-981a-a644628e397a") },
                    { new Guid("7e350803-06b0-46aa-a862-5129f5c97c77"), 3, new DateTime(2017, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Ultra Las Rocas", "Dense and rich-tasting, with a muscular mix of dark currant and black olive flavors that are supported by firm tannins. Cocoa powder and slate notes fill the finish, along with peppery hints. Drink now through 2022. 10,000 cases made.", 20.0m, "2015", new Guid("9a88f759-601d-447b-ab51-006e4cbbd3f6") },
                    { new Guid("2c99954b-9ffe-4bd6-bcac-1f8bf4170ca6"), 3, new DateTime(2017, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tannat Uruguay Reserva", "Well-sculpted, with dense, rich flavors of dark plum, dried blackberry and dark currant that are supported by firm tannins. Christmas pudding and rich savory notes show on the finish. Drink now through 2022. 12,000 cases made.", 17.0m, "2015", new Guid("945e3368-2915-4c83-a94b-224e82b1cf73") },
                    { new Guid("103ffc52-a06d-4961-acbf-a42cbb8f24ad"), 3, new DateTime(2017, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Dogliani", "A beam of bracing acidity at the core of this intense, saturated red helps delineate the blackberry, boysenberry, blueberry and graphite flavors. This has grip and complexity, but just needs a little time. Best from 2018 through 2024. 9,000 cases made.", 16.0m, "2015", new Guid("14de06c6-c705-4b60-8949-491d4e426366") },
                    { new Guid("c3777b7e-201a-4344-b9c7-baac268bc553"), 3, new DateTime(2017, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Ball Buster Barossa Valley", "Bold, with dense notes of dark chocolate-covered coffee bean, plum paste, wild blackberry, cigar box and date bread. Expressive and generous. The velvety tannins linger. Shiraz, Cabernet Sauvignon and Merlot. Drink now through 2027. 8,500 cases made.", 23.0m, "2014", new Guid("c94f369c-c628-487b-8446-3728ad87abfa") },
                    { new Guid("ca5cc678-a6f3-4d86-92f5-82c502337bca"), 6, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Albariño Rias Baixas", "This big white is round and polished, with good weight and solid acidity behind the pear, blanched almond and white tea flavors. Ginger and briny notes give this a savory character. A muscular style. Drink now through 2022. 3,000 cases imported.", 21.0m, "2016", new Guid("ecd58c0d-0d2b-4dc0-b8d5-1e1c0ab92aa5") },
                    { new Guid("67a48be1-9202-4e4b-b815-900c96c9980b"), 3, new DateTime(2017, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Agrelo Remolinos Vineyard", "An elegant style, with a core of vibrant cherry, plum and currant flavors that are supported by fresh acidity. This is supple midpalate, with a finish that lingers with dried ginger and white pepper details. Drink now through 2022. 18,000 cases imported.", 22.0m, "2015", new Guid("66f94155-89aa-4594-b513-cf0608828ae6") },
                    { new Guid("246ccbc4-9dc1-4f00-a9ae-55e8599df64f"), 6, new DateTime(2017, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Clare Valley The Lodge Hill Dry", "A taut and linear white, with a lip-smacking blend of peach, lime and green apple flavors, finishing on a refreshing note. Drink now. 3,000 cases imported.", 20.0m, "2016", new Guid("788b3564-5fdb-4c10-9159-aecc4e836f51") },
                    { new Guid("374405b2-39c7-4195-8ac3-3c6a5775df8f"), 6, new DateTime(2017, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc High Valley", "Focused and precise, with detailed lemon meringue, orange blossom and lime zest flavors that are intense, fragrant and harmonious on the long, crisp finish. Drink now. 2,198 cases made.", 20.0m, "2015", new Guid("2840c245-c88d-42e9-99b3-54463ade2ec8") },
                    { new Guid("1cf24483-0bc4-43e1-a9e1-61989951eb1a"), 3, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Rhône-Villages Séguret Tradition", "A good bolt of graphite drives underneath while lightly mulled plum and blackberry fruit form the core. Singed mesquite and tobacco fill in on the finish. Grenache, Syrah and Carignan. Drink now through 2020. 3,500 cases made.", 20.0m, "2014", new Guid("b2d189f0-42bf-4d1f-a555-87042231527a") },
                    { new Guid("ffb3e9b6-6ad5-4f9f-af7d-61c74ad52106"), 3, new DateTime(2017, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Corse", "Lightly rustic, with bramble and dried anise notes weaving throughout. The core of violet plum sauce and raspberry coulis is pure and vibrant, extending through a lengthy finish. A fun, character-filled wine. Niellucciu, Syrah and Grenache. Drink now through 2019. 6,500 cases made.", 20.0m, "2015", new Guid("668ba648-a83c-49a9-b6b1-2c81e2a6d530") },
                    { new Guid("cd7c08d8-3416-4c01-8ad8-386d455af21a"), 3, new DateTime(2017, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riverside Barossa", "A supple, seamless mix of cherry and raspberry flavors, with dried violet, savory and smoky details, accented by hints of sarsaparilla. Polished and refined, especially on the effortless finish. Grenache, Mataro and Shiraz. Drink now through 2030. 2,000 cases made.", 20.0m, "2015", new Guid("907d5cbb-c0df-4fc0-861d-e800b98297bf") },
                    { new Guid("7f840479-7013-4539-a298-53cb3f061270"), 3, new DateTime(2017, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valpolicella Classico Superiore San Michele Ripasso", "This mouthwatering red is medium-bodied and expressive, offering lightly chewy tannins and flavors of sun-dried cherry, strawberry preserves and star anise, with pleasing hints of herb and mineral on the finish. Drink now through 2022. 5,000 cases made.", 28.0m, "2015", new Guid("a6b7a33e-bee4-40de-be94-6603c4e651a3") },
                    { new Guid("4b45ae96-d0cf-4cc5-baab-13b6f517083a"), 6, new DateTime(2018, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Île de Beauté White", "A bright and distinctive white, offering lively lemon zest, honeysuckle, quinine and tangerine notes mixed together, with good zip. Drink now. 22,000 cases made.", 15.0m, "2017", new Guid("47b7575a-fe26-4bab-b19c-130c03227b13") },
                    { new Guid("553212b5-69f1-42de-bec1-06ccd7533bd6"), 6, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mâcon-La Roche Vineuse", "Complex, exhibiting saline and mineral elements, along with white peach, apple and spice flavors. Beautifully balanced and focused, remaining intense and long on the juicy finish. Drink now through 2024. 500 cases imported.", 25.0m, "2016", new Guid("1f48f985-3a45-493c-be0f-a76df1bcf967") },
                    { new Guid("3c9ae1f2-0509-489b-a917-b4a74950847e"), 3, new DateTime(2018, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Margaret River Filius", "Opens with a fresh green accent reminiscent of tomato leaf and sage, giving a savory, peppery note to the plush, velvety cassis and plum core. Finds balance between grace and power, with spice and herb elements lingering on the finish. Drink now through 2028. 800 cases imported.", 27.0m, "2015", new Guid("ad307684-d795-4a81-b4e2-34bb5db121a3") },
                    { new Guid("7ca86346-c668-45e2-8885-de05de81f1f4"), 3, new DateTime(2018, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Undicicomuni Arnaldo Rivera", "The sweet fruit and solid structure play off each other as this evolves. Starts with floral, cherry, strawberry and licorice flavors, melding into the firm, resonant tannins as the finish extends. Complex, balanced and set for a long life. Best from 2022 through 2036. 4,150 cases made.", 30.0m, "2013", new Guid("db436c70-be69-4fa6-b1f5-e941e9497d16") },
                    { new Guid("71e54434-805f-405a-99e3-8e1cd89ab899"), 6, new DateTime(2018, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Venezia-Giulia Slatnik S Label", "Like walking through a Turkish bazaar, this wafts spice and citrus notes that pull you into the glass, while vibrant, lightly zesty acidity and light tannins form a firm, well-knit frame for the spiced range of glazed apricot, kumquat, spun honey, rose and mint flavors. Long and intriguing. Chardonnay and Friulano. Drink now through 2030. 400 cases made.", 35.0m, "2015", new Guid("3f881ba1-979d-4147-9506-3f1232751d13") },
                    { new Guid("87a38043-0011-4872-8d7a-52e4ff650581"), 6, new DateTime(2018, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Garnatxa Blanca Terra Alta Via Terra", "This white has a thick texture, delivering pear, guava, tarragon and blanched almond flavors. Firm acidity and a touch of tannins give this a solid frame. Muscular, but remains balanced. Drink now through 2023. 5,000 cases made.", 15.0m, "2017", new Guid("922dbda9-57b2-452f-b668-24db6b745ba4") },
                    { new Guid("c3068545-9af8-4f1f-8100-679732f0d9be"), 3, new DateTime(2018, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Amador County", "Epitomizes fine Sierra Foothills Zinfandel, offering vibrant, briary raspberry, smoked pepper and wild sweet anise flavors that stretch out on the zesty finish. Drink now through 2023. 6,200 cases made.", 22.0m, "2015", new Guid("e3f90f6c-784b-43b5-9060-2fd5ca9af926") },
                    { new Guid("e51304aa-319e-45de-8ac8-b6856013a29c"), 6, new DateTime(2018, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Qualitätswein Mosel From Old Vines", "A pure, well-sculpted, off-dry version, with persimmon, lime and slate flavors coating the palate. Hints of orchard blossom and herbs add to the complexity, making you reach for another sip. Drink now through 2024. 7,000 cases made.", 19.0m, "2016", new Guid("176663d4-6a6e-46c7-9e86-077e6630343c") },
                    { new Guid("95dd3f94-c3ce-48be-9b79-4731b97e2dfa"), 3, new DateTime(2018, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toro", "A velvety texture carries ripe fruit flavors of plum and blackberry in this plush red. Cocoa, loamy earth and mineral notes add depth and interest. Ripe tannins are well-integrated, and balsamic acidity keeps this lively. Drink now through 2029. 8,000 cases made.", 67.0m, "2014", new Guid("b6b79198-6f2a-460b-b7bd-7c3b17e93b56") },
                    { new Guid("a5463d34-4884-45fd-9783-1e59fae3fc64"), 3, new DateTime(2018, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Garnacha Aragón Old Vines", "Round, smooth and dense, this red shows harmony and balance. Plum and blackberry flavors mingle with black olive, toast and mineral notes, supported by well-integrated tannins and fresh acidity, leaving a lovely floral finish. Drink now through 2027. 20,000 cases made.", 16.0m, "2015", new Guid("2a89a941-4668-48d8-9084-57621ad816d1") },
                    { new Guid("8e8e2aee-64cd-4b6d-9f60-b84181d6dff4"), 3, new DateTime(2018, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Milla Cala Cachapoal Valley", "Rich and refined, delivering unctuous dark fruit and spice flavors that offer a creamy svelteness. Slate and mineral accents linger on the complex finish, with dark chocolate and olive notes. Cabernet Sauvignon, Carmenère, Cabernet Franc, Syrah and Merlot. Drink now through 2022. 4,000 cases made.", 39.0m, "2012", new Guid("4a1ac596-8cfc-4e4d-ace1-a4421607b16e") },
                    { new Guid("f8cc3d9c-b8d2-414e-a07f-87ea81015d56"), 3, new DateTime(2018, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Heathcote Georgia's Paddock", "Fragrant notes of dried rose petal and white truffle indicate something special. Inside, this offers a fleshy, velvety mix of toasted rye bread, spicy plum jam and framboise flavors. Elegant and plush, but with plenty of power within, flexing on the expressive, expansive finish that goes on and on. Drink now through 2035. 1,500 cases made.", 85.0m, "2015", new Guid("48fad980-90b5-476e-a44e-d46f07dbac80") },
                    { new Guid("0480d861-4b1c-4220-b6ab-e3e6192745ea"), 3, new DateTime(2017, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Calabria GB", "Finely balanced and creamy, this bold red offers a rich range of black cherry coulis, fig cake, dried marjoram and oak spice flavors, layered with sculpted tannins. Long on the palate, with a lasting spicy mineral note. Gaglioppo, Magliocco Canino, Nerello Cappuccio and Greco Nero. Drink now through 2027. 3,500 cases made.", 45.0m, "2014", new Guid("d7aade20-1488-4ca9-af2a-409758bc6b7f") },
                    { new Guid("3f87ef0a-1aad-4a93-b6ea-87fb91831f88"), 3, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nero d'Avola Terre Siciliane Versace", "A well-focused, rich red in a balanced, medium-bodied frame, this offers a subtle underpinning of tarry mineral notes layered over light, sculpted tannins and flavors of mulberry and dried fig, with hints of herbes de Provence and mocha. Drink now through 2025. 1,166 cases made.", 33.0m, "2015", new Guid("42b891f5-b28f-4d9b-b0ea-7add454c71f2") },
                    { new Guid("55de77b8-b8c5-4d48-b773-29fa58f2c7c5"), 6, new DateTime(2017, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Greco di Tufo G", "Tarragon and thyme notes lace flavors of green melon and lime granita in this juicy, light- to medium-bodied white. Fresh, balanced and minerally, with a spiced finish. Drink now through 2020. 5,500 cases made.", 27.0m, "2016", new Guid("dff66133-6bfb-4381-b48a-58b7af558f86") },
                    { new Guid("6c16911f-61dd-4507-b6cd-cd1b5bfb0a4a"), 6, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Verdicchio dei Castelli di Jesi Podium", "An elegant, creamy white, backed by sleek, citrusy acidity, this shows a subtle mix of poached pear, grated ginger and stony mineral flavors, accented by hints of crushed almond. Drink now through 2020. 3,500 cases made.", 25.0m, "2014", new Guid("c9d00548-1af3-4e1b-84f0-9d137eae7826") },
                    { new Guid("15cfa8b0-2299-4ca4-883e-a9140705b181"), 3, new DateTime(2017, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Adelaida District Estate", "Combines rich, deep flavors with a measure of finesse and refinement, allowing the earth-laced red berry and spicy herb notes to shine. Ripe, sturdy tannins lend this structure and traction. Best from 2020 through 2034. 1,000 cases made.", 85.0m, "2014", new Guid("d639197d-85ab-4d14-b772-8567f06dae81") },
                    { new Guid("d3a86e8a-10b3-4707-a701-9d3dd964e6f1"), 5, new DateTime(2017, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Cuvée Ste.-Anne", "A bright, finely meshed version that shows beautiful harmony throughout, offering flavors of Gala apple, spring forest, almond pastry and lemon curd riding the creamy mousse. Lasting, spiced finish. Disgorged February 2016. Drink now through 2022. 1,300 cases imported.", 50.0m, "NV", new Guid("bd8c69c5-a095-4d8d-b42f-6f72c578a650") },
                    { new Guid("f89baab8-8d42-4f2d-a725-2cdf5e473811"), 5, new DateTime(2017, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Nature Vino Espumoso de la Finca", "A minerally version, with up-front petrol and spice notes lacing the poached cherry, salted almond and lemon peel flavors that ride the delicate mousse. Elegant and harmonious. Disgorged February 2017. Drink now through 2020. 4,100 cases made.", 33.0m, "2013", new Guid("168975be-124d-462d-8ac6-fa36619142b0") },
                    { new Guid("d4f9823c-5c37-4551-9937-af31dad07e85"), 6, new DateTime(2017, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vernaccia di San Gimignano Tenuta Le Calcinaie", "A bright, juicy version, boasting floral, peach, grapefruit and honey aromas and flavors. Pure and elegant, finishing with a characteristic hint of bitterness. Drink now. 2,350 cases made.", 18.0m, "2015", new Guid("2ed0436e-6bb5-4592-9b55-9eabb09dbb99") },
                    { new Guid("a23852eb-9ebc-418c-950a-3053d827ea59"), 3, new DateTime(2019, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges", "A touch of gaminess in the aroma gives way to blackberry, black cherry, earth and iron flavors in this rich, solidly structured red. Vibrant and balanced, though this needs a little time to integrate. Terrific length. Best from 2023 through 2042. 1,083 cases made, 150 cases imported.", 68.0m, "2016", new Guid("c72ccdb2-f518-4bd9-9ff1-e6ecc716192d") },
                    { new Guid("77fe4136-c872-45d4-bdd3-99c710e5fca7"), 3, new DateTime(2017, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mercurey Clos des Myglands", "Ripe and pure, featuring cherry, blackberry, raspberry, mineral and light toast notes. Balanced and intense, playing out on the long, mouthwatering finish. Best from 2019 through 2032. 1,400 cases imported.", 53.0m, "2015", new Guid("c72ccdb2-f518-4bd9-9ff1-e6ecc716192d") },
                    { new Guid("6ca0860d-f0da-4c89-b0c9-b0a7ada44763"), 3, new DateTime(2017, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Dutton Ranch", "Well-centered on a mix of cherry, plum and raspberry flavors, elegant and refined, with sufficient tannins to hold attention. Ends with a clean, mouthwatering aftertaste that echoes the fruit and spice notes. Drink now through 2024. 5,880 cases made.", 44.0m, "2015", new Guid("9ed3713b-3dc6-47fb-9593-5ff16e80ff0a") },
                    { new Guid("44548710-e1bb-4287-8e31-421168ae7f39"), 6, new DateTime(2017, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Manzanilla Sanlúcar de Barrameda La Guita", "Distinctive, with verbena and tarragon notes leading the way, followed by lemon pith and jicama flavors. Flashes of gooseberry and talc run through the finish. Bottled June 2016. Drink now. 200,000 cases made.", 15.0m, "NV", new Guid("aa92d2be-47f1-443a-92c8-0dd4dc0cf552") },
                    { new Guid("ace5a87f-512b-4630-91c6-e4263bb07b67"), 3, new DateTime(2014, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viño de la Tierra de Castilla y León Sardon de Duero Selección Especial", "This red shows a smooth texture that masks the firm structure, with well-integrated tannins and bright acidity supporting focused flavors of cherry, berry, mineral and loam. Balanced, graceful and deep. Tempranillo, Syrah, and Cabernet Sauvignon. Drink now through 2025. 37,000 cases made.", 35.0m, "2010", new Guid("7b628c54-e1f1-43ee-8c8c-15ce7e0148b4") },
                    { new Guid("ccd543a1-0cf9-4858-be34-6d750710bc0a"), 3, new DateTime(2014, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Zolo Reserve", "A deeply colored and robust red, with a core of lusciously spiced dark plum, blackberry and dark currant flavors that are long and pure. Medium-grained tannins and firm acidity lend this a muscular structure. The rich finish is filled with dark chocolate and Asian spice notes. Drink now through 2020. 4,000 cases made.", 19.0m, "2012", new Guid("55bdafe2-2318-45fe-8532-1d031e8efff0") },
                    { new Guid("5a74c4b6-0de2-4f98-918f-92a3c0b74910"), 3, new DateTime(2014, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Patriota Mendoza", "A rich red, packed with concentrated flavors of blackberry, dark plum and mocha, featuring plenty of dark chocolate notes. Offers a ripe texture, with creamy accents and hints of savory herb on the long finish. Drink now through 2020. 12,500 cases imported.", 23.0m, "2012", new Guid("3167480e-36ba-4bdc-a042-8d6350078b49") },
                    { new Guid("4c930ca9-a4d5-4921-a349-590051239403"), 3, new DateTime(2010, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah-Petit Verdot Dominio de Valdepusa Caliza", "Licorice, iron and black pepper notes accent a core of plum and currant fruit in this firm, focused red, which has muscular tannins and plenty of oak. Best from 2011 through 2018. 6,800 cases made.", 16.0m, "2006", new Guid("3679c6fb-8bb6-4b17-b659-e7570d209056") },
                    { new Guid("dc628985-be86-4fe9-b6c1-ded3563dbcb4"), 3, new DateTime(2010, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Viña Fausto Orellana de Escobar", "Ripe and vivid, with delicious boysenberry, fig, pastis and raspberry ganache notes carried by a long, graphite-filled finish. Fruit-driven, but racy and long, with impressive precision to the ample fruit. Great mouthfeel. Drink now through 2012. 1,470 cases made.", 48.0m, "2007", new Guid("291cf103-ea08-403e-a2de-c96077014c1b") },
                    { new Guid("7a2de5a8-fa8e-4674-a24c-7a62ece74117"), 6, new DateTime(2010, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rias Baixas", "Firm, even chiseled, this flinty white delivers focused mineral, almond, pear and kumquat flavors. There's enough weight for food, but a light spritz keeps this lively. Albariño. Drink now through 2011. 5,000 cases imported.", 18.0m, "2008", new Guid("bd70d315-a708-4e8f-a2de-81290119b4d0") },
                    { new Guid("7ef7e540-3eab-46dd-a4a8-85b2ad3277b3"), 6, new DateTime(2019, 10, 24, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Oregon", "Vibrant, with plush fruit, offering medium-weight lemon and quince flavors that take on expression and polish on the finish. Drink now through 2020. 38,270 cases made.", 15.0m, "2018", new Guid("8082c94b-2827-44e3-a045-01238079b301") },
                    { new Guid("3d89d69d-6a05-4ed1-9d14-7e5bcd437cfd"), 6, new DateTime(2010, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Federspiel Trocken Wachau Loibner", "There's lots of vibrant acidity to the peach, gooseberry and grapefruit flavors, with hints of white chocolate midpalate. The powerful, crisp and mineral-laden finish features some smoky notes. Drink now through 2017. 2,400 cases imported.", 24.0m, "2008", new Guid("fa24463e-70c4-40ad-b00f-5525db700fa3") },
                    { new Guid("1e863566-7f69-4eb4-a1be-c7f1353066a4"), 6, new DateTime(2012, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Rheingau", "Very balanced and lively, with well-defined flavors of red peach, melon, baked apple and spice. Well-concentrated, with a long, luscious, cream- and vanilla-filled finish. Mouthwatering. Drink now through 2040. 2,000 cases made.", 30.0m, "2010", new Guid("03d1cb98-0353-4ed2-988a-730357c27781") },
                    { new Guid("ca4853f0-9c75-467f-8fe4-79cd5e9d7c43"), 6, new DateTime(2010, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Albariño Rias Baixas Lagar", "This shows impressive power for the region, with a waxy texture, a full body and expressive vanilla and leesy notes that support pear and grapefruit flavors. A distinctive wine. Drink now through 2012. 15,000 cases made.", 13.0m, "2009", new Guid("3f2b378b-ad76-4603-843f-e9e0baff541b") },
                    { new Guid("1e4c1eda-a3f1-41d7-83c1-a2b89d3e4d35"), 3, new DateTime(2010, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley The Watcher", "Fresh and vibrant, this is medium-weight and juicily focused to show its blackberry, black plum and white pepper flavors, lingering with hints of white chocolate on the harmonious finish. Drink now through 2018. 8,000 cases made.", 22.0m, "2008", new Guid("f62dc669-b309-4e88-849e-c6c2ef3b60b0") },
                    { new Guid("0081deaf-0c9b-4df9-a0c1-42704f2c962f"), 5, new DateTime(2010, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Réserve", "This vinous bubbly combines elements of freshness and maturity. Toast, mineral, mushroom, citrus and spice aromas and flavors are enticing and complex, and there's a nice airy texture, yet no shortage of intensity and grip. The long aftertaste features toast and mineral. Drink now through 2013. 4,600 cases made.", 52.0m, "NV", new Guid("f363f990-2ec7-4833-aa76-969fbff3e045") },
                    { new Guid("51ebee31-3d56-482d-97ab-9979feeb1f11"), 3, new DateTime(2010, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vireo Columbia Valley", "Firm, rich and distinctive for the black olive and roasted meat overtones that swirl around the red berry and cherry flavors, persisting impressively on the long, expressive finish. Stylish and focused, this has miles to go. Cabernet Sauvignon, Syrah and Merlot. Drink now through 2016. 900 cases made.", 25.0m, "2006", new Guid("1221f746-f57e-40a6-966a-ccadf0894f71") },
                    { new Guid("64bf6bfc-78e1-45e2-a324-c3a3a2c8b258"), 6, new DateTime(2010, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Santorini", "This rich, refined and full-flavored white has fine cut to its apple, grapefruit and peach flavors, with lively acidity and plenty of creamy notes, which extend with golden raisin and tropical fruits on the powerful finish. Drink now through 2016. 4,000 cases made.", 19.0m, "2009", new Guid("7e803d68-3efd-4059-88b6-240fe5672e2e") },
                    { new Guid("abde4009-4be0-4cfb-914c-ca2ebd76f980"), 3, new DateTime(2010, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vacqueyras Cuvée G.", "Smoky, with alluring freshly drawn espresso and bittersweet chocolate notes leading the way for dark, velvety crushed plum, hoisin sauce and maduro tobacco. The long, muscular finish is nicely integrated, with a lingering mineral note. Drink now through 2012. 6,000 cases made.", 24.0m, "2007", new Guid("1832a7fa-586f-409f-ac6d-4982ce3f3595") },
                    { new Guid("1ebd9fb3-d436-4b79-a825-2965abb1ef57"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Bedrock Heritage Sonoma Valley", "A knockout wine that captures a genuine sense of place and vintage. Rose petal, wild berry and Asian spice aromas open to layered, distinctive flavors of huckleberry pie, licorice and spicy mineral. The tannins are big but ripe, and there's plenty of lively acidity behind it all to keep this rolling. A blend of Zinfandel, Carignane and 21 other field-blended varieties. Best from 2015 through 2022. 500 cases made.", 37.0m, "2011", new Guid("809a1404-9fd0-479f-a532-c41d456859c1") },
                    { new Guid("8e6220ac-7b69-4c47-b7b3-e56c1553e631"), 3, new DateTime(2010, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Bedrock Heirloom Sonoma Valley", "An incredible Zinfandel blend that offers depth, balance and a distinctive sense of place. Bold aromas of violet, wild berry and licorice lead to rich, ripe and layered flavors of huckleberry, fresh sage and brown baking spices. The finish goes on and on. Zinfandel, Carignane and Petite Sirah. Drink now through 2015. 200 cases made.", 35.0m, "2008", new Guid("809a1404-9fd0-479f-a532-c41d456859c1") },
                    { new Guid("0ceb7b7a-680e-4b0b-bb34-f97e17176e8e"), 3, new DateTime(2011, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valpolicella Superiore Ripassa", "There's a symphony of flavor in this medium-bodied red, which offers ripe boysenberry and damson plum fruit overlaid by lovely rose and violet floral notes, with details of loamy espresso, anise and fresh herbs. A finely cut wine, with a velvety mouthfeel coating lightly chewy tannins and vivacious acidity. Drink now through 2022. Tasted twice, with consistent notes. 13,700 cases imported.", 28.0m, "2007", new Guid("8a4397d4-5dda-41d8-9fe2-1c495937970d") },
                    { new Guid("ddecafbc-5266-44ae-bbb3-0a0584963e7d"), 6, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Carneros Hyde Vineyard", "Precise and powerfully structured, with intense Asian pear, apple and ripe citrus flavors that feature rich, savory notes. Displays concentrated minerality on the spicy and textured finish. Should blossom in the cellar. Best from 2021 through 2025. 1,256 cases made.", 65.0m, "2016", new Guid("ccd29551-2673-4bb8-a238-4e9077218d06") },
                    { new Guid("e2468740-3f61-4da7-a5ec-20db19a965bb"), 6, new DateTime(2016, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Ritchie Vineyard", "Shows amazing vibrancy and purity of flavor, brimming with juicy, lemon-scented green apple, white peach and tangerine notes, enlivened by zesty acidity and a long, persistent aftertaste that maintains focus and depth. Drink now through 2020. 1,250 cases made.", 65.0m, "2013", new Guid("ccd29551-2673-4bb8-a238-4e9077218d06") },
                    { new Guid("d4be6f5e-a563-455e-9654-260bd52eabed"), 3, new DateTime(2010, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Annum", "A bold, rich, expansive style, offering tiers of opulent blackberry and wild berry flavors, with touches of black and red licorice and a notable rustic hot brick wall earthiness, all of it dense, focused, complex and concentrated. Ends with a long, persistent finish. Drink now through 2020. 1,700 cases made.", 85.0m, "2007", new Guid("ccd29551-2673-4bb8-a238-4e9077218d06") },
                    { new Guid("60fd8af4-5c80-4800-a0c0-72a48b2919f8"), 3, new DateTime(2011, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero de Restia Crianza Selected Harvest", "This maturing red is beautifully balanced, with fresh, ripe fruit flavors of black cherry and blackberry, and deeper notes of cedar, tobacco, mineral and spice. The firm tannins are softening, yet this remains fresh and focused through the long finish. Tempranillo. Drink now through 2015. 5,000 cases made.", 15.0m, "2004", new Guid("9bc7cc98-889a-4158-b9dd-6ab9e5e3220e") },
                    { new Guid("052a9fe9-c5b1-48fb-a4d3-d11ba21197da"), 3, new DateTime(2010, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Modus", "This red is phenomenal on the nose, with blueberry, light toasty oak, coffee and sweet tobacco. The palate is full and utterly captivating, with its combination of fruit and very subtle, silky tannins. Goes on and on. Best after 2014. 7,000 cases imported.", 35.0m, "2007", new Guid("0b2ee059-b69d-4bef-8879-6f2365ed406e") },
                    { new Guid("a93ea0fa-c02f-4b71-bf07-1aa44077569a"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Oakville Materium Weitz Vineyard", "A riveting wine, notably rich and sophisticated, with layers of plush yet firm mocha, blackberry, plum and wild berry flavors that are full-blown. A dash of lavender and mineral, with floral scents, give this great depth, persistence and range of flavor, followed by a grand finish. Best from 2012 through 2024. 460 cases made.", 125.0m, "2007", new Guid("853df5ad-e932-40ab-b29e-1e634bd1cb0c") },
                    { new Guid("cf28cae6-52bb-497e-a1b4-7c9f870cd9b9"), 3, new DateTime(2010, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Headwaters Napa Valley", "Firm, rich and vibrant, this is intense, focused, concentrated and persistent, with a taut band of dried currant, mineral, plum and spice, gaining depth and complexity while maintaining a presence of balance and finesse. Cabernet Sauvignon, Merlot and Petit Verdot. Drink now through 2018. 1,667 cases made.", 40.0m, "2007", new Guid("a783ae42-5e15-49cc-bec2-61a014b68956") },
                    { new Guid("3458cde7-090c-46c9-8f4d-818d453c7e02"), 3, new DateTime(2017, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel California Old Vine", "Rich and supple, this is an explosion of pure, decadent fruit. Floral huckleberry and Chinese five-spice powder aromas open to layered blackberry, dark chocolate and licorice flavors that linger. Drink now through 2026. 4,500 cases made.", 25.0m, "2015", new Guid("809a1404-9fd0-479f-a532-c41d456859c1") },
                    { new Guid("8c2211ff-116e-49d6-ae11-366317f6a5f0"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon St. Helena", "Aromas of roasted herb, red pepper and sage are complex, elegant and spicy, joined by ripe currant, plum and black cherry fruit, but this is at its best on the full-bodied palate, where the texture is supple, graceful and polished. Drink now through 2020. 2,000 cases made.", 125.0m, "2007", new Guid("2b9b8baf-b656-4eb6-8ee9-9cca89ab4015") },
                    { new Guid("fa42d367-30e7-4e1e-8456-08ef67af8390"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Casablanca Valley Ritual", "This is mouthfilling, but stylish and focused, offering lush cherry and raspberry fruit that's layered with additional spice and sweet tobacco notes. Nicely integrated toast frames the finish. Drink now. 9,000 cases made.", 20.0m, "2008", new Guid("db02ea9b-ba35-411a-a970-d2aa4399ec13") },
                    { new Guid("7fc5eac6-b539-4258-8764-283e39db8094"), 3, new DateTime(2014, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Colchagua Valley El Caballero", "Firm and well-focused, with zesty red plum and wild berry flavors, supported by crisp acidity and chewy tannins. Green herb and spice notes appear on the finish. Drink now through 2018. 10,000 cases imported.", 12.0m, "2011", new Guid("db02ea9b-ba35-411a-a970-d2aa4399ec13") },
                    { new Guid("c2d7bab3-46be-4db0-bf6d-39f8d8d45e3a"), 3, new DateTime(2011, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Selección de Familia Crianza", "Velvety and voluptuous, this generous red delivers plum, blackberry, chocolate and toast flavors, with broad, soft tannins and just enough acidity to stay lively. The finish is graceful. Drink now through 2016. 55,000 cases made.", 18.0m, "2008", new Guid("5ac81f66-409c-4b68-92dd-6f8001734416") },
                    { new Guid("1f1b0481-f694-44a3-8f83-7cd10b564bfb"), 3, new DateTime(2011, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Roussillon-Villages", "This ripe, sauve red, delivers a full flush of luscious red fruit flavors and silky spicy notes. Broad midpalate, with a powerful, chocolate-filled finish that features notes of toffee and hints of cream. Grenache, Mourvèdre, Syrah and Carignan. Drink now through 2018. 4,500 cases made.", 22.0m, "2008", new Guid("4eb6f914-3815-44eb-bee6-8907f617e791") },
                    { new Guid("c8920236-3502-475f-8aad-da11b54ab96d"), 5, new DateTime(2011, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Blanc de Noirs New Mexico", "Elegant and focused, with creamy vanilla and apple aromas and rich yet crisp flavors of baked pear and cinnamon bread. Drink now through 2014. 20,000 cases made.", 14.0m, "NV", new Guid("3636ba1d-c9ae-4e25-9e26-6a0d3cb60d5d") },
                    { new Guid("a9fd8c6f-7bb3-4f78-aa5d-57f7641d480a"), 3, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Dão", "A well-structured, muscular red, with a touch of Asian spice to the wild plum and dried raspberry flavors. Beef and mineral notes mark the finish. Distinctive, with a very <i>sauvage</i> allure. Drink now through 2015. 10,000 cases imported.", 9.0m, "2008", new Guid("00ce9001-a3ba-47d2-87cc-33a435b9bda3") },
                    { new Guid("fb29861c-79b4-4ba4-9e3a-2ac1ab05f6f6"), 3, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Offering Santa Barbara County", "Fresh, floral, grapey wild berry and blackberry flavors are snappy, intense and vibrant, yet also delicate and firm, with a minerally edge. Lively, vivid and detailed, this ends with a mixed thread of berry notes. Grenache, Syrah, Mourvèdre and Viognier. Drink now through 2020. 960 cases made.", 25.0m, "2008", new Guid("065948ee-78f0-44ed-b7dd-a25f312ef691") },
                    { new Guid("7c0dbc92-a93f-419f-9046-8c9fabd3c729"), 3, new DateTime(2011, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Grand Barossa", "Dense and juicy, opening out to a lively texture under the cherry, fruitcake and spice flavors. Offers ripeness and intensity, without a lot of weight. Drink now through 2018. 5,500 cases made.", 18.0m, "2008", new Guid("0949d4c1-d121-45fd-b5df-2e79dc2f6ff1") },
                    { new Guid("d806cc66-05e2-4ca1-b15b-21b572bdbb04"), 3, new DateTime(2011, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yamhill-Carlton District Resonance Vineyard", "Plush, deep and complex, yet graceful, offering a mouthful of mineral-accented cherry, raspberry, green tea and spice flavors that sail smoothly and serenely over refined tannins. This has richness with moderate weight. Drink now through 2019. 900 cases made.", 42.0m, "2009", new Guid("847733c2-4898-4c07-95fb-cc0187bf751f") },
                    { new Guid("51631041-26ec-45e5-a151-db99f636d4b2"), 3, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Santa Barbara County", "Complex and layered, offering a firm core of dried berry, mineral, cedar and sage, with dashes of pepper and spice. Full-bodied, sleek and focused, ending with a touch of creamy oak. Drink now through 2020. 1,148 cases made.", 25.0m, "2008", new Guid("745e46c3-c854-479a-ad82-bcc96ac5d5cc") },
                    { new Guid("551b2c54-308f-4308-ace0-347b37bcac7d"), 3, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino La Togata", "Very pure aromas and flavors of raspberry and strawberry mark this juicy, elegant Brunello. The acidity keeps this focused and taut, while the vibrant tannins will give it longevity. Shows terrific balance and harmony. Best from 2013 through 2026. 4,000 cases made.", 60.0m, "2006", new Guid("ae3066e0-3b03-4561-8e5c-4337db813fde") },
                    { new Guid("86014d3f-911d-4161-9499-8be1a0b7a366"), 3, new DateTime(2011, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Serralunga", "Like its macerated black cherry and plum flavors, this is both sweet and intense, taking on a bittersweet chocolate richness as it powers its way to a long aftertaste. The ripeness is supported by a dense core of tannins. Best from 2014 through 2035. Tasted twice, with consistent notes. 1,600 cases made.", 50.0m, "2006", new Guid("8bbd112a-745d-4b40-b0d4-aac532020f5d") },
                    { new Guid("a6c1f1a9-9613-4a70-8222-f955ce4c9716"), 3, new DateTime(2012, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Primus The Blend Colchagua Valley", "Dark crushed cherries, cassis and graphite notes are supported by a full-bodied frame of fine tannins and fresh acidity, as the compact finish unwinds with hints of cured olive, grilled herb and spice. Cabernet Sauvignon, Syrah, Carmenère and Merlot. Drink now through 2017. 16,000 cases made.", 20.0m, "2009", new Guid("db02ea9b-ba35-411a-a970-d2aa4399ec13") },
                    { new Guid("74ed5472-5d8d-49f4-80f5-4e64a42b3867"), 6, new DateTime(2013, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Coast", "Pure, clean scents of ripe apple, melon, pear and subtle citrus form the core of this bright, elegant, delicate version. Subtle oak comes through on the finish. Drink now through 2019. 900 cases made.", 34.0m, "2011", new Guid("e95b9a56-3df1-4700-bdf6-8592aa0e6811") },
                    { new Guid("5a858907-30d6-4a28-ab1d-f37d1fddf7ef"), 3, new DateTime(2019, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Dirt Worshipper Central Coast", "Offers an impressive combination of bold, concentrated fruit with a polished, refined structure, delivering black plum, licorice and smoky pepper flavors, accented by crushed stone and fresh earth details. The tannins are broad but harmonious. Syrah, Roussanne and Viognier. Drink now through 2027. 1,123 cases made.", 75.0m, "2016", new Guid("2a099a34-4ed9-488e-a504-c464bc8f3fc9") },
                    { new Guid("54c1bb95-1f5e-4cff-8eeb-01f22fa69ff8"), 3, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "The Dirt Worshipper Paso Robles", "A brilliant wine, showing focus, intensity and purity, with an amazing array of flavors that are supple, layered, deep and persistent, built around ripe boysenberry, blackberry, wild berry and huckleberry. Ends with a wonderful lift. Syrah and Viognier. Drink now through 2022. 810 cases made.", 45.0m, "2008", new Guid("2a099a34-4ed9-488e-a504-c464bc8f3fc9") },
                    { new Guid("04b085a7-d615-4a26-a6c5-8249c1549085"), 3, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ursa Columbia Valley", "This has great presence, offering a dense cloud of blackberry, cherry and spice flavors that hover, almost weightless, over a sleek frame of fine tannins, with just enough acidity to keep it fresh. The finish sails on beautifully. Merlot, Cabernet Franc, Cabernet Sauvignon, Malbec and Petit Verdot. Best from 2012 through 2020. 1,095 cases made.", 35.0m, "2008", new Guid("deeb329b-933f-4cd3-b371-85260442d449") },
                    { new Guid("edb41467-6af3-467b-812a-6fc169794c03"), 6, new DateTime(2010, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Finger Lakes Dry Reserve", "Though still compact now, this is really juicy and ripe, with a great core of yellow apple, melon rind, jasmine and white peach notes backed by a bracing edge. The long, quinine-tinged finish needs a little time to settle in. Drink now through 2013. 400 cases made.", 23.0m, "2008", new Guid("52d7b9a9-f30e-4710-80ac-7568ce4a4022") },
                    { new Guid("e6aadfea-1bb3-4af3-be56-47958955f474"), 3, new DateTime(2010, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Médoc", "Ripe but restrained, with nicely sinewy embedded, structure carrying solid black currant, black cherry and fig fruit, followed by graphite and sweet earth on the finish. Drink now through 2011. 25,979 cases made.", 30.0m, "2008", new Guid("c7ab9644-2384-4af8-a377-2c5e66611a26") },
                    { new Guid("bc5acecd-c35b-4951-bb19-b2d0c31e5519"), 6, new DateTime(2010, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mâcon-La Roche Vineuse Vieilles Vignes", "A touch of spice adds depth to the peach, citrus and mineral notes in this expressive white. This is vibrant, building to a long aftertaste of spice and mineral. Drink now through 2014. 1,500 cases imported.", 18.0m, "2008", new Guid("527873e8-c0f4-4303-8b12-725bc6a1bb48") },
                    { new Guid("37f39947-d483-46e1-b9fb-441bb30c6086"), 3, new DateTime(2014, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley Legado Reserva", "This complex red unfurls with a lithe minerality, flanking the red berry, plum and dark cherry flavors. Notes of cedar and cigar provide richness. Offers a crisp texture, with a firm finish that lingers with slate and dark chocolate details. Drink now through 2019. 8,000 cases made.", 17.0m, "2011", new Guid("f1b58bbf-c38b-4a61-93bd-08bf6302c19f") },
                    { new Guid("0a48fe24-1b18-4d1d-8afa-a082b42ad5ac"), 3, new DateTime(2010, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Choapa Valley Legado Reserva", "A smoky, restrained style, with mulled black currant and blackberry fruit sitting in reserve and mesquite, graphite and white pepper notes weaving in and out. The finish lets the smoky hint linger nicely. Drink now through 2011. 5,000 cases made.", 15.0m, "2007", new Guid("f1b58bbf-c38b-4a61-93bd-08bf6302c19f") },
                    { new Guid("020bd09a-976d-4142-b39a-9f4c28ae5faf"), 3, new DateTime(2015, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Marchigue", "A ripe, dense and rich red, with concentrated roasted plum and dark cherry flavors, flanked by notes of dried meat and Asian spice. Bittersweet chocolate details linger with hints of cream on the finish. Drink now through 2020. 3,500 cases made.", 23.0m, "2011", new Guid("1f8b57ff-96a0-4056-a387-1aeefa6c37d0") },
                    { new Guid("77299062-82ba-4e24-b0de-7969a14e13ce"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Colchagua Valley", "Rock-solid, delivering nice, tightly wound grip that holds a core of blackberry, Black Mission fig, licorice snap and maduro tobacco in reserve. Rich but sleek, with a long, sweet finish full of earth and black fruit. Drink now through 2011. 1,900 cases made.", 23.0m, "2008", new Guid("1f8b57ff-96a0-4056-a387-1aeefa6c37d0") },
                    { new Guid("166b7e26-cbdf-48ae-b2b4-3e5207ec5d72"), 3, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Keefer Ranch", "Beautiful pure, ripe, riveting aromas of wild rose, black cherry, blackberry, wild raspberry, mineral and spice lead to a medium- to full-bodied palate, turning silky and elegant on the finish, with just the right touch of acidity and tannins. Drink now through 2019. 550 cases made.", 45.0m, "2009", new Guid("e95b9a56-3df1-4700-bdf6-8592aa0e6811") },
                    { new Guid("441c69f6-86c1-4ebf-b878-6a38dd7cb669"), 3, new DateTime(2009, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vin de Pays des Collines Rhodaniennes Sotanum", "Sleek and driven, with a gorgeous beam of blackberry and mulled fig fruit laced with mouthwatering graphite and iron notes. The long, spice-filled finish is tightly focused, with finely beaded acidity. This project continues to improve. Syrah. Drink now through 2014. 1,580 cases made.", 66.0m, "2007", new Guid("998a69af-6f8b-4383-b400-b1b88ba5dba6") },
                    { new Guid("99f234af-eb2c-4302-b56b-b6ea7da72fcf"), 6, new DateTime(2011, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Eola-Amity Hills Seven Springs Vineyard La Source", "Vibrant and poised, balancing its intense flavor profile of pear, quince and lemon blossom with layers of minerality on an electric structure that sparks with beautifully modulated acidity. A stunning white that has tremendous depth and presence. Drink now through 2020. 487 cases made.", 60.0m, "2009", new Guid("5cc8d3d1-5847-4e22-b289-e06b884b5e19") },
                    { new Guid("0225734e-4ce5-4fb2-8bf0-c73fdb11728a"), 3, new DateTime(2010, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Eola-Amity Hills Seven Springs Vineyard La Source", "Refined, elegant, focused and complex, piling layer upon layer of subtle flavors onto a racy, deftly balanced frame. Blackberry, currant and mineral notes compete for attention and mingle harmoniously on the long, expressive finish. Best from 2012 through 2020. 310 cases made.", 65.0m, "2008", new Guid("5cc8d3d1-5847-4e22-b289-e06b884b5e19") },
                    { new Guid("ad49b04d-464d-47cc-b3fa-99cff15a2525"), 6, new DateTime(2009, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kamptal Strassertaler", "This elegant Grüner has an intense minerality that is supported by a vibrant acidity. There's fine balance to the grapefruit, lentil and white pepper flavors, with hints of anise and savory herb on the precise finish. Drink now through 2018. 500 cases imported.", 12.0m, "2007", new Guid("a1d9b347-3ec5-4de4-843c-aa19ad456cf2") },
                    { new Guid("c3095c9f-87ae-4e31-ad48-d9161a5e97b4"), 3, new DateTime(2011, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Yakima Valley Jolie Bouche Boushey Vineyard", "Focused, sharply defined and beautifully expressive, this packs an enormous range of red berry, pomegranate, tomato leaf and roasted beet flavors into a taut, savory jet of deliciousness. Stylish, balanced and raucous all at the same time. Drink now through 2018. 385 cases made.", 36.0m, "2008", new Guid("e0c6984b-ed10-47ba-bb95-30968970a939") },
                    { new Guid("a265dad6-c1e7-46e1-a382-36ad1165b848"), 3, new DateTime(2009, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Red Mountain Ceidleigh", "Smooth, round and generous, this positively glows with gorgeous plum, blackberry and floral spice aromas and flavors, echoing on the long, expressive finish. This just doesn't quit. Drink now through 2014. 1,200 cases made.", 29.0m, "2006", new Guid("e0c6984b-ed10-47ba-bb95-30968970a939") },
                    { new Guid("2bb0ce87-3983-43e7-9654-d986c89f932a"), 3, new DateTime(2009, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Cuvée de Trois", "Offers great balance and purity of flavor, depth and complexity built around spicy wild berry, black cherry, mineral, anise and spice. Full-bodied, fresh and lively. The finish sails on. Drink now through 2013. 1,900 cases made.", 28.0m, "2007", new Guid("2b7fe519-bc31-4b6a-a8f5-84f1a1461727") },
                    { new Guid("344cf047-7ca2-4373-b92e-25793aeb35f7"), 3, new DateTime(2012, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Auguste Favier Réserve", "Ripe, pure and racy, with mouthwatering anise, steeped plum and hoisin sauce notes allied to a terrific graphite spine that carries the very lengthy finish while ganache, pepper, Turkish coffee and fresh espresso fill in on the back end. Shows the powerful structure and authoritative fruit of the vintage. Best from 2015 through 2030. 1,665 cases made.", 58.0m, "2010", new Guid("7e05f5ad-dfb0-4cd3-8a91-a72c19e5e44a") },
                    { new Guid("fb101f73-7759-4fa4-9b5c-11b34e58cd63"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Collection Charles Giraud", "Dark roasted Kenya AA coffee, braised fig, charred mesquite, mulled currant and blackberry fruit all work together in this huge and muscular but seamless and velvety offering. Graphite, brown bread, mineral and espresso notes weave through the finish, which has loads of pepper- and loam-tinged grip in reserve. Grenache and Mourvèdre. Best from 2011 through 2030. 665 cases made.", 75.0m, "2007", new Guid("7e05f5ad-dfb0-4cd3-8a91-a72c19e5e44a") },
                    { new Guid("5b8335f6-cc4c-4043-8a20-70cbedaf1a2c"), 3, new DateTime(2009, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley Cailloux Vineyard", "Plush and polished, this brims with cherry and blackberry aromas and flavors that are nicely shaded with streaks of mineral and green olive. Supple, harmonious and expressive as the finish keeps sailing on and on. Best from 2011 through 2018. 744 cases made.", 65.0m, "2006", new Guid("5f740df6-5ee7-486f-a79b-76bf511036ff") },
                    { new Guid("c84606d3-9919-43ad-9d34-9af02d6c5367"), 3, new DateTime(2009, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Santa Barbara County Colson Canyon Vineyard", "Firm and musclebound, with rich, chewy tannins. Full-bodied, this is the most complex and potent of the 2007 Tensleys, offering layers of earthy wild berry, sage, mineral, dried currant and floral scents. Drink now through 2016. 1,259 cases made.", 38.0m, "2007", new Guid("40e2bf30-a455-4d9e-901b-31c3236527f7") },
                    { new Guid("47da5773-dc56-4f06-a0f4-98145009037a"), 3, new DateTime(2016, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Eighteen Seventy-Three", "Ultrarich, layered and tightly focused on a dense core of meaty blackberry, currant, loamy earth, anise, crushed rock and graphite notes. For all the flavor and depth, this features a persistent finish that folds in smoky, toasty oak, adding an extra dimension of texture, flavor and aroma. Drink now through 2028. 4,723 cases made.", 80.0m, "2013", new Guid("2d3847f9-daf4-43e1-9bf5-0c7c4832d4c9") },
                    { new Guid("0b52e25b-0a6e-4d4d-ab17-8a09c7f1a743"), 3, new DateTime(2009, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Kathryn Hall", "A big, dense, concentrated wine, with wonderful aromas marked by chewy tannins, yet the core currant and blackberry flavors and savory herb and earth nuances provide a solid foundation. Best from 2010 through 2016. 3,040 cases made.", 75.0m, "2006", new Guid("2d3847f9-daf4-43e1-9bf5-0c7c4832d4c9") },
                    { new Guid("f3227d58-04c1-46a0-a077-8413577ac67d"), 6, new DateTime(2015, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kamptal Hasel Alte Reben Reserve", "Mouthfilling flavors of ripe citrus, baked apple and buttercream are loaded with luscious notes of custard and pastry. The ripe finish is long and rich-tasting.  Drink now through 2022. 1,000 cases made.", 37.0m, "2014", new Guid("a1d9b347-3ec5-4de4-843c-aa19ad456cf2") },
                    { new Guid("31b76d2b-07ee-4afd-b8c9-d457dd170458"), 3, new DateTime(2016, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Russian River Valley Montafi Ranch", "Zinfandel doesn't get much better than this. Rich aromas of black raspberry and orange zest open to balanced, complex and luxurious flavors of blackberry, mint and baking spices. A knockout. Drink now through 2027. 871 cases made.", 47.0m, "2014", new Guid("5420c5f2-205c-4f19-b7bd-2ec23de77a44") },
                    { new Guid("3530679d-1bc9-4d38-a150-85b028871e25"), 3, new DateTime(2009, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Russian River Valley Papa's Block", "This gorgeous Syrah has amazing complexity, depth and concentration, gushing with pure, rich wild berry, blackberry and boysenberry fruit that's simply delicious right now. Holds its tight focus and gains depth and nuance, with pepper, spice and savory flavors. Drink now through 2016. 307 cases made.", 43.0m, "2007", new Guid("5420c5f2-205c-4f19-b7bd-2ec23de77a44") },
                    { new Guid("a0692d8b-c20b-4850-a85f-1f4d4cdcdc2f"), 3, new DateTime(2017, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "James Berry Vineyard Paso Robles Willow Creek District", "A dramatic wine that weds power and opulence, maintaining impeccable balance along the way. Brooding dark berry and loamy mineral aromas lead to rich flavors of currant, licorice, dried sage and smoky pepper. Ripe tannins frame the finish. Grenache, Mourvèdre, Syrah, Counoise and Roussanne. Best from 2018 through 2028. 1,400 cases made.", 98.0m, "2014", new Guid("a8711833-ba13-48cc-91b6-438662603b3f") },
                    { new Guid("b788fdf2-e60f-41a3-9c77-d87739f7afbe"), 3, new DateTime(2010, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "James Berry Vineyard Paso Robles", "An amazing wine, dense, rich and layered, offering a mix of power and finesse, with concentrated dark berry fruit, mineral, sage, herb and cedar notes that are pure, intense, focused and persistent. Grenache, Mourvèdre and Syrah. Drink now through 2018. 950 cases made.", 67.0m, "2007", new Guid("a8711833-ba13-48cc-91b6-438662603b3f") },
                    { new Guid("78a3c29d-adea-4dca-94e6-fb20fe258aa4"), 3, new DateTime(2009, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Broken Stones Paso Robles", "Offers waves of ripe, exotic dark berry fruit, with wild berry, raspberry and blackberry jam notes that are full-bodied, rich and intense, pure and focused, ending with a long, complex, persistent finish. Syrah, Grenache and Mourvèdre. Drink now through 2015. 800 cases made.", 45.0m, "2006", new Guid("a8711833-ba13-48cc-91b6-438662603b3f") },
                    { new Guid("3b7210fe-d66b-46c7-bc96-b600d4560e52"), 3, new DateTime(2009, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Marcenasco", "This fabulous Nebbiolo displays aromas of very ripe strawberry and cappuccino. Full-bodied, with supervelvety tannins and incredible concentration. All the tannins are coated with gorgeous fruit. Best after 2013. 5,000 cases made.", 44.0m, "2005", new Guid("ae371dbc-9b6b-4c06-a3ae-39dfb9a8dcda") },
                    { new Guid("27a466ff-dba1-4474-83bb-b88dd57e390d"), 2, new DateTime(2008, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vidal Niagara Peninsula Ice Wine", "Intense aromas and flavors of beeswax, orange marmalade, vanilla and apricot. Concentrated, yet lithe and supple, with bright acidity and great harmony. Lovely finish and long marmalade aftertaste. Drink now through 2020. 3,200 cases made.", 55.0m, "2006", new Guid("e6230047-863a-435c-baea-6c2d21b3390b") },
                    { new Guid("6b22e771-67ea-4bb8-802b-5327a0c69062"), 3, new DateTime(2008, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Il Borgo", "This is dark and rich, with loads of blackberry and currant aromas. Full-bodied and very ripe, with soft, velvety tannins. Rich and luscious. Goes on and on. One of the best Cabreos in a long time. Sangiovese and Cabernet Sauvignon. Best after 2011. 3,500 cases made.", 60.0m, "2006", new Guid("57db6420-71f3-43cf-bfb4-6caa042a7c1c") },
                    { new Guid("dd53279c-df35-4bda-9b7c-c71fcdc07180"), 6, new DateTime(2019, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Kremstal Kremser Kogl", "Mineral-driven and well-built, this is bursting with notes of iodine, lentil and white pepper, while nectarine and pear flavors take a back seat. Very energetic, with enough structure to stand up to a serious meal. Drink now through 2026. 2,000 cases made, 1,200 cases imported.", 17.0m, "2018", new Guid("c86a0d52-64c1-46f9-b218-338204bd418c") },
                    { new Guid("466fac3d-8510-4370-8035-0abb6b2c915b"), 6, new DateTime(2008, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Kremstal Schiefer", "Full-bodied, creamy and spicy, with ripe, rich flavors of peach, pear, apricot and grapefruit. There's plenty of acidity to back it up, with a long finish of white raspberry and custard. Drink now through 2015. 1,250 cases made.", 21.0m, "2006", new Guid("c86a0d52-64c1-46f9-b218-338204bd418c") },
                    { new Guid("c51e392a-e75f-4e91-a822-712ecc2f9442"), 3, new DateTime(2008, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Galilee Yarden", "Rich and refined, showing focused flavors of red plum, berry and spice, with appealing herbal overtones. Has excellent balance and structure, backed up by crisp acidity. Mineral and smoke notes fill the firm finish. Kosher. Drink now through 2012. 13,000 cases made.", 27.0m, "2004", new Guid("4d100219-462d-47f4-9b9e-9cca258b1238") },
                    { new Guid("7eb14bdb-4abb-4abe-9d87-0ea7731aca79"), 3, new DateTime(2010, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Russian River Valley Papera Ranch", "A real knockout. Bold and intense, yet complex, with ripe cherry fruit deftly balanced by vibrant acidity. Offers floral aromas with notes of black raspberry, pepper and lingering Asian spice that lead to full but fleshy tannins. Drink now through 2015. 651 cases made.", 40.0m, "2007", new Guid("5420c5f2-205c-4f19-b7bd-2ec23de77a44") },
                    { new Guid("553445f1-ad32-43e6-9dc5-6c9d13a435f3"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Greppicante", "Deliver currant, toasty oak and coffee aromas and flavors. Full-bodied, with velvety tannins and a lot of rich fruit. Very polished and attractive. Cabernet Sauvignon, Merlot and Cabernet Franc. Best after 2012. 5,265 cases made.", 28.0m, "2007", new Guid("53760836-a185-4ba1-919b-795b1a9c6db1") },
                    { new Guid("bedf1b1d-8f35-484c-be01-7f6a95d84e49"), 3, new DateTime(2009, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Esprit de Beaucastel Paso Robles", "Well-balanced, intense yet elegant. Full-bodied, showing vivid blueberry and wild berry flavors, with hints of nutmeg, cedar and coconut. Ends with a long, complex finish that shows a pleasant loamy, earthy quality. Mourvèdre, Grenache, Syrah and Counoise. Drink now through 2014. 3,500 cases made.", 45.0m, "2006", new Guid("93d59c04-d253-4d70-bae2-0e6133da00a4") },
                    { new Guid("fa1bf4f9-04e5-4729-8c53-16207efaeaac"), 3, new DateTime(2011, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes de Tablas Paso Robles", "Charmingly fruity, supple and fun to drink, exhibiting ripe plum, black cherry and wild berry fruit that's smooth and layered, with a long, fruity finish. Grenache, Syrah, Counoise and Mourvèdre. Drink now through 2017. 1,050 cases made.", 30.0m, "2009", new Guid("93d59c04-d253-4d70-bae2-0e6133da00a4") },
                    { new Guid("f27b8f19-2b92-414f-b712-374d392c584b"), 3, new DateTime(2009, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Eola-Amity Hills Seven Springs Vineyard", "Bright and juicy, with lively acidity and a streak of distinctive minerality running through the raspberry and floral flavors, lingering on the refined finish. Best from 2010 through 2014. 1,519 cases made.", 48.0m, "2007", new Guid("5cc8d3d1-5847-4e22-b289-e06b884b5e19") },
                    { new Guid("5a15ff71-66f4-41df-9677-2cb5cf3d7cef"), 6, new DateTime(2009, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fuissé Vieilles Vignes", "Bright and pure, offering lemon, apple and stone flavors. Taut and racy, with emphasis on the acidity. Shows fine drive and intensity, with a mouthwatering finish. Best from 2010 through 2016. 3,000 cases made.", 30.0m, "2007", new Guid("3945e4c6-8e97-42b6-812b-3ed48344aa67") },
                    { new Guid("2d61a0ab-3b7d-41db-9b52-70d041fa08bc"), 6, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Single Vineyard", "Offers a succulent mix of citrus and tropical fruit, with Meyer lemon, passion fruit and lemon verbena notes accented by a distinct sea salt hint. Generous and refreshing, with a long, persistent finish. Drink now. 16,000 cases made.", 13.0m, "2013", new Guid("5cc5e41d-4165-4a78-9bc3-e8b4558b630f") },
                    { new Guid("f1764aaf-d7e3-4e91-b1d5-02feac88bd34"), 3, new DateTime(2009, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Marlborough", "Lithe, medium-weight, generous and appealing for the coffee nuances that swirl through the plum and currant fruit, remaining vivid and expressive through the long finish. Drink now through 2015. 20,000 cases made.", 30.0m, "2007", new Guid("5cc5e41d-4165-4a78-9bc3-e8b4558b630f") },
                    { new Guid("32834723-0522-423f-a71b-5c0c12f139a0"), 6, new DateTime(2013, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja White Viña Gravonia Crianza", "A beautiful example of a traditional white Rioja. Fresh and elegant, with harmonious, complex flavors of apple, mint, almond, beeswax and rose water. Delicate yet intense, balanced and long. Drink now through 2023. 2,083 cases made.", 36.0m, "2003", new Guid("e9b129cc-fcbf-4adf-8dce-4f6291b94864") },
                    { new Guid("1d200d19-dda1-4add-aba1-473d5ae521f1"), 6, new DateTime(2009, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja White Viña Tondonia Reserva", "Brioche, chamomile and dried pear flavors are rich and expressive in this polished white. Silky and fresh on the palate, with a haunting honeyed and floral finish. Best on its own, as a meditation wine. Drink now. 1,666 cases made.", 49.0m, "1990", new Guid("e9b129cc-fcbf-4adf-8dce-4f6291b94864") },
                    { new Guid("432f6f80-fdf1-4fba-a94d-94dbf26d42b4"), 6, new DateTime(2009, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Qualitätswein Trocken Rheinhessen Westhofener S", "A smooth, peach-infused white, lush and expansive, yet backed by a firm structure. Picks up energy on the long finish, which adds a peppery accent. Drink now through 2020. 2,083 cases made.", 48.0m, "2007", new Guid("97000a1e-f9a2-4cb4-b48d-55f5fead8c2a") },
                    { new Guid("150d01e5-17e7-42ae-b223-d0dd12152079"), 3, new DateTime(2009, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coteaux du Languedoc La Clape Les Bartelles", "A big, rich and ripe red, with a meaty aroma that leads to intense mocha and dark chocolate notes. Hints of cream, dark cherry and spice complete the package. Finishes with intense beefy notes. Drink now through 2014. 2,200 cases made.", 35.0m, "2007", new Guid("23bd4cd4-9ae4-4d37-8512-3a2c5d138661") },
                    { new Guid("61396410-91d1-4d6a-ae8c-79ba0f8c6e4e"), 3, new DateTime(2009, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vacqueyras", "Really fresh, with a solidly built core of red cherry, red currant and plum fruit woven with incense, briar and pastis hints. The long, fleshy finish stays racy and focused. Delicious. Drink now through 2012. 5,000 cases made.", 21.0m, "2007", new Guid("180f8410-f98c-4fe9-9da8-d4b909efa7cd") },
                    { new Guid("71c0b385-7e89-4af5-a2f3-994541f56a2c"), 3, new DateTime(2009, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Jumilla", "This polished red shows black cherry, licorice, mineral and tobacco notes, harmonious and deep, supported by firm tannins. Medium-bodied, well-integrated and stylish. Drink now through 2012. 5,000 cases made.", 16.0m, "2006", new Guid("97e36165-fa00-407f-a4ed-379e53cbde41") },
                    { new Guid("3a09d9f2-044d-448a-9786-423b05eb9297"), 3, new DateTime(2009, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Columbia Valley", "Smooth, polished and wide-open in texture, with gorgeous plum and blueberry fruit getting added nuances from hints of Indian spices. The finish lingers effortlessly. Drink now through 2016. 2,436 cases made.", 20.0m, "2006", new Guid("6844c9df-61cf-4519-a043-aaba08495141") },
                    { new Guid("a613fed5-2632-45f8-bbb9-9ab3bc19bfa1"), 3, new DateTime(2009, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tempranillo Rioja Viña Zaco", "Black cherry and blackberry flavors are framed by smoky chocolate oak in this modern red, which is taut and focused, with firm tannins and bright acidity. Drink now through 2012. 10,000 cases imported.", 15.0m, "2006", new Guid("083261e3-a6b7-42f5-b4af-1d6c2f51e583") },
                    { new Guid("90350858-0b96-41bd-9518-20b81e9020cb"), 6, new DateTime(2009, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Grigio Alto Adige", "Displays spicy apple and peach notes, with silky mineral character. Medium-bodied, with clean acidity and a fruity finish. Subtle and layered. Drink now. 23,700 cases made.", 15.0m, "2008", new Guid("d4be42ee-f5c2-4b6c-bdc1-018db7d6630a") },
                    { new Guid("e04697ae-2f2f-4857-8a68-94124b8a90cc"), 6, new DateTime(2009, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viognier-Pinot Gris South Australia", "Smells like Viognier, with all the floral and sweet lychee fruit up front, then lightens up in the mouth, with the freshness and pizzazz of Pinot Gris. A lovely balance. Drink now. 5,000 cases made.", 16.0m, "2007", new Guid("46395693-7c97-47dc-bc6a-7434d6baf570") },
                    { new Guid("0f48683c-505e-4db6-9bde-27bc6861c881"), 6, new DateTime(2014, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Mosel Dr. L", "This succulent white is filled with plush red peach, cherry and white currant flavors that are supported by zesty acidity. Long and creamy on the finish, offering plenty of spicy overtones. This is impressive for the category. Drink now through 2016. 100,000 cases made.", 12.0m, "2012", new Guid("4d735682-17cd-452b-bedd-37bb5371e615") },
                    { new Guid("ae23ccfa-031e-48e2-8078-f1d768978882"), 6, new DateTime(2009, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling QbA Mosel-Saar-Ruwer Dr. L", "Sleek and tangy, showing apricot, lime and mango flavors matched to a lively structure. There's good harmony and persistence through the finish. Drink now through 2018. 75,000 cases made.", 12.0m, "2008", new Guid("4d735682-17cd-452b-bedd-37bb5371e615") },
                    { new Guid("0fa75c82-deb0-4119-9d38-dca4ece16c69"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sangiovese-Merlot-Cabernet Sauvignon Toscana", "Bright plum, dried cherry and flowers on the nose. Full-bodied, with fine tannins and refined berry and cherry flavors. Drink now. 80,000 cases made.", 12.0m, "2006", new Guid("66361d7c-ed14-466f-896b-e359225ab692") },
                    { new Guid("a352815f-523b-4174-9e4d-0ba4a1f1e378"), 6, new DateTime(2009, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Rutherford", "Key lime, passion fruit, melon and nectarine flavors are ripe, juicy and fleshy, with great intensity, a vivid acidity and terrific focus and clarity. Drink now. 15,360 cases made.", 19.0m, "2008", new Guid("33f6aa1e-a25b-49ca-9038-421ab11195d8") },
                    { new Guid("06f36626-49f8-454e-96b5-e108e1b372e4"), 3, new DateTime(2009, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Salanques", "A plush texture carries rich flavors of brandied cherry, dark chocolate, cocoa and mountain herbs in this expressive red. The tannins are firm but well-integrated, and the finish is bright and juicy. Drink now through 2014. 1,000 cases imported.", 40.0m, "2006", new Guid("1a81b1a4-20e8-4c19-8a08-527402fab586") },
                    { new Guid("416b75d8-79f8-4ba4-bfd0-b26dac8883cc"), 6, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Hemel-en-Aarde Valley", "Lush and inviting, with layers of fig, persimmon, mango and creamed Jonagold apple fruit, all supported by finely beaded acidity that lends definition and length to the finish. More cut and length than just weight in the end. Drink now through 2017. 4,028 cases made.", 32.0m, "2010", new Guid("285de08e-c5fa-41ea-9011-68f1dc8c6b78") },
                    { new Guid("9bb59d63-c540-4707-87bc-79c3be00b4e4"), 3, new DateTime(2009, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Hemel-en-Aarde Valley", "Bold, aromatic and ripe, but very elegant, with racy acidity providing the ground wire for the rich black cherry, bramble, incense and sweet earth notes, followed by a long, multifaceted finish. Drink now through 2013. 5,213 cases made.", 44.0m, "2007", new Guid("285de08e-c5fa-41ea-9011-68f1dc8c6b78") },
                    { new Guid("a6a8c676-6e86-4d0f-8237-5fefe1dc67c2"), 3, new DateTime(2009, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Adelaide", "Polished, plush and generous with its cinnamon- and citrus peel-accented plum and cherry flavors, hinting at roasted meat on the smooth finish. Drink now through 2015. 7,000 cases imported.", 14.0m, "2007", new Guid("25da3f67-1ddd-4e8f-9312-9d7cc2e008ab") },
                    { new Guid("67156e21-ed1e-4534-ae79-c83659860336"), 6, new DateTime(2017, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Patelin de Tablas Blanc Paso Robles", "This vibrant and mouthwatering white shows green apple, Meyer lemon and dried pineapple flavors that are fresh and juicy. Lemon verbena and lime sherbet notes reverberate on the long, detailed finish. Grenache Blanc, Viognier, Roussanne and Marsanne. Drink now. 2,920 cases made.", 25.0m, "2015", new Guid("93d59c04-d253-4d70-bae2-0e6133da00a4") },
                    { new Guid("960eaaa0-13f0-494f-9c3e-7e0d5f75994c"), 3, new DateTime(2011, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Primitivo Salento Torcicoda", "Jammy yet focused, offering layers of roasted coffee, mesquite and spice to the concentrated blackberry and damson plum skin fruit. Picks up a tarry edge on the tangy finish. Drink now. 30,000 cases imported.", 22.0m, "2008", new Guid("a27768eb-0c7a-43c4-bc4f-038061a0a683") },
                    { new Guid("f9543e3c-9718-422c-9195-8cf53f8e149c"), 3, new DateTime(2011, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "California", "Starts understated, building depth and dimension, featuring bright, rich black currant, anise, sage and black licorice character. A full-bodied orchestration of flavors at the end weave together into a tapered finish. Cabernet Sauvignon, Merlot, Syrah and Cabernet Franc. Drink now through 2022. 2,300 cases made.", 48.0m, "2008", new Guid("8aaa708b-2236-48a2-8637-aca7818051cf") },
                    { new Guid("72cf91f1-f9ad-4d5d-b5db-08f92c9c6ed0"), 3, new DateTime(2011, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Maremma Toscana Le Sughere di Frassinello", "This red is imbued with black currant, cherry and cedar aromas and flavors. The tannins are firm, but nicely entwined with the texture, and this finishes with a harmony of fruit and spice. Sangiovese, Cabernet Sauvignon and Merlot. Best from 2014 through 2022. 2,250 cases imported.", 22.0m, "2009", new Guid("5333cfdf-3ceb-405a-a5e3-0d355dcfac3e") },
                    { new Guid("d16bd3ab-181c-4ef8-9549-cc20b2aa8b6a"), 6, new DateTime(2011, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Finger Lakes Dry", "This delivers a very pure, mouthwatering beam of anise, slate and green apple flavors, with a long, driven, quinine-tinged finish. Delicious, crunchy acidity really hangs on nicely. Drink now through 2014. 1,400 cases made.", 16.0m, "2009", new Guid("b0309d84-eefe-421e-905f-422bff971c1f") },
                    { new Guid("51bf972e-0ea4-4fd9-8fa9-783dcc7fb9f3"), 3, new DateTime(2013, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cerasuolo di Vittoria Classico", "Complex layers of salumi, forest floor and spice box lead off in this medium-bodied red, as firm tannins settle around the pure cherry and currant fruit. Needs time in the cellar, but the long, minerally finish shows potential. Best from 2014 through 2020. 5,000 cases made.", 35.0m, "2010", new Guid("1aaaa8ea-747d-4591-aebe-0404f388aeb3") },
                    { new Guid("24916f15-6465-4a98-8475-ca2e5cf33c73"), 3, new DateTime(2015, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Colchagua Valley Royale Los Lingues Vineyard", "A ripe and muscular red, offering dark plum, dark cherry and currant flavors, filled with notes of dried beef and Asian spice. Grippy midpalate, with a creamy allure. Mint and dark chocolate accents underlie the long, rich finish. Drink now through 2020. 4,160 cases made.", 26.0m, "2012", new Guid("daa39415-34b9-4c09-b275-33dd976ffd7e") },
                    { new Guid("0d2ab1f6-bc38-4c50-87d9-def5e2ae4fd3"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Carmenère Colchagua Valley Royale", "This compact red needs air in order to open up the core of crushed wild berry and dark black currant notes, tightly wound with fine tannins and hints of tobacco, graphite and spice. Best from 2014 through 2019. 13,000 cases made.", 26.0m, "2010", new Guid("daa39415-34b9-4c09-b275-33dd976ffd7e") },
                    { new Guid("ed5c934c-2592-4e5b-a94e-156405690c9d"), 6, new DateTime(2013, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Godello Bierzo Selección", "A generous white, delivering bold flavors on a lush texture. The pineapple, peach and crème brûlée notes are framed by sweet vanilla and tangerine acidity. This is distinctive, in the modern style. Drink now. 5,000 cases made.", 14.0m, "2010", new Guid("e51743a4-1aa5-4669-a523-194f752261d8") },
                    { new Guid("6f040e7f-d6e8-4f3a-8bd6-3c8fb2bc0253"), 3, new DateTime(2013, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Barrel Monkeys", "Supple, succulent and generous, with pure blackberry and purple plum flavors, hinting at vanilla and cream as the intense finish lingers on a medium-weight frame. Drink now through 2021. 1,600 cases imported.", 20.0m, "2011", new Guid("3e68ffb7-195e-4532-896e-568be0f65ea8") },
                    { new Guid("ecbdbd54-92b7-412c-ad72-457364ec61ea"), 3, new DateTime(2013, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Cahors White Label", "This ripe and alluring red offers rich, meaty accents and medium-grained tannins, with minty notes to the dried red fruit and crushed plum flavors. Cocoa powder and mocha hints fill the powerful finish. Drink now through 2018. 8,000 cases made.", 24.0m, "2010", new Guid("6232e585-c8b3-4db3-b46c-2bb2103bb0cf") },
                    { new Guid("d9159454-f0d9-499f-8426-e3218f992269"), 3, new DateTime(2013, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Excelsus", "This focused red shows good density, a restrained character and solid depth, offering plum, espresso, tobacco and mineral flavors that are harmonious and long. Not showy, but solid. Drink now through 2022.  ", 18.0m, "2009", new Guid("29e93f70-9044-4019-9d28-92327e81106c") },
                    { new Guid("9e31e7a4-fbfc-4869-af9b-81f5f1fb9149"), 6, new DateTime(2013, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Manzanilla Jerez La Gitana", "Bright, with saline, sunchoke and blanched almond notes backed by a piercing, chalk-framed finish. This is all precision and cut. Drink now. 66,665 cases made.", 19.0m, "NV", new Guid("822a9aa6-f91f-4ddd-bda9-029f797141c7") },
                    { new Guid("5f96d002-c8ae-4b26-8066-c5991603c366"), 3, new DateTime(2013, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Castillon Côtes de Bordeaux", "Set apart by range and depth, with gorgeous blueberry, blackberry and plum notes intertwined with additional açaí berry and loganberry fruit flavors. The toasted apple wood frame is subtle and caressing, accented by a lilting anise accent that flitters through the finish. Best from 2014 through 2024. 1,250 cases made.", 34.0m, "2010", new Guid("56442b27-0958-439f-838b-e0cbb1e6f11a") },
                    { new Guid("337642fc-f74f-4e04-a1b6-67d9e15a4e89"), 3, new DateTime(2013, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Estate Blend Paderewski Vineyard Paso Robles", "Combines richness with subtlety and finesse, weaving together fresh earth, chocolate-covered espresso bean and vivid dark berry flavors with touches of hazelnut, gliding to a long, graceful finish. Syrah, Grenache, Mourvèdre and Tempranillo. Drink now through 2022. 1,699 cases made.", 40.0m, "2010", new Guid("9d9cbfae-277b-4ff5-b2f8-72a60262688e") },
                    { new Guid("f734d728-3c63-4ae9-b99c-3cbba18816d8"), 6, new DateTime(2013, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Columbia Valley Kung Fu Girl Evergreen", "Lovely, expressive, bright and vibrant, with apple, lime and citrus blossom flavors, persisting enticingly as the off-dry finish lingers against natural acidity. Drink now through 2015. 70,600 cases made.", 12.0m, "2012", new Guid("c85e78e3-7412-4d24-a8b6-86a99eb5f33a") },
                    { new Guid("9462aa75-8092-47af-b580-eb29784718e2"), 3, new DateTime(2013, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Pertimali", "Aromatic, suave and silky, boasting aromas and flavors of flowers, cherry, strawberry and leather, all boosted by bright acidity and firm, well-integrated tannins. Refined and long, with a lingering finish of mineral and spice. Best from 2016 through 2035. 700 cases imported.", 55.0m, "2008", new Guid("05009b98-9211-4e08-a71e-0e6a663131d0") },
                    { new Guid("126ff6a8-3dc6-4a76-a9ce-33173fcfd2c9"), 3, new DateTime(2016, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja 904 Gran Reserva", "This supple red delivers bright, focused flavors of dried cherry, orange peel, vanilla, sandalwood, tobacco and spice, with light tannins and fresh acidity. An elegant rendering of the traditional style. Drink now through 2022. 12,500 cases made.", 55.0m, "2007", new Guid("ad29e298-d456-42cb-9bff-74d562d136d4") },
                    { new Guid("e2a2a69d-ce9f-4a56-af37-50f1a88ad138"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Viña Ardanza Reserva", "Cedar, tobacco and spice hints frame dried cherry, berry and vanilla notes in this silky red. The flavors mingle nicely, framed by juicy acidity and light but firm tannins. The wine has a lovely, weightless intensity. A fine example of the traditional style. Drink now through 2020. 50,000 cases made.", 35.0m, "2004", new Guid("ad29e298-d456-42cb-9bff-74d562d136d4") },
                    { new Guid("dbcae3d7-b8ec-4e37-ac14-e35148d2ec64"), 3, new DateTime(2013, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri", "A pretty red, boasting black cherry, black currant, violet and mineral aromas and flavors. Supple and silky, with hints of herbs and spices. Though dense, the tannins are well-covered by sweet fruit and flesh, but provide ample support. Excellent length. Best from 2015 through 2025. 7,900 cases made.", 30.0m, "2011", new Guid("4b2f871d-04d4-481c-8d0e-cd0bd9c8e1b0") },
                    { new Guid("e87cb4ca-c5f0-4c16-a882-a00f1d87695e"), 3, new DateTime(2013, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Dundee Hills Revana Vineyard", "Tangy, focused and juicy, with blackberry, currant and subtle spice flavors, finishing with an open feel to the expressive finish. Shows impressive purity, clarity and length. Drink now through 2020. 1,600 cases made.", 42.0m, "2010", new Guid("02a683bb-43f4-4ce2-90ce-ed992217beb9") },
                    { new Guid("34ea4224-0a28-4ccb-b061-13a08fd29400"), 3, new DateTime(2013, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Albe", "The rose, strawberry and cherry aromas and flavors are beautifully displayed on an elegant frame in this pure and delicately wrought red, which shows harmony and depth. The fine lingering finish expresses energy, with a mineral element. Best from 2015 through 2032. Tasted twice, with consistent notes. 7,000 cases made.", 42.0m, "2008", new Guid("06ce44c5-92f9-4eea-8e8a-7a5e13749a9f") },
                    { new Guid("0f0fea93-908f-48a2-b84f-2b12d37c36d4"), 3, new DateTime(2016, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley In The Rocks", "Supple and expressive, this opens up like a giant flower, spreading the dark plum, blackberry, crème fraîche and spice flavors throughout the seamless whole. The finish sails on against barely noticeable tannins. This has tremendous power, but feels like it's floating. Drink now through 2025. 602 cases made.", 70.0m, "2013", new Guid("f88b6fb2-f3c6-4c7b-80b8-dea1c7f6f448") },
                    { new Guid("3d4d371e-b2b5-4851-827e-38880d389d70"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Walla Walla Valley Stonessence", "Polished, plush, round and deeply expressive, this Syrah pulses with purple plum, black currant, wild blueberry, clotted cream and exotic spice flavors that mingle effortlessly and seamlessly. The finish sails on against fine tannins. Drink now through 2025. 184 cases made.", 70.0m, "2010", new Guid("f88b6fb2-f3c6-4c7b-80b8-dea1c7f6f448") },
                    { new Guid("a3e15258-2b71-43c1-a19b-c7beb47d9555"), 3, new DateTime(2012, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Aglianico Irpinia", "Smells of the mountain, featuring aromas of pine, wildflower and stone that give way to ripe wild berry notes and firm tannins. Structured, with a minerally aftertaste. Drink now through 2016. 1,600 cases made.", 20.0m, "2008", new Guid("54ed7ce8-6a32-4d63-9a32-7926f5edb46f") },
                    { new Guid("4856f9a6-5c2a-48e1-8ee1-ebfef29877a8"), 6, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sancerre Les Boucauds", "This white is still tight, exhibiting a very chiseled feel to the flint and gooseberry notes. The mouthwatering, rapier finish lets chive flower and fleur de sel notes chime in. Could have some staying power in the cellar. Drink now through 2015. 5,000 cases made.", 27.0m, "2011", new Guid("3533aef0-9dc3-4eb0-b250-a98ca2c6d45b") },
                    { new Guid("6413ad57-918a-4bb4-9267-f7b12727114c"), 6, new DateTime(2012, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Moscofilero Péloponnèse", "A rapierlike white, delivering apple, pear and grapefruit flavors flanked by plenty of mineral and slate notes. Ripe orange accents linger as plenty of spicy hints mark the vibrant, long-lasting finish. Drink now through 2018. 5,000 cases made.", 15.0m, "2011", new Guid("07c3b0d5-2b89-4ff9-9e54-e320d79a8c7d") },
                    { new Guid("43588251-541e-4ef3-a33c-a9910390ebb7"), 3, new DateTime(2018, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja 890 Gran Reserva Selección Especial", "Cedar and sandalwood aromas give way to flavors of tea, tobacco, dried cherry, forest floor and orange peel in this expressive red. Round on the palate, supported by muscular tannins and juicy acidity. Big but graceful, mature yet lively, in the traditional style. Drink now through 2030. 4,753 cases made.", 175.0m, "2005", new Guid("ad29e298-d456-42cb-9bff-74d562d136d4") },
                    { new Guid("af5a62f7-8320-4da4-b141-4b540051e9c3"), 6, new DateTime(2016, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Ancient Lakes Of Columbia Valley Kung Fu Girl Evergreen", "Tangy and zingy, with lively acidity that balances against gobs of ripe pear and apricot flavors, carrying through the long, lime-accented finish. Drink now through 2020. 160,000 cases made.", 12.0m, "2015", new Guid("c85e78e3-7412-4d24-a8b6-86a99eb5f33a") },
                    { new Guid("445f8fef-07a5-4c39-88bc-80b8d73ecf7b"), 3, new DateTime(2013, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley Blueline Vineyard", "This rich and fleshy red offers bold aromas of black cherry and toasty oak, with ripe, plush currant, mocha and spice flavors. A backbone of firm, slightly minerally tannins runs throughout. Drink now through 2018. 1,400 cases made.", 75.0m, "2010", new Guid("5c1bfcf3-8420-48ac-a982-701f8660d0a8") },
                    { new Guid("e54d2434-9af0-4e3f-963e-a4ae80c2f824"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Aglianico del Vulture Macari'", "This dark red offers concentrated black cherry reduction, cassis, pine, hot stone and dark tobacco notes. Well-integrated, delivering fresh acidity and fine tannins. Drink now through 2018. 150 cases imported.", 25.0m, "2007", new Guid("2cc290e5-9f2a-4d2c-ac69-298c4b248b63") },
                    { new Guid("d1608876-dca3-4295-a57e-da05534c4ea7"), 3, new DateTime(2014, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon-Carmenère Colchagua Valley", "This suave version shows refined, mineral-infused flavors of dark currant, plum and dried blackberry. Anise and cocoa powder notes provide added complexity. Drink now through 2020. 8,000 cases imported.", 14.0m, "2011", new Guid("a07a54c5-891b-485a-b66c-b7087b360b4b") },
                    { new Guid("765e05c4-70f3-4d58-859f-70c2be512232"), 3, new DateTime(2014, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos Apalta Limited Release Colchagua Valley", "Big and rich, this red is filled with powerful flavors of blackberry, dark currant, black olive and dark chocolate that are built on an ironclad frame. Notes of cocoa powder and cream chime in as well, and the muscular finish lingers with hints of savory herb. Carmenère, Cabernet Sauvignon and Merlot. Best from 2014 through 2022. 2,000 cases imported.", 89.0m, "2010", new Guid("e2870cb1-accb-4e00-8dc9-2c219de80888") },
                    { new Guid("dc152fcd-23c8-412c-97be-58e156de8aca"), 3, new DateTime(2014, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Rabajà", "Bright, with cherry, strawberry and floral aromas and flavors, this is backed by a firm yet integrated underlying structure. Beautifully poised for development, so hands off for now. Juicy and long, delivering a lingering aftertaste of cherry, spice and mineral. Best from 2015 through 2027. 1,420 cases made.", 55.0m, "2010", new Guid("763e4f7c-dd23-4296-8b3e-f5928e156eb3") },
                    { new Guid("177df1ca-c738-4de0-abb3-00f77827cc22"), 3, new DateTime(2014, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma County 55% Slope", "Dark, rich and layered, with tiers of dense dark berry, graphite-crushed rock, cedar, mocha and brownie flavors, ending with dried herb and tobacco accents. The fruit density stands out. Drink now through 2024. 3,385 cases made.", 45.0m, "2009", new Guid("ef2f2344-4faa-4465-8251-4a9bf0436f9c") },
                    { new Guid("8a094e1f-b3e4-49d2-89d8-62983ba33f30"), 3, new DateTime(2014, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sangiovese-Cabernet Sauvignon Toscana Poggio al Tufo Rompicollo", "Presents a whiff of blackberry, bacon and ink, followed by dark berry, black pepper and spice notes. Rich and supple, with ample structure for support and a long, fruit-, spice- and mineral-filled aftertaste. Sangiovese and Cabernet Sauvignon. Best from 2015 through 2022. 3,000 cases imported.", 18.0m, "2011", new Guid("6618cf17-f617-483d-91af-630f75d1b154") },
                    { new Guid("df99469f-835a-4888-9943-dea3e319bc44"), 3, new DateTime(2014, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Colheita", "Mocha, hot spice and chocolate notes fill this decadent red to the brim. Very long midpalate, with accents of slate and spice. Finishes with smoke, cream and cedar details. An exotic red from Portugal. Drink now through 2019. 8,160 cases made.", 15.0m, "2011", new Guid("b95f4d22-b736-407b-96f9-649d4d68935d") },
                    { new Guid("229607f8-0d61-40fb-85fd-eeedf0fd1ac0"), 3, new DateTime(2014, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Hallberg Ranch", "Impressive, offering richness, vibrancy and subtle charm, marked by snappy blackberry, red berry, spice, sage and underbrush notes. Ends with a minerally edge that holds the flavors. Drink now through 2021. 11,100 cases made.", 42.0m, "2011", new Guid("7412c2ab-8251-4c5e-8c4a-1af1f55fe748") },
                    { new Guid("5a98b49b-378e-461a-b9cf-201407f05174"), 3, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Volpolo", "Restrained, exhibiting black currant, cherry, cedar and sage aromas that pick up licorice, spice and mineral flavors. This is concentrated and focused, with a beam of acidity and an iron accent on the finish. Best from 2016 through 2024. 7,500 cases made.", 35.0m, "2011", new Guid("291c3018-ff7a-4d27-b9ad-22624aa1b3b9") },
                    { new Guid("9c63e898-70fe-464c-ab76-c3d5b7e4b798"), 3, new DateTime(2014, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yamhill-Carlton District Mineral Springs Ranch", "Vibrant, expressive and multi layered, offering black cherry, red plum, sassafras and dead leaf flavors on a long and silky frame. The tannins are submerged, allowing the gorgeous flavors to float through the finish. Drink now through 2022. 1,500 cases made.", 60.0m, "2012", new Guid("500b3c5f-e0d3-4f95-b55b-02ec62e5b550") },
                    { new Guid("b7fa0ead-75c8-4337-8176-29d6b2661bc8"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sta. Rita Hills", "Pure, rich and vivid, with snappy wild berry, pomegranate, lavender, black tea and mint notes, ending with firm tannins and a stemmy edge, lending traction to the palate. Drink now through 2023. 1,226 cases made.", 40.0m, "2012", new Guid("083bd88f-99d8-4d3a-a1d2-30e82e94761a") },
                    { new Guid("a06516b5-727f-4778-bf46-f42e0f4e9087"), 3, new DateTime(2013, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Cancelli", "A rich, charming red, full of cherry, blackberry, spice and earth flavors. Leans toward a modern style, yet displays a touch of wild herbs and firm tannins, lending a sense of place. Sangiovese and Syrah. Drink now through 2020. 13,333 cases made.", 10.0m, "2011", new Guid("75614ef6-60f2-4f05-bd99-28f8713a123a") },
                    { new Guid("751b46a4-6917-48c9-9c52-c1b49a2698a9"), 6, new DateTime(2013, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Aubin La Chatenière", "This rich white is, balanced by vibrant acidity, offering lemon, apple and baking spice flavors. Shows dramatic tension between the richness and acidity, with a lingering aftertaste of lemon and stone. Best from 2015 through 2023. 375 cases made.", 48.0m, "2011", new Guid("69834995-7a25-44e0-ac73-d65ca2063d70") },
                    { new Guid("7fab04b6-2b33-4e2e-bf55-170ac32c1648"), 3, new DateTime(2017, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Garnacha Campo de Borja Veraton", "Expressive flavors of cherry, raspberry and dark chocolate mingle in this polished red, balanced by notes of licorice and wild herbs. The tannins are well-integrated, and balsamic acidity keeps this balanced. Dense and harmonious, in the modern style. Drink now through 2022. 3,000 cases made.", 30.0m, "2014", new Guid("8b84c854-9112-4a94-86b5-c9deaea4dc8a") },
                    { new Guid("0bf89ede-38b4-48cf-af8c-5576657bf90b"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Garnacha Campo de Borja", "This polished red shows graceful power. The black cherry and raspberry flavors are deepened by smoke and mineral notes, framed by firm, well-integrated tannins and kept lively by crisp acidity. Shows impressive harmony and focus. Drink now through 2018. 1,000 cases made.", 45.0m, "2010", new Guid("8b84c854-9112-4a94-86b5-c9deaea4dc8a") },
                    { new Guid("ac2e08a3-5c4f-447c-a215-a7d168a65d3d"), 3, new DateTime(2013, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Prapò", "Warm and generous, this red balances notes of plum, spice and leather with high-toned elements of cherry and flowers. A vibrant wine, with a long aftertaste bolstered by dusty tannins. Best from 2017 through 2032. 310 cases made.", 60.0m, "2009", new Guid("28328a26-fcb8-460f-949a-ea038838cb1a") },
                    { new Guid("1e3159d5-e982-4709-9402-cdfd1f1438eb"), 3, new DateTime(2013, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Swartland", "Densely packed, offering boysenberry, red currant paste, raspberry ganache and plum skin notes backed by accents of singed wood spice, licorice root and black tea. The roasted apple wood frame holds everything together. This should round into form soon enough.  Shiraz, Mourvèdre, Grenache and Cinsault. Best from 2014 through 2018. 1,000 cases made.", 42.0m, "2009", new Guid("f23eb6da-4727-436e-8ca1-9c1c7c3e356c") },
                    { new Guid("ccfde4e2-f4bb-437f-8497-b169f5dda074"), 3, new DateTime(2013, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Taurasi Radici Riserva", "Dark and polished, with fine tannins framing the complex mix of game, cherry reduction, cassis and mineral notes. Concentrated, delivering a long finish of tangy fruit and tar. Drink now through 2030. 5,000 cases made.", 75.0m, "2006", new Guid("e0c19152-7940-41d6-9ec8-854c6cc2f9be") },
                    { new Guid("2deb0e99-1eb1-46d5-8fd2-c8d82b3ba215"), 0, new DateTime(2013, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes de Provence Rosé Miraval", "Refined and elegant, offering pure and concentrated flavors of dried red berry, tangerine and melon. The focused finish features flint and spice notes, with a hint of cream. Drink now. 15,000 cases imported.", 28.0m, "2012", new Guid("0abca459-3a74-4bdb-91d4-c23fd7ae32e9") },
                    { new Guid("ec24daef-89f2-473e-a70c-93550a0b3a13"), 3, new DateTime(2013, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Valtellina Superiore Sassella", "Expressive, with a floral note, hints of aromatic anise and dried thyme, and ripe black raspberry and wild strawberry flavors. Shows fine-grained tannins and a smoky base note that pushes through on the fresh finish. Drink now through 2023. 3,600 cases made.", 30.0m, "2009", new Guid("5ce8a643-62f4-4bda-9425-295ca4444b4d") },
                    { new Guid("0855a949-b5a1-4a4e-b984-7a3fd015afee"), 6, new DateTime(2013, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Arbois Pupillin Vieilles Vignes", "Delivers toasted hazelnut and Brazil nut aromatics, followed by a ripe, well-stuffed core of pear, fig and melon flavors, revealing a lightly bracing edge through the ginger-tinged finish. A long stony echo takes over in the end. A beguiling and distinct expression of Chardonnay. Drink now through 2018. 15 cases imported.", 49.0m, "2009", new Guid("1c17113a-59c1-4235-b7f6-abaf3a22ece2") },
                    { new Guid("c63ba3cf-fc0a-47e0-b06a-a9f899f25f19"), 6, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sandstone Hemel-en-Aarde Valley", "A rich and piercing white, with lemon zest, fleur de del, chamomile and tarragon notes showing serious cut and drive through the rapier finish. A backdrop of lemon curd fills in the aftertaste. Gorgeous and distinctive. Sauvignon Blanc, Chardonnay and Sémillon. Drink now through 2017. 726 cases made.", 25.0m, "2008", new Guid("2c90be9a-fb37-439d-bf5a-998cfef36805") },
                    { new Guid("a1398b54-7e5f-4e1d-a20a-0267a4715862"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Naoussa", "A hint of orange peel to the lively dried raspberry, date and green fig flavors, which hang together with the support of vibrant acidity and medium-grained tannins. Complex and savory, with an alluring finish of sandalwood. Xinomavro. Drink now. 27,778 cases made.", 18.0m, "2009", new Guid("e6a7a37b-39af-4216-aab3-15d2471e1ae9") },
                    { new Guid("a416fd42-d6cd-442c-863e-4db33c225e36"), 3, new DateTime(2013, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Crozes-Hermitage Aléofane", "A briary, juicy style, with plum and blackberry fruit enlivened by savory, anise, alder and pepper notes. The energetic finish features solid grip for mid-term cellaring. Drink now through 2022. 56 cases imported.", 35.0m, "2010", new Guid("31c48a8b-c0ff-4688-84a2-d38a8239a0a1") },
                    { new Guid("ad8da5ee-ffa6-4af8-9981-e16127b8383d"), 6, new DateTime(2012, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Amontillado Jerez Los Arcos Solera Reserva", "A more flattering style, with an off-dry edge to the walnut paste, hazelnut, date and clove-studded orange notes, which show nice focus and zip on the finish. Drink now. 2,000 cases imported.", 16.0m, "NV", new Guid("3e3f6be8-f108-4fbb-a339-754a78bae1b3") }
                });

            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("378c0d4b-467f-40e1-a833-66e8ed16ba0f"), 6, new DateTime(1998, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Western Australia Omrah Unoaked", "Bright and sharply focused, this jazzy mouthful of citrus, pear and mineral flavors remains juicy and vivid through the delicate finish. Nice now, but it should fill out with cellaring. Best from 2000 through 2005. 5,000 cases made.", 16.0m, "1997", new Guid("7b57f2d4-2b00-45e0-9d96-1bd501b554c5") },
                    { new Guid("d0ce6bc5-90b3-4dcc-84eb-f9c310aed93e"), 3, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Maria Valley Clark & Telephone Vineyard", "Very dense and superconcentrated, this offers an impressive mix of dense blackberry, raspberry and huckleberry, holding its focus and maintaining its supple texture. Drink now through 2023. 3,475 cases made.", 44.0m, "2010", new Guid("8c3c70d4-22bd-4676-96fa-8584ca755215") },
                    { new Guid("54f9c65e-9189-4c6e-abe5-8c7b0ec4dc14"), 3, new DateTime(2013, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Inspiración Selección", "Fresh and focused, this sleek, lively red shows black cherry, wild berry, licorice, mineral and toasty flavors that glide over the palate, supported by well-integrated tannins and bright acidity. A graceful, modern style. Drink now through 2018. 5,000 cases made.", 20.0m, "2010", new Guid("aba58115-d32a-4e3e-b71c-82e4c67dd17b") },
                    { new Guid("8a9f6679-1b53-4902-b160-d6766e48fba3"), 3, new DateTime(2012, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir McMinnville Estate Cuvée", "Sumptuous and distinctive for its range of cherry, boysenberry, floral and dark chocolate flavors, dancing suavely through the long, expressive finish. The tannins are submerged, and this has miles to go. Drink now through 2019. 1,000 cases made.", 32.0m, "2009", new Guid("28ce2026-9f91-4ada-907f-62c753f25643") },
                    { new Guid("f0cf0365-ba2a-4af9-a8f6-1290b3145764"), 3, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Río Negro Valley", "Gorgeous from the start, delivering mulled spice, mocha, blueberry, black tea, plum sauce and warm currant confiture aromas and flavors all woven together, carried by a long spice- and graphite-filled finish. This has weight, but stays driven thanks to finely beaded acidity. A beauty. The best vintage yet for this wine. Malbec. Drink now through 2022. 275 cases made.", 105.0m, "2009", new Guid("426769eb-44aa-44f5-aa8b-c7d6caf394a4") },
                    { new Guid("611d6fd0-4524-4678-baa0-faa15adb5bf1"), 3, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Minervois La Livinière La Cantilène", "This displays rich, powerful aromas of ripe red fruit and olive, with plenty of muscle and sinew. The focused finish offers pepper notes, with savory herbal shadings and hints of graphite. Suave. Syrah, Grenache and Carignan. Drink now through 2014. 5,000 cases made.", 25.0m, "2008", new Guid("676dc7d1-8ce4-49be-944f-930bf15c3afe") },
                    { new Guid("f22c6e5a-1f6f-448b-bf93-3cadc9840456"), 6, new DateTime(2017, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Condrieu Pagus Luminis", "This has power, with a large core of creamed peach, nectarine and yellow apple fruit. Lengthy, pure and vibrant, with a mineral streak running underneath and chamomile and honeysuckle notes gilding the finish. Dreamy wine. Drink now through 2022. 1,200 cases made.", 69.0m, "2015", new Guid("abde2b31-5ba9-4a92-8d33-c1b868fb0340") },
                    { new Guid("e76ef78a-e675-40f3-9c66-a1b40cb2290f"), 3, new DateTime(2011, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Joseph Caroline Cuvée Prestige", "Deliciously ripe and focused, with mouthwatering cassis, blackberry and anise notes and a long, spice- and floral-filled finish. Moderately toasty, with well-integrated grip, this rock-solid red shows impressive latent length. Drink now through 2015. 2,800 cases made.", 45.0m, "2009", new Guid("abde2b31-5ba9-4a92-8d33-c1b868fb0340") },
                    { new Guid("1d827ae1-edc4-4fbd-89d9-d834fc697002"), 6, new DateTime(2011, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chassagne-Montrachet Les Chenevottes", "An enticing combination of peach, ripe apple, lime blossom and hazelnut aromas and flavors, this delicate and racy white leaves a mouthwatering impression on the finish, picking up a mineral note on the long aftertaste. Best from 2012 through 2023. 200 cases imported.", 65.0m, "2008", new Guid("646ba161-ea89-47f1-a4a7-119d9cd94a9d") },
                    { new Guid("88d3fdf4-e43a-47e5-81ec-88d8c96d38a8"), 3, new DateTime(2011, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cornas Empreintes", "A very fleshy Cornas, with layers of plum sauce, spice cake, fig bread and cassis rolling over one another, while the anise and iron notes hang in the background for now. The finish is strong and intense. Best from 2012 through 2020. 1,500 cases made.", 55.0m, "2009", new Guid("c5c6ff0b-57f8-40c0-a543-ce020f088430") },
                    { new Guid("1127e63b-c6db-405b-80bb-dfaf887aaecb"), 3, new DateTime(2011, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chinon Franc de Pied", "Rich and fleshy, with nice black tea, crushed plum and macerated black currant notes all gliding through the graphite-tinged finish. An iron note lingers as this fleshes out impressively in the glass. Drink now through 2015. 100 cases made.", 32.0m, "2009", new Guid("15550f4e-995b-4de8-bf41-7e313e8d3007") },
                    { new Guid("9004b001-f9b9-4850-9c16-97629d82734d"), 3, new DateTime(2011, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toro Gago", "Sleek and firm, this red delivers a firm core of cherry and berry flavors, with accents of smoke, cocoa, mineral and herb. More dense than broad, with lively underlying acidity. Drink now through 2018. 5,000 cases made.", 30.0m, "2007", new Guid("0fb63540-b63d-44a5-bb41-af0c8ec25f8b") },
                    { new Guid("6e970cb7-0eac-40fd-9f77-3acd4961caf4"), 3, new DateTime(2011, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Amarone della Valpolicella Classico Ambrosan", "Offers an aromatic mix of kirsch, dried roses, dried marjoram, blackberry candy, cardamom, fresh earth and smoke, with supple tannins and ripe acidity. There's power to this, but it's wrapped in a silky texture that results in an overall vibrant, stylish impression. A hint of espresso lingers on the finish. Drink now through 2024. 5,000 cases made.", 65.0m, "2004", new Guid("f2bf4b3d-4651-46ce-8a5b-f82048e7b8a5") },
                    { new Guid("ed271033-9dc9-4311-b92f-7d82b127aba1"), 3, new DateTime(2012, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Paso Robles Fracture", "Features amazing richness, focus and purity of flavor, presented on a beam of acidity. Offers great aromatics and flavors to match, with tiers of blackberry, mahogany, wild berry, raspberry and black licorice that form a tight, persistent core, ending with a pleasant loamy earthiness. Drink now through 2026. 866 cases made.", 70.0m, "2010", new Guid("fe392b3a-bb8a-4ab3-9728-736ca5997053") },
                    { new Guid("e4744311-bcce-428c-9154-dde78e035f72"), 6, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Federspiel Trocken Wachau Terrassen", "This features a supple texture, with flavors of ripe pineapple and apple tart accented by a rich creaminess. Ginger snap and white chocolate details show on the airy, elegant finish. Drink now through 2017. 17,000 cases made.", 14.0m, "2012", new Guid("d5b62d0e-bcbc-43f5-9461-a24a931853a7") },
                    { new Guid("f63d698a-7fd1-4e9d-8508-39e6ee468bb9"), 6, new DateTime(2011, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Smaragd Trocken Wachau Terrassen", "A racy style, with crisp peach and apple flavors that are generously spiced. The focused finish shows notes of sea salt and pepper. Fresh and vibrant. Drink now through 2020. 6,000 cases made.", 26.0m, "2009", new Guid("d5b62d0e-bcbc-43f5-9461-a24a931853a7") },
                    { new Guid("eed81400-529a-492f-ac44-9c282c2b6a72"), 3, new DateTime(2011, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vacqueyras Les 2 Monardes", "A rich, fleshy style, displaying lots of roasted chestnut, warm ganache and espresso notes, with a dense core of blackberry and steeped black currant fruit. Everything melds nicely on the smoldering finish. Serious Vacqueyras. Drink now through 2016. 2,500 cases made.", 26.0m, "2009", new Guid("508383a6-07b2-4a75-8d4c-bbe4a8a0b7eb") },
                    { new Guid("3b0dfecf-2ab9-414c-8de7-23b71d4f5e47"), 6, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Happy Canyon of Santa Barbara Sybarite", "Crisp, clean and smooth, with notes of mango, melon, peach and tangerine, that are vibrant and juicy, supported by a refreshing acidity and mineral details, with plenty of focus. Drink now. 3,416 cases made.", 21.0m, "2010", new Guid("2c226334-c3c3-43a3-81d8-c720866c8dd4") },
                    { new Guid("ba0dd831-e3c4-4213-8a0b-362ddf1b76b6"), 6, new DateTime(2011, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Qualitätswein Trocken Kremstal Undhof Kögl", "A rich, well-crafted white, with a smoky essence to the deep well of white peach, citrus and fresh-cut apple flavors. The firm finish is pure and sleek. Drink now through 2022. 2,000 cases made.", 28.0m, "2009", new Guid("9483fd3e-05e9-4671-91c4-66765c09e2cb") },
                    { new Guid("0f4104b6-5d69-40f8-84f8-93ea596287ee"), 3, new DateTime(2011, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Sondraia", "Already harmonious and supple, this red offers black cherry, raspberry and floral flavors, showing both intensity and finesse. Bright and focused, with refined tannins and a lingering aftertaste that echoes berry and spice. Cabernet Sauvignon, Merlot and Cabernet Franc. Best from 2014 through 2024. Tasted twice, with consistent notes. 5,000 cases made.", 35.0m, "2008", new Guid("fd725bf3-6cf7-4789-946d-bf8632bb3962") },
                    { new Guid("df7493a7-8f87-400b-9130-b770bb395cb9"), 6, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel Wehlener Sonnenuhr", "Vivid violet and black currant aromas lead to apricot, passion fruit and slate flavors in this sleek and flavorful Riesling. Though less intense on the finish, this lingers nicely. Best from 2013 through 2025. 500 cases made.", 30.0m, "2009", new Guid("e626cbde-ffb8-4ee7-9afd-984115297a9b") },
                    { new Guid("874e89f7-d302-406e-a878-36e466354165"), 5, new DateTime(2011, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Majeur", "There's a firm, chalky edge to this minerally Champagne, and mouthwatering flavors of candied lemon peel, green apple, crystallized honey and biscuit. This has an elegant texture and an overall sense of style. Drink now through 2020. 50,000 cases made.", 40.0m, "NV", new Guid("b69c4396-b082-415b-93c9-dfef8428cdde") },
                    { new Guid("93370f87-4167-468a-93f0-ee05d5c40bf2"), 3, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast Marcassin Vineyard", "Graceful, elegant and stylish, offering a pleasant touch of maturity, with a complex array of anise, cherry, raspberry, red licorice and fresh porcini flavors leading to a medium body. Shows the benefits of cellaring. Drink now through 2018. 960 cases made.", 125.0m, "2006", new Guid("7fee29b2-be52-4291-be10-874f56887996") },
                    { new Guid("ece63c40-b605-4ea3-a4aa-02c62ec9a0a0"), 6, new DateTime(2011, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Coast Marcassin Vineyard", "At once bold, rich and layered, with a measure of delicacy and finesse, unfolding to tiers of fig, melon, bubble gum, spice and honeysuckle. Finishes with a dash of cedary oak. Drink now through 2017. 725 cases made.", 125.0m, "2006", new Guid("7fee29b2-be52-4291-be10-874f56887996") },
                    { new Guid("a135fb00-4f7c-47f9-912a-814c6582f300"), 6, new DateTime(2015, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Finger Lakes Dry Argetsinger Vineyard", "Taut, lip-smacking acidity harnesses the core of Jonagold apple, heather, white peach and candied citrus peel notes in this ripe white, keeping this focused and energetic through the finish. Drink now through 2018. 285 cases made.", 30.0m, "2012", new Guid("b0309d84-eefe-421e-905f-422bff971c1f") },
                    { new Guid("a17d06ec-0b63-4c77-ab0d-0c50a3c8a81b"), 6, new DateTime(2013, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Federspiel Trocken Wachau Terrassen", "A plush, creamy style, with tropical fruit flavors of ripe pineapple and mango complemented by notes of peach cobbler and pear tart. Features a long, rich and spice-filled finish. Drink now through 2019. 9,000 cases made.", 13.0m, "2011", new Guid("d5b62d0e-bcbc-43f5-9461-a24a931853a7") },
                    { new Guid("a60f2ae7-0fe2-4ad5-bac4-2ffc77dbf793"), 3, new DateTime(2017, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Oublié Paso Robles", "Expressive and impressively structured, with savory blackberry aromas and accents of rosemary, thyme and crushed stone, opening to complex, densely layered flavors of currant, pepper and espresso. Grenache, Mourvèdre and Counoise. Best from 2018 through 2028. 1,500 cases made.", 80.0m, "2014", new Guid("fe392b3a-bb8a-4ab3-9728-736ca5997053") },
                    { new Guid("524625b7-7885-434d-901a-20cd67e080ee"), 3, new DateTime(2012, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Gavarini Chiniera", "Hints of coffee and sandalwood mark this rich yet racy red, whose core flavors are cherry, strawberry and underbrush. There's some muscle and density, firming up on the finish, with adequate sweet fruit for balance. Best from 2015 through 2030. 1,200 cases made.", 57.0m, "2008", new Guid("33b901d2-f9ad-424b-87f1-e32dc2dcf6c3") },
                    { new Guid("ebdc6ff6-e787-4938-b5b9-6ba5b906e67d"), 3, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley Peterson Family Vineyard", "Rich, layered and well-structured, with floral red currant aromas and complex flavors of black cherry, anise and toasty spice that lead to big but ripe tannins. Drink now through 2018. 1,028 cases made.", 56.0m, "2009", new Guid("f31d8045-9194-477c-8c1d-00dea9841d60") },
                    { new Guid("bfb373ea-22a8-4897-9ca7-3918c6fc8df8"), 6, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja White Conde de Valdemar Finca Alto Cantabria", "This expressive white shows pear, lanolin, almond and herbal flavors that are lean yet polished, with crisp acidity and a light tannic grip. A traditional style. Drink now through 2016. 10,000 cases made.", 15.0m, "2011", new Guid("aba58115-d32a-4e3e-b71c-82e4c67dd17b") },
                    { new Guid("d38311be-1646-4640-8004-aa1453160edd"), 6, new DateTime(2012, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Greco di Tufo Devon", "A suave white, with a creamy edge to the ripe melon, citrus and blanched almond notes that build with intensity, as the finish lingers with an aftertaste of underbrush, citrus zest and chalk. Drink now through 2016. 1,500 cases made.", 25.0m, "2011", new Guid("ec7d29db-3462-4264-bc94-6892a70cb6af") },
                    { new Guid("61dc625a-f38c-4969-8427-38b3c3c05e95"), 3, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera del Monferrato Maraia", "A sleek, racy style, this red boasts raspberry, boysenberry, graphite and spice flavors. Balanced and focused, with lingering echoes of fruit, mineral and spice. Drink now through 2024. 10,000 cases made, 7,800 cases imported.", 13.0m, "2017", new Guid("b6cc7def-f9e1-41a5-b0d4-8e39cfbbb8a7") },
                    { new Guid("afac5bf1-b2b1-4d20-b713-560a499f352c"), 3, new DateTime(2012, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera del Monferrato Maràia", "A dark, succulent red, exuding black cherry, blackberry and violet aromas and flavors. Pure and focused, with a long aftertaste of sweet fruit, flowers and spice. Drink now through 2016. 4,250 cases imported.", 12.0m, "2011", new Guid("b6cc7def-f9e1-41a5-b0d4-8e39cfbbb8a7") },
                    { new Guid("a58d69b2-730a-4953-a270-943b044d04e8"), 6, new DateTime(2012, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grüner Veltliner Qualitätswein Trocken Wachau Hefeabzug", "This powerful, plush-tasting Grüner boasts concentrated flavors of apple tart, baked peach, ruby grapefruit and gooseberry. Unctuous in the midpalate, with a lingering finish of smoke and spice notes. Very expressive. Drink now through 2020. 879 cases imported.", 29.0m, "2010", new Guid("48b7bb8b-62d8-40cc-bc61-9392f4ec5fff") },
                    { new Guid("a42fb466-7341-4952-a917-8c76ca47d402"), 0, new DateTime(2012, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tavel Roc-Epine", "This offers a very tasty mix of red and black cherry fruit, along with muddled strawberry, light spice and tea shadings. A flash of tobacco marks the full-bodied finish. Solid Tavel. Drink now. 30,000 cases made.", 17.0m, "2011", new Guid("9f048115-09fb-4d60-98fa-c9678594e5cc") },
                    { new Guid("182c10b7-81ae-47fa-81bf-7538c4dc0ec3"), 3, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Colchagua Valley Antu", "This dark, ripe red delivers jammy layered flavors of plum paste, fig and kirsch. Full-bodied, with well-embedded acidity and fine tannins, joined by hints of spice box, flint and olive on the long, driving finish. Drink now through 2015. 6,000 cases made.", 19.0m, "2009", new Guid("f22503e1-eacb-467d-a583-9ab5759041b4") },
                    { new Guid("12bdd7b6-b55f-4675-8364-03e7eb6d4742"), 3, new DateTime(2013, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Marlborough Province", "Features intoxicating aromatics of orange zest, dried lavender and fresh, peppery earth notes, with a succulent, juicy core of raspberry and strawberry. Supple in texture, with the flavors lingering on the fresh, juicy finish. Drink now through 2022. 500 cases imported.", 28.0m, "2010", new Guid("a490f613-725f-4fec-b6d0-13174d3829cd") },
                    { new Guid("320d7b45-b684-4bd9-9206-8671ce84e829"), 6, new DateTime(2012, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Province", "This bright, juicy white delivers succulent notes of passion fruit, honeydew melon, Key lime and Asian pear, which together make a clear, focused and intense chorus that persists on the long finish. Drink now. 27,000 cases made.", 23.0m, "2011", new Guid("a490f613-725f-4fec-b6d0-13174d3829cd") },
                    { new Guid("8bc5a9bb-7cfc-49e8-9b39-4b8783269468"), 6, new DateTime(2012, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Eden Valley Dry", "Vibrant, bracing and compelling, this white aims pear, pineapple and lime flavors straight to the palate. Persists impressively on the finish. Drink now through 2018. 1,600 cases imported.", 18.0m, "2011", new Guid("0ba7c944-ad40-414f-9e69-9138d4c42021") },
                    { new Guid("c30df919-21eb-41c6-9a5b-2293a6a96acb"), 6, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Savoie Apremont Cuvée Gastronomie", "A structured, complex and intense-tasting white, with flavors of white cherry, quince, mineral, beeswax and hints of tarragon. A smoky essence runs through as well, which carries into to the finish, with notes of dried basil. Jacquère. Best from 2013 through 2021. 4,000 cases made.", 17.0m, "2011", new Guid("a20afcae-2c6d-4434-a9dc-fbbcdfd4cc6d") },
                    { new Guid("c08238ae-7ce3-4b13-8592-8dcaa7b5fbc7"), 3, new DateTime(2012, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Almansa La Atalaya", "A very aromatic red, offering floral and spice overtones to the flavors of cassis, ripe cherry, damson plum and smoky mineral. Firm tannins structure this muscular red, but it's balanced, and should show more with time. Garnacha Tintorera and Monastrell. Best from 2014 through 2019. 10,000 cases made.", 16.0m, "2009", new Guid("b4b7fd66-9ed0-4a82-8954-8f87932a21df") },
                    { new Guid("bdb273c7-94fe-4001-bc5a-280a1bb7c984"), 3, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vigneti delle Dolomiti Red Alta Luna Phases", "Base notes of chocolate and coffee liqueur enrich smoke, tea leaf, roasted plum and black currant candy flavors in this well-honed red, while a supple juiciness and a hint of dried mint linger on the finish. Teroldego, Lagrein and Merlot. Drink now through 2018. 6,500 cases imported.", 14.0m, "2009", new Guid("c5088d6f-9845-4a0a-8cb7-ccaa20119298") },
                    { new Guid("0e413022-8939-4eed-8ec9-3ec591b52e71"), 3, new DateTime(2014, 1, 1, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vino Nobile di Montepulciano Incanto", "Rich and rangy, this black cherry, plum-, tar- and spice-infused red is backed by a base of beefy tannins. Balanced overall, with a long finish of earth and spice. Best from 2016 through 2027. 22,000 cases made.", 15.0m, "2010", new Guid("6f66f339-2473-4b14-9647-4778f755a749") },
                    { new Guid("39b375cb-1953-421a-b22e-aefef7c1d14e"), 3, new DateTime(2012, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vino Nobile di Montepulciano Leone d'Oro", "This red is meaty and graphite-tinged, offering sweet cherry, licorice and spice flavors. Balanced, with dense tannins and a long, tar- and spice-filled aftertaste. Best from 2014 through 2028. 2,000 cases imported.", 24.0m, "2009", new Guid("6f66f339-2473-4b14-9647-4778f755a749") },
                    { new Guid("cac7c729-c6b5-41e5-8c9e-5b11bd525097"), 6, new DateTime(2012, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vouvray Marie de Beauregard", "This has weight and depth, but remains stylish, with ginger and glazed pear notes in reserve while persimmon, green almond and piecrust notes lead the way. Lovely cut on the finish keeps the ginger edge echoing. Should develop nicely in the cellar. Drink now through 2020. 1,200 cases imported.", 20.0m, "2010", new Guid("f67ee4e1-a829-4b9f-9139-979a815685e4") },
                    { new Guid("bf8fd482-0509-44b9-8d6e-e594c408fe8a"), 3, new DateTime(2019, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Cahors Château de Mercuès", "A minerally undertone gives depth to the raspberry reduction and red plum compote flavors of this concentrated, focused red, with infusions of floral, tea and herb on the plush finish. Drink now through 2024. 8,500 cases made, 1,500 cases imported.", 25.0m, "2016", new Guid("e34de01b-891e-482b-b03e-e2c3071fe1e8") },
                    { new Guid("6e0ff845-4534-4ebc-a4f6-e4c64c44f4c8"), 3, new DateTime(2012, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Cahors Pigmentum", "A ripe and luscious red, with rich flavors of dark cherry and dark chocolate that echo with plenty of spiciness. Features a sculpted finish of cocoa powder and raspberry. Drink now through 2018. 15,000 cases made.", 12.0m, "2010", new Guid("e34de01b-891e-482b-b03e-e2c3071fe1e8") },
                    { new Guid("80bb7d1e-7e6b-42f3-99c1-f519c3855655"), 0, new DateTime(2012, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rosé Columbia Valley", "Lively, refreshing and vibrant, with mulberry and pear flavors that show a leafy hint, all on a silky texture. Syrah. Drink now. 12,000 cases made.", 11.0m, "2011", new Guid("dab3a371-df0a-4d60-b687-aad7e7d38eb1") },
                    { new Guid("eb9beed7-d757-4bd8-9596-6cf1caa1a2b0"), 3, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti", "Vibrant and rich, this harmonious red offers cherry, strawberry and floral notes. Picks up some of the typical underbrush and tobacco accents on the lingering finish. Drink now through 2018. 25,000 cases imported.", 9.0m, "2010", new Guid("a33665d1-1aac-4174-bf4e-c20fe4c1d850") },
                    { new Guid("be8cfa93-4d07-4bd0-941d-2dd09b67570d"), 3, new DateTime(2012, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Paso Robles Steinbeck Vineyard", "Pure, rich layers of blackberry and wild fruit are intense and focused, firming with a crushed rock minerality and a long, persistent finish. Drink now through 2022. 1,459 cases made.", 24.0m, "2010", new Guid("55effea5-ead8-4885-b9d0-be140c721185") },
                    { new Guid("dbd3e0a3-e3f8-4f28-8e6c-976764351ec6"), 3, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Rentas de Fincas Reserva", "This red delivers intense flavors in a plush texture. Cherry, berry and spice notes mingle over pillowy yet supportive tannins, as bright acidity keeps this focused and fresh. Still youthful, with plenty of stuffing to age. Drink now through 2018. 2,000 cases imported.", 18.0m, "2005", new Guid("437fb4c0-f2e5-4a2b-819f-b77691b37744") },
                    { new Guid("ff6e8a8b-7e9d-498d-a92a-593f6d0eb83a"), 3, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montsant Blau", "Dense yet lively, this expressive red shows morello cherry, floral, licorice and mineral flavors supported by light, firm tannins and fresh acidity. Mountain herb and smoke notes keep this from turning candied, but the sweet fruit lingers nicely. Drink now through 2016. 6,500 cases made.", 12.0m, "2009", new Guid("10c8bc76-7cb7-4fee-94d2-5ec64ec8d9c2") },
                    { new Guid("28029691-9796-4b17-9941-12a5a29012b6"), 3, new DateTime(2012, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Paso Robles Hope Family Vineyard", "This red exhibits pure, rich, ripe and concentrated layers of blackberry, boysenberry and black licorice, with tannins that give the flavors depth and traction. Drink now through 2022. 1,175 cases made.", 42.0m, "2010", new Guid("d0e547f5-9908-46e8-af72-ac0194537c33") },
                    { new Guid("e7507d45-a528-4607-a7f9-2043a73cbada"), 3, new DateTime(2019, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Manoella", "Zesty currant and boysenberry notes are tangy and inviting in this spicy, medium-bodied red, with moderate tannins supporting the herb, tea and mineral details on the finish. Touriga Nacional, Touriga Franca, Tinta Roriz and Touriga Francesa. Drink now through 2023. 2,000 cases made, 250 cases imported.", 22.0m, "2016", new Guid("42fdc237-462f-494f-b0db-287a7ab55060") },
                    { new Guid("dfcd99e3-e610-4e23-af02-37fbe5203356"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Les Terrasses Velles Vinyes", "A light texture carries racy, intense flavors in this expressive red, with bright cherry, licorice, mineral, <i>garrigue</i> and spice notes mingling over light tannins and buoyed by lively acidity. Balanced, focused and elegant, with a lingering finish. Drink now through 2021. 12,000 cases made.", 40.0m, "2011", new Guid("91e8215a-2f4b-4acf-877c-563a40c103b2") },
                    { new Guid("2139a1c7-98bc-4015-9eec-a1c55360b352"), 3, new DateTime(1999, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Les Terrasses", "Plenty of ripe, fresh fruit in this red, with flavors of black cherries, blackberries and plums, along with accents of toast and coffee. Yet it remains smooth and graceful on the palate, with well-integrated tannins and a long, fruity finish. Drink now through 2005. 9,500 cases made.", 25.0m, "1996", new Guid("91e8215a-2f4b-4acf-877c-563a40c103b2") },
                    { new Guid("ab4f83a3-9f70-469c-a255-78d473f764b0"), 3, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Eastside Road Neighbors", "This gorgeous wine is rich and supple, displaying ripe, plummy black cherry and blackberry fruit that's intense and spicy, pure and focused, with a long, pure, lingering finish. Drink now through 2020. 584 cases made.", 49.0m, "2009", new Guid("6378e53c-faba-44fe-89f1-0d776a53dd01") },
                    { new Guid("a43468f3-fedc-4270-9242-6577130953d8"), 3, new DateTime(1992, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley", "Just delicious; a lovely layering of currant, berry, vanilla and cocoa butter aromas and flavors, all resting on a silky texture that spins out the flavors on a long finish. A seductive wine to drink now through 1996. 501 cases made.", 20.0m, "1990", new Guid("6378e53c-faba-44fe-89f1-0d776a53dd01") },
                    { new Guid("29146857-016f-4059-980b-0cc569817e7e"), 3, new DateTime(1990, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma Coast", "Remarkable for its rich, deep, complex chocolate, cherry, toast and vanilla flavors that are smooth and concentrated, backed with toasty oak and firm, supple tannins. It glides across the palate. Elegant and stylish, delicious to drink now, but should improve through 1993. 597 cases made.", 18.0m, "1988", new Guid("6378e53c-faba-44fe-89f1-0d776a53dd01") },
                    { new Guid("d06b37bc-e417-4dab-95a5-df16c0b38229"), 3, new DateTime(1989, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Allen Vineyard", "A remarkably rich and complex wine, with layers of black cherry, raspberry, chocolate and vanilla flavors that are very harmonious and elegant, finishing with subtley and delicacy. Has the tannin to age three to five years, but it's hard to wait.  ", 20.0m, "1987", new Guid("6378e53c-faba-44fe-89f1-0d776a53dd01") },
                    { new Guid("fbb8747a-8d5c-49ad-9eca-393912440c25"), 3, new DateTime(2004, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Masseto", "Wonderful aromas of blackberries, raspberries and fresh tobacco, with a hint of exotic fruit. Full-bodied, with loads of velvety tannins and a long, long finish. Very silky indeed. A gorgeous, seductive, classy red. A monument to Tuscany. Layers and layers of fruit and tannins. Perhaps the greatest Tuscan red ever. Best after 2012. 2,670 cases made.", 250.0m, "2001", new Guid("825508eb-f419-4cb1-ae33-22ac2c7d34f4") },
                    { new Guid("bd36029d-fd3f-4258-b81b-d1ac85cbbe85"), 6, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Toscana Poggio alla Gazze", "Aromas of peach, flint, gunpowder and ripe fruit come from this full-bodied yet reserved Tuscan white, with fine acidity and a long, long, flavorful finish. A Sauvignon with finesse, like a fine Pouilly-Fumé. Drink now. 6,665 cases made.", 25.0m, "2000", new Guid("825508eb-f419-4cb1-ae33-22ac2c7d34f4") },
                    { new Guid("5fbe0ebb-2648-4f8b-82a5-097628b17733"), 3, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Superiore Ornellaia", "A cool and sleek Tuscan red, with tarragon and fresh sage aromas, blackberry and cherry undertones. Full-bodied, with a wonderfully silky palate of fine tannins and a long, fruity finish. Very sophisticated and reserved. Best after 2004. 13,330 cases made.", 110.0m, "1998", new Guid("825508eb-f419-4cb1-ae33-22ac2c7d34f4") },
                    { new Guid("ff2e7ce8-7be8-4fde-a9ed-3da3bd80970e"), 3, new DateTime(2000, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Ornellaia", "This Tuscan gem is black in color, with complex aromas of black currants, minerals and wet earth. Full- bodied, with lots of tannins, but very polished. Long, long finish. Super well-made—a great Ornellaia. Best after 2004. 11,415 cases made.", 100.0m, "1997", new Guid("825508eb-f419-4cb1-ae33-22ac2c7d34f4") },
                    { new Guid("a9249fd9-add5-47e6-960f-53cdd69a8c67"), 3, new DateTime(1995, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Masseto", "Better than the 1990 or '91 Masseto. Deep and profound Merlot exhibiting intense berry, mint and cassis aromas and flavors. Full-bodied but very elegant and refined; wonderful silky tannins and succulent finish. Drink now.  ", 59.0m, "1992", new Guid("825508eb-f419-4cb1-ae33-22ac2c7d34f4") },
                    { new Guid("c27d2fd5-76f9-46a8-b8ad-89fb82f6bbf0"), 3, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ornellaia", "An outstanding discovery. Ripe and tannic, with lots of tobacco flavor to add depth to the raisin and cherry flavors. Supple, harmonious and complex, a bit tough, built like a claret for long-term aging. Try it in 1993 to see how it is developing. 1,000 cases made.", 25.0m, "1986", new Guid("825508eb-f419-4cb1-ae33-22ac2c7d34f4") },
                    { new Guid("b9639a39-40bf-42fb-990b-1ba84f86cc39"), 6, new DateTime(1989, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Kumeu", "  ", 29.0m, "1987", new Guid("65ea8330-092b-476a-a0e9-f1a1d6dc7c27") },
                    { new Guid("e0460a56-8193-461f-b56b-d7546cfce7ad"), 6, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Vaillons", "Ripe and generous, oozing butterscotch aromas and flavors to go with a core of lemon and pear flavor, all of it resting on a firm texture that allows some lovely honey and toast nuances to emerge on the finish. Drinkable now, but this one has a long future. Try 1993 to '96.  ", 20.0m, "1988", new Guid("af0e6972-ce08-4bc7-a42d-b8bebcccaafb") },
                    { new Guid("949d6b0a-6933-4e50-a40e-b829629bcd68"), 6, new DateTime(1991, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley", "Silky, complex and very Burgundian in style. Smells very toasty and buttery, with rich, almost unctuous pear, butterscotch and spice flavors accented by hazelnut, honey and cream notes. The flavors linger a long time on the finish.  1,553 cases made.", 19.0m, "1990", new Guid("d7a6c2d1-2fbf-4553-98aa-e9ceae9c6858") },
                    { new Guid("51ce8b40-002b-4664-8110-b52421e45c12"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Charbonneau Red Walla Walla County", "A very seductive, supple, flavorful Washington red. Spicy oak wraps around a solid core of ripe cherry, currant, and herb flavors. The tannins are abundant, but soft. Tempting now, but could hold until 1997 or beyond. 59 percent Merlot and 41 percent Cabernet Sauvignon. 404 cases made.", 26.0m, "1988", new Guid("d7a6c2d1-2fbf-4553-98aa-e9ceae9c6858") },
                    { new Guid("d027b672-7bd9-4ece-8152-1c4ef0a6a629"), 3, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley", "The first sniff reveals a world-class Cabernet. Remarkably concentrated, deep and supple, loaded with luxuriant cherry and currant flavors graced by luscious chocolate and vanilla overtones. This wine already has great complexity and harmony. Should age well at least through the mid-1990s.  ", 19.0m, "1986", new Guid("d7a6c2d1-2fbf-4553-98aa-e9ceae9c6858") },
                    { new Guid("838bc016-070b-4539-afb0-e5865d0730e7"), 5, new DateTime(1993, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne R de Ruinart", "Rich and earthy with complex pear, toast and honeyed flavors that taste mature, picking up carmel and earth flavors that offer uncommon depth and richness. Delicious now. 2,000 cases made.", 37.0m, "NV", new Guid("da515c80-5124-49b3-91ca-de4360e1fe7e") },
                    { new Guid("4cc86c57-4588-4ca7-a2e6-fb61bd50931d"), 5, new DateTime(1989, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Blanc de Blancs Champagne Dom Ruinart", "Extremely rich and toasty, deep and concentrated; intriguing, with layers of tightly wound vanilla, pear and spicy smoke notes in a very deliberate style. Its deep concentration of fruit makes it a good bet to improve until 1994. A fine, if pricey, Champagne for the cellar.  ", 61.0m, "1982", new Guid("da515c80-5124-49b3-91ca-de4360e1fe7e") },
                    { new Guid("6982f8cb-3f8f-490e-991e-62c9f41092b8"), 3, new DateTime(2005, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Relentless Napa Valley", "A tremendous wine from the start, with captivating aromas of rich currant, beef, blackberry and wild berry. Tightly focused and impeccably balanced, this offers wonderful complexity and depth with a long, rich, intense finish. Syrah and Petite Sirah. Drink now through 2010. 2,500 cases made.", 62.0m, "2002", new Guid("dd6a1692-275c-4842-ae40-055369384e9b") },
                    { new Guid("5d9550d5-2560-42bc-86d2-1692b99fc27d"), 6, new DateTime(1996, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Carneros Red Shoulder Ranch", "Big, ripe, intense and juicy, loaded with rich, complex flavors. The echoes of ripe pear, anise, fig and spice are cast nicely in a full-bodied style. Yet for all its size and depth, it maintains a sense of elegance and finesse right through the long finish. 3,500 cases made.", 23.0m, "1994", new Guid("dd6a1692-275c-4842-ae40-055369384e9b") },
                    { new Guid("a6a6411c-a540-4159-9dcd-482d18a329c3"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Stags Leap District Hillside Select", "Sleek and elegant, with appealing black cherry, raspberry, spice and toast shadings. Offers a supple texture, with bright, ripe flavors and good length. Tempting now for its elegance and finesse, but it has the stuffing to age through another decade. 2,000 cases made.", 45.0m, "1991", new Guid("dd6a1692-275c-4842-ae40-055369384e9b") },
                    { new Guid("77438904-4380-4502-95f4-7ea7f5969021"), 6, new DateTime(1993, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Barrel Select", "Youthful, tight and focused, with spicy pear, nutmeg and melon flavors and pretty oak shadings. An impeccably well-balanced wine that allows the ripe fruit to play off the toasty oak and stay together through the finish. Drinkable now, but may benefit from cellaring. 6,000 cases made.", 15.0m, "1991", new Guid("dd6a1692-275c-4842-ae40-055369384e9b") },
                    { new Guid("b22bd43e-2be5-4d58-9373-6257db5fb00e"), 3, new DateTime(1989, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Stags Leap District", "Elegant and polished, but plenty of tannin to carry the cedary, tobacco-tinged Cabernet flavors of currant and plum through to a long finish. Great depth and finesse now, so imagine what it will be like in 1995. 5,000 cases made.", 20.0m, "1986", new Guid("dd6a1692-275c-4842-ae40-055369384e9b") },
                    { new Guid("b57dd7f7-201b-4218-93c8-15e5caae4336"), 3, new DateTime(1998, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Washington Canoe Ridge Vineyard Artist Series #4", "A smooth and silky Washington Cab, its dark plum, blackberry and vanilla flavors tumbling over the palate, finishing with sweet ripeness wrapped in polished, fine-grained tannins. Folds some pretty oak into the mix on the aftertaste. Delicious now. Best from 1999 through 2005. 2,340 cases made.", 28.0m, "1995", new Guid("d7a6c2d1-2fbf-4553-98aa-e9ceae9c6858") },
                    { new Guid("92a639f2-220d-47c8-ba6c-57f8dea3ba4a"), 6, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Edna Valley Wolff Vineyard", "Ripe and intense, with rich, perfumed apple, pear and spice flavors that pick up a note of butterscotch from oak. Turns elegant and stylish on the finish, showing a hint of lemon and citrus. A great value. Drink now through 2010. 7,532 cases made.", 17.0m, "2004", new Guid("6fc999e3-71db-49d6-8449-d6de7a391b3a") },
                    { new Guid("97893834-ac55-44eb-ad42-23f4b36d585e"), 6, new DateTime(1995, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay New Zealand", "Smooth, round and spicy, a mouthful of generous apple, tropical fruit, vanilla and citrus flavors singing harmoniously on the finish - this tells a story in the glass. Best from 1996 through 1997. 3,000 cases made.", 27.0m, "1993", new Guid("65ea8330-092b-476a-a0e9-f1a1d6dc7c27") },
                    { new Guid("6c44fb05-19b8-4612-9c93-44b1ed02a4b6"), 3, new DateTime(1989, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie", "Great concentration and firm structure carry the beautiful violet-tinged bl ack cherry and plum fruit, which turns spicy and bit leathery on the finish . Tannic and dense but not heavy-handed. Drink now through 2000.  ", 31.0m, "1986", new Guid("8a9b27aa-8277-49f4-97c9-09a656b5bdc0") },
                    { new Guid("3ac18643-77df-4966-bd29-1179b89a63f7"), 3, new DateTime(1990, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Les Fleurs du Chaparral Napa Valley", "A terrific California novelty. With earthy flavors and aromas reminiscent of a good northern Rhône, it's big and luscious, with a core of plum and blackberry flavors and attractive accents of game and smoke. This is a well-balanced, beautifully structured wine. The tannins are firm but fine. It's tempting now, but should improve through 1993. 105 cases made.", 15.0m, "1987", new Guid("bff73943-bee3-4f84-b671-dd05df2d7dd6") },
                    { new Guid("b05daa89-c26e-4a18-b2e9-6237bd553287"), 3, new DateTime(1989, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Sonoma County", "Beautifully concentrated and long, with enough fine tannin to hold it for years in the cellar. After a touch of earthiness on the nose, blueberry, plum and anise aromas and flavors burst over the palate. Should be an elegant wine by 1992-93.  ", 12.0m, "1986", new Guid("bff73943-bee3-4f84-b671-dd05df2d7dd6") },
                    { new Guid("4a326ea0-5f9b-48f6-9505-c2dd96c00e75"), 3, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie Château d'Ampuis", "Rock-solid, offering dark currant and blackberry paste flavors that race along, thanks to well-embedded graphite and charcoal notes. The finish lets warm fruitcake, plum skin and singed juniper details fill in, with plenty of grip. Mouthwatering acidity and a long echo of sweet tapenade keep this racy and defined. Best from 2015 through 2035. 2,200 cases made.", 206.0m, "2010", new Guid("e2f0449c-ad7a-4925-a2c0-b6b36c086150") },
                    { new Guid("9cb80bf0-1994-4c6a-8873-1dba7d51db0c"), 3, new DateTime(1994, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie La Mouline", "No gimmicks here, just intense ripe fruit piled on top of ripe raspberry flavor. Rich and seductive, firm and balanced. A pedigreed Rhône for the cellar. Drink now through 2008. 500 cases made.", 135.0m, "1990", new Guid("e2f0449c-ad7a-4925-a2c0-b6b36c086150") },
                    { new Guid("42dca62a-25d2-4ec1-8ec6-569cc4c849fc"), 3, new DateTime(1992, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie La Landonne", "A dramatic, classic wine, with a rough, youthful edge. Combines deep, rich, penetrating fruit nuances that echo classic Côte-Rôtie roasted flavors of currant, plum and earthy, leathery notes enhanced by spice and pepper. The tannins are firm and tight. C 700 cases made.", 150.0m, "1988", new Guid("e2f0449c-ad7a-4925-a2c0-b6b36c086150") },
                    { new Guid("5bdbcce5-2b69-4864-9000-75b1a3cdb569"), 3, new DateTime(1990, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Hermitage", "Very firm and elegant, with a burst of raspberry and black pepper flavors that just go on forever on the finish. Soft tannins and a touch of new oak frame the wine beautifully, and with time it ought to emerge gorgeously. Drink starting 1993.  ", 34.0m, "1986", new Guid("e2f0449c-ad7a-4925-a2c0-b6b36c086150") },
                    { new Guid("003e864e-5fc1-47bf-a6cf-3f679c48a833"), 3, new DateTime(1990, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie La Turque", "A concentrated, powerful, flavorful wine, just oozing with plum, berry and spice aromas and flavors that are long and sweet on the finish. There 's lots of complexity here already, but it's so tight that it could keep developingwell into the next century 250 cases made.", 100.0m, "1986", new Guid("e2f0449c-ad7a-4925-a2c0-b6b36c086150") },
                    { new Guid("3ca4ff0f-c37a-4df2-bd82-da47d34e9807"), 3, new DateTime(1989, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie Brune et Blonde", "Full and generous, a peppery, spicy wine that's sweet with the scents of vanilla and black cherry. Long, silky and rich, with the structure and tannic bite to age. Should improve even more in the bottle. Drink now to 2000.  ", 30.0m, "1985", new Guid("e2f0449c-ad7a-4925-a2c0-b6b36c086150") },
                    { new Guid("ebd4b797-840d-488a-93a6-e7ddddffe8b0"), 3, new DateTime(1989, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Beaune Clos des Fèves", "Rich, almost chocolaty on the nose, turning bright, berryish and satiny on the palate, with a lean texture but deep flavors; it's focused and long. Drink now through 1994.  ", 25.0m, "1985", new Guid("5664f809-5726-4e4d-85a3-01e5d56c1ff4") },
                    { new Guid("bc2a5cf9-eeb0-4ab5-94d3-9977526b0149"), 3, new DateTime(1995, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Sonoma Valley", "Perennially one of California's tip-top Merlots. Dark in color, with lots of rich currant, herb, tobacco and buttery oak, but it retains a sense of elegance, even for such substantial weight and flavor. 6,700 cases made.", 30.0m, "1992", new Guid("b9345d6e-f8a4-4ab0-863e-1b3054ea296c") },
                    { new Guid("f398ea0d-c127-4b0a-aedc-9680c52fd8eb"), 6, new DateTime(2009, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Kumeu Estate", "Bright, tangy and elegant, this vibrant wine has grapefruit and pineapple flavors at the fore, picking up apple and spice as the finish lingers effortlessly. Drink now through 2017. 7,000 cases imported.", 35.0m, "2007", new Guid("65ea8330-092b-476a-a0e9-f1a1d6dc7c27") },
                    { new Guid("91a6129c-babd-43ab-8963-f5be31c372dd"), 3, new DateTime(1989, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Sonoma County", "Enormously rich, tannic and complex, with layers of concentrated cedar, plum, cherry and herb flavors framed by firm tannins and oak. Gets better with every sip. Drink now.  ", 20.0m, "1986", new Guid("b9345d6e-f8a4-4ab0-863e-1b3054ea296c") },
                    { new Guid("68e05974-d9d6-4342-a88a-c3d154b70b9e"), 6, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Yakima Valley Barrel Fermented", "With its lovely peach-vanilla aromas and flavors, this strikes an extraordinary balance between fruit and oak, all of which is supported firmly and harmoniously. Beautiful to drink now.  ", 10.0m, "1987", new Guid("5e10bd81-2d1f-4756-b555-0c8a31149af4") },
                    { new Guid("00435ad6-9210-45f9-b07a-587c0a263c40"), 6, new DateTime(1992, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Triere Vineyard", "A very exotic wine that emphasizes the nutmeg and vanilla flavors of oak aging and backs it up with buttery pear accents. Soft and delicate in texture, but full in flavor, with complexities that develop on the finish. 16,500 cases made.", 15.0m, "1990", new Guid("44999d1d-5f8c-4d9d-8d14-764fae55684c") },
                    { new Guid("0d0a430e-d906-4662-917d-19e370a7fbdd"), 3, new DateTime(1989, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Vintage Selection", "After showing minty, spicy aromas this settles into rich, mouthfilling currant and cherry flavors, smooth, supple and generous right through the finish. Delicious and drinkable.  ", 24.0m, "1985", new Guid("44999d1d-5f8c-4d9d-8d14-764fae55684c") },
                    { new Guid("55e01c1b-e2d7-49d0-9b99-ca089d0a3cb7"), 3, new DateTime(1989, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gigondas", "Has beautifully defined plum, black cherry and raspberry flavor that's rich, ripe and deep. The vibrant fruit echoes long and full on the palate. Tannins are fine and integraged. Gigondas doesn't get much better. Drink now through 1997.  ", 15.0m, "1986", new Guid("7063db25-aa1d-4f30-83a5-812e068e13df") },
                    { new Guid("33820bae-7a62-4c10-a002-e5bde44644c9"), 6, new DateTime(1989, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay California Select", "Lean, rich, crisp and flavorful with ripe fruit, layers of pear, spice, clove and nectarine notes that keep you coming back for another sip-and finding more complexity. Elegant and subtle, with a lingering finish. A new line for Hess. Drink now through 1994. 7,700 cases made.", 9.0m, "1988", new Guid("b98ee3aa-d1ac-49ce-aa9f-efb9f9ab3cbf") },
                    { new Guid("368e801d-ae92-4ddd-b329-2b225c32ffc4"), 6, new DateTime(1992, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer California", "An intensely flavorful, full-bodied, Alsatian-style wine, with layers of heady fruit, spice and mineral flavors that linger on the long finish. Dry in sugar, but rich in fruit and body, this is smooth in texture and very distinctive. 1,600 cases made.", 11.0m, "1991", new Guid("b5633250-73fe-4980-8db6-c131b89361c0") },
                    { new Guid("d33fb104-c91f-4c25-8c58-8781b3231daa"), 3, new DateTime(1989, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Insignia Napa Valley", "Extremely herbal and earthy, very rich, supple and almost unctuous, but loaded with olive, spice, currant and plum flavors. Long and complex, but so distinctive that not everyone will love it. 4,700 cases made.", 52.0m, "1985", new Guid("b5633250-73fe-4980-8db6-c131b89361c0") },
                    { new Guid("367bcb66-7a53-408e-98c1-200c61053881"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay Frémiets", "Beautiful, vivid and ripe, sporting a gorgeous core of plum and red berry flavors and silky texture. Tannins creep up on the finish, so should need until 2000 to show it all.  ", 42.0m, "1993", new Guid("53c6e4ed-9609-45fd-a4f8-0f33014a3aad") },
                    { new Guid("0ab60722-5bbf-40dd-b272-f97c449b18a9"), 6, new DateTime(1990, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet", "Incredibly rich and toasty, with butterscotch and oak, but has the fruit integrity to stand up to the flavors imparted by heavily toasted barrels. It's big and full-bodied, with pear and tangerine notes and generous acidity. Drink now through 1994. 3,000 cases made.", 33.0m, "1987", new Guid("53c6e4ed-9609-45fd-a4f8-0f33014a3aad") },
                    { new Guid("f993e7d9-1aac-4d74-a176-718fca3d32ec"), 6, new DateTime(1989, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Les Chalumaux", "Gorgeous Puligny with richness and complexity, well-honed honey, toast, pear and spicy nutmeg flavors and a silky-smooth texture and a pretty aftertaste that lingers.  ", 36.0m, "1986", new Guid("53c6e4ed-9609-45fd-a4f8-0f33014a3aad") },
                    { new Guid("2ee40328-1f17-40ca-9955-8c4d1fa08a28"), 3, new DateTime(1995, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Columbia Valley", "Rich, plush and elegant, a high-fidelity voice that sings out its plum, black cherry, vanilla and spice flavors. A Washington Merlot that echoes ripe fruit and oak on the long finish. Drinkable now. 1,800 cases made.", 12.0m, "1992", new Guid("5e10bd81-2d1f-4756-b555-0c8a31149af4") },
                    { new Guid("a47f9e80-d240-447d-bb33-225c09f7e202"), 6, new DateTime(2003, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Edna Valley West Slope Edna Ranch", "Remarkably complex and elegant, with a delicious range of flavors built around vibrant pear, nectarine, honeysuckle, creamy fig and light toasty-hazelnut scented oak. Turns sleek and vibrant, with a wonderful aftertaste of complex flavors. Drink now through 2005. 4,362 cases made.", 12.0m, "2001", new Guid("6fc999e3-71db-49d6-8449-d6de7a391b3a") },
                    { new Guid("a716ab03-acd5-4abe-8ce4-4b3b41b8ab47"), 6, new DateTime(1989, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Edna Valley MacGregor Vineyard", "Earthy, ripe and complex, without showing any heaviness. Elegant and ever-changing in the glass, mingling aromas and flavors of toast, honey, apple, pear and more. Drinkable now, but it can only improve with cellaring.  ", 14.0m, "1987", new Guid("6fc999e3-71db-49d6-8449-d6de7a391b3a") },
                    { new Guid("f7fc17ac-4a04-4756-bc9a-71b50e6416b9"), 3, new DateTime(1994, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma Valley", "A claret style that's fresh and vibrant, with black cherry, plum and buttery toast flavors. Folded neatly together in complex and enticing style. Drinks well now. 1,325 cases made.", 10.0m, "1992", new Guid("68a43e24-6a6a-48e1-90a8-12fc5b00c570") },
                    { new Guid("00628978-7d01-42d5-820f-232b394944f7"), 3, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Eastern Australia", "A stylish wine, with power and grace in equal portions, offering generous currant, toast and plum flavors shaded by hints of pepper and gaminess. Beautifully balanced from start to finish, with dark cherry flavor holding on through the aftertaste. Drinkable now. A great value. 24,000 cases made.", 9.0m, "1990", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("762da7c2-a448-4767-a645-d8c20aaac5d0"), 6, new DateTime(1991, 1, 1, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Hunter Valley Show Reserve", " 4,600 cases made.", 16.0m, "1989", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("8532ef8d-4503-401c-ae8c-33575ca2604a"), 3, new DateTime(1989, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Hunter Valley", "Concentrated and generous, a classically-structured wine with beautiful spice-tinged cassis and cherry aromas and flavors, supple texture, powerful and elegant at the same time. A terrific Cabernet at a reasonable price. Drink Now-1997.  ", 11.0m, "1986", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("030f0844-04ec-41b7-b040-11528573ed03"), 3, new DateTime(1989, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Hunter Valley", "Amazingly rich and complex already, and it's just a baby. Firm, fine tannins support a classic structure and a panoply of aromas and flavors. Built around a solid core of plum fruit, it also hints at cedar, leather, cinnamon and sandalwood. Almost drinkable now, but best 1994 to 1998.  ", 9.0m, "1986", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("32bc66e1-14ea-4a1a-adfe-26bd9080f90b"), 3, new DateTime(1989, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Mountain Counterpoint", "Deeply colored and aromatically pleasing. Young, ripe and concentrated with layers of cedar, currant, spice and cherry that linger on the finish. Drink now to 2000.  ", 13.0m, "1987", new Guid("4b079c21-aa03-429d-b9e1-b3f694e72956") },
                    { new Guid("97686029-bc13-46b9-aa53-9691cd24075c"), 3, new DateTime(1992, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Valley", "Firm, focused and flavorful, offering distinctive spice and mint-tinged Cabernet characteristics, lots of currant and plum flavors and more richness, depth and concentration than most '89s. Oak seasoning adds a nice dimension, and the tannins are sufficient to warrant cellaring through 1995 to '98. 23,000 cases made.", 15.0m, "1989", new Guid("4f591ad3-06c0-4398-84c5-e8e39d664d4a") },
                    { new Guid("84fb83b7-f12e-4000-8ca5-99ae918106d4"), 3, new DateTime(1991, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Valley Jack London Vineyard", "Smells great, and packs plenty of ripe cherry and currant flavor into the tannic structure, which bodes well for the future. Hints of spice and vanilla add nuances to the intense flavor. A deep-colored, beautifully balanced, cellar-worthy wine that should start to show all it has around 1997 to 2000. 6,000 cases made.", 14.0m, "1987", new Guid("4f591ad3-06c0-4398-84c5-e8e39d664d4a") },
                    { new Guid("9f30515e-6131-4088-bd6d-7162bf5f52c8"), 3, new DateTime(1989, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Valley Artist Series", "Rich and concentrated, with plenty of elegance and finesse to the currant, plum and tobacco flavors, which are long and harmonious on the finish. The tannins are present without being overwhelming. Drinkable now and well into the 21st century. 4,000 cases made.", 30.0m, "1986", new Guid("4f591ad3-06c0-4398-84c5-e8e39d664d4a") },
                    { new Guid("6410e4b5-c677-4211-80e5-54e5942a221f"), 2, new DateTime(1989, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tawny Port Australia Old Trafford", "Sensationally complex and rich, a dessert in a glass with sweet flavors of caramel, coffee and many spices, extraordinarily long and dynamic. It just stays with you forever.  ", 15.0m, "NV", new Guid("ac0033f0-2db9-4741-a8df-64c268281169") },
                    { new Guid("cbf54c39-5eda-4965-8ba9-7bacfda5c27e"), 3, new DateTime(1989, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Vineyard Selection 82", "Incredibly intense and concentrated, with tart black cherry, currant, spice and cedar flavors that are sharply focused and framed by firm tannins and toasty oak. Drink now through 1996. 1,540 cases made.", 25.0m, "1986", new Guid("f396bbca-906e-4280-9c64-e9733332807c") },
                    { new Guid("dafc2def-e3f6-461c-abb8-9712479c7325"), 3, new DateTime(1993, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah McLaren Vale Show Reserve", "Deep, dense, dark and powerful, with layers and layers of plum, berry, chocolate, coffee and cedar flavors finishing round and generous. Drink now. 1,900 cases made.", 16.0m, "1990", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("78d3ded1-b2cd-400e-ae0e-9ea952708617"), 3, new DateTime(2001, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Bella Oaks Vineyard", "Supple and elegant, a very impressive style, with creamy black cherry, currant, plum, olive, dill and spice. Finishes with polished tannins and a lingering aftertaste. Drink now through 2009. 4,700 cases made.", 50.0m, "1996", new Guid("a45c6daa-3dc7-43b2-a9fc-20f37ecd9230") },
                    { new Guid("11709ba0-553e-4fc9-8cd7-ca6f58da6f8e"), 5, new DateTime(2018, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Dom Pérignon Legacy Edition", "There's power to this graceful Champagne, with the vivid acidity swathed in a fine, creamy mousse and flavors of toasted brioche, kumquat, pastry cream, candied ginger and poached plum that dance across the palate. An underpinning of smoky mineral gains momentum on the lasting finish. Drink now through 2033.  ", 180.0m, "2008", new Guid("3c92e614-3fe7-471d-9a87-20b9f8cfa597") },
                    { new Guid("e8ad761d-8f8f-4d22-91fa-e02862dd09c9"), 5, new DateTime(1988, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Cuvée Dom Pérignon", "Defines the richness and elegance of Champagne, delicate and concentrated w ith spicy pear, toasty vanilla, subtle and fine with a touch of hazelnut on the finish.  ", 150.0m, "1982", new Guid("3c92e614-3fe7-471d-9a87-20b9f8cfa597") },
                    { new Guid("3a41e60d-a932-4eb1-a538-ff575352f691"), 3, new DateTime(2010, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Santa Ynez Valley", "Offering wonderful floral, grapey plum and wild berry aromas, this is rich, smooth and supple on the palate, with tiers of dark berry, mocha, mineral and spice flavors that glide along. Drink now through 2015. 12,325 cases made.", 22.0m, "2006", new Guid("81bfbe58-65b4-4295-bef6-c99a2893e93c") },
                    { new Guid("1793e0fc-37c2-416f-aebf-c69b887d6793"), 3, new DateTime(1995, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Santa Barbara County Zaca Vineyards", "An outstanding Syrah from this California winery. Rich and fruity, with gobs of ripe cherry, wild berry, currant and raspberry flavors. It picks up anise, cedar and spice notes, then turns smooth and complex on the finish. The tannins are fine and the flavors keep pouring through. Achieves its rich character without the heavy feel. 3,200 cases made.", 13.0m, "1993", new Guid("81bfbe58-65b4-4295-bef6-c99a2893e93c") },
                    { new Guid("6eb4153f-d818-44bf-ae57-935e95a4d9af"), 3, new DateTime(1988, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Barbara County Reserve", "Very toasty and spicy, yet smooth as silk on the palate. Aromas and flavors of cinnamon, strawberry and raspberry are nicely supported by the right frame of oak. Superb balance.  ", 15.0m, "1986", new Guid("81bfbe58-65b4-4295-bef6-c99a2893e93c") },
                    { new Guid("9e7f8961-2284-41a7-8295-b485c3197af9"), 3, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Knights Valley Reserve", "Offers rich, smooth, layered flavors of blackberry, wild berry, plum and cassis. Full-bodied, yet graceful and elegant, this is most interesting on the finish, where the flavors glide along and pick up a subtle lift of spice and mocha. Drink now through 2024. 3,602 cases made.", 45.0m, "2009", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("9843da04-99c5-4276-94c0-b418f1547538"), 3, new DateTime(2003, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alluvium Red Knights Valley", "Dark, rich and polished, with complex, concentrated currant, tobacco, olive, bell pepper and blackberry flavors. Finishes with a flash of spicy, toasty oak and ripe, integrated tannins. Merlot, Cabernet Sauvignon, Cabernet Franc, Malbec and Petit Verdot. Best from 2004 through 2011. 9,000 cases made.", 30.0m, "1999", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("86d3a99f-f405-401e-9c08-b86973326522"), 6, new DateTime(2002, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Sbragia Limited Release", "A rich, complex and deeply concentrated style, with layers of pear, fig, smoke, apricot and honey. Bold and intriguing, it's a mouthful of Chardonnay that both relies on oak and carries it well. Drink now through 2005. 3,000 cases made.", 40.0m, "2000", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("28d422ee-81ad-4397-b1c3-19a4f0473ccf"), 3, new DateTime(1995, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Howell Mountain Bancroft Ranch", "Smooth and harmonious, with a supple core of black cherry, currant, earth and toasty oak that folds together nicely on the finish, the flavors becoming more complex and elegant as it progresses. This vintage adds one more to this California vineyard's fine track record for world-class Merlot.  9,000 cases made.", 29.0m, "1992", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("4c2d2494-9179-41f6-8899-b6f62a6c8b4d"), 3, new DateTime(1993, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Knights Valley", "Firm and ripe, a medium-weight wine with a fleshy texture and spicy, cedary currant and mineral aromas and flavors, all of it smoothly shaped into a wine that should continue to improve through 1999. 35,000 cases made.", 13.0m, "1990", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("e1bf0770-812b-4be6-85f0-9e1fa3f92b7d"), 3, new DateTime(1989, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Martha's Vineyard", "A beautiful wine, one of the great Martha's ever, rich, elegant, loaded with refreshing black cherry, mint, and chocolate flavors that are the hallmark of Martha's Vineyard. Smooth and supple yet with the stuffing and tannins for long aging. 4,421 cases made.", 40.0m, "1984", new Guid("a45c6daa-3dc7-43b2-a9fc-20f37ecd9230") },
                    { new Guid("3bc59421-d4f4-42e0-85a9-3c0011ff7fb5"), 6, new DateTime(1995, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Hunter Valley Roxburgh", "Deep, rich, complex and mouth-filling. This oozes butter, spice, toast and vanilla flavors around a core of pear and apricot that remains elegant. Delicious now. 1,700 cases made.", 28.0m, "1991", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("c79378ed-357f-46a0-b133-43641db7d205"), 3, new DateTime(1995, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Australia", "A ripe, supple and silky Shiraz that is generous with its berry, spice and tar flavors, which also linger enticingly on the finish. Drinkable now. 60,000 cases made.", 10.0m, "1994", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("7c5a4f44-7994-4776-ac10-5e1fed3b3c48"), 3, new DateTime(1997, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah McLaren Vale Balmoral", "Deep and aristocratic, this is an elegant wine with power. Layers and layers of blueberry, blackberry, pepper and cream flavors make it stunning to drink now, but best to give the fine mesh of tannins until 2000 to 2003 to resolve. 3,000 cases made.", 35.0m, "1994", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("8c2875a2-8b76-40a5-9ad8-2758a6568d80"), 3, new DateTime(1989, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Sonoma Valley Rhinefarm Vineyards", "Defines Merlot character. Concentrated, intense and supple, with integrated plum, cherry, tobacco and cedar aromas and flavors and a lush, chocolaty finish. Delicious now but should develop until 1994. 5,320 cases made.", 18.0m, "1987", new Guid("68a43e24-6a6a-48e1-90a8-12fc5b00c570") },
                    { new Guid("98adb400-733f-4cd5-b954-5a7f66cab8de"), 3, new DateTime(1989, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley", "Smells gorgeous, but it needs time to soften the tannins. Aromas and flavors of currant, vanilla and toast waltz beautifully to a firm, elegant beat. Great structure. Drink 1993.  ", 11.0m, "1986", new Guid("6c31aff4-9c8a-4b10-88a8-7087d50e9340") },
                    { new Guid("4815e55a-1333-4b5b-aa3b-652b006fd44f"), 2, new DateTime(1997, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Riesling Columbia Valley Late Harvest", "Sweet, but balanced by crisp acidity and an explosion of honey-tinged pear, pineapple and floral flavors that persist through the rich finish. Not a \"sticky\", but a lovely mouthful of Riesling intensity from Washington. Delicious now, may be at its best after 2000. 17,400 cases made.", 7.0m, "1996", new Guid("8ecf33a8-1d3c-414e-ba7f-614925f1ffa8") },
                    { new Guid("5f05eaa6-09c8-4111-a0ae-69814fc4c6c9"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Washington", "An exciting wine at a good price. Crisp, bright and concentrated, with sharply focused blueberry, currant and raspberry aromas and flavors that gush across the palate. Delicious to drink now, but has the structure and power to age gracefully well into th 7,700 cases made.", 12.0m, "1989", new Guid("8ecf33a8-1d3c-414e-ba7f-614925f1ffa8") },
                    { new Guid("004247b8-dad1-406d-b02c-64578475cda9"), 6, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Johannisberg Riesling Yakima Valley", "Firm and flavorful with beautifully defined peach aromas and flavors and hints of the typical piney character as well; just a bit off-dry, balanced and very appealing.  ", 6.0m, "1988", new Guid("8ecf33a8-1d3c-414e-ba7f-614925f1ffa8") },
                    { new Guid("fcaf2210-6b8d-4528-9c53-89930fc72621"), 3, new DateTime(1989, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Alexander Valley", "A classic claret-style Zinfandel at a great price showing elegant berry, cassis and pepper aromas and flavors, a gentle touch of toasty oak, long on the finish. A faultless Zin. Best around 1990 to 1992.  ", 7.0m, "1986", new Guid("8163b375-169a-4021-9e60-777408ee9412") },
                    { new Guid("24f75b4f-3128-410c-afb2-5d210e20aa37"), 3, new DateTime(1989, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera Piemonte", "  ", 6.0m, "1986", new Guid("36de3835-7bb7-472a-9186-6fdfe87e22b4") },
                    { new Guid("2331dbb6-340f-4e81-89d6-068456477d4c"), 3, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri-Sassicaia Sassicaia", "Lovely, subtle yet complex aromas of currants, sage and green olives lead to a full-bodied red with a solid core of fruit and well-integrated tannins. Still very reserved on the finish, but those who are patient should be rewarded. Cabernet Sauvignon and Cabernet Franc. Best after 2005. 13,000 cases made.", 160.0m, "1998", new Guid("432e6352-d174-47fc-9526-0c76ddf1ac71") },
                    { new Guid("dafd91ac-a7af-4b7b-acdb-e8c631a98df5"), 3, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sassicaia", "Remarkably flavorful and complex for such a lean wine. It's beautifully balanced, harmonious, seamless. Hints of plum, spice, anise, tobacco and toasty oak compete for attention, but the wholeis greater than the sum of its parts. Drink now through 2005.  ", 50.0m, "1986", new Guid("432e6352-d174-47fc-9526-0c76ddf1ac71") },
                    { new Guid("1db52602-02a7-4764-90c5-70a958f5ff13"), 3, new DateTime(1989, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cask 23 Napa Valley", "A big, powerful, distinctive wine that balances its intense currant and cherry flavor against an onslaught of earthy, tobacco aromas and flavors. It will not appeal to everyone. Enormously complex, deep and long, this is a wine whose flavors should keep developing. Best from 1995 through 2000. 1,018 cases made.", 75.0m, "1985", new Guid("33b61ab4-0094-413c-a0f5-84a4b0648964") },
                    { new Guid("c960caab-074a-4706-bd92-a7f4ed0e19f3"), 3, new DateTime(1998, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Red Rock Terrace", "Lovely orchestration of complex cherry, currant and floral flavors. Classic in its balance and grace. Long on the finish. A great young Red Rock Terrace. Better than when tasted upon release, last year.--Diamond Creek vertical. Best from 2005 through 2012. 900 cases made.", 75.0m, "1995", new Guid("469a4cdd-6e1b-4e87-9091-da67266d15cf") },
                    { new Guid("9aa4d584-5336-4c66-8e2a-c3077080d26c"), 3, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Volcanic Hill", "Incredibly intense and concentrated, deep and perfumed, firm and tannic, but with a rich core of currant, herb and cherry scented Cabernet flavors that promises a long life in the bottle. Best from 1998 through 2002. 726 cases made.", 40.0m, "1987", new Guid("469a4cdd-6e1b-4e87-9091-da67266d15cf") },
                    { new Guid("9c969815-0e95-48ee-8a50-3512cf36540f"), 3, new DateTime(2014, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico San Lorenzo Gran Selezione", "Offers a combination of power and elegance, with a racy feel. Notes of wild cherry, strawberry and rose, with leafy, woodsy flavors, permeate this driving red. The finish is long, but this needs time to shed the tannins. Well-defined from start to finish. Sangiovese, Malvasia Nera and Merlot. Best from 2017 through 2030. 8,000 cases made.", 52.0m, "2010", new Guid("def27ca4-7b44-420a-a7b7-bf621190ea60") },
                    { new Guid("df8570cc-ca88-4bc9-afa6-8742e3fc6a97"), 3, new DateTime(1997, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vigna l'Apparita", "Powerful and exotic, this is the wine of the year from Tuscany. An outstanding Merlot displaying masses of currant and berry, hints of spice, it's full-bodied, with loads of velvety tannins and a long, long finish. Gives Château Pétrus a run for its money. Best after 2000. 1,000 cases made.", 100.0m, "1993", new Guid("def27ca4-7b44-420a-a7b7-bf621190ea60") },
                    { new Guid("76559823-6266-4b93-bafc-cf02f46ef6f0"), 3, new DateTime(1989, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Bellavista", "Very spicy, opulent and ripe, almost exotic in its heady fruit intensity both in aroma and flavor, round and rich on the palate, firmly supported by fine tannins. The fruit flavors seem to echo for hours. A great cellar candidate, perhaps best around 1996. 1,000 cases made.", 30.0m, "1985", new Guid("def27ca4-7b44-420a-a7b7-bf621190ea60") },
                    { new Guid("3202f0f3-3abd-4bf2-a6a2-734694f01466"), 3, new DateTime(2017, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley Three Palms Vineyard", "A powerful red, with concentrated flavors of red plum, cherry and boysenberry that are layered with plenty of rich spice and mineral accents. Touches of slate and cardamom make for a complex finish. Drink now through 2023. 3,170 cases made.", 98.0m, "2014", new Guid("5cd1919d-7aef-44c7-8ec3-91674b240b0c") },
                    { new Guid("ce0685cd-d816-4c3c-843e-8301165d3063"), 3, new DateTime(1989, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Carneros Fleur de Carneros", "An elegant, precisely balanced wine for current drinking. Beautifully aromatic with spicy plum, cranberry and toast flavors and uncommon intensity. Has finesse and subtlety, finishing with spicy cinnamon and flavor complexities that linger on the finish.  ", 9.0m, "1987", new Guid("bf32f064-9b1a-45b2-b6d9-11d4365d914a") },
                    { new Guid("315602df-94d6-4d78-ab56-95551ab52ce8"), 3, new DateTime(2007, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "GSM McLaren Vale", "Bright, focused and impressively long and detailed. Offers blueberry, cream and subtle spice aromas and flavors on a frame that's fleshed out with fine tannins, which let the finish open up gracefully. Grenache, Syrah and Mourvèdre. Best from 2008 through 2013. 6,000 cases imported.", 22.0m, "2003", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("a1943ad9-35b3-4822-87db-f6d7b5dc4493"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "GSM South Australia", "Rich, dense and focused, a chewy mouthful of plum, blueberry and dusky spice flavors, with meaty texture and flavors that mingle with the fruit on the long, generous finish. Grenache, Shiraz and Mourvèdre. Best from 2007 through 2015. 4,000 cases imported.", 30.0m, "2001", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("349b5ba9-3b80-4681-b1b9-daf7de266a48"), 3, new DateTime(2003, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Mudgee Hill of Gold", "Brighter, livelier and juicier than most Australian Shiraz, a vibrant wine with crisp blackberry, plum and vaguely leafy-herbal flavors that persist beautifully on the harmonious finish. Drink now through 2010. 23,000 cases made.", 17.0m, "2001", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("86fae435-55e3-4a38-8046-fddcad60e072"), 3, new DateTime(2000, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon-Merlot-Petit Verdot McLaren Vale Traditional", "Dark, ripe and complex, this red blend reveals its depth with layers of ripe plum, blackberry, coffee and earth. Finishes with a wide range of tasty herb flavors and rich, full tannins. Drink now through 2008. 4,000 cases made.", 20.0m, "1997", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("ce4687b1-ffda-416b-9e4e-5b552e8bf328"), 3, new DateTime(2000, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grenache-Syrah-Mourvèdre McLaren Vale GSM", "Rich and distinctive, a bit lighter than moststraight Aussie Shirazes but dripping withflavors, corralling a wild range of meatyblackberry, red cherry, black cherry and pepperflavors into a smooth wine of enormous characterand depth. Appealing already. Best after 2002. 4,500 cases imported.", 22.0m, "1997", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("164aa90f-5c09-47a0-892c-3908c594d704"), 3, new DateTime(1999, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Coonawarra Show Reserve", "Ripe, generous and supple, with pretty plum and currant flavors that persist beautifully on the finish. A bit rough around the edges, which cellaring should soften. Drink now through 2006. 15,000 cases made.", 22.0m, "1996", new Guid("218144c7-a164-45cb-81b3-6a4e5dd0162d") },
                    { new Guid("9bd80839-cf60-4aaa-ab15-031732b572fe"), 3, new DateTime(1989, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Napa Valley Orion", "Dark, dense and remarkably supple, with gorgeous plum, peach and cherry flavor graced with hints of mint and spice, rounded out with delicious oak. Comes off like a Bordeaux with Rhône flavors, very seductive. Drinkable now if you don't mind some velvety tannins. Not cheap.  ", 30.0m, "1987", new Guid("b46d6e21-91c2-4d34-9428-6aa21f001fae") },
                    { new Guid("e188795d-b609-4ed4-bbee-fb964e231b83"), 3, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Private Reserve", "Enormously deep, rich and concentrated with layers of currant, cherry, plum, coffee and spice notes set off by toasty oak. A remarkably complex wine that echoes fruit flavors on the finish. Drink now. 8,000 cases made.", 57.0m, "1985", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("74f24c59-6fb9-48cc-888f-ac76b836fa5f"), 5, new DateTime(1989, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Monterey County", "Rich and ripe, showing lovely Pinot Noir cherry flavors in the nose, with an herbal hint in the aromas. Nutty on the palate and complex, with a long, terrific finish that really makes this one stand out from the crowd. Full flavored and impressive.  ", 13.0m, "1985", new Guid("d05c0999-d877-40ac-9dc9-a92da51ce156") },
                    { new Guid("095f7724-4fff-4e21-aa2d-e5a49d94feab"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Washington Limited Bottling", "Focused and flavorful, a solid mouthful of currant, berry and spice flavors echoing anise and vanilla on the crisp finish. The tannins are balanced for aging from now through '95. 512 cases made.", 13.0m, "1988", new Guid("853705e9-e4e1-467d-9b81-1e8817c16042") },
                    { new Guid("403671eb-4421-4cd2-8c98-c7a71cd29801"), 3, new DateTime(1990, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo", "Rich and elegant, with generous floral and berry aromas and flavors supported by-rather than hidden by-the tannins. One of the most elegant Barolos we've tried, long and pure. Drink now through 2000. 606 cases made.", 24.0m, "1985", new Guid("01098f8b-a64b-4d21-b68a-979f32188275") },
                    { new Guid("502a3d6e-8242-411e-a74b-c9e24c685090"), 3, new DateTime(1990, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Cannubi Boschis", "Ripe, round and generous and smooth and spicy from aging in new oak barrels, but bursting with Barolo flavors that echo forever on the finish. With its layers of berry, cherry and anise flavors, it can only devlop more depth with cellaring through at least 1995.  ", 30.0m, "1985", new Guid("a8b2f776-f21c-4fa3-b2f0-84466de5efaf") },
                    { new Guid("5fd68ef9-a612-4ad1-925d-1fa71cb1c172"), 6, new DateTime(1990, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay King Valley Reserve", "A Chardonnay that's rich, complex, delicious and beautifully balanced, with everything you could ask for. Flavors of fig, pear, butter and spice last from the aroma to the lingering finish. 35,000 cases made.", 16.0m, "1987", new Guid("b38694ed-0f0d-4baa-bd57-6c1f3c56c435") },
                    { new Guid("1135ab77-f9f4-4c6d-9fd9-0ed0a27e66bc"), 3, new DateTime(2003, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Carmignano Villa di Capezzana", "This excellent wine is a relatively good value for the quality. Intense aromas of berry, spice and pepper follow through to a full-bodied palate, with silky tannins and a rich finish. Best after 2004. 5,800 cases made.", 25.0m, "2000", new Guid("6515d551-5311-4d0c-acc2-a515e6052339") },
                    { new Guid("48ddb796-2660-4696-b2f3-8948578983c1"), 3, new DateTime(2002, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Ghiaie della Furba", "Fantastic. Very ripe and rich, with raisin and cured beef character. Full-bodied, with big, velvety tannins and a long, long finish. A massive but impressive red. Cabernet Sauvignon, Merlot and Syrah. Best after 2005. 3,250 cases made.", 45.0m, "2000", new Guid("6515d551-5311-4d0c-acc2-a515e6052339") },
                    { new Guid("92a720ac-532b-405c-8e10-1907b6c6d0a0"), 3, new DateTime(1990, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ghiaie della Furba", "Ripe and deep, with tightly furled raspberry, plum and cassis flavors beneath a veneer of minerally, earthy character. Not very tannic, but tight enough and powerful enough to require cellaring until it opens up. Best after 1993. 1,295 cases made.", 20.0m, "1985", new Guid("6515d551-5311-4d0c-acc2-a515e6052339") },
                    { new Guid("818d148f-5a78-4dcc-8575-25685197b2ea"), 3, new DateTime(1990, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Columbia Valley Reserve", "This unusually concentrated youthful wine is not yet elegant or complex, but it has vivid, focused black cherry and grape flavors in a lush texture, with stiff, young tannins that will need till 1993 or beyond to loosen up. 1,140 cases made.", 12.0m, "1987", new Guid("02c8f576-c466-445b-ae5e-359e86f14a44") },
                    { new Guid("b79535fa-bbb1-4c0a-ac2c-cf7552092289"), 3, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Prunaio", "A Sangiovese blend with lots of complexity on a tight, elegant frame. Shows anise and smoke aromas and cherry, strawberry and blackberry flavors wrapped in a veil of tannin that will need until 1993 to '95 to soften. Offers plenty of style and finesse. 2,820 cases made.", 19.0m, "1986", new Guid("a68f1842-b0c3-4ee4-bb4d-585233034273") },
                    { new Guid("90ddc574-3f59-4841-b1b2-97b9b24e83be"), 3, new DateTime(1991, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée Clos des Réas", "Flavorful, graceful and elegant, this beautifully proportioned wine balances plum, raspberry and strawberry flavors against nuances of oak, offering depth and power at the same time. Stash away until 1995 and watch it develop into a beauty. 1,100 cases made.", 50.0m, "1988", new Guid("08be2137-a29c-4458-bf3a-a08f0b1d77fa") },
                    { new Guid("8c7eca81-2a8f-4d61-8ab1-a08f3ba8f120"), 6, new DateTime(1990, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault Genevrières", "It's wound up tight, but this is a very polished and elegant wine underneath. A toasty, nutty, oaky quality accents the intense, tight grapefruit, apple and pineapple flavors that linger beautifully on the tangy finish. Won't show its best until 1993 to 400 cases made.", 37.0m, "1988", new Guid("ed5cf22d-b87b-4214-bc0e-aec027a8000e") },
                    { new Guid("e3cee31d-8eab-4603-86c7-3cef627466b5"), 3, new DateTime(1991, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera d'Alba", "Exuberantly fruity and flavorful, supported by crisp acidity and rich tannins, and bursting with blackberry, raspberry and currant flavors that echo on the long finish. Beautifully balanced and built . 250 cases made.", 13.0m, "1989", new Guid("01098f8b-a64b-4d21-b68a-979f32188275") },
                    { new Guid("e92af202-b290-49f8-91b0-77525b5db055"), 3, new DateTime(1990, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Il Poggiolo", "Spicy, elegant and plush in texture, with supple tannins supporting beautifully focused raspberry, anise and toast aromas and flavors. A hint of tamarind adds an exotic note to this A-1 wine that ought to keep improving through at least the rest of the century. 480 cases made.", 34.0m, "1985", new Guid("5e763851-6e94-46be-8cf5-f22ecd19229a") },
                    { new Guid("9ab01f37-e0a8-406c-a3a6-1acb1df03253"), 3, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gevrey-Chambertin Clos Prieur", "Beautifully concentrated, focused, harmonious, artfully balanced between the generous blackberry and cherry flavors and the vanilla, toast and spice of oak. Full-bodied, bursting with fruit and already graceful. Has the stuffing to age well into the late '90s. 150 cases made.", 29.0m, "1987", new Guid("cb07053c-a240-4fde-994f-8ff7ac3dc1e7") },
                    { new Guid("bec0eb23-997a-47e5-ad74-87e74c45c6e5"), 3, new DateTime(1990, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley Claret Style", "Delicious layers of complex, concentrated and rich cassis, currant, vanilla and plum flavors abound in this sharply focused, beautifully structured style. It's long and elegant, with just the right balance of tannin, oak and acidity. 1,200 cases made.", 20.0m, "1987", new Guid("e3cd313e-111f-475b-be2f-5c96004b3e87") },
                    { new Guid("6d80cf3e-2f49-4515-8c14-5496a622a99b"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Valley Monte Rosso Vineyard Family Vineyard Selection", "Impressive for its rich, tight, deeply concentrated core of spice- and anise-laced currant and blackberry flavors. Sharply focused, intense and concentrated, its mouthfilling fruit flavors are long and complex on the finish, with well-integrated tannins. Best from 2004 through 2014. 1,365 cases made.", 50.0m, "1999", new Guid("b1da6efa-5646-43d8-8d0e-10f45e5339fb") },
                    { new Guid("6c129f4b-2095-4c15-9445-75f245448b44"), 3, new DateTime(1990, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Valley Monte Rosso", "Very ripe and rich, with aromas of cedar and fruit and well-integrated currant, cherry, berry, oak and toast flavors. The tannins are firm, but not overbearing on the finish. The best Martini Cabernet since the sensational 1970. Best from 1995 through 2000. 1,450 cases made.", 20.0m, "1987", new Guid("b1da6efa-5646-43d8-8d0e-10f45e5339fb") },
                    { new Guid("53747dcc-b872-4ae1-a589-9cc436d73259"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Fontalloro", "Chocolate, blackberry and black cherry aromas lead to a full-bodied palate, with velvety tannins and a deep concentration of fruit. Powerfully rich and polished. Big and structured. A blockbuster Sangiovese. Best after 2012. 3,300 cases made.", 52.0m, "2006", new Guid("c8951728-5fcb-4742-ab1b-e57ca2322e14") },
                    { new Guid("18a18579-bfa4-43fb-ba00-4293ec7082f4"), 3, new DateTime(1992, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Berardenga", "A blockbuster, with masses of plum, tar and berry aromas and flavors. Coats your palate with fruit and tannins. Drink now.  ", 12.0m, "1990", new Guid("c8951728-5fcb-4742-ab1b-e57ca2322e14") },
                    { new Guid("67986391-6ad7-408a-9317-88e5b89866fb"), 3, new DateTime(1992, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Berardenga Riserva", "Another winner from this estate. Made in a New World style, with lots of new wood, showing toasted coconut, milk chocolate and fruit aromas and flavors. Round and velvety, with lots of cedar notes on the finish. Drink now.  ", 17.0m, "1988", new Guid("c8951728-5fcb-4742-ab1b-e57ca2322e14") },
                    { new Guid("e25dcc4a-281b-4774-b75e-ee9ce363b75a"), 3, new DateTime(1990, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Berardenga Rancia Riserva", "Outstanding. Very rich and concentrated, almost chocolaty in flavor and full-bodied, with supple texture and plenty of cherry and ripe plum flavors, firm tannins and hints of smoke and spice. Needs more time to mature. Best from 1991 through 1994. 900 cases made.", 23.0m, "1985", new Guid("c8951728-5fcb-4742-ab1b-e57ca2322e14") },
                    { new Guid("8830dcbd-aed1-423b-a69a-f402768ab88f"), 3, new DateTime(2003, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Maria Valley", "Smooth, rich and concentrated, with a tasty core of plum, blackberry and wild berry shaded by toasty, smoky oak. It all comes together on the finish, where it turns complex and plush. Drink now through 2007. 9,213 cases made.", 25.0m, "2001", new Guid("c01430f9-f17b-457d-9dd2-63d07b36881c") },
                    { new Guid("9d3b2d77-71f7-4e92-8fba-6fd2840331f4"), 6, new DateTime(1999, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Maria Valley", "Wonderfully rich and silky, with polished, focused pear, nectarine and toast, showing more nuances of flavor on the finish. Drink now. 21,300 cases made.", 17.0m, "1997", new Guid("c01430f9-f17b-457d-9dd2-63d07b36881c") },
                    { new Guid("bebfff9f-f303-4a56-9c87-34f6ec79ea0d"), 6, new DateTime(1990, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Alexander Valley Winemaker's Reserve", "A rich, smooth, creamy style that offers a broad array of pretty honey, melon, butterscotch and apple flavors along with a subtle touch of nutmeg and spice. Satiny smooth texture, and a long full finish. Better than previously reviewed. Drink now through 1993. 3,500 cases made.", 24.0m, "1987", new Guid("49a9568a-ead5-4e21-9628-ad6d7e35334c") },
                    { new Guid("d15b0216-2a52-462a-a1df-120ab05758a2"), 6, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Maria Valley Estate", "A bold, complex style that packs in lots of ripe pear, fig, honey and butterscotch flavors and frames them with toasty, smoky oak. It all adds up to a rich, full-bodied mouthful of Chardonnay from this premier California winery. 2,772 cases made.", 28.0m, "1993", new Guid("c01430f9-f17b-457d-9dd2-63d07b36881c") },
                    { new Guid("b2e650f1-ba87-43e3-acb5-7d8264608f2d"), 3, new DateTime(1991, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Poggio all'Oro Riserva", "Has all the ripeness, richness and complexity one could ask for in a beautifully modulated, elegant package. The aromas and flavors run toward plum, spice, coffee and anise, turning toward prune on the finish. Maintains its elegance through the long, com 1,200 cases made.", 37.0m, "1985", new Guid("09e7b6e6-8344-4557-ada7-33810941faf9") },
                    { new Guid("8de7eaac-95ac-4b82-9a44-0216d85fd58a"), 3, new DateTime(1998, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Summus", "A blockbuster red wine from Italy--the best Summus I've ever tasted. Shows an intense blackberry character offset by hints of wet earth. It's full in body, with full, mouthpuckering tannins yet a sweet and fruity aftertaste. Needs some age to come together. Best after 2001. 5,000 cases made.", 45.0m, "1995", new Guid("09e7b6e6-8344-4557-ada7-33810941faf9") },
                    { new Guid("0a8b2b78-4fc8-4e7b-b3a5-b96512b02652"), 3, new DateTime(1990, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County", "Ripe and jammy, with beautifully defined raspberry aromas and flavors and hints of plum, nutmeg, vanilla and toast to make it more interesting. Claret-style in structure and well balanced. Lovely now but should continue to develop. Drink now through 1994. 6,500 cases made.", 12.0m, "1988", new Guid("aa87e327-455c-4e34-9e5f-f3326862bde3") },
                    { new Guid("b1d4fba6-9054-452b-9c60-b84eb23e1f87"), 3, new DateTime(1990, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Reserva", "Lean and rather one-dimensional, but pleasant, with nutty, bright cherry flavors. Silky and rather simple on the palate. Drink now.  ", 11.0m, "1985", new Guid("1ebb7621-5f0f-4dd9-b515-75ce278fe0a7") },
                    { new Guid("88ac2e11-e4ad-40be-aacd-4c669c108244"), 3, new DateTime(1990, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Maria di Brun", "Very firm, tannic and austere, with hints of spice and earthiness on the reined-in raspberry and anise aromas and flavors that grow more complex on the finish. Drink now.  ", 37.0m, "1985", new Guid("36816791-8915-4b78-92ac-fcbe3ef928f6") },
                    { new Guid("47f32c0d-931c-45d4-9619-23f425603fdb"), 5, new DateTime(1998, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Napa Valley Prestige", "Toasty aromas and firm texture introduce this wine, which sports nut, fig, pear, honey and lemon flavors. Good on the midpalate, striking a fine balance between delicacy and substance. Drink now through 2002. 150,000 cases made.", 16.0m, "NV", new Guid("68d1ab48-175e-4a0e-81e6-56328efb2530") },
                    { new Guid("f9b810cd-268a-4396-a242-0d40ab437dc6"), 5, new DateTime(1994, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "DVX Napa Valley", "Crisp and clean, with a band of toast, ginger, pear and spice. Gains depth and complexity on the finish, where the flavors fan out. Mumm's new prestige cuvée. 3,000 cases made.", 25.0m, "1990", new Guid("68d1ab48-175e-4a0e-81e6-56328efb2530") },
                    { new Guid("dc134b8d-fd0a-433d-92f9-efa84bbf8f9d"), 5, new DateTime(1990, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Napa Valley Carneros Winery Lake", "Elegant, complex and toasty, with tart lemon, honey and spice flavors extending into a long finish. It's a mouthful of flavor but not full-bodied. It's balanced, creamy and strikes a middle chord between lightness and richness. 2,000 cases made.", 22.0m, "1987", new Guid("68d1ab48-175e-4a0e-81e6-56328efb2530") },
                    { new Guid("29f650ca-89f9-4b76-a1a1-8a5ae3c00b97"), 3, new DateTime(2002, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Walla Walla Valley", "Rich and ripe, packed with beautiful currant and berry flavors, framed with superfine tannins that let the fruit and subtly spicy overtones linger effortlessly on the long finish. Drink now through 2010. 2,329 cases made.", 60.0m, "1999", new Guid("cd60aa2c-e8fb-48e1-ba84-7cbfe4346f47") },
                    { new Guid("ee830d5a-72c9-440c-ac72-454703a163c6"), 3, new DateTime(1990, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Washington", "Loaded with layers of fresh currant, plum and cherry flavors framed by toasty, cedary oak that echoes vanilla and chocolate notes. Rich and concentrated, with flavors that linger on the finish. Elegantly balanced and tannic enough to cellar. Best after 1994. 605 cases made.", 22.0m, "1987", new Guid("cd60aa2c-e8fb-48e1-ba84-7cbfe4346f47") },
                    { new Guid("f1eece0c-94bf-4e9e-bbbe-d9746c323619"), 3, new DateTime(2014, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Tavola", "Fresh and inviting, with cherry and raspberry notes at the core, showing cinnamon and hibiscus hints around the edges. The flavors float easily into the long, expressive finish. Drink now through 2019. 12,000 cases made.", 25.0m, "2012", new Guid("fafe2578-5210-4ab1-9ec2-db417efd8d02") },
                    { new Guid("22176c64-bcac-416a-98ff-f4d97c2ceed8"), 3, new DateTime(1994, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Oregon Reserve", "Smooth, ripe, generous and elegant, with beautifully delineated currant, plum and spice flavors that come into sharp focus and glow on the finish. Delicious now and should be fine through 1998. 730 cases made.", 30.0m, "1992", new Guid("fafe2578-5210-4ab1-9ec2-db417efd8d02") },
                    { new Guid("da4aff36-cd0c-4dd9-8896-1b6035e4751f"), 6, new DateTime(1996, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Fontanelle", "This complex and distinctive Chardonnay from Italy blends toasty, spicy oak aromas with ample fruit and nutty nuances to make a concentrated, focused, nicely balanced whole. Long on the finish and thoroughly enticing. 1,200 cases imported.", 17.0m, "1993", new Guid("09e7b6e6-8344-4557-ada7-33810941faf9") },
                    { new Guid("51cfabf2-6332-4c09-9673-0c2a382720bd"), 3, new DateTime(1993, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley 20th Anniversary", "Ripe, smooth and complex, offering silky, elegant cola, cherry, currant and vanilla flavors that are rich and intense, with subtle nuances and deep, lingering flavors. Drinks well now, but appears to have the intensity and depth to cellar through 1996. T 640 cases made.", 35.0m, "1990", new Guid("fafe2578-5210-4ab1-9ec2-db417efd8d02") },
                    { new Guid("9ac73970-4fca-46f4-b0ae-7a6078bfb33c"), 3, new DateTime(1992, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Rufina", "Amazingly concentrated for a simple Chianti, this is overflowing with wild raspberry and smoke characteristics and shows a wonderful, smooth texture. Gorgeous. Drinkable now.  ", 13.0m, "1990", new Guid("ad014866-1ddf-4a9d-8523-b1cf937a65df") },
                    { new Guid("ead75ba9-2cb9-4474-9705-6fb69a817312"), 3, new DateTime(1990, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Rufina Bucerchiale Riserva", "A great wine. It's beautifully focused, complex and deep in flavor, with notes of cherry, spice and chocolate and tannins that are firm but soft. The fruit complexity lingers a long time on the finish. Drink now to 1994. 500 cases made.", 19.0m, "1985", new Guid("ad014866-1ddf-4a9d-8523-b1cf937a65df") },
                    { new Guid("39795098-003a-4768-8f02-3bc7a40c0a0c"), 6, new DateTime(2017, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Gris Alsace Réserve Personnelle", "A mouthwatering Pinot Gris, with exotic notes of papaya and grated coconut accenting the peach, anise and almond blossom flavors. Sleek acidity focuses this through to the spicy, mineral-tinged finish. Drink now through 2029. 1,500 cases imported.", 38.0m, "2015", new Guid("7fd77954-589e-4375-87aa-f3f6d13b0b1b") },
                    { new Guid("b5b04fe4-7b69-44ac-b9f5-b3327a81454b"), 6, new DateTime(2010, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Réserve", "A medium-bodied Riesling, structured with firm acidity and closely knit flavors of lemon peel, Granny Smith apple, lemongrass and stone. Shows good intensity, but in a well-honed package, with a lingering, smoky finish. Needs time in the cellar. Drink now through 2030. 4,500 cases made.", 25.0m, "2008", new Guid("7fd77954-589e-4375-87aa-f3f6d13b0b1b") },
                    { new Guid("cf72816e-4f6e-4ccc-9d4b-1471b4ac0822"), 6, new DateTime(1997, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Clos Ste.-Hune", "The essence of French Riesling. Ripe, with honey, floral, apple and stone aromas and flavors, yet powerful and sporting a steel-girder construction, this seems youthful at almost 6 years of age and poised to last forever. The finish goes on and on. Terrific expression of site. 500 cases made.", 60.0m, "1991", new Guid("7fd77954-589e-4375-87aa-f3f6d13b0b1b") },
                    { new Guid("2fa79453-dfa0-49fa-aac3-e3588e8ffd7b"), 6, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Cuvée Frédéric Émile", "  ", 15.0m, "1988", new Guid("7fd77954-589e-4375-87aa-f3f6d13b0b1b") },
                    { new Guid("69e2114f-ad4c-47ba-8b27-9294734ba4c3"), 3, new DateTime(2018, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Contra Costa County Ancient Vines", "Lively and supple, with floral raspberry and fresh pepper aromas and sleekly appealing cherry and licorice flavors that glide lightly along the finish. Drink now through 2022. 50,000 cases made.", 15.0m, "2016", new Guid("e8c85583-b0f1-4b67-9d6d-5d700f501929") },
                    { new Guid("cff96aae-d4e8-47ba-9818-27443a33156e"), 3, new DateTime(1990, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Oakley Cuvée Contra Costa County", "Well knit, supple and beautifully flavored with vivid black cherry, spice and blackberry. Elegant in texture and nicely balanced with good acidity and moderate tannins. Drink soon. 3,100 cases made.", 12.0m, "1988", new Guid("e8c85583-b0f1-4b67-9d6d-5d700f501929") },
                    { new Guid("86607964-2d37-4b8b-91cb-4fc92cf5dc31"), 6, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Long Island Grand Vineyard Selection", "Very smooth, harmonious and balanced, offering well-defined pear, orange and vanilla aromas and flavors, hinting at butterscotch and honey on the long finish. A delicious wine for current drinking, and probably worth aging. Drink now through 1993. 400 cases made.", 18.0m, "1988", new Guid("0d912167-323c-4692-ba0e-243be5f0ca23") },
                    { new Guid("d1fa37cc-217e-4f80-9eb3-b8313c0dc62c"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sant'Antimo Tavernelle", "This outstanding Cabernet Sauvignon from Tuscanyhas interesting aromas of ripe fruit and meat,with hints of dried herbs. It's full-bodied andvery concentrated, with plenty of velvety tanninsand a long finish. Give it some time in thebottle. Best after 2001. 7,500 cases made.", 37.0m, "1997", new Guid("09e7b6e6-8344-4557-ada7-33810941faf9") },
                    { new Guid("ab0f8451-b778-4149-870a-69370fb26078"), 3, new DateTime(1990, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Reserve", "Deliciously rich and complex, with ripe, concentrated plum, cherry and spice notes that offer depth and grace. The finish follows through, showcasing the plummy richness. The toasty oak echoes on the finish. Drink now to 1995.  ", 20.0m, "1987", new Guid("fafe2578-5210-4ab1-9ec2-db417efd8d02") },
                    { new Guid("59818200-46c9-446c-9377-deeb0ab73c5f"), 6, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Barbara County Reserve", "An outstanding reserve-style wine that comes at you with green apple and spice flavors that soften and swirl into honey and cream notes on the long finish. Drinkable now. 2,816 cases made.", 23.0m, "1992", new Guid("c01430f9-f17b-457d-9dd2-63d07b36881c") },
                    { new Guid("2095b423-6704-497c-a624-feabdec878de"), 6, new DateTime(1993, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Santa Barbara County", "Firm and flavorful, with a nice array of spicy, toasty, slightly herbal pear, honey and grapefruit aromas and flavors that ride gently into a long finish. Stylish, harmonious and graceful, showing a definite touch of oak. Drinkable now. 2,803 cases made.", 9.0m, "1992", new Guid("c01430f9-f17b-457d-9dd2-63d07b36881c") },
                    { new Guid("15d90e23-ddbd-4318-b821-642373bf7846"), 6, new DateTime(1990, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Barbara County", "An outstanding wine that's reasonably priced. Rich, intense and concentrated, with sharply focused honey, apple and pineapple flavors that dance on the palate, framed by just the right amount of oak. Complex and intriguing. Drink now. 6,538 cases made.", 12.0m, "1988", new Guid("c01430f9-f17b-457d-9dd2-63d07b36881c") },
                    { new Guid("d26cb038-9ceb-4830-9ff6-51c6278f33ac"), 3, new DateTime(2019, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Howell Mountain", "An elegant style, featuring lilting flavors of red currant and dried raspberry that show hints of cedar and paprika. Fine-edged, with savory richness midpalate. Dried green herbal notes emerge on the minerally finish. Drink now through 2026. 1,657 cases made.", 85.0m, "2016", new Guid("f4a9c06f-522b-4b1e-bc83-1cdeebdd7165") },
                    { new Guid("300d0b6b-b141-41bd-b1df-10fc8530a9e5"), 3, new DateTime(1990, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Howell Mountain", "A beautifully defined wine, with deep, rich, intense and supple black currant flavors. It's pure and complex, with a pretty overlay of toasty oak flavors that blend together, creating a wine of great harmony and finesse. Has enough tannin to last a decade or more. Best after 1993. 2,700 cases made.", 25.0m, "1987", new Guid("f4a9c06f-522b-4b1e-bc83-1cdeebdd7165") },
                    { new Guid("94b8e74d-e926-4256-98b3-7b62c46996e7"), 6, new DateTime(1989, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Saarburger Rausch", "  ", 17.0m, "1988", new Guid("136d2793-d6ac-493b-a0bd-0a1289b2649a") },
                    { new Guid("a2d582d5-ff95-4b59-9922-14fc317e8a09"), 6, new DateTime(1989, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Maximin Grünhäuser Abstberg", "  ", 18.0m, "1988", new Guid("c35fdfc0-f043-451e-a858-4fe822f7bbdd") },
                    { new Guid("97c1bd4c-f4c1-440d-93cc-1b8b7b8dcc56"), 6, new DateTime(2013, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel Graacher Himmelreich", "Features a savory aroma, with flavors to match, showing good acidity to the juicy apple, pear and white currant flavors. Curry notes extend on the finish, displaying plenty of cream. Powerfully structured and deep with spice. Drink now through 2032. 1,000 cases made.", 42.0m, "2011", new Guid("a1aaf1db-cab4-48dd-b8c1-1ae1f005f207") },
                    { new Guid("344cb8b7-f8be-4d07-80c9-c07f42a6cae4"), 6, new DateTime(2006, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Auslese Mosel-Saar-Ruwer Graacher Himmelreich", "The aroma is marked by matchstick now, but this is positively crunchy on the palate. The vibrant structure and clarity set the tone for its peach, citrus and mineral notes. It's refreshing and alive, with a mouthwatering finish. This needs time. Best from 2009 through 2030. 1,200 cases made.", 38.0m, "2004", new Guid("a1aaf1db-cab4-48dd-b8c1-1ae1f005f207") },
                    { new Guid("6df4aa26-2c8c-4bd7-8d03-860e6c3de392"), 6, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Auslese Mosel-Saar-Ruwer Wehlener Sonnenuhr", "  ", 35.0m, "1989", new Guid("a1aaf1db-cab4-48dd-b8c1-1ae1f005f207") },
                    { new Guid("32b7765a-31e4-4d59-ba8b-dbc07aa4bf49"), 6, new DateTime(1989, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Wehlener Sonnenuhr (AP 2)", "  ", 24.0m, "1988", new Guid("a1aaf1db-cab4-48dd-b8c1-1ae1f005f207") },
                    { new Guid("ca8e3b88-1c41-4125-9d73-a38686e8d4af"), 3, new DateTime(1990, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino", "Rich and opulent, with generous cherry, raspberry and anise aromas and flavors flecked by touches of chocolate, coffee, nutmeg and other exotic spices. The tannins are there but not domineering, making for a seductive wine that ought to start being presenable around 1994 to '98. 6,600 cases made.", 36.0m, "1985", new Guid("84c78f9f-c386-4d51-b60e-b17e0aa87f3e") },
                    { new Guid("9ff0d7ef-687d-403f-b293-baa0401920cb"), 3, new DateTime(2005, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley Landslide", "A delicious Cabernet at a terrific price. Smooth, rich and juicy, with tiers of lively black cherry, wild berry and blackberry fruit that are deeply concentrated with a supple, elegant mouthfeel. On the finish the tannins show some grip and a nice dusty, cedary oak quality adds a note of complexity. Best from 2005 through 2011. 7,175 cases made.", 33.0m, "2001", new Guid("5d10a75c-5158-4416-ae06-0113349440c1") },
                    { new Guid("f85a9196-2c2b-4a56-97c0-bfb3e45829f9"), 3, new DateTime(2019, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Oakville Reserve", "This delivers a very tightly focused, almost piercing beam of cassis, raspberry coulis and bitter cherry fruit, scored with singed alder and iron notes. Incense and tobacco details chime in on the finish. Long and refined, with the fruit leaving a sweet and mouthwatering echo. Best from 2022 through 2038. 4,800 cases made.", 150.0m, "2016", new Guid("f1f9524a-a606-4952-b1f1-fe660d2750d1") },
                    { new Guid("1357ec36-7020-4d60-83a0-6ec3ff660011"), 6, new DateTime(1998, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Reserve", "Smooth, ripe, rich and creamy, with pretty pear, vanilla, tangerine and spicy flavors. Turns silky-smooth on the palate and finishes with a long, rich, complex aftertaste, where hints of anise and butterscotch emerge. Drink now through 2002. 3,500 cases made.", 29.0m, "1995", new Guid("5d10a75c-5158-4416-ae06-0113349440c1") },
                    { new Guid("a0da35b7-13b3-4330-9114-7caf87729f9a"), 3, new DateTime(1990, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley Reserve", "Dense, rich and complex, this wine keeps unfolding with every sip, offering coffee, cherry, nutmeg, plum, and then herb and currant flavors and a velvety texture. Drink from 1995. 2,300 cases made.", 30.0m, "1985", new Guid("5d10a75c-5158-4416-ae06-0113349440c1") },
                    { new Guid("f51ccf60-ffee-4eeb-b370-e079df94e691"), 2, new DateTime(1990, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Johannisberg Riesling Napa Valley Edelwein Gold", "With its golden color and honey, apricot and subtle spice aromas and flavors, this is a knockout from the first whiff to the last echo of the long finish. A rich, sweet wine that is drinkable and seductive now and should continue to develop through 2000. 1,000 cases made.", 22.0m, "1989", new Guid("611b1e6c-f3d4-4b9c-bbc6-fe7cd4140081") },
                    { new Guid("d729a33d-1b45-4fb0-a86b-3fa777cc4bf0"), 6, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Fumé Blanc Napa Valley", "Rich and opulent, with sharply focused sweet pea, fig, melon and herb notes that are intense and lively. Has fine depth, elegant balance and flavors that linger long and full on the finish. Drink now through 1993. 8,000 cases made.", 10.0m, "1988", new Guid("a1b9022d-bb90-491b-85c8-308a942bf059") },
                    { new Guid("e360933f-bf59-4aaa-8302-a89589450c09"), 6, new DateTime(1989, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Johannisberg Riesling Napa Valley", "Distinctive for its earthy, slate-like aromas and juicy, crisp, nectarine, peach, spice, pine and pear flavors that are complex and very attractive. Exceptionally well-made Riesling. 4,500 cases made.", 8.0m, "1987", new Guid("a1b9022d-bb90-491b-85c8-308a942bf059") },
                    { new Guid("e0e187b7-425f-48d0-9c9c-d4484708e826"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Yakima Valley Otis Vineyard David Lake Signature Series", "Smooth and polished, an elegant red with depth of flavor that sneaks up on you. Currant, blackberry, smoky spice and dried tomato flavors compete for attention, building in intensity as the fine tannins subside on the long finish. Best from 2006 through 2015. 2,380 cases made.", 29.0m, "1999", new Guid("30602439-662e-4c0e-ad22-c8de7f7767a6") },
                    { new Guid("fa95cf50-b5af-42df-8b03-e397495d563c"), 3, new DateTime(1998, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Yakima Valley Red Willow Vineyard David Lake Signature Series", "Dark, dense and peppery, with lots of black cherry and blackberry flavors at the core, chewy tannins and a bite of earthy spice on the finish. Give it until 1999 to 2000 to soften. 2,400 cases made.", 24.0m, "1995", new Guid("30602439-662e-4c0e-ad22-c8de7f7767a6") },
                    { new Guid("19a24b08-a655-40d2-a17c-2ce68f190e1d"), 6, new DateTime(1993, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley Woodburne Cuvée", "This Washington wine has all the facets and depth you want in a barrel-fermented, malolactic-style Chardonnay.  Apple and pear flavors are spiced up by toasty, buttery aromas and a spicy, butterscotchy note on the finish.  Rich, beautifully balanced and complex. 7,500 cases made.", 12.0m, "1991", new Guid("30602439-662e-4c0e-ad22-c8de7f7767a6") },
                    { new Guid("050b1596-c9c0-4abf-97e7-a0d18a38261b"), 3, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Yakima Valley Otis Vineyard", "  ", 18.0m, "1986", new Guid("30602439-662e-4c0e-ad22-c8de7f7767a6") },
                    { new Guid("49b7a434-f11b-47ec-8be0-338828e7e953"), 3, new DateTime(1989, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pessac-Léognan", "Rich and elegant with a firm, tannic structure, but the fruit comes rolling through on the finish. The Cabernet flavor is matched with more austere earth, cedar, mineral and plum flavors. Drink now or cellar until 1996.  ", 30.0m, "1986", new Guid("a25a3b79-32d6-4e9d-82bc-ac29c8a3aa9b") },
                    { new Guid("86b3671c-72df-43cc-9957-34f3b0f612d9"), 3, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Coltassala", "A sturdy and tannic Sangiovese, with complex aromas that are intriguing and distinctive, but a tough texture makes it difficult to enjoy now. Packed with cherry, olive, coffee and spice flavors that linger nicely on the finish. Try after 1997. 1,900 cases made.", 26.0m, "1990", new Guid("13f2b5b9-3163-482c-9428-9481fd9fdded") },
                    { new Guid("07ccafb0-4e6f-410a-9083-b2080e0294e4"), 6, new DateTime(1990, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Sonoma County", "A polished, distinctive wine that's crisp and flavorful, with ripe, spicy, concentrated flavors of rich, honeyed quince and fig. Has hints of grassiness and impressive length. Drinkable now. 10,000 cases made.", 8.0m, "1988", new Guid("5d10a75c-5158-4416-ae06-0113349440c1") },
                    { new Guid("cceecc9f-b093-4f75-b1fb-a7867bba3aea"), 3, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Unico Gran Reserva", "Sappy aromas and flavors detract from an otherwise nicely built, smooth and buttery wine. Drinkable now, but don't wait. 1,000 cases made.", 75.0m, "1979", new Guid("3ff8d16a-be73-4441-a050-c6fbcc32d6da") },
                    { new Guid("224f26f4-42d9-439d-b913-9e482c098cc1"), 3, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Valbuena 3.° Reserva", "A vivid, vibrant Spanish wine, with focused, concentrated wild berry and plum aromas and flavors framed by sweet oak and a touch of anise. There is so much fruit it is tempting to drink it now, but it has the structure to last through at least 1994 or 1995. 1,000 cases made.", 55.0m, "1985", new Guid("3ff8d16a-be73-4441-a050-c6fbcc32d6da") },
                    { new Guid("b06b23d7-a78f-4daa-a540-e73652c42971"), 6, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Schoenenbourg", "  ", 19.0m, "1988", new Guid("9ab763a1-fdec-4263-afc5-e3fe8931282a") },
                    { new Guid("324e1536-2655-40cd-8542-fe6493febcec"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Profile Napa Valley", "Massive, with wonderful richness, depth and concentration, this Cabernet blend has layers of complex currant, black cherry, plum, vanilla and blackberry and a long, detailed, firmly tannic finish. Best from 2002 through 2012. 5,200 cases made.", 85.0m, "1997", new Guid("06e49f30-3624-4646-8ebb-4acae646e213") },
                    { new Guid("941581a9-d229-465f-b99f-042d3c26d22e"), 6, new DateTime(1997, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Starmont", "A remarkably complex California white, with tiers of ripe pear, fig, apple and vanilla flavors that linger for a long aftertaste. The texture is smooth and polished finishing with a creamy quality. 10,000 cases made.", 18.0m, "1995", new Guid("06e49f30-3624-4646-8ebb-4acae646e213") },
                    { new Guid("d210da03-a78c-4679-ba14-8dd3e41d905c"), 5, new DateTime(2007, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Extended Tirage Willamette Valley", "A stunner. Still fresh after 10 years. Pear, apple and orange marmalade flavors weave through a delicate structure and a flavor profile with distinct toastiness. It's the freshness that makes this elegant sparkler special. Drink now. 775 cases made.", 40.0m, "1997", new Guid("3cd4c4d4-d7ea-4fbd-966e-c405c5a67886") },
                    { new Guid("f9197e89-f88d-4f0a-ab12-92654587bac8"), 3, new DateTime(2006, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Nuthouse", "Polished and rich in texture, a bit hot but expanding beautifully to show off its ripe cherry and blackberry and faint notes of earth and shiso that linger along with the fruit, wrapped in a light veil of toasty oak. Best from 2008 through 2013. 1,810 cases made.", 45.0m, "2003", new Guid("3cd4c4d4-d7ea-4fbd-966e-c405c5a67886") },
                    { new Guid("2dd73d73-17be-4855-9093-55e912855cd7"), 6, new DateTime(1998, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Willamette Valley Reserve", "Bright layers of pear, citrus and quince unfold beautifully on an elegant frame, combining fresh flavors with velvety texture and impressive length. Drink now through 2002. 553 cases made.", 20.0m, "1995", new Guid("3cd4c4d4-d7ea-4fbd-966e-c405c5a67886") },
                    { new Guid("8b852924-0385-4ec4-9bcf-d9a99ee2bd87"), 6, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Oregon Barrel Fermented", "Best Oregon Chardonnay we've ever tasted.Full-bodied and rich but graceful, with plenty offruit flavor and complex accents of honey, spiceand vanilla. Its elegant, supple style supportsclassic pear, apple and melon notes. Drink now. 1,000 cases made.", 19.0m, "1987", new Guid("3cd4c4d4-d7ea-4fbd-966e-c405c5a67886") },
                    { new Guid("1b141bfe-f8c8-4665-ad2a-c53b55e0d673"), 3, new DateTime(1990, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tierra de Barros Lar de Barros Reserva", "Earthy, ripe, complex and distinctive, with toast, cherry, prune and chocolate flavors intertwined in a soft, velvety package that firms up and gets a little tannic on the finish. Long and concentrated, this complex wine is unlike any other we have tasted. 12,500 cases made.", 8.0m, "1986", new Guid("189d2a50-74ee-43ed-b462-f44d7b6f2c6f") },
                    { new Guid("67af1310-f993-425f-8d4a-fa49c5baef8c"), 6, new DateTime(2002, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Cuvée Sauvage", "Rich, elegant and stylish, with pretty hazelnut, toasty marshmallow, pear, fig and citrus flavors that are nicely proportioned. Drink now through 2007. 3,600 cases made.", 35.0m, "1999", new Guid("fdeb0aee-941c-4ede-befd-5ef824714c46") },
                    { new Guid("cda759ef-afee-4913-810d-80384f5d451d"), 3, new DateTime(2001, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Magnificat Napa Valley", "Appealing from start to finish. It's smooth and seductive, with pretty toasty oak, spice, currant and black cherry flavors. Mocha, raspberry and cinnamon come in on the finish, along with plush tannins. Great length. Cabernet Sauvignon, Merlot and Cabernet Franc. Drink now through 2010. 11,500 cases made.", 35.0m, "1997", new Guid("fdeb0aee-941c-4ede-befd-5ef824714c46") },
                    { new Guid("3d1cc55c-c25e-47a2-9885-1733dfaba293"), 6, new DateTime(1990, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Reserve", "Big, rich and creamy, with buttery pear and subtle oak shadings that offer complexity and finesse and a rich toasty-smoky aftertaste that lingers. Elegant and stylish. Drink now. 600 cases made.", 15.0m, "1987", new Guid("fdeb0aee-941c-4ede-befd-5ef824714c46") },
                    { new Guid("90142373-cbeb-4b04-aabf-197ff373efe0"), 5, new DateTime(1990, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Gold Label", "A super Champagne. Wonderfully focused and well balanced, with focused lemon and apple aromas, sweet, ripe lemon flavor and hints of dough. Medium-bodied, with a creamy mouth-feel. 40,000 cases made.", 37.0m, "1985", new Guid("f6811d3d-2bb6-4a3f-889f-672b617ff13e") },
                    { new Guid("3c289ec6-6963-46c8-84d6-0e675f13ea0e"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Carneros Premiere Vineyard", "Lots of flavor and a delicate texture add up to a wine with finesse and elegance. Floral, pear and hazelnut aromas and flavors are immediately attractive, with richness to burn. Spicy fruit notes emerge on the finish. Drink now. 1,000 cases made.", 14.0m, "1989", new Guid("1cbbb31f-505f-4c36-b8d8-940f830bdb68") },
                    { new Guid("898fd6b5-01da-4851-b70f-d77d1d991fde"), 6, new DateTime(1990, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pessac-Léognan White", "One of the best Bordeaux whites we've tasted in a while, deliciously rich and complex with tiers of citric, earth, stone and fig flavors that are intense and lively. The finish goes on and on. Definitive style. Drink now through 1995. 3,500 cases made.", 15.0m, "1987", new Guid("5de68eb4-9fc9-41e7-a796-dce1890d87f9") },
                    { new Guid("9cf6aea9-6680-4d35-a356-f28653e21176"), 3, new DateTime(1996, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Bonny's Vineyard", "Dark, rich and complex, with tiers of polished currant, cherry, herb, dill and tea flavors. A delicious mouthful of Cabernet that packs in lots of flavor yet manages to remain elegant and not overly tannic. Picks up a complex tarry note on the aftertaste.  ", 50.0m, "1991", new Guid("8d2de1b1-849c-450b-9992-94efe35d8841") },
                    { new Guid("353eed3d-84d7-4653-8c07-95d9571cf3f4"), 3, new DateTime(1990, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley", "Smooth, harmonious and distinctive, with well-integrated currant, smoke, herb and meat aromas and flavors marked by lavish oak. A beautifully crafted wine, echoing all the complex flavors on the finish. Drink from 1995. 17,500 cases made.", 48.0m, "1986", new Guid("8d2de1b1-849c-450b-9992-94efe35d8841") },
                    { new Guid("1e49a099-92ee-489a-92e7-5283e39851f0"), 3, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Monprivato", "A pure, ethereal Barolo, boasting rose, cherry and strawberry aromas and flavors, with a touch of tar. Very harmonious and elegant, with firm yet well-delineated tannins supporting the whole. Old-school and refined. Best from 2016 through 2035. 1,830 cases made.", 125.0m, "2007", new Guid("b8bbe0fd-cacc-4fa8-a549-1c5c01484954") },
                    { new Guid("59946521-b087-4a5e-8f7d-c372aad1bc95"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Marcarini", "Sensational expression of Nebbiolo, exploding with black cherry, wild berry, lemon verbena and anise flavors without an overlay of oak, an exotic wine that holds itself in check just enough to qualify as elegant. Fresh and vibrant, tempting to drink now, but probably best from 1996. 600 cases made.", 32.0m, "1988", new Guid("b8bbe0fd-cacc-4fa8-a549-1c5c01484954") },
                    { new Guid("c4f87a6f-9b46-4838-be32-1c39fe594e19"), 3, new DateTime(1990, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Santo Stefano di Perno", "Powerful, complex and elegant, with blackberry, plum, cedar, smoke and gamy aromas and flavors that carry through the long finish. Tannic but balanced. Cellar until 2000. A better showing than two previous tastings. 800 cases made.", 35.0m, "1985", new Guid("b8bbe0fd-cacc-4fa8-a549-1c5c01484954") },
                    { new Guid("3f56d794-a1c5-44c2-b23e-054765b3ef0f"), 3, new DateTime(1992, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Barbara County", "Complex and intriguing for its band of flavors, offering lots of smoky raspberry, cherry, earth and tar notes that give dimension to the cola and oak shadings. Intense and lively, yet smooth in texture, finishing with a rich core of fruit flavor and a decadent edge that adds to the wine's interest. Drink now. 4,000 cases made.", 17.0m, "1990", new Guid("58e46c98-bb71-4a85-b27a-d3a63f978427") },
                    { new Guid("2766274b-8a01-45bb-9969-a8ebf7c38c01"), 6, new DateTime(1990, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Barbara County Barrel Select", "A real hummer. Rich, lush and fruity without being heavy, it shows tons of fruit and a long, lingering aftertaste. Has bold, complex aromas and flavors of pineapple, butter, pear, cream and honey, full-bodied with firm acidity. 1,000 cases made.", 25.0m, "1988", new Guid("58e46c98-bb71-4a85-b27a-d3a63f978427") },
                    { new Guid("e86a4c94-5af4-4d95-9e9e-2524e5449302"), 3, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Old Vines", "Well oaked, with lots of smoky, toasty flavors, but the fruit stands right up to it, with ripe cherry, raspberry and plum notes. Complex, flavorful aftertaste. Drink now through 2003. 25,000 cases made.", 20.0m, "1997", new Guid("d49a01a0-28ec-4899-ae2f-6a4d0352ec5f") },
                    { new Guid("75036620-f346-4ea9-a407-85894e34fefa"), 3, new DateTime(1995, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma Valley Old Vines", "Complex and appealing, the minty, spicy notes are followed by pretty cherry and raspberry flavors. Turns smooth and supple, with mild tannins. A big, bold style that manages to hang in balance. 1,680 cases made.", 18.0m, "1993", new Guid("d49a01a0-28ec-4899-ae2f-6a4d0352ec5f") },
                    { new Guid("12ca899a-e8d2-492f-b12c-94f3b0d1138a"), 3, new DateTime(1990, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Sonoma Valley Reserve", "Haul out the thesaurus. This one deserves all the adjectives you can find. Rich, opulent, supple and complex, with generous plum and vanilla aromas and flavors, magnificent depth and concentration. Drinkable now. 1,042 cases made.", 28.0m, "1986", new Guid("d49a01a0-28ec-4899-ae2f-6a4d0352ec5f") }
                });

            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("afefd68c-4e9f-4030-81a8-c5a4564c90a2"), 3, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Washington Limited Bottling", "A deep color and dense, concentrated raspberry and spice aromas mark this as something special even before the elegant plummy richness hits the palate. A velvety texture and sense of harmony make it deceptively drinkable today, but it can age, at least through 1993 to '95.  ", 10.0m, "1987", new Guid("853705e9-e4e1-467d-9b81-1e8817c16042") },
                    { new Guid("20a3755b-978f-4f00-bd2b-eb2b72c3fb51"), 3, new DateTime(1992, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Selezione del Trentennio '30'", "Intense and concentrated, with sharply focused cherry, raspberry, anise and rose petal flavors that are deep and complex. The tannins and acidity taste raw. Very tightly wound. Cellar until 1999 for best results. 2,000 cases made.", 28.0m, "1988", new Guid("dcd28546-c220-49a7-9724-49052ec83f16") },
                    { new Guid("726ea795-ede1-45d6-9b8d-eb3e9ef62b0b"), 6, new DateTime(1988, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Private Reserve", "Firm and concentrated, a tightly wound wine with toasty aromas and ripe but reined-in flavors of pear and butterscotch. Silky texture, beautifully foc used and long. Shows a great deal of subtlety and plenty of room to grow.  ", 16.0m, "1986", new Guid("b66dde21-363a-4f92-b9dd-d755b5b0e12c") },
                    { new Guid("593da6a7-9ff4-493b-8996-e56ceeaaca9b"), 6, new DateTime(1999, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Valley Durell Vineyard", "This is a delicious mouthful of Chardonnay. It's big, bold, rich and creamy, brimming with tiers of flavor--vanilla, pear, anise, fig and melon--and finishing with harmonious notes of smoke and nectarine. Drink now through 2002. 2,847 cases made.", 45.0m, "1996", new Guid("f84dd971-999d-4a27-a2ab-771522ae679c") },
                    { new Guid("fec7da89-b04e-48cb-9c4c-9e30dfdb010c"), 3, new DateTime(1988, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Pèppoli", "Concentrated and beautifully formed, with sharply focused cherry, blackberr y and plum flavors, firm structure, long and ripe. Needs a year in the cell ar to tame the power.  ", 16.0m, "1985", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("831f829a-9f0b-401a-aab0-6bfa1587996b"), 6, new DateTime(2017, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay California Vintner's Reserve", "Plush, with loads of richness to the well-honed flavors of beeswax, baked pear and lemon tart. Minerally midpalate, with a fresh and zesty finish that finishes with pastry notes. Drink now through 2020. 100,000 cases made.", 17.0m, "2015", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("25fb719f-f343-4da0-97cc-46b0391c0f78"), 3, new DateTime(2001, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley Great Estates", "A tremendous effort, very rich and full-bodied, with plush tannins wrapped around a complex, concentrated core of earthy currant, toasty oak, hints of sage and chocolate and rich black cherry fruit. Beautifully focused, elegant and tapered on the long, detailed finish. Drink now through 2008. 4,280 cases made.", 40.0m, "1997", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("67a67ede-a003-4377-82c5-e292e36f3ffe"), 6, new DateTime(2001, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Monterey Great Estates", "Maintains a sense of elegance and finesse, even with its rich, creamy core of earth, pear, fig, citrus, hazelnut and nectarine flavors. Long and complex on the finish. Drink now through 2005. 4,029 cases made.", 27.0m, "1999", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("72b270eb-4a61-48e5-b37d-e36787c7b673"), 6, new DateTime(1999, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Maria Valley Camelot Vineyard Single Vineyard Series", "Brimming with complex flavors, this California Chardonnay offers a harmonious blend of ripe pear, nectarine, pineapple and spice, with smoky, toasty oak filling in the gaps nicely. Drink now through 2002. 62,000 cases made.", 19.0m, "1996", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("c484ee1c-1772-42ec-aade-fc472a823248"), 3, new DateTime(1992, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cardinale Meritage California", "Rich, ripe and concentrated, bursting with plum, blueberry and currant aromas and flavors laced with spicy, chocolaty oak that echoes vanilla and cinnamon on the long finish. Keeps pumping out the flavor for minutes on end. A really classy mouthful; should develop and soften. Best from 1997 through 2000. 1,900 cases made.", 44.0m, "1987", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("d44f21cc-edac-4f9e-8f51-97502656b2b1"), 6, new DateTime(1990, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Lake County Vintner's Reserve", "With powerful grassy, fruity aromas, full, crisp flavors and a lingering, light finish, this is unmistakably Sauvignon Blanc. Herbs, vanilla and tasty peach flavors fill out the profile. 80,000 cases made.", 9.0m, "1989", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("d95d7bf9-b31d-423e-a271-e511a2389bf3"), 3, new DateTime(1989, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon California Cardinale", "You'd have to travel far to find a more complex and concentrated wine, from the first taste of currant, cherry and plum to the last waft of buttery oak and plum on the long finish. The extraordinary structure and integrated tannins should last beyond 2000. 1,000 cases made.", 45.0m, "1985", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("4988f392-29aa-4877-b370-f29a6d409426"), 3, new DateTime(1988, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Alexander Valley", "Effusively fruity, bursting with cherry aroma and flavor, complicated by other berries and the right touch of oak, beautifully structured, firm, tight and long. Needs time to lose the tannic toughness on the finish. Best after 1991.  ", 16.0m, "1986", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("f5f12b67-7c75-4def-91c1-207f46bdca01"), 3, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon California Proprietor's Reserve", "Sleek and elegant with an overlay of toasty French oak, layers of spice, currant, cassis and cherry flavors that are very attractive, beautifully balanced, supple tannins and a long, satisfying finish. Best after 1992.  ", 20.0m, "1985", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("ffe4c21b-dc19-442f-aa3a-278d210ff4ac"), 3, new DateTime(1988, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Santa Cristina", "A stylish wine that offers great value. Good concentration of cherry and ra spberry fruit with overtones of smoke, chocolate and ripe plum. Delicious n ow but it will be even better to drink after 1991 when the tannins have smo othed out.  ", 6.0m, "1985", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("5b1fd11f-7bcc-4682-8dcd-541cc106e19d"), 3, new DateTime(1988, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Sonoma Valley Durell Vineyard", "Supple and rich, with spicy cherry aromas and flavors, beautiful overtones of sweet oak and cinnamon; voluptuous and succulent—you could drink it now in a fit of sheer hedonism, but give it till 1991 or '92 for the full effect.  ", 14.0m, "1986", new Guid("2307d7db-d4be-42d6-9f58-3974be8db4aa") },
                    { new Guid("9f09b8e6-3a35-4eef-bc4d-ca09623343b9"), 2, new DateTime(1995, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscat Canelli California Vin de Glacière", "Sweet, rich and profound, a Muscat of astounding depth and complexity. The rich apricot, golden raisin and honey flavors persist on the long finish. A wonderful dessert wine. 5,000 cases made.", 15.0m, "1994", new Guid("7fd64f77-589c-4d69-9491-d331455afb1a") },
                    { new Guid("9ecbf0a0-3138-439a-87a6-31f7f80d84a9"), 2, new DateTime(1993, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscat Canelli Monterey Vin de Glacière", "Brilliantly spicy, fruity and generous, showing its flavors with digital clarity—nutmeg, cinnamon, pear, apple and honey—finishing sweet, rich and amazingly balanced. Drinkable now. 3,200 cases made.", 15.0m, "1992", new Guid("7fd64f77-589c-4d69-9491-d331455afb1a") },
                    { new Guid("8191808f-feb7-4bb5-a1d7-b335e6223a4f"), 3, new DateTime(1988, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Oregon Bethel Heights Vineyard", "Very pretty aromas and flavors, featuring violet, blackberry and a touch of lime, crisp balance, long and intense. Very tannic, but the rest is lovely .  ", 18.0m, "1985", new Guid("7fd64f77-589c-4d69-9491-d331455afb1a") },
                    { new Guid("a2abb581-160d-411c-90d8-6ee2e13437d8"), 3, new DateTime(1988, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Le Cigare Volant California", "Big and rich, with incredibly deep cherry and blackberry fruit, massive concentration, long and turning spicy on the finish, but it's not at all heavy . Smooth and appealing now, but it can only get better with age. Drink now through 1995.  ", 14.0m, "1986", new Guid("7fd64f77-589c-4d69-9491-d331455afb1a") },
                    { new Guid("753d1633-df8d-4691-9a79-a8be8aeed788"), 3, new DateTime(2005, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Rufina Castello di Nipozzano Riserva", "Very pretty aromas of licorice, berry and lightly toasted oak. Full-bodied, with layers of fruit and silky tannins. Long finish. Serious wine. Serious value. Best after 2005. 72,800 cases made.", 21.0m, "2002", new Guid("f696a22c-33db-4254-95e0-e3499fd5eea7") },
                    { new Guid("8e8970db-0584-42e4-bf4b-959355f0d53e"), 3, new DateTime(2005, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Castelgiocondo Ripe al Convento Riserva", "Very structured. Superattractive aromas of plum, blueberry and pressed flowers follow through to a full-bodied palate, with firm, silky tannins and a long, caressing finish. Layers and layers of ripe fruit, with everything in beautiful proportions. Almost a replay of the 1997, which scored 98 points. Best after 2006. 2,200 cases made.", 99.0m, "1999", new Guid("f696a22c-33db-4254-95e0-e3499fd5eea7") },
                    { new Guid("6596a74b-91e2-403f-a0ab-9c1d677831e4"), 3, new DateTime(2003, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Rufina Montesodi", "A beauty. Dark, with beautiful aromas of blackberry, mineral and spice. Full-bodied, with a solid core of fruit and tannins and a long, long finish. Pure Sangiovese. Best after 2006. 5,800 cases made.", 40.0m, "2001", new Guid("f696a22c-33db-4254-95e0-e3499fd5eea7") },
                    { new Guid("70f81c36-9f3d-460a-91ff-ad82d74520ff"), 3, new DateTime(2002, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Castelgiocondo", "Superbly crafted. Pretty aromas of black cherry, cream and licorice. Full-bodied, yet incredibly tight and smooth, with ultrafine tannins and a long, long, velvety finish. Best after 2004. 12,000 cases made.", 62.0m, "1997", new Guid("f696a22c-33db-4254-95e0-e3499fd5eea7") },
                    { new Guid("6b249b0b-cece-401c-9758-f5e757399ee2"), 3, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pomino Tenuta di Pomino Red", "Densely concentrated, both color and aroma, but lean and elegant, almost delicate of structure, showing beautifully focused plum, anise and cassis, with a touch of chocolate and wee bit of herbs on the finish. Remarkable and distinctive.  ", 12.0m, "1985", new Guid("f696a22c-33db-4254-95e0-e3499fd5eea7") },
                    { new Guid("2524786b-0be0-4790-af27-7b8d37b8ce21"), 6, new DateTime(1992, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Les Preuses", "Bold and intense, with rich, lively pear, peach, honey and citrus flavors that are fresh and lively. Complex and concentrated, showing a burst of fruit on the finish. Drinkable now. 1,000 cases made.", 40.0m, "1990", new Guid("7bcb7a71-52df-4dc5-9530-58d6f5b255b6") },
                    { new Guid("c52c0a40-45c2-4468-96ab-914ce50d5f7b"), 2, new DateTime(1997, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscat California Vin de Glacière", "Seductive, sweet and silky, remarkable for the purity of its spicy pear, apricot and citrus flavors, hinting at litchi on the slick finish. Utterly delicious now. Why wait? 13,500 cases made.", 15.0m, "1996", new Guid("7fd64f77-589c-4d69-9491-d331455afb1a") },
                    { new Guid("cb5064fa-2fda-4b19-8969-4640946109fe"), 6, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Les Clos", "Toasty and concentrated with rich pear, apple, spice and toasty spice flavo rs that explode on the palate. It's tart and vibrant with depth, complexity and a long full flinty finish that says Chablis.  ", 40.0m, "1986", new Guid("7bcb7a71-52df-4dc5-9530-58d6f5b255b6") },
                    { new Guid("84e7af91-8dec-476f-ba72-e6bcdad911a0"), 3, new DateTime(1995, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Solaia", "What a beautiful wine! A classy Tuscan vino da tavola with a crisp character, but there are plenty of lovely, earthy cassis and sweet, ripe plum flavors. Delicious from start to finish. Watch for the tannins; better in 1998. 5,000 cases made.", 65.0m, "1991", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("0c2a4d24-fc1b-46ef-8eb9-42b555d94c0f"), 3, new DateTime(2000, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Superiore Guado al Tasso Tenuta Belvedere", "A balanced and harmonious young red. Dark ruby color, with loads of grape, berry, mineral and violet aromas. Full-bodied, with loads of blackberry and cherry character, fine tannins and a medium finish. Gorgeous wine. Best after 2005.  ", 52.0m, "1997", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("0ee16f46-472c-48dd-a950-06edcb21d449"), 3, new DateTime(1988, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Il Grigio Riserva", "Remarkably youthful color and lovely violet-tinged fruit on the nose only hints at the rich, raspberry, cherry and plum fruit on the palate. Hints of toast and chocolate chime in on the velvety finish.  ", 11.0m, "1982", new Guid("47d20af2-7fbb-4432-aa06-5def37311ad1") },
                    { new Guid("bf5e949a-08a0-45d9-9cf7-14e8cdf27305"), 3, new DateTime(1988, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Predicato di Bitùrica", "Dark, dense and ripe, with concentrated cherry, berry and plum on the nose and palate, silky smooth, chocolatey, beautifully balanced. Drinkable already, but it has the stuff to grow.  ", 19.0m, "1982", new Guid("47d20af2-7fbb-4432-aa06-5def37311ad1") },
                    { new Guid("f49fd3f3-1364-43f7-bedf-06ac7c0e5cfb"), 3, new DateTime(1990, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Pesquera Reserva", "This tight, concentrated and complex Spanish red oozes with fruit flavor and has very firm tannins and acidity. Blackberry and raspberry flavors linger on the finish, joined by hints of butter and spice. Tempting to drink now, but better to cellar throug 2,500 cases made.", 25.0m, "1986", new Guid("04d4d3d8-b9f0-481f-b4b5-2cb9169bdc8d") },
                    { new Guid("01368fcd-7ab2-4ae2-bb21-0e08f4e6276e"), 3, new DateTime(1989, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Pesquera Crianza", "Jam-packed with concentrated rapsberry and plum aromas and flavors, plus plenty of tannin to earmark this for the cellar, perhaps until 1993-95. Beautifully balanced and focused.  ", 20.0m, "1986", new Guid("04d4d3d8-b9f0-481f-b4b5-2cb9169bdc8d") },
                    { new Guid("00c819ce-4a2c-459c-bd0d-0f5a2047c976"), 3, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Pesquera Janus Reserva", "Deep, rich, ripe and powerful, with slightly earthy-herbal plum and cherry flavors, big and complex, tannic, with a touch of anise on the finish. Complex and fascinating. It will need a good 5-10 years to shed its tannin, but the concentration is superb.  ", 75.0m, "1982", new Guid("04d4d3d8-b9f0-481f-b4b5-2cb9169bdc8d") },
                    { new Guid("8cd89780-bb39-4c0e-bcc2-5792e154ff8f"), 3, new DateTime(2019, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley RWT Bin 798", "Offers a lush, juicy mix of blackberry, blueberry and black cherry flavors that appears seamless, integrating with notes of amaretto, clove and lavender. Plush, juicy and very generous on the long, expressive finish. Drink now through 2034. 1,156 cases imported.", 150.0m, "2017", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("3cf28f9a-7c63-424d-ba32-1dc8541a20e2"), 3, new DateTime(2009, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Australia St. Henri", "Aromatic, complex and silky, slipping in its gorgeous blackberry, currant and plum fruit against a seething background of toast, spice, mineral and loamy earth. The texture is seamless, and the flavors just take off, sailing through the long, expressive finish. Best from 2011 through 2020. 1,000 cases imported.", 65.0m, "2005", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("56b837e2-98f4-4f32-a1b6-ae11f095e802"), 3, new DateTime(2003, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Valley RWT", "Rich in character and aristocratic in structure, a noble red that wears its subtle cherry, blackberry, chocolate, pepper and sweet spice flavors on a polished frame like a bespoke suit, letting them all play out elegantly on the well-buffed finish. Drink now through 2015. 3,800 cases made.", 69.0m, "2000", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("b0a40094-5c22-405b-ba61-59bc2aa977df"), 6, new DateTime(2002, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Adelaide Hills Thomas Hyland", "Silky, supple and creamy, beautifully balanced to layer its intense nectarine, pear, lime and cream flavors, which linger effortlessly on the elegant finish. Drink now through 2008. 14,390 cases imported.", 14.0m, "2001", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("11bea734-6d71-4467-9d64-200712537818"), 3, new DateTime(2001, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Coonawarra Bin 128", "Rich flavors of dark berry and cherry are balanced against a wiry backbone of fine tannins and acidity in this outstanding Shiraz, gaining momentum on the finish. Impressive for its intensity and elegance. Drink now through 2010. 82,489 cases made.", 22.0m, "1998", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("e09c4304-cf64-470e-9f2e-d9b6f9db3f6d"), 3, new DateTime(2000, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Solaia", "Dark ruby in color, with extremely ripe raisin and spice aromas. Full-bodied and very chewy, with loads of polished tannins and a long, long finish that's big and fruity. A solid, muscular red fromTuscany, this is the greatest Solaia ever made. Best after 2004. 7,000 cases made.", 115.0m, "1997", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("5e817971-40b5-4fb2-b141-28cce08c6eb3"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Australia Kalimna Bin 28", "Smooth and harmonious but tightly wrapped in fine layers of tannin, focusing its blackberry, anise and toasty-peppery flavors in a solid beam that keeps shining through the finish. Best in 2003 to 2005. 35,000 cases made.", 18.0m, "1993", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("c4f40af7-17b3-41c4-97d7-050efd4d535d"), 3, new DateTime(1995, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet-Shiraz South Australia Bin 389", "Rich, smooth and plush, with complex currant, black cherry, anise and spice flavors that are bold and intense. Finishes with firm tannins, but the fruit still manages to gush through on the finish. 25,000 cases made.", 16.0m, "1992", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("8000ef05-6657-4636-90ee-62a3627d39b9"), 3, new DateTime(1995, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz South Australia Grange", "Amazingly ripe, rich and opulent, with layers of earthy currant, black cherry and anise flavors that are smooth and supple. The silky texture leads into a long, lingering aftertaste that offers echoes of fruit. The tannins are fine but substantial enough to age. Try from 1997 to 2005. 8,000 cases made.", 100.0m, "1990", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("09c937d4-8fc6-4952-a0dd-b7e41ebe8a72"), 3, new DateTime(1988, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grange Hermitage South Australia Bin 95", "A masterpiece, loaded with rich, smoky, plum, bacon, herbs and anise flavors woven together in a stylish packege, offering depth, complexity and impeccible balance, finishing with chocolate vanilla. Has intensity for decades.  ", 49.0m, "1981", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("7782b10e-eff3-4440-bb87-40b70fcc5659"), 3, new DateTime(1988, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Penedès Gran Sangre de Toro Reserva", "Rich and complex, with earthy, leathery aromas and flavors around a solid core of cherry and berry fruit, all of which comes together in a complex finish that lasts and lasts. There's enough to take it at least four or five years.  ", 9.0m, "1983", new Guid("94d4e8bf-c5a1-4aae-85c2-4913dd1cfe5c") },
                    { new Guid("48bb5fe4-bdeb-4179-9923-dabad2c642d6"), 6, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Penedès White Milmanda", "Stunning wine, beautifully balanced, smoothly knit, flavorful and long. It does a great impersonation of a Chevalier-Montrachet, with all the spice, toast and honey around a solid core of pear fruit. Will it age like it? Who knows, but it's pro.  ", 35.0m, "1987", new Guid("94d4e8bf-c5a1-4aae-85c2-4913dd1cfe5c") },
                    { new Guid("42de2c14-92b2-4dc0-8d8b-6390f2286a4f"), 3, new DateTime(2007, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Tignanello", "Offers aromas of blackberry, with hints of raisin and lots of spices. Full and velvety, with wonderful concentration and a long, rich finish. Very stylish and exciting. Sangiovese, Cabernet Sauvignon and Cabernet Franc. Best after 2012. 26,665 cases made.", 79.0m, "2004", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("80cdd0c8-4d35-4025-8481-4c9b164d6f02"), 6, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Umbria White Cervaro della Sala", "Wonderful aromas of apple pie, honey and cream follow through to a full body, with toasted coconut and rich tropical fruit on the palate. Very long. Always an excellent white. Chardonnay and Grechetto. Drink now. 13,000 cases made.", 42.0m, "2000", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("3baddb36-206b-4099-a780-c384d59beced"), 3, new DateTime(2002, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Pian delle Vigne", "Amazing Brunello. Fantastic aromas of blackberries, flowers and minerals. Captivating. Full-bodied, with incredible layers of ripe fruit and silky tannins. The finish last for minutes. Racy and firm now. Give it some bottle age. Best from 2004 through 2014. 12,000 cases made.", 70.0m, "1997", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("2a6bf60d-c6bd-4aab-85b9-c9a202e2a045"), 3, new DateTime(2001, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bolgheri Superiore Guado al Tasso", "Wonderful balance here. Violet and berry aromas, with intense tarragon and currant character. Full-bodied and gorgeous, with wonderful finesse and subtlety. Very long. Best after 2005. 9,500 cases made.", 65.0m, "1998", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("de626db0-d3da-4596-9013-9217db5d2915"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Badia a Passignano Riserva", "Impressive Chianti Classico riserva. Aromas of currants, blackberries and toasted oats. Full-bodied, with velvety tannins and a long, long finish. Still needs time to open. Best after 2001. 10,000 cases made.", 40.0m, "1997", new Guid("ca05b151-69e9-43be-8e5a-93ea8c2b57c6") },
                    { new Guid("fa3d8fc5-5083-447b-a8b6-ea04af4b56a7"), 3, new DateTime(1995, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon South Australia Bin 707", "Ripe and generous, elegantly balanced and velvety. The focused currant and plum flavors are shaded by a touch of smoke and spice, the fruit persisting beautifully on the finish. Best in 1997. 10,000 cases made.", 40.0m, "1992", new Guid("271676f6-ef37-41a0-b242-8a29043c5e0c") },
                    { new Guid("b7c8d04f-1bcc-47dd-88eb-7511d90bffb9"), 3, new DateTime(1988, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Julien", "Outstanding wine. Incredibly toasty and vanilla-like in aroma, with rich, thick flavors of coffee, smoke, cherry and chocolate. Velvety in texture, strong in tannins, beautifully balanced, with a long, sweet, cherry finish. Best Beychevelle in years.  ", 35.0m, "1985", new Guid("88299cd9-81c0-497e-9b71-beb27a50f733") },
                    { new Guid("c6716b4b-0a6c-4035-80fc-8df2eabaaa93"), 3, new DateTime(2012, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Monte Bello Santa Cruz Mountains", "Taut and structured, with cedar-laced currant, dark berry and dried berry notes joining a mix of minerally fresh earth and tobacco flavors, offering a wide range of tight-knit fruit overall. Firmly tannic on the finish, yet the ripe purity pushes through. True to form Monte Bello in a classic style. Cabernet Sauvignon, Merlot and Petit Verdot. Best from 2014 through 2030. 2,800 cases made.", 150.0m, "2009", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("b96f18a1-a1ad-4712-858a-758e6847b65d"), 6, new DateTime(2010, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Cruz Mountains Monte Bello", "There's wonderful perfumed, floral aromas of honeysuckle, lavender, ripe pear and apple in this pure, rich and silky white, which unfolds to reveal layers of fruit and light spicy, toasty oak nuances. Ends with a long, persistent finish that's engagingly complex. Drink now through 2016. 2,000 cases made.", 60.0m, "2006", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("6aa33153-708d-41d3-b27a-133a4725e2d8"), 3, new DateTime(1992, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée", "Displays stunning balance in a village wine, with alluring violet, berry and chocolate flavors, fine tannins and a long, delicious finish. Drinkable now.  ", 30.0m, "1990", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("08f5e0bc-aea5-4bc2-9446-6f8b79004552"), 3, new DateTime(1991, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Beaune Les Boucherottes", "Remarkably charming and elegant, with ripe, rich cherry, tea, raspberry and spicy oak flavors that spread out on the palate. The tannins are very fine and well integrated, and the crisp acidity lends life to the flavors on the finish. Persistent from sta 625 cases made.", 33.0m, "1988", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("6c7a7c39-d5c1-441c-bf0e-760175632ea5"), 3, new DateTime(1991, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chambertin-Clos de Bèze", "An elegant, dynamic wine, with beautifully focused black cherry, currant and toast aromas and flavors that sail across the palate on a smooth structure, echoing on the long finish. A harmonious, gloriously flavorful wine that should be at its best after 1995. 350 cases made.", 97.0m, "1988", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("3ae3f517-cec5-478b-bfc2-02f650290b3c"), 3, new DateTime(1988, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bonnes Mares", "From the dense color to the intense aroma and the rich, complex flavors of strawberry, plum, rose petal and toast, this powerful but beautifully balanced wine commands your attention and delivers everything it has, with fines se. At the price, it s still a good value.  ", 48.0m, "1985", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("d598be2a-521f-4423-9165-dac3d24f2505"), 3, new DateTime(1988, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Clos des Corvées", "Big, rich and deep, with incredible balance and concentration of ripe plum- cherry fruit and mahogany wood, velvety texture, sweet toast tea leaf on th e finish. Couldn't ask for better balance or length.  ", 46.0m, "1985", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("3c666dfc-ce64-4c54-9103-ce4efc1b9e3c"), 3, new DateTime(1988, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Beaune Clos des Ursules", "Ripe, rich and velvety, with gorgeous buttery cherry, chocolate, toast and spice flavors, firmly supported with just enough acidity and tannin, an exotic, sexy wine. Complexity, richness and grace in one package. One of the great values of this vintage.  ", 30.0m, "1985", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("9a2aa2c9-3702-435a-812c-8f5a3a91f84b"), 3, new DateTime(1988, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "I Sodi di San Niccolò", "Loaded with rich, ripe, spicy, plummy fruit, hints of violet and vanilla, beautifully shaped and framed with oak. Smooth and elegant, but containing e nough soft tannin to carry it for years. One of the greatest of the new Italian reds.  ", 25.0m, "1985", new Guid("88e1c232-503e-4fcc-8b0c-e5072f259b39") },
                    { new Guid("b81373a6-424f-4c0f-b6e4-0af21b14fec3"), 3, new DateTime(2001, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Langhe Sperss", "A superb Sperss, with refined aromas of minerals, flowers, berries and just a hint of mint. Full-bodied and powerful, with chewy tannins and a long berry, cherry and mineral aftertaste. Impressive structure. Best after 2005. 2,000 cases made.", 190.0m, "1997", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("615f64e8-c0ad-43f7-aa48-6b6925425cb7"), 3, new DateTime(1999, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Langhe Sorì San Lorenzo", "The Romanée-Conti of Piedmont. An extraordinary red. Thick and ripe, with an elegant structure that makes it eerily balanced. Oozes blackberry, violet, mineral, smoke and petrol notes. Refined, with sweet tannins, it's tempting now if you don't mind a grip on the finish. Drink now through 2016. 870 cases made.", 200.0m, "1996", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("71dbdf53-8c7a-43df-9f8a-334c750ba291"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Sperss", "Complex and fascinating, offering all sorts of floral, earthy overtones to the solid core of black cherry, blackberry and anise flavors. An intense wine that shows many facets, including a welcome whiff of vanilla and toast on the aftertaste. 2,700 cases made.", 60.0m, "1989", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("db2576c5-a14e-4829-9b10-4a43c3085041"), 3, new DateTime(1998, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Moulin-à-Vent Château des Jacques", "Impressive structure. The muscular tannins and firm acidity are well balanced, and there are hints of ripe fruit, game and toast flavors, but it's a bit closed right now. Best from 1999 through 2005. 4,000 cases made.", 19.0m, "1996", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("e1d74501-b52a-48fe-a8e0-77316444c5d0"), 3, new DateTime(1989, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Darmagi", "Dense and concentrated, starting out very firm and tannic but opening up magically on the finish with a burst of plum, berry, leather and toast flavors that echo forever. May be best around 1995 to 2000. Best Cabernet yet from Gaja, but whoa, the price.  ", 70.0m, "1985", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("7bcdc56c-6d88-40db-9e87-0eb7ac1b0dd1"), 3, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Sorì San Lorenzo", "A bit more austere than the others, but it hangs a lot of flavor on an elegant frame, showing toasty, spicy raspberry aromas and flavors, tightly wound and long.  ", 88.0m, "1985", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("85fdd43f-f51f-4b0f-9960-812ba9d5fcd5"), 3, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Costa Russi", "Remarkable in its concentration and elegance, with marvelously focused raspberry and cherry flavors and hints of chocolate and anise. Muscular; without an ounce of excess weight and with just a touch of tannin to lose. Drink 1993-2005 .  ", 83.0m, "1985", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("7d549fd5-a407-47d6-8eda-0734225d314c"), 3, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Sorì Tildìn", "A majestic wine, beautifully harmonious, seamless and integrated, with magn ificent fruit, matchless complexity and stunning depth of flavor. Hints of violet, toast and nutmeg enrich the plummy raspberry fruit. Long and tight on the finish.  ", 94.0m, "1985", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("15c92ee2-2481-4917-8eca-04b615ede05f"), 3, new DateTime(1988, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Corton", "Rich, rough and magnificently complex, a powerful wine with cascading plum, cedar, chocolate, decaying violets, smoke, and on and on. With a supple te xture underneath the tannin, this one is what Corton is all about.  ", 75.0m, "1985", new Guid("e307828e-84e3-4014-989c-50ec3688baed") },
                    { new Guid("90bdfb26-7653-49ab-9c32-5ddbeaa161bd"), 3, new DateTime(1989, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Romanée St.-Vivant", "Indescribably concentrated and elegant at the same time, starting off deceptively smooth and gentle, then building up the intensity and vibrancy until it climaxes to a sensational finish that echoes cherry, tobacco, berry and vanilla seemingly forever. Tannic enough to need until at least 1995 to soften.  ", 195.0m, "1986", new Guid("b323ee33-5a70-4af3-b977-416a3697bb9c") },
                    { new Guid("ec6b607f-61dd-4895-83df-db6423cfca33"), 3, new DateTime(1988, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "La Tâche", "Impressive concentration and depth, the kind of intense flavor, firm tannin, grace and harmony that assures a great wine in 10 to12 years. Flavors are so complex and harmonious that it s hard to pick out the individual elements, but you can find plum, vanilla, lilac and nutmeg.  ", 225.0m, "1985", new Guid("b323ee33-5a70-4af3-b977-416a3697bb9c") },
                    { new Guid("d93b0f83-265e-4f49-a831-e1e28d16a69e"), 3, new DateTime(1988, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Richebourg", "A truly great wine. Amazing concentration, brimming with focused cherry, plum, smoke and cinnamon flavors, all tied together into a harmonious package that keeps echoing and changing with every sip. Beautfiully smooth but firm on the palate, it has the uncanny feeling of power with no weight, only mass.  ", 210.0m, "1985", new Guid("b323ee33-5a70-4af3-b977-416a3697bb9c") },
                    { new Guid("8beae933-12a3-40cf-bf6a-d9c616d55ea4"), 3, new DateTime(1988, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Margaux", "A beautifully structured and proportioned wine, soft yet rich with cascading flavors of ripe plum, cherry and cassis that are perfectly integrated with toasty oak, firm acidity and firm but polished tannins. Finish is long, full and deep.  ", 76.0m, "1985", new Guid("1350b30d-2c6e-441e-b8fe-684c701c751c") },
                    { new Guid("157d2482-6c25-4f99-a86f-2d6d5922c0fb"), 6, new DateTime(1992, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bordeaux White", "A new white wine from the famous Pauillac estate. Flavorful, soft and generous, with herbal, earthy aromas turning toward spice and vanilla on the palate. Plush and strongly oak-accented, with solid pear and cream flavors at the center. Drink now. 2,400 cases made.", 29.0m, "1990", new Guid("b5cf7d7d-16f5-4fce-8098-e781db5e414b") },
                    { new Guid("87ebd24d-ca5a-4658-b8bb-40bfb14a8ab6"), 3, new DateTime(1991, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pauillac Château Haut-Bages-Avérous", "The second wine of Château Lynch-Bages is very tightly wound, with an elegant core of black cherry, herb and cedar flavors cloaked in tough but fine tannins. This is a wine with obvious style and class that will need until 1998 to 2005 to sort itself out. Not for early drinking.  ", 20.0m, "1988", new Guid("b5cf7d7d-16f5-4fce-8098-e781db5e414b") },
                    { new Guid("f506fadd-6d19-4247-b0d5-247d245bf47b"), 3, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco", "Stunning, almost buttery in its richness, smooth, rich and complex, has power and finesse, plus chocolatey richness that can only become greater. Has the fruit of Barbaresco, the finesse and elegance of Bordeaux and the power and intensity of California.  ", 45.0m, "1985", new Guid("f95853fd-5fcf-45ed-a19b-b101cffd5110") },
                    { new Guid("040cdc72-157a-4f0a-9513-1ba15eb0ebd0"), 3, new DateTime(1999, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Santenay Clos de Malte", "Impressive quality for a Santenay. Clean, pure, full-bodied, with ripe blackberry and wild raspberry flavors, wonderful <i>terroir</i> and mineral character, this has breeding and class written all over it. Firm structure, with crisp acidity. Good aging potential. Best from 2003 through 2010. 2,800 cases made.", 22.0m, "1996", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("bb815190-d171-43b7-a5e5-3250814d0347"), 3, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bourgogne", "Infused with graphite and cherry aromas and flavors, this red is expressive and silky, with a good underlying structure for balance. Drink now through 2023. 35,000 cases imported.", 19.0m, "2015", new Guid("a600f898-8caa-4178-9de6-a5c1f21b12ca") },
                    { new Guid("4a5c2802-2bc8-4f8c-a2f1-fe2956a1f856"), 3, new DateTime(1988, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley", "Packed with flavor, dense in color, aroma and massively concentrated, hints of cedar, tar and anise surrounding a solid core of currant and berry fruit, yet not terribly weighty. Once it loosens up, maybe around the turn of the century, it should be lovely.  ", 25.0m, "1985", new Guid("b7e0c27c-5bfb-4826-add6-27f0f43cd776") },
                    { new Guid("05a00662-00e4-454c-9c8e-4c993ccbb7ab"), 6, new DateTime(2007, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Cruz Mountains Santa Cruz Mountain Estate", "Great balance, depth, elegance and finesse. Shows lots of spicy, toasty, smoky oak, but also plenty of rich, vibrant, smooth and concentrated pear, fig and melon flavors before returning to the toasty oak. Drink now through 2011. 2,000 cases made.", 35.0m, "2005", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("c626ac3d-3dd6-4a5c-b890-6604ca2478a9"), 3, new DateTime(1997, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Lytton Springs Dry Creek Valley", "This single-vineyard bottling is altogether pleasing for its earthy wild berry, plum and anise flavors that fan out on the lingering finish. Not your run-of-the-mill Zin. A blend: 84 percent Zinfandel, 14 percent Petite Sirah, 2 percent Carignane. Drink now. 11,900 cases made.", 23.0m, "1995", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("f31eb55a-5c64-4275-8a62-99941d495b0c"), 3, new DateTime(1995, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Geyserville Sonoma County", "This is well oaked, with an interesting band of flavors. A hint of dill accompanies the ripe plum and cherry notes and it's smooth and supple on the finish. This Zinfandel-based blend gains complexity and depth on the palate as the flavors expand. Drinkable now.  6,300 cases made.", 20.0m, "1993", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("635c0927-ed95-41fe-8327-bda721c0c642"), 6, new DateTime(1995, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Cruz Mountains", "Ripe and complex aromas and flavors of spice, pear and honey that persist and build through the finish. A toasty, buttery character accents it nicely. Another beauty from Ridge. 3,300 cases made.", 20.0m, "1992", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("bef51545-d922-415c-ae65-89d7ee3d133c"), 3, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Dusi Ranch", "Bold, ripe and jammy in an older style of Zin. Ripe, incredibly lush black cherry and plum flavors. Enticing now, but it should hold through 1998. 4,100 cases made.", 14.0m, "1992", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("ef7c9017-bc85-4cbb-84b2-f4187c7f8912"), 3, new DateTime(1994, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma Valley Pagani Ranch Late Picked", "This packs in lots of ripe, jammy flavors, yet maintains its elegance and finesse. Plenty of intense and concentrated black cherry, wild berry and currant, finishing with a long, full, rich finish. Delicious. Drink now through 1998. 2,689 cases made.", 16.0m, "1992", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("86ca9194-4f1a-4a7a-8e59-e526bff0a38e"), 3, new DateTime(1993, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Dry Creek Valley Lytton Springs", "Full-blown and fully oaked, displaying a fine, deep color, rich fruit flavors and polished tannins. Lush in texture and sweet with vanilla and spice notes from oak aging, but backed by solid blackberry, currant and black cherry flavors. A concentrated California Zinfandel. Best from 1995 through 2000. 7,430 cases made.", 18.0m, "1991", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("b8438d2c-5345-435d-8909-0582d59b0c73"), 3, new DateTime(1989, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Geyserville", "  ", 15.0m, "1987", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("670e1d04-7487-4921-8967-8c2dc135a8c8"), 3, new DateTime(1989, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Lytton Springs", "Enormously ripe, fruity and complex, teeming with jammy flavors and finishing with richness and finesse. Drink now to 1994. A blend: 82 percent Zinfandel, 13 percent Carignane, 5 percent Grenache. 3,448 cases made.", 18.0m, "1987", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("56af0c06-cf5d-462e-8f52-4badb743822e"), 3, new DateTime(1988, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Santa Cruz Mountains Monte Bello", "Rich, tannic, intense and young, but it's packed with concentrated plum, currant, mint, toast and chocolate flavors that unfold with elegance and finesse. Will only get better and more complex with time. Built to last. Drink now through 2000. 2,220 cases made.", 35.0m, "1985", new Guid("ba58b3b7-2529-4223-b7d5-37f79447fb18") },
                    { new Guid("3625731b-f6aa-46c6-8f36-dd27c871268b"), 3, new DateTime(1988, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pomerol", "A massive concentration of rich, intense plum, cherry, spice, smoke and cho colate flavors that are wrapped in thick, rich tannins and cedary oak. It g ains intensity and vibrancy on the palate, a powerful wine with a distinct personality that will stand 20 years of cellaring- and then some. A great Petrus.  ", 160.0m, "1985", new Guid("5e0cb236-2f63-48ff-be39-5885e9bcc2b9") },
                    { new Guid("5e8b23a2-ed5b-4729-a8ff-f1103c7a318b"), 3, new DateTime(2008, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Marsannay", "Lean and solidly built, despite its ripe cherry notes up front. Tannins and acidity dominate now, but this has a stony character and length, with loads of mineral on the aftertaste. Best from 2010 through 2020. 8,000 cases made.", 20.0m, "2005", new Guid("5ee71ca0-902f-4e8f-999b-551cd5cc9934") },
                    { new Guid("9828c11c-8534-4723-80c3-f823fd2cb9d4"), 3, new DateTime(2007, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay En Chevret", "Very pure, elegant and harmonious, with violet, rose, blackberry and boysenberry flavors and a hint of spice. As silky as this is, the well-integrated tannins offer support. Drink now through 2020. 1,100 cases made.", 48.0m, "2005", new Guid("5ee71ca0-902f-4e8f-999b-551cd5cc9934") },
                    { new Guid("bfa4d5ac-e48d-4778-b8ab-7462c2dabf9e"), 6, new DateTime(1993, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montrachet", "Packed to the brim with coconut, butterscotch, apple and pineapple character. Full-bodied, with amazing acidity and class.  Try now. 600 cases made.", 195.0m, "1990", new Guid("5ee71ca0-902f-4e8f-999b-551cd5cc9934") },
                    { new Guid("08cd2854-bff1-436c-9820-cb662e5bd400"), 3, new DateTime(1988, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Romanée St.-Vivant Les Quatre Journaux", "Seamlessly complex, full of flavor yet so beautifully knit it's hard to pic k out the nutmeg-tinged black cherry, chocolate and vanilla flavors. Velvet y in texture, filled with intense, sweet fruit. Full-bodied, toasty, yet de licate, it has all the attributes of greatness.  ", 99.0m, "1985", new Guid("5ee71ca0-902f-4e8f-999b-551cd5cc9934") },
                    { new Guid("7fbc1c42-4b4b-404c-a33d-4b09e7ee1c65"), 6, new DateTime(2016, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Véran", "Though rich, this is offset by vivid acidity, creating a nice tension to the lemon, apple and melon flavors. Long and tangy on the finish. Drink now through 2018. 8,000 cases imported.", 19.0m, "2014", new Guid("b1fa82cd-551d-46c2-869d-ac9b618a076b") },
                    { new Guid("b41f0fce-1ebf-43ad-994e-8329a0c39d3c"), 3, new DateTime(1992, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Charmes-Chambertin", "Opulent and sensational, with ripe fruit flavors that ooze from the glass. Not only does it have amazing fruit, but the tannins are equally impressive and are in near-perfect balance. Drink in 1998.  ", 81.0m, "1990", new Guid("b1fa82cd-551d-46c2-869d-ac9b618a076b") },
                    { new Guid("6a77b29d-fe3e-4ae8-9039-97bcc97d8df2"), 3, new DateTime(1991, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Morey-St.-Denis Monts Luisants", "This lively, beautifully focused wine is bursting with delicious plum, raspberry and blackberry aromas and flavors, with a slight meaty edge that adds depth and richness. The flavors echo on a long finish. Approaching drinkability, but try to hold off until 1994 or '95.  ", 38.0m, "1988", new Guid("b1fa82cd-551d-46c2-869d-ac9b618a076b") },
                    { new Guid("896920f4-724b-4555-95c4-bef51eec262b"), 6, new DateTime(1991, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Corton-Charlemagne", "A masterpiece, with magnificent flavors and structure. Full-bodied, with subtle yet intense straw, earth, lemon and apple aromas. Has superbly balanced, intense flavors and a sublime backbone of acidity. Drink now to 1998.  ", 92.0m, "1989", new Guid("b1fa82cd-551d-46c2-869d-ac9b618a076b") },
                    { new Guid("e55988a4-dfc7-45ba-8002-60c8ade8fb01"), 6, new DateTime(1988, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Montrachet Marquis de Laguiche", "Uncommon depth and complexity, the rich intense butterscotch, pear, peach a nd smoky flavors combine to give it a gorgeous supple texture. It's beautifully framed with toasty oak that's fully integrated. Graceful and subtle on the finish.  ", 200.0m, "1986", new Guid("b1fa82cd-551d-46c2-869d-ac9b618a076b") },
                    { new Guid("5d2e89e1-37c2-44d1-898d-39917d5db6e4"), 6, new DateTime(2013, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bordeaux White Y", "Creamy and caressing overall, with brioche and macadamia nut flavors framing the core of white peach, yellow apple and green almond notes. The long, salted butter finish also features pleasant echoes of quinine and tangerine. Should unwind nicely in the cellar. Drink now through 2023. 835 cases made.", 150.0m, "2011", new Guid("7694b9df-8b8b-40ce-8adf-a4b63c506853") },
                    { new Guid("d7237b41-6c19-4d46-b8be-e3877e03185e"), 2, new DateTime(1988, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauternes", "Utterly magnificent, a very great wine, certainly the best Yquem since 1975 . Every sip brings out another set of nuances, and the aftertaste goes on forever. Deep gold, unbelievably concentrated and flavorful, yet superbly balanced.  ", 120.0m, "1983", new Guid("7694b9df-8b8b-40ce-8adf-a4b63c506853") },
                    { new Guid("eba01525-0cd5-4d01-bce1-30dc1206851a"), 3, new DateTime(2012, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon St. Helena Family Estate Grown", "This beautifully crafted red is marked by a distinctively complex and subtle mix of dried herb, baking spices and floral scents, with a tight core of currant and blackberry flavors. Finishes with a surge of flavors that build and expand. Best from 2014 through 2028. 2,992 cases made.", 145.0m, "2009", new Guid("b7e0c27c-5bfb-4826-add6-27f0f43cd776") },
                    { new Guid("8ef27b45-4b3f-4282-a84f-ba9206719fad"), 3, new DateTime(1991, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Campogiovanni", "Lean, firm and bursting with fruit. Has strong raspberry, cherry, plum and anise flavors, and hints of vanilla and spice on the finish. Beautifully balanced, with elegance, charm and impressive length. Almost drinkable now, but best after 1995. 500 cases made.", 28.0m, "1986", new Guid("47d20af2-7fbb-4432-aa06-5def37311ad1") },
                    { new Guid("4d007a6b-18d9-4741-9a66-205e898dcf28"), 6, new DateTime(2016, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Carneros Hudson Vineyard", "Combines richness with a creamy texture, allowing the taut citrus, pear, melon and fig notes to fan out, gaining depth and length. Displays beautifully integrated smoky oak nuances. Drink now through 2020. 1,060 cases made.", 80.0m, "2013", new Guid("f84dd971-999d-4a27-a2ab-771522ae679c") },
                    { new Guid("cfdd0204-ada0-4421-b826-dc3f2dc3bf95"), 3, new DateTime(1992, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Poggio Rosso Riserva", "A wine in near perfect harmony and balance. Deeply colored, with lovely berry, violet and fruit aromas and flavors and a soft, caressing finish. A superb wine that's wonderful to drink now or hold.  ", 18.0m, "1988", new Guid("47d20af2-7fbb-4432-aa06-5def37311ad1") },
                    { new Guid("438857e9-5105-45a0-a347-9a81d9678847"), 3, new DateTime(1988, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Arte", "The distinctive raspberry tang of Nebbiolo is the beautiful core around which this elegant, full-bodied wine is built. So artfully balanced it's drinkable now, but the tightly wound flavors need 3-4 years to unfold.  ", 22.0m, "1985", new Guid("8a8d3c75-be24-4d64-95a2-cadb26d0d866") },
                    { new Guid("2599a83b-85cf-4e42-b90d-2404622a4ee7"), 3, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Limited Reserve", "The first reserve from Silverado is a classic for the cellar. Plush and generous aromas, with rich and sharply focused flavors, and delicious cherry, plum, currant, oak and spice notes stay with you from start to finish. Tight and concentrated, with supple texture and firm tannins. Best from 1996 through 2005. 1,400 cases made.", 35.0m, "1986", new Guid("b013636b-485f-4749-ab37-d638da447331") },
                    { new Guid("75af0a24-4281-4518-99da-233c6ddb6739"), 6, new DateTime(1988, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace", "A serious but eminently likeable Gewürz. Has clearly defined, concentrated peach, spice and grapefruit flavors backed by good acidity. Fruity, round in the mouth and well-balanced. Honey and more fruit come through on the finish.  ", 11.0m, "1985", new Guid("abede830-2dc6-48b5-948f-2ac211093a4d") },
                    { new Guid("85c1960c-9d7c-44a9-8000-c305e70c5b79"), 3, new DateTime(1988, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah-Sirah Dry Creek Valley", "Bursting with fresh, lively fruit. Lots of black cherry, blackberry and raspberry flavors in spades, It's fleshy and jammy with enough tannin and acidity to clean off your palate.  ", 10.0m, "1985", new Guid("765fc86d-e3c0-4169-8e0b-c5d2b0d0f737") },
                    { new Guid("3d7eb6d5-207d-4952-81ea-ed88fd919c26"), 3, new DateTime(1988, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley Reserve", "Deep in color and fairly tannic with deep berry fruit and oaky aromas. On the palate it's concentrated with rich, elegant, black cherry flavors. Has intensity but it's not overblown.  ", 14.0m, "1985", new Guid("9b21e81f-f6f9-440b-9f09-b53facb7c332") },
                    { new Guid("0a65dea9-6af6-4c0b-8f62-0cd9320104b8"), 6, new DateTime(1988, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gravion Napa Valley", "Vivid and stylish, with crisp structure and sharply defined aromas and flavors. Full-bodied, with delicious oak, vanilla, orange peel and lemon flavors that are well-intergrated and deep; long, spicy finish. Sémillon and Sauvignon Blanc.  ", 9.0m, "1986", new Guid("9b21e81f-f6f9-440b-9f09-b53facb7c332") },
                    { new Guid("0ffbb573-65f8-47e6-b9c3-1a4fd25c2c47"), 6, new DateTime(1988, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Marlborough", "Full of sweet-seeming honey, butter and vanilla flavors with a hint of nutm eg and pineapple. A forward, gorgeous wine that's nicely oily in texture, w ell structured with acid and lasts a long time on the finish.  ", 10.0m, "1986", new Guid("77bc9124-7598-424d-8eab-932b87d41685") },
                    { new Guid("9b365057-0698-4203-8ece-c865a8b3adb5"), 6, new DateTime(1994, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Napa Valley Barrel Fermented", "Intense and flavorful, with a complex band of buttery pear, hazelnut and spicy flavors that stay with you on a long, full finish. Barrel fermented, this is the best Caymus Sauvignon Blanc in years. Delicious to drink now. 10,000 cases made.", 10.0m, "1992", new Guid("f8a61a7d-135e-48f8-9b1f-60f40cfb2c89") },
                    { new Guid("0554b392-a507-4200-975f-d134b4238419"), 6, new DateTime(1991, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Conundrum California", "Fruity, spicy and complex with tiers of spicy Muscat flavors that blend in nicely with the toasty pear, mineral, nutmeg, peach and more. Hard to imagine a wine with more variety of flavor. Rich and smooth, with a long finish. 6,275 cases made.", 18.0m, "1990", new Guid("f8a61a7d-135e-48f8-9b1f-60f40cfb2c89") },
                    { new Guid("e1af0431-4ada-4794-b11a-c51bb82d6904"), 3, new DateTime(1989, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Special Selection", "Amazingly deep and complex, as rich in color and aroma as it is on the palate, supple in texture but it just keeps coming at you with waves of plum, cherry, vanilla, chocolate and anise flavor, vibrant, exciting, as great a California wine as we've ever tasted.  ", 38.0m, "1984", new Guid("f8a61a7d-135e-48f8-9b1f-60f40cfb2c89") },
                    { new Guid("143e8c92-b296-4f50-9944-b9c74c7913fe"), 3, new DateTime(1988, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Cuvée", "Ripe style with attractive plum, spice, cherry and cassis fruit, packed wit h tannins and crisp acidity. Firm, tight and balanced, built for the next decade.  ", 12.0m, "1985", new Guid("f8a61a7d-135e-48f8-9b1f-60f40cfb2c89") },
                    { new Guid("0e83f088-62e9-4c44-9fb8-f5f544556e55"), 3, new DateTime(1990, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Stags Leap District", "Big and firm, with plenty of generous raspberry and plum aromas and flavors, opening up beautifully on the palate to a long finish. The tannins are tough enough to indicate cellaring until 1993 or '94. A harmonious wine that just needs time. 1,941 cases made.", 14.0m, "1987", new Guid("b013636b-485f-4749-ab37-d638da447331") },
                    { new Guid("45c91b5b-a838-454e-a177-e1efc8683f40"), 3, new DateTime(1989, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Dry Creek Valley", "Marvelously concentrated, with cassis and cherry aromas and flavors framed by toasty vanilla overtones that make it harmonious and balanced. Should develop through 2000, but worth trying now. Great price, too. 1,100 cases made.", 9.0m, "1986", new Guid("8cac6417-1231-4966-81d5-92a2debf93ef") },
                    { new Guid("0a2f265b-67c8-473a-811f-c7caafb485fd"), 6, new DateTime(1988, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bâtard-Montrachet", "Rich and lushly layered, with cascading flavors of pear, buttery, vanilla a nd more, concentrated, powerful, yet elegant and smooth. Absolutely delicio us now, but given time in the cellar it should be devastating.  ", 93.0m, "1986", new Guid("5c554fea-7b82-4bbd-878f-d2aa329fbd1a") },
                    { new Guid("849c818c-774d-4973-a45f-094622fc0ae5"), 3, new DateTime(1989, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Reserve Napa Valley", "Textbook Cabernet aromas and flavors packed into a firm, elegant package, powerful, smooth and supple, finely focused, glowing with plum, cherry and cassis flavors, extremely long. Drink now.  ", 42.0m, "1985", new Guid("e2316d2e-3327-4673-a849-4279c8496d8e") },
                    { new Guid("ab8bfbc3-7aaa-421c-b948-c8aa36c12c9d"), 3, new DateTime(1988, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Three Palms Vineyard Napa Valley", "Loaded with fruit and concentrated flavors that are rich and tannins, filled with pretty currant, black cherry and plum flavors, along with buttery oak that offers depth and dimension. Has tannin for aging. Drink 1993.  ", 22.0m, "1985", new Guid("e2316d2e-3327-4673-a849-4279c8496d8e") },
                    { new Guid("619546db-9b48-40e4-85b8-0cf141988cf7"), 3, new DateTime(2000, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Sammarco", "Black color. Aromas of blackberries, minerals and mint. Very subtle. Full-bodied, with amazing concentration of silky tannins and a finish that goes on and on. Monumental Sammarco. Best ever for me. Best after 2005. 3,500 cases made.", 73.0m, "1997", new Guid("d7b1c1ce-b26f-42e9-a523-af3619c1f4dc") },
                    { new Guid("bd6399e8-2d2a-4f51-81b6-698e4a18691a"), 3, new DateTime(1999, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Vigna d'Alceo", "Even better than the superb 1996, this young wine has an amazing amount of pure fruit. A thick and rich Cabernet, with loads of tar, green tobacco, currant and fruit character. Full-bodied, with chewy tannins and a tobacco and wet earth aftertaste. Seriously well structured yet wonderfully harmonious. Best after 2003. 1,000 cases made.", 85.0m, "1997", new Guid("d7b1c1ce-b26f-42e9-a523-af3619c1f4dc") },
                    { new Guid("cc052496-065d-4a7c-b898-f2cc7c741238"), 3, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico", "Firm and concentrated, a tightly wound, superbly balanced wine bursting with ripe cherry, cedar, black pepper and haunting overtones of oak. Delicious now, it should be lovelier after a year or two in the bottle.  ", 8.0m, "1985", new Guid("d7b1c1ce-b26f-42e9-a523-af3619c1f4dc") },
                    { new Guid("0e523bb4-865b-4ade-b47b-0b18e5aff71b"), 3, new DateTime(1988, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Réservé", "Deliciously rich, ripe and jammy with effusively ripe plum, cherry and raspberry fruit that's soft, smooth and silky. Opulent style, crisp acidity and enough tannin for a decade or more. A real mouthful of wine from a great producer. Drink now through 2000.  ", 41.0m, "1985", new Guid("46e05bbe-639f-4e21-96f6-83d7cb2fa19d") },
                    { new Guid("20fd6135-af9c-4e87-b8be-33f00439a9dd"), 6, new DateTime(1990, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Les Folatières Domaine Jean Chartron", "Pear, honey and spice aromas and flavors merge effortlessly on an elegant, supple structure, becoming smooth and harmonious. A classy wine from first whiff to last echo of the aftertaste. Drink now. 1,000 cases made.", 38.0m, "1988", new Guid("2640c096-decf-48ea-9e28-029ec2b1fefb") },
                    { new Guid("55dab668-e18c-40c0-b6c9-6a67a821d35e"), 6, new DateTime(1990, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chassagne-Montrachet Morgeot", "Incredible depth and concentration with layers of fresh, ripe, rich honey, pear, spice and toasty vanilla flavors on the finish that echo on and on. You rarely see wines of this kind of depth, concentration and complexity anywhere. Drink now through 1995 200 cases made.", 34.0m, "1988", new Guid("2640c096-decf-48ea-9e28-029ec2b1fefb") },
                    { new Guid("9cbb81f5-024c-4d9c-8ac8-dc4f2af74ccb"), 6, new DateTime(1988, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Clos de la Pucelle Domaine Jean Chartron", "Hums with intensity, yet it sits lightly on the palate, beautifully focused with pear, apple, toast and honey in exquisite balance. The flavors seem t o get even more concentrated on the finish.  ", 50.0m, "1986", new Guid("2640c096-decf-48ea-9e28-029ec2b1fefb") },
                    { new Guid("30841527-2669-4ea0-bb07-44921d6095f1"), 3, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Dry Creek Valley", "Very firm and concentrated and focused, amazingly layered with blackberry, cassis and plum fruit, cedary overtones from spicy oak and a bracing touch of acidity and tannin to wrap it up and keep it from showing everything it has for 2-3 years.  ", 7.0m, "1986", new Guid("8cac6417-1231-4966-81d5-92a2debf93ef") },
                    { new Guid("a668dd07-57a2-4354-888d-b2890c3df184"), 3, new DateTime(1988, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Echézeaux", "Rich in flavor, highly polished and concentrated, with black cherry, plum, currant, spice, and a lovely touch of oak, put together with subtlety and g race. Power with elegance.  ", 47.0m, "1985", new Guid("6ecda4a3-326f-4e95-9dbe-02bb38eff72a") },
                    { new Guid("52a9ba47-3272-42ba-a24e-82084a4fe9c7"), 6, new DateTime(1990, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Napa Valley", "Hard to imagine a better California Sauvignon Blanc. Rich, intense and loaded with rip fig, lemon, herb, and spice flavors, it has a very enticing, fine structure with lively flavors that build to the finish and carry on. Best from 1990 through 1992. 15,500 cases made.", 8.0m, "1988", new Guid("b013636b-485f-4749-ab37-d638da447331") },
                    { new Guid("5d68755f-297e-4d2a-a87e-3076892e2b26"), 3, new DateTime(1988, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Carneros", "Distinct and well proportioned, rich and tasty with ripe strawberry, cherry, spice and oaky flavors that add up to complexity and dimension. The flavors stay with you from start to finish. Has enough tannin for three to five years in the cellar.  ", 14.0m, "1986", new Guid("4e4bf93b-7bf2-49b1-9477-f25d12b3b76a") },
                    { new Guid("3287402d-d724-4826-b621-225dd0f2201a"), 6, new DateTime(1998, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Vine Hill Vineyard", "A crisp and lively Chardonnay with a rich, concentrated, tightly wound band of nectarine, pear, citrus and spice. Gains momentum on the palate, and finishes with a wonderful display of fruit and oak complexity. Best from 1999 through 2006. 2,782 cases made.", 42.0m, "1995", new Guid("f84dd971-999d-4a27-a2ab-771522ae679c") },
                    { new Guid("5fe012be-d34f-4857-81c2-2bd06a78813d"), 6, new DateTime(1992, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Valley Durell Vineyard Sand Hill", "Rich, dense and young, with toasty, buttery notes over tight fruit flavors that will need a few years to develop fully. Creamy, peachy, pearlike and intense, but still a bit raw from oak; drinkable now. 2,470 cases made.", 28.0m, "1990", new Guid("f84dd971-999d-4a27-a2ab-771522ae679c") },
                    { new Guid("8700c13a-c0e6-4dd1-af90-ee46375b287a"), 6, new DateTime(1989, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Valley Kistler Vineyard", "Kistler is hot. Complex and delicious, with great balance and a long aftertaste. Brimming with ripe, butterscotchy flavors over ripe, round fruit. Big and full-bodied but elegant. Drink now through 1992.  ", 25.0m, "1987", new Guid("f84dd971-999d-4a27-a2ab-771522ae679c") },
                    { new Guid("b66936e2-6b34-4570-924e-5572da44b5b0"), 6, new DateTime(1988, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Dutton Ranch", "Rich, crisp and tightly wound with pretty spice, apple, pear and vanilla flavors peeking through. A pleasant kiss of oak adds complexity and dimension. Finish offers butterscotch and finesse.  ", 18.0m, "1987", new Guid("f84dd971-999d-4a27-a2ab-771522ae679c") },
                    { new Guid("9680d482-9898-4872-9255-596bf52508e7"), 6, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Fumé Blanc Sonoma County", "A textural delight, with fresh, vibrant white peach, melon and lemon-lime citrus flavors that are fragrant, fleshy and rich, with a whiff of fresh-cut grass and juicy acidity. The flavors echo on the long finish. Drink now. 26,270 cases made.", 13.0m, "2005", new Guid("27e45790-691b-48bc-8674-635fdf68203a") },
                    { new Guid("902d02c3-f259-4a26-8774-284fd3e9aa7a"), 3, new DateTime(1996, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma County Cinq Cépages", "An elegant style that delivers a ripe, intense core of currant, coffee, cedar and cherry flavors while maintaining its balance and finesse. Finishes with a long, fruity aftertaste that lingers on and on. Gives you the flavor, richness and depth of the more expensive Cabernets at about half the price. 9,000 cases made.", 22.0m, "1993", new Guid("27e45790-691b-48bc-8674-635fdf68203a") },
                    { new Guid("5217733a-a33e-451b-b86b-56ccb5343b0e"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma County Reserve", "A bold, smooth, rich and exotic style of Cabernet that packs in lots of extra flavors. The well-focused currant, cherry, mineral and toasty oak flavors are deftly balanced, expanding on the finish where the herb, chocolate and smoke notes enhance the pure fruit.  This should hold well. Best after 1999. 1,000 cases made.", 38.0m, "1990", new Guid("27e45790-691b-48bc-8674-635fdf68203a") },
                    { new Guid("5f193f65-e778-4f94-a47d-49a2211a6140"), 2, new DateTime(1993, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Johannisberg Riesling Alexander Valley Late Harvest Special Select", "Deep, dark, ripe and rich, a gorgeous, unctuous wine that offers cascades of honey, floral, caramel, apricot, pear and ginger aromas and flavors, finishing almost delicate for such a rich wine. Fabulous to drink now, perhaps best in 1996-1998. 475 cases made.", 25.0m, "1989", new Guid("27e45790-691b-48bc-8674-635fdf68203a") },
                    { new Guid("c1f6ff33-6b67-49ea-9509-fef857b11d44"), 6, new DateTime(1990, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Alexander Valley Robert Young Vineyard Reserve", "Beautifully ripe, fruity and gaining maturity, this is complex in aroma, full and pearlike in flavor and beginning to show the intriguing effects of age in the earthy, buttery notes and creamy, velvety texture. Stays crips and fruity on the finish. It can safely cellared through 1995.  ", 40.0m, "1985", new Guid("27e45790-691b-48bc-8674-635fdf68203a") },
                    { new Guid("0f12825e-6666-4ea6-b699-a8b5c5448622"), 6, new DateTime(1988, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Alexander Valley Belle Terre Vineyard", "  ", 17.0m, "1986", new Guid("27e45790-691b-48bc-8674-635fdf68203a") },
                    { new Guid("f40e6188-6e24-47e0-a2be-66289ec45293"), 3, new DateTime(1994, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Stags Leap District", "A ripe and richly concentrated wine, with focused cherry, berry and currant flavors. On the finish, the tannins really kick in, but it also delivers a lot of fruit. This is a tremendous wine, especially in its price range. Best in 1997 and thereafter. 12,850 cases made.", 17.0m, "1991", new Guid("b013636b-485f-4749-ab37-d638da447331") },
                    { new Guid("e491ffb5-e06c-4700-8379-36a48f099e1e"), 3, new DateTime(2007, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Mendocino", "An elegant style, with a complex array of raspberry, wild berry and strawberry fruit that's delicate and refreshing. Supple in texture, ending with a burst of berry and pepper flavors and ripe, fine-grained tannins. Drink now through 2011. 2,193 cases made.", 19.0m, "2004", new Guid("9d39ffa9-0868-475d-a8dd-c89a82effcac") },
                    { new Guid("8db1660e-fd0d-4827-adeb-764bb4bfb5a4"), 6, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Anderson Valley Première Reserve", "Bold, ripe, rich and creamy with pretty spice and vanilla flavors, but also a load of pear, apple, citrus and toast notes on a long, full, complex finish. Wonderful sense of harmony and finesse; drinks well now but should hold through 1995. 3,400 cases made.", 15.0m, "1991", new Guid("9d39ffa9-0868-475d-a8dd-c89a82effcac") },
                    { new Guid("256eed26-35ec-4b82-bed4-c1d9e36eb5f7"), 2, new DateTime(1989, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Anderson Valley Late Harvest Vineyard Selection", "Very ripe and honeyed, with rich apricot, honey and slightly floral flavors that are smooth and carefully balanced. Shows restraint, but is plenty tasty. Give it some time to open up.  ", 19.0m, "1986", new Guid("9d39ffa9-0868-475d-a8dd-c89a82effcac") },
                    { new Guid("aaf53ca6-0adf-4866-8dd6-80159c6da5d1"), 6, new DateTime(1988, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Riesling Anderson Valley", "A bit sweet, firmly balanced with crisp acidity, packed with juicy grapefruit and green apple flavors. There's a lovely hint of apricot on the nose. Very fine structure, vivid flavors and impressive length.  ", 7.0m, "1987", new Guid("9d39ffa9-0868-475d-a8dd-c89a82effcac") },
                    { new Guid("558dc039-ade7-42e4-9cd3-fbc24c6109b3"), 3, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Carneros", "Distinct for its richness and complexity, the tart cherry, currant and berry flavors are young and lively but quite enticing. Has smoke, vanilla and chocolate flavors on the finish. Tempting now but capable of aging. Best now through 1998. 9,865 cases made.", 11.0m, "1986", new Guid("0ca29efb-1a1a-4c35-a9c8-380a754e6ff5") },
                    { new Guid("57447820-c7c4-4458-9c36-089d6e2f257f"), 3, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Carneros Private Reserve", "Intense yet elegant with firm tannins and lively ripe currant, black cherry and cranberry flavors that are tart and sharply focused. Finishes with a mouthful of berries. Drink now to 2002. 2,900 cases made.", 18.0m, "1985", new Guid("0ca29efb-1a1a-4c35-a9c8-380a754e6ff5") },
                    { new Guid("d1f60ff5-3c2f-497e-9b56-e5451d2d7c21"), 6, new DateTime(1988, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Lake County", "Juicy, ripe, fruity and complex, distinctive, with very pretty grapefruit, melon, apple, and herb flavors. Soft, delicate and broad in structure, this is the second great Sauvignon Blanc in a row from this winery.  ", 7.0m, "1987", new Guid("0ca29efb-1a1a-4c35-a9c8-380a754e6ff5") },
                    { new Guid("3008ed2b-ede4-40fe-aef5-26f42feb2770"), 6, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Anderson Valley", "Nearly identical to a fine Alsace Gewürz. Lovely rose petal and grapefruit aromas, with crisp, concentrated flavors of apple and grapefruit.  ", 7.0m, "1987", new Guid("d44efa0c-2aba-4170-beaf-07e1d2195f0f") },
                    { new Guid("495f2e4e-679c-4cb5-b85f-3f1289f55423"), 3, new DateTime(1988, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Franc North Coast", "Rich, lush and elegant with ripe fruit, spicy, concentrated raspberry, cherry, herb and chocolate flavors that spread out on the palate. Impeccibly well balanced, seducitive now, but it should only get better.  ", 14.0m, "1986", new Guid("f878f317-b256-4ff2-8745-d04852181b09") },
                    { new Guid("9ed2304e-dbe2-4c4a-bc51-8cecc20b710b"), 3, new DateTime(1998, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Carneros Reserve", "Smooth and silky. An elegant style, with dense, rich black cherry, plum, leather, earth and beef flavors, picking up a nice touch of toasty oak on the finish, where the tannins kick in. Delicious. Drink now. 2,700 cases made.", 35.0m, "1995", new Guid("4e4bf93b-7bf2-49b1-9477-f25d12b3b76a") },
                    { new Guid("ad196cdd-0bce-45f4-9d10-38f0637b2fc2"), 6, new DateTime(1996, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Carneros Reserve", "Bold, ripe, smooth and creamy, a real mouthful of Chardonnay. Its tiers of ripe pear, fig and honey flavors are framed by smoky, toasty oak. An altogether complex and beautifully crafted young wine, with a rich butterscotch aftertaste that still has all those delicious flavors chiming in. Kudos to this California winery. Drink now through 1997. 1,130 cases made.", 25.0m, "1994", new Guid("4e4bf93b-7bf2-49b1-9477-f25d12b3b76a") },
                    { new Guid("f431cfd4-5ec2-47db-9e02-0bdd8beea588"), 2, new DateTime(1996, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Riesling Anderson Valley Late Harvest Sweet Cluster Selected", "Frankly sweet but balanced, with enough racy acidity to showcase the pineapple, honey and apricot flavors with great style. Finish is long and luxuriant. Delicious now, it should keep improving. Best from 2000 through 2004. 369 cases made.", 14.0m, "1994", new Guid("9d39ffa9-0868-475d-a8dd-c89a82effcac") },
                    { new Guid("eb71123c-1052-4058-a08d-3d9a6eb756a8"), 3, new DateTime(1990, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Corton Les Renardes", "Earthy, woody and tannic red Burgundy in a reined-in style, with a beautifu l core of rich, supple, concentrated strawberry, cherry and spice flavors. Wonderfully balanced; should age a decade with ease. Begin drinking in '92. 250 cases made.", 36.0m, "1987", new Guid("286bacbd-02ef-4f68-a861-8598b8e7318d") },
                    { new Guid("943db3ef-9cf2-4226-b225-6de9e5fd2f60"), 3, new DateTime(1988, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pommard Clos de la Platière", "Unexpectedly deep and rich, big and mouthwatering, with ripe, sweet cherry, blackberry, raspberry fruit, a distinctive cedary character from oak, all in magnificent balance. A muscular wine with grace.  ", 45.0m, "1985", new Guid("286bacbd-02ef-4f68-a861-8598b8e7318d") },
                    { new Guid("93fdde53-faaa-45df-9dc6-dc8c85183fba"), 5, new DateTime(2003, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Special Cuvée", "A beautiful Champagne. Complex and elegant, evoking ginger, jasmine, citrus and honey aromas and flavors, accented by a biscuit and grain component coming as much from barrel fermentation as yeast aging. Very firmly structured and long. Enjoy with Asian foods or Parmesan cheese. Drink now through 2006. 9,500 cases imported.", 45.0m, "NV", new Guid("67e35a11-32cb-47ce-833a-50ca605e6365") },
                    { new Guid("864ab56b-5d23-4764-9afe-e5f9fd2e23ac"), 3, new DateTime(1989, 1, 1, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée Les Chaumes", "Generous and sweet on the nose, rich and velvety on the palate, bristling with chocolate, vanilla and cherry flavors, harmonious, elegant and long. Should develop at least through 1993. One of the few '86 red Burgundies worth time in your cellar.  ", 47.0m, "1986", new Guid("e6299e60-8efa-4739-848a-834fc48db5af") },
                    { new Guid("933f27e7-289e-46c0-ace3-78aff52fe1b0"), 3, new DateTime(1988, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée Les Beaux-Monts", "Tightly focused and concentrated, sending a current of flavor right down th pepper and vanilla, elegant and firm, gaining complexity on the finish. Even at the price, it s a deal.  ", 55.0m, "1985", new Guid("e6299e60-8efa-4739-848a-834fc48db5af") },
                    { new Guid("ecde4d08-e66f-4333-b022-eac0aa857d51"), 3, new DateTime(1995, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley The Montelena Estate", "Uncommonly dark and intense, even for this vintage, with a rich core of leathery currant, spice, cedar and anise flavors that are young and rambunctious. This is a massive wine that needs to age through 1999. 12,000 cases made.", 30.0m, "1991", new Guid("687124bf-cc74-4f00-8c62-ce56c1da1952") },
                    { new Guid("b4f983fe-91eb-461e-b9fa-61405666fad0"), 3, new DateTime(1994, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Calistoga Cuvee", "Bold, ripe and supple, with a rich, focused core of currant, black cherry, plum and exotic spice flavors. Builds richness, intensity and depth on a long, complex finish. Beautifully crafted. Drink now. 12,000 cases made.", 15.0m, "1992", new Guid("687124bf-cc74-4f00-8c62-ce56c1da1952") },
                    { new Guid("7bde6513-f170-4878-8e90-f77d832cc34c"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Oakville", "Sturdy, rich and detailed, with complex, earthy currant, sage, mineral, tobacco and anise flavors that fan out and saturate the palate. Finishes with muscular tannins. Best Mondavi Oakville yet. Best from 2002 through 2012.  ", 45.0m, "1997", new Guid("6a6dcf4b-5b16-426d-9ce8-ee922b9270aa") },
                    { new Guid("c50e3151-6c6d-451c-bf1a-49bd6a7edd59"), 3, new DateTime(1994, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Unfiltered", "A racy style with lots of ripe, juicy Cabernet fruit. Echoes currant, plum and black cherry notes before picking up some toasty oak on the finish. Drinkable now. 73,460 cases made.", 18.0m, "1991", new Guid("6a6dcf4b-5b16-426d-9ce8-ee922b9270aa") },
                    { new Guid("fcf043b0-d6b7-40c5-bd52-d74a7892cc35"), 3, new DateTime(1994, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Napa Valley", "Ripe, rich and supple, with plush cherry, raspberry and plum-laced Zinfandel fruit. Carries its flavors exceptionally well, finishing with a long burst of fruit and smooth, polished tannins. Drinks well now, but short-term cellaring should soften it. Made with grapes from Howell Mountain, West Napa and Mount Veeder. 1,188 cases made.", 14.0m, "1992", new Guid("6a6dcf4b-5b16-426d-9ce8-ee922b9270aa") },
                    { new Guid("56791773-7acf-475f-a0a6-52d11c9be412"), 3, new DateTime(1989, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Napa Valley Reserve", "Remarkably complex with toasty oak, fresh cherry, leather and spicy flavors that are rich and elegant, displaying uncommon complexity, depth and finesse. The spicy cinnamon finish lingers on and on. Best Mondavi Pinot yet.  ", 31.0m, "1985", new Guid("6a6dcf4b-5b16-426d-9ce8-ee922b9270aa") },
                    { new Guid("8e65621d-627b-4d18-8e8d-4adcadb6006a"), 3, new DateTime(1989, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Reserve", "Firm, intense and lively, with rich, elegant and concentrated plum, currant and spice flavors. A remarkably complex and tasty wine. Released at the same time as the '85 Reserve. Best after 1992.  ", 35.0m, "1986", new Guid("6a6dcf4b-5b16-426d-9ce8-ee922b9270aa") },
                    { new Guid("2e855fad-1b21-4d13-8d86-655e53156428"), 3, new DateTime(1988, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos de Vougeot", "Intense of flavor but delicate of structure, offering cranberry, violet, ci nnamon and caramel aromas and flavors folded neatly into a lean package tha t just seems to keep expanding on the finish. Defines finesse and elegance.  ", 49.0m, "1985", new Guid("8f77446a-601c-4188-8fde-8602db8ce683") },
                    { new Guid("bf3df64b-1ace-4a9d-80fe-113c896b60ee"), 3, new DateTime(1990, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Les Vignes Rondes", "Immediately attractive, with opulent cherry and plum flavors framed by sweet, toasty oak. The flavors unfold with complexity on the long finish. Intense and concentrated, but elegant enough to keep you coming back for another sip. Drink now through 1995. 300 cases made.", 35.0m, "1987", new Guid("e6299e60-8efa-4739-848a-834fc48db5af") },
                    { new Guid("f0fd9df9-1fb2-4069-9c82-e1e9d3d48e94"), 3, new DateTime(1988, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Maurizio Zanella", "Smooth and supple, with rich berry, cherry and smoky flavors, velvety and expansive on the palate, yet elegant and balanced with acidity. Blends the best of Sangiovese and Cabernet, plus a nice touch of oak, and it feels like it can only get better. Drink now through 1992.  ", 38.0m, "1985", new Guid("90a4b5fe-4fca-45a5-92eb-3845afff0833") },
                    { new Guid("bcc6a387-3299-4cc6-9d96-10778ee18dc3"), 3, new DateTime(1988, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Graves", "Firm and concentrated with flavors that spread out on the palate. It's rich and woody, very structured and firm, packed with cassis and black cherry fruit and cedary tobacco. Offers an abundance of flavors in an elegant package. One of the finest Haut-Brions we've tasted.  ", 70.0m, "1985", new Guid("c71caa07-06dc-43e7-bf51-d1b271760a66") },
                    { new Guid("ad27e30a-2e23-4284-90b4-6b7517e6d31a"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Aux Murgers", "Lots of wood in this brooding, dark-colored Burgundy. Plenty of plum, chestnut and currant flavors, full body, a fresh core and solid tannins. Try in 1999.  ", 60.0m, "1993", new Guid("f16e33d6-ba40-4914-9a8a-aa8bf0f0b470") },
                    { new Guid("16e405a5-ecc5-4f95-b82a-13ae0a7a956e"), 3, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée Au Cros Parantoux", "A powerful Burgundy with a raw edge that needs cellaring to smooth the bristly texture, but it has gorgeous cherry, spice and chocolate-toast flavors that become deeper and deeper with every sip. Drink now through 2000.  ", 63.0m, "1987", new Guid("f16e33d6-ba40-4914-9a8a-aa8bf0f0b470") },
                    { new Guid("7d4caa07-105b-4424-b27b-3797290f80ed"), 3, new DateTime(1989, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée Aux Brûlées", "Stunningly beautiful berry and spice aromas and flavors capture your attention from the first sniff right through to the velvety finish, growing richer and more elegant with every sip. Has a bit of tannin to lose on the finish, so cellaring until 1994 is indicated. Delicious already.  ", 63.0m, "1987", new Guid("f16e33d6-ba40-4914-9a8a-aa8bf0f0b470") },
                    { new Guid("ed42fecb-b233-4b6f-9b2f-071a6c11f2f1"), 2, new DateTime(1995, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Riesling Russian River Valley Late Harvest Special Select Oak Meadow Vineyard", "Gloriously sweet, rich, supple and complex. A dessert wine from California that unfolds a many-layered swirl of honey, caramel, apricot, pear and exotic tropical fruit and spices, all balanced gorgeously on a fine thread of acidity. Wonderful now, but should be fine through 1998, or even 2000. 300 cases made.", 28.0m, "1993", new Guid("e2f5d76b-2304-42dd-a8a4-fa4799ee4143") },
                    { new Guid("615124e1-1bba-4dc0-805a-d6e61a937e9d"), 6, new DateTime(1992, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Réserve Spéciale", "Starts slow, with tightly wound pear, vanilla and spice flavors, but gains velocity and depth, picking up pretty nutmeg and spice flavors on the finish. A well-crafted wine that shows richness, depth and complexity. Tight and youthful; drinkable now, or cellar through 1995. 250 cases made.", 45.0m, "1990", new Guid("e2f5d76b-2304-42dd-a8a4-fa4799ee4143") },
                    { new Guid("536f3293-7042-4c37-b6e4-8cd237c11967"), 3, new DateTime(1988, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma County", "A luscious Cabernet, supple and silky, elegant and stylish, loaded with str awberry, cherry and spicy plum flavors, supple tannins that are tightly reigned in. Graceful and attractive. Long full finish. Drink now, but it should only get better.  ", 19.0m, "1985", new Guid("e2f5d76b-2304-42dd-a8a4-fa4799ee4143") },
                    { new Guid("04a1dda6-fd06-4ae2-9d5f-b78fd67efab5"), 3, new DateTime(1988, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Sonoma Valley Olive Hill Vineyard", "Gorgeous fruit, rich, supple, satiny with beautiful structure, amazing intensity and depth, rich cassis, butter, toasty oak and spicy flavors, plenty of tannin, but impeccibly balanced. Distinct and exciting. Drink 1994 to 2000.  ", 16.0m, "1985", new Guid("7a6347be-3b2f-4588-aefe-b53000890808") },
                    { new Guid("c99f11ca-9076-41ce-9a2b-b8eb928d9820"), 3, new DateTime(2003, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Pajana", "Layers and layers of fruit. Intense aromas of blackberry, tobacco, mineral and dried mushroom. Full-bodied, with big velvety tannins and rich and wonderful fruit. Long, long finish. Goes on and on. I like it better than the outstanding 1998. Clerico is a master again. Best after 2010. 550 cases made.", 75.0m, "1999", new Guid("8a8d3c75-be24-4d64-95a2-cadb26d0d866") },
                    { new Guid("40f05133-52f4-4326-8ae1-de44df97409a"), 3, new DateTime(1990, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Ciabot Mentin Ginestra", "Smells like anise-flavored grappa at first, turning tannic and powerful on the palate, but the raspberry and vanilla flavors emerge forcefully from the grip of tannin. Big enough to need until the turn of the century, but it has the power and complexity to win in the end. Needs cellaring until 1998 to 2000. 800 cases made.", 27.0m, "1985", new Guid("8a8d3c75-be24-4d64-95a2-cadb26d0d866") },
                    { new Guid("692d8678-3490-4a76-9170-c8fb6b08feb2"), 3, new DateTime(1988, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Lebanon", "An intriguing wine with great complexity. Leathery and smoky aromas and mature, ripe fruit flavors of spice and plum. The color and aromas seem mature but it is still tart and somewhat tannic and should age graciously.  ", 17.0m, "1980", new Guid("dadab440-50c9-4137-a826-85a8ad44ae3f") },
                    { new Guid("24b42875-cd11-46f1-b475-7029f73d17f0"), 3, new DateTime(1991, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Les Lavières", "Powerful, rich aromas and flavors of plum, raspberry and nutmeg carry through this mouthfilling, dramatic wine. The broad range of flavors persists on the long finish, and it has a modest level of well-integrated tannins to shed in the cellar. Best after 1993. 750 cases made.", 33.0m, "1988", new Guid("e6299e60-8efa-4739-848a-834fc48db5af") },
                    { new Guid("48ff15bf-884d-4816-a594-2b675ed696a3"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos Vougeot", "A giant of a wine. The greatest Clos Vougeot we have ever tasted. Mint, spice, dried herb and red berry flavors, full body, loads of tannins and a long, silky finish. An amazing wine, even for such a great vintage. Try in 2000 or later. 225 cases made.", 90.0m, "1993", new Guid("e6299e60-8efa-4739-848a-834fc48db5af") },
                    { new Guid("bf29466d-d95a-4222-b423-b53dd7a924a0"), 2, new DateTime(1988, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barsac", "Beautifully crafted, plump, very long and complex. Medium gold, lush and si lky, with lemon, fig, apricot, pear, and a particualrly elegant balance of acidity, fruit and wood. Power and finesse in the same package. Drink now through 2000.  ", 50.0m, "1983", new Guid("0df0f1c9-4f75-4313-af4c-01b8da444271") },
                    { new Guid("f0b450db-b119-450a-a17d-7b1432f86403"), 5, new DateTime(1998, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Spécial Cuvée", "A satisfying, rich and flavorful brut in a nicely balanced package. It combines bright, fresh fruit flavors with mellow toast and vanilla accents from aging, is smooth in texture, vibrant in balance and long on the finish. Drink now through 2000. 80,000 cases made.", 30.0m, "NV", new Guid("67e35a11-32cb-47ce-833a-50ca605e6365") },
                    { new Guid("b8199787-92b5-4b39-9d0d-e95a6c94b2e2"), 5, new DateTime(1988, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Grande Année", "Rich, toasty and very assertive, high in extract and intensity with a heavy toasty flavor that compliments the pear and cherry flavors. Smoky flavors carry the finish. It's a rich style that may be to powerful for some. Drink now.  ", 30.0m, "1982", new Guid("67e35a11-32cb-47ce-833a-50ca605e6365") },
                    { new Guid("a7ba9abd-1c00-4d57-83b3-e27f31e57285"), 3, new DateTime(2006, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vino Nobile di Montepulciano", "A rich, round red, with lots of jammy berry and strawberry character. Full, round and caressing. Lots going on. Needs time. Best after 2007. 16,600 cases made.", 22.0m, "2003", new Guid("4f2bf8bb-fb9e-4d03-8f00-3078cbd21d09") },
                    { new Guid("f03fe5e3-dca1-46a4-9697-0bd165033031"), 3, new DateTime(2002, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Toro Desiderio", "Big and ripe red, with loads of black licorice, tar and sultanas. Full-bodied, with big, velvety, chewy tannins. Long, ripe fruit aftertaste. A blockbuster. Merlot and Cabernet Sauvignon. Best after 2005. 3,750 cases made.", 48.0m, "1999", new Guid("4f2bf8bb-fb9e-4d03-8f00-3078cbd21d09") },
                    { new Guid("9e8e3a14-f765-4bfb-9b05-d357cd2e7fa5"), 3, new DateTime(2001, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vino Nobile di Montepulciano Grandi Annate Riserva", "Emits phenomenal aromas of crushed berries, roses, lilacs and currants. Full-bodied, with an incredible backbone of ultrafine tannins and a long, fruity finish. Not big and thick, but powerful and subtle. A very special wine indeed; the greatest Vino Nobile ever made. Hard not to drink it now. Best after 2005. 2,600 cases made.", 40.0m, "1997", new Guid("4f2bf8bb-fb9e-4d03-8f00-3078cbd21d09") },
                    { new Guid("8d6e4b02-422e-48dd-bed4-69efb85ed776"), 6, new DateTime(1988, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Il Marzocco", "One of the greatest Italian whites ever. Rich and aromatic with apricot, nutmeg and honey on the nose, and full, concentrated fruit and a touch of oak on the palate, with moderate tannins and good extract. Has great complexity and weight.  ", 16.0m, "1986", new Guid("4f2bf8bb-fb9e-4d03-8f00-3078cbd21d09") },
                    { new Guid("252835d5-a27c-41a4-a6b9-2ac025db3b90"), 3, new DateTime(1988, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Estèphe", "Hard-edged St. Estephe with enough of a finish of smooth vanilla and fruit to suggest that it will be more likeable as it ages and the tough, tannic structure and tobacco-herbal flavors resolve. Has the stuff you'd expect from a classed growth.  ", 16.0m, "1985", new Guid("1752c1e4-d515-4390-8b67-b7fcc93ebda8") },
                    { new Guid("5392460c-9f78-4582-b7a9-77c39a66807a"), 3, new DateTime(1996, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Crozes-Hermitage Les Jalets", "A bright burst of raspberry flavor gives this wine panache, and it has the concentration to back it up, with pretty chocolate and floral notes on the finish. It's monolithic now, but should develop more complexity with age. 27,000 cases made.", 14.0m, "1994", new Guid("8069b176-3786-4b96-99c4-eb02034afd05") },
                    { new Guid("cb5b4909-9ea8-4f03-bc9f-5e48d24069b4"), 3, new DateTime(1987, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Hermitage La Chapelle", "  ", 30.0m, "1985", new Guid("8069b176-3786-4b96-99c4-eb02034afd05") },
                    { new Guid("71cf192f-f872-428d-9b2f-ed8f8b496c38"), 3, new DateTime(1988, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côte-Rôtie Les Jumelles", "Full throttle Côte-Rôtie with gorgeous fruit, plums, cherries and berries, soft and sikly on the palate but with a firm tannic backbone that bodes wil l for the future. A massive but elegant offering. A great bottling of Les Jumelles.  ", 35.0m, "1985", new Guid("8069b176-3786-4b96-99c4-eb02034afd05") },
                    { new Guid("c91e3d19-d126-489e-8e29-2a7ef43e5214"), 3, new DateTime(1988, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gevrey-Chambertin", "Firm and tannic, with concentrated raspberry, black cherry and red currant fruit, overtones of sweet oak and flowers, a many-faceted wine with mouthwa tering flavors, structure and texture. A lot of Burgundy for the price.  ", 25.0m, "1985", new Guid("218a89bb-d82c-4b22-a296-b28892017936") },
                    { new Guid("8c56f449-e2ac-471f-bf2f-6b8c677d2326"), 5, new DateTime(1999, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Royal Apanage", "The concentration is impressive in this intense, aromatic, full-bodied Champagne, with its assertive doughy and toasty aromas leading into flavors of ripe fruit and spices that linger enticingly on the finish. Drink now. 50,000 cases made.", 33.0m, "NV", new Guid("e339eea4-f28f-4885-9943-c9bc6059a88b") },
                    { new Guid("71009dff-cda6-4a37-b8e8-81d3ce1447fa"), 5, new DateTime(1988, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne", "Delicate style with plenty of charm and complexity. It's very toasty and creamy with rich honey and very delicate nuances. The finish is long and smooth with plenty of complexity. At the price a relative bargain.  ", 24.0m, "1982", new Guid("e339eea4-f28f-4885-9943-c9bc6059a88b") },
                    { new Guid("408b54b3-9293-4ae3-94c3-28c4d0cd9187"), 6, new DateTime(1988, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley", "Spicier, toastier and more complex than most California Chardonnays, emphas izing richness of texture and beautiful overtones of honey, tied together b eautifully in a superbly balanced, long finish.  ", 14.0m, "1986", new Guid("88345210-b8e0-4c87-b2f4-b43b0d82caae") },
                    { new Guid("871eac68-5321-4ac0-8fcd-fb4f05ea81fe"), 6, new DateTime(2018, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Tré Terre Vineyard Select Collection", "Strikes a fine balance between vibrant citrus and green apple and pear notes. Lightly oaky and creamy midpalate, this is deftly elegant and refined. Drink now through 2022. 5,400 cases made.", 28.0m, "2016", new Guid("ad89dcf4-810e-4f0d-893e-8f49bee4cdf8") },
                    { new Guid("66105397-d226-4ac1-a282-cd6245fd799d"), 6, new DateTime(1998, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa-Sonoma Counties Reserve", "Shows impressive depth and concentration of fruit, brimming with ripe, rich, complex and concentrated fruit flavors, tiers of pear, tropical fruit, fig, melon and citrus shadings, yet manages to remain elegant and refined, too. Delicious. Drink now through 2002. 4,000 cases made.", 34.0m, "1995", new Guid("ad89dcf4-810e-4f0d-893e-8f49bee4cdf8") },
                    { new Guid("64877ad4-2c02-4930-af09-0c968c75a468"), 6, new DateTime(1995, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Fumé Blanc Reserve Sonoma County", "Ripe, rich, round and spicy featuring new oak flavors backed by a mouthful of nectarine, pear and orange notes that expand on the finish. Superbly crafted in its supple, oak-centered style. 4,500 cases made.", 15.0m, "1994", new Guid("ad89dcf4-810e-4f0d-893e-8f49bee4cdf8") },
                    { new Guid("6f046f48-9c64-4cee-a744-c638ab94da5d"), 3, new DateTime(1993, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Siena Sonoma County", "A solid wine with beautifully articulated currant, spice and vanilla aromas and flavors, smooth and generous on the long finish. Has style and grace to age through at least 1998. A blend of Cabernet Sauvignon, Sangiovese, Merlot and Cabernet Franc. 375 cases made.", 20.0m, "1991", new Guid("ad89dcf4-810e-4f0d-893e-8f49bee4cdf8") },
                    { new Guid("a37d9cae-d74e-4440-bf23-f164e13459bb"), 6, new DateTime(1989, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay California Reserve", "Beautifully defined Chardonnay, rich, intense and concentrated with layers of pear, spice, apple and pineapple with a touch of earthiness on the finish. Drink now.  ", 28.0m, "1986", new Guid("ad89dcf4-810e-4f0d-893e-8f49bee4cdf8") },
                    { new Guid("c39bff7b-0ea5-4185-af50-0f4927121d5f"), 6, new DateTime(1988, 7, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Alexander Valley", "Well defined apple, pear and spicy flavors in a crisp, elegant, concentrated style that bodes well for drinking now or for cellaring three or more years. Smoky pear aftertaste lingers.  ", 13.0m, "1986", new Guid("ad89dcf4-810e-4f0d-893e-8f49bee4cdf8") },
                    { new Guid("19c48f7e-e383-496e-829f-cd9b12e861a3"), 3, new DateTime(1988, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Riserva", "Sharply focused, tightly wound, powerful, elegant and tannic, but cut with a laser beam of raspberry and red cherry flavor; bracing acidity on the finish. This might be ready to drink by the turn of the century.  ", 80.0m, "1982", new Guid("c102288c-e308-4591-be4e-72d548395bdd") },
                    { new Guid("a1dae2d1-84c2-4b7a-ba08-54a150a10b59"), 3, new DateTime(1988, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Napa Valley", "Broad, rich and deep with layers of black cherry, mint and cassis flavors and a toasty, cedary overlay. Very nicely integrated, ample tannins that are firm but not rough, and a long full finish. Give this one at least five years. Best Opus One to date. 10,000 cases made.", 50.0m, "1984", new Guid("65da0457-847c-4c2b-acb9-5604fc20bf7e") },
                    { new Guid("e45419cc-ee93-4a04-992a-fc5549ec68ff"), 3, new DateTime(1988, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Emilion", "Great intensity of flavor without too much weight, beautifully craftte, ele gant and structured, supple and rounded, with plum, spice, chocolate and cu rrant flavors that are well knit. Finishes with great length, finesse and s ubtlety.  ", 37.0m, "1985", new Guid("8b9bdb83-6470-4f34-9654-d5f29feac2ac") },
                    { new Guid("f87f5c53-cee2-44c5-8d1c-8154fac98afa"), 3, new DateTime(2000, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Vigorello", "This Tuscan red is big and plummy, full-bodied and tannic, with loads of vanilla and coconut, lots of pepper and berry character, too. Long, slightly hard finish. Needs time to mellow. Best after 2003. 5,000 cases made.", 32.0m, "1997", new Guid("47d20af2-7fbb-4432-aa06-5def37311ad1") },
                    { new Guid("85cc05ea-aeb5-4d6e-881a-2b5f0ba4d255"), 3, new DateTime(2016, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Asili Riserva", "Intense aromas of cherry, rose, licorice and tar pick up tobacco, spice and mineral elements on the palate. Concentrated, yet elegant and expressive, with more to give. Ends with a long aftertaste of fruit, underbrush and mineral. Best from 2018 through 2032. 1,110 cases made.", 59.0m, "2011", new Guid("dcd28546-c220-49a7-9724-49052ec83f16") },
                    { new Guid("f144a7be-7012-43d0-a929-c720307dc7e8"), 3, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbaresco Rabajà Riserva", "This fruity version features floral, cherry and plum flavors, along with savory leather, licorice and tobacco notes. A chewy texture and lively acidity keep this well-defined through the long finish. Complex and classy. Best from 2023 through 2038. 1,420 cases made.", 66.0m, "2013", new Guid("dcd28546-c220-49a7-9724-49052ec83f16") },
                    { new Guid("e1c3bc4a-d853-4545-be1e-d36cf047aea7"), 6, new DateTime(1990, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fuissé", "A great value in white Burgundy. Spicy, full-bodied, clean and packed with youthful fruit flavors that should develop with time. Layers of nutmeg, vanilla, pear and apple give it complexity. Tempting now, but should improve. Drink now through 1993. 5,000 cases made.", 15.0m, "1988", new Guid("460d9443-93ad-4156-ad89-81f78b0d7b83") },
                    { new Guid("bb125722-f1d6-4ea6-8318-c55831285b4d"), 3, new DateTime(1995, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rosso di Montalcino", "Big and opulent, offering stunning quality for a Rosso—it's more like a Brunello—and boasting masses of plum, black cherry, cedar and dark chocolate flavors. Lush and generous finish. Drinkable now.  ", 18.0m, "1993", new Guid("80e560bb-23b8-470c-a3dd-1981d2cbd633") },
                    { new Guid("b8ae38be-f097-4230-8135-6348dd2d3fc8"), 5, new DateTime(1995, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne L.P.", "A vibrant Brut that pairs high-toned cream, vanilla and butter aromas with zesty fruit flavors and lively acidity. It's focused, harmonious and lush, staying sprightly on the lingering finish. 400,000 cases made.", 32.0m, "NV", new Guid("b237ede1-bb7b-41f4-b3cf-316b543e1d6f") },
                    { new Guid("f473974b-2e8e-4756-a6da-fcd609597091"), 3, new DateTime(1995, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Napa Valley Chiles Mill Vineyard Unfiltered", "Intense yet elegant, focused on the spicy cherry, wild berry and light jammy notes. Has firm tannins to frame the flavors. Drinkable now through 1998. 1,350 cases made.", 15.0m, "1993", new Guid("5fed5dd2-ae55-4a36-82e6-d98f78f56863") },
                    { new Guid("31e21302-10ac-428e-bd50-43d89f45fece"), 6, new DateTime(1995, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Paarl", "Incredibly complex. Like a premier cru white Burgundy. Wonderful toasted oak, mineral, earthy and fruit components combined with full body and great acidity make this wine a superb package. Drink now through 1997. 2,000 cases made.", 20.0m, "1994", new Guid("3743d63d-8743-48a4-a03d-9940a4db3065") },
                    { new Guid("6666925b-ff47-4203-8c35-64c1bc3bf833"), 3, new DateTime(1998, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Sonoma County Green Valley Don Miguel Vineyard", "A delicious young Pinot Noir from a distinguished California vineyard, delivering ripe, rich and supple black cherry, wild berry, plum and spice flavors that are well focused, deep and lively. The wine finishes with just the right amount of tannin, and the aftertaste is rich and complex. Best now to 2002. 3,656 cases made.", 25.0m, "1995", new Guid("5dfe939a-8a2e-4268-bf5c-59bebb49b7e2") },
                    { new Guid("77b6dd07-0bc4-4bac-bfb6-00e18b211ad1"), 6, new DateTime(1995, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Green Valley Don Miguel Vineyard", "A rich, smooth and polished white from this Spanish-owned venture. This has a creamy texture and ripe, broad pear, apple and spice notes that build in intensity. The finish is elegant and refined. 6,745 cases made.", 20.0m, "1992", new Guid("5dfe939a-8a2e-4268-bf5c-59bebb49b7e2") },
                    { new Guid("c036b357-6383-49fd-931f-f98eaccfea08"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Echézeaux Vieille Vigne", "Amazing intensity of vivid flavors. Ripe plum, black cherry, minty taste. Of medium body, it shows finesse despite the massive, supple tannins. Long and flavorful finish. Try after 2000.  ", 60.0m, "1993", new Guid("ade5ef30-8c8d-4cfa-9ba8-b3adcb81dffc") },
                    { new Guid("8cdd2d75-b924-4675-ae80-5b87fd15be65"), 3, new DateTime(1995, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Napa Valley", "Bold, ripe, rich and complex. It's still tightly wound, but the layers of cherry, plum and spice flavors open up nicely and linger on the finish. Drinkable now. 625 cases made.", 30.0m, "1992", new Guid("6c4a5a68-b2f2-4d24-a0b7-f9592549bf25") },
                    { new Guid("d44bc628-f3fb-4508-90e2-753fbfeb4b99"), 3, new DateTime(1995, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale", "Massively flavorful but not heavy, expanding on its tar, prune, black cherry, coffee and cola flavors, then ringing out echoes of berries and spice on the long finish. A distinctive, complex and powerful Australian red. Tempting now, but best from 1999 on. 5,000 cases made.", 12.0m, "1992", new Guid("13914bc8-e10b-44ab-80c0-16789e75bc7f") },
                    { new Guid("70250a34-87af-43ee-89ab-722096ba0d3f"), 6, new DateTime(2002, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Mendoza Catena Agrelo Vineyards", "Delicious stuff. Juicy, with fig, pineapple and pear flavors, fat texture, and a long, bright finish that lets the flavors hang on. Drink now through 2003. 14,000 cases made.", 17.0m, "2000", new Guid("78458689-c373-448c-9b0a-bed642bb8f67") },
                    { new Guid("394c32f9-a94b-4bc6-8bb2-ea32c22bfaa7"), 6, new DateTime(1996, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay California", "Bold, ripe and exotic, boasting complex fig, apricot, honey, pear and vanilla notes, an altogether opulent and mouthfilling bottle of Chardonnay. Packs in lots of flavor, yet manages to maintain its finesse. 5,000 cases made.", 18.0m, "1994", new Guid("f8be37d8-e6d1-438c-8680-0a28a48c539c") },
                    { new Guid("da142047-7475-42ae-9adf-670d91f4969e"), 3, new DateTime(2001, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Malbec Mendoza Catena Lunlunta Vineyards", "Serious Malbec. Dense, with a brooding feel to the smoky currant, plum and spice flavors, this Argentine red is really packed with fruit, with ripe, thick tannins coming in on the finish. Just needs a little time to strut its stuff. Best from 2002 through 2004. 9,900 cases made.", 20.0m, "1999", new Guid("78458689-c373-448c-9b0a-bed642bb8f67") },
                    { new Guid("f96f561c-4499-4dce-af2c-ba32b399361d"), 3, new DateTime(1995, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Reserva", "An exciting new venture from the venerable Spanish estate of Bodegas Vega Sicilia. It's a rich, concentrated blockbuster that coats your mouth and lingers on your taste buds. Plenty of roasted and coffee nuances, almost jammy berry and black cherry and powerful yet silky tannins—this has it all. It promises great drinking in 1997 and beyond.  4,500 cases made.", 20.0m, "1991", new Guid("120e1bf4-0e35-47e3-b9bd-2ce911e6ba45") },
                    { new Guid("5115f460-2f09-4a9b-ae38-ec5684d9b796"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Napa Valley Napanook Vineyard", "Marked by an earthy, cedary edge, it manages to showcase a supple, harmonious core of currant, tobacco, mineral and anise flavors. Finishes with thick but polished tannins. Drink now. 6,500 cases made.", 55.0m, "1991", new Guid("437fc3d2-6bac-4d1f-9fa6-fbdd45b43b63") },
                    { new Guid("a0345a1a-bec2-4a3b-a2f3-5dee294603f4"), 3, new DateTime(1995, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Morisoli Vineyard Reserve", "Remarkably complex and concentrated, with an appealing core of currant, black cherry, vanilla and spice. Finishes with smooth, polished tannins and a wonderful sense of harmony. 1,000 cases made.", 28.0m, "1992", new Guid("d2f7dfcb-6d4e-4f51-859e-6db751aea8ed") },
                    { new Guid("cd9bbe78-ffef-440e-aa63-e4498a0e82e5"), 3, new DateTime(1997, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sangiovese Sonoma County Coniglio Selezione", "Might be the best California Sangiovese going. Harmonious, smooth and supple, with plum, cherry and berry flavors that fold together nicely on the finish, with a nice streak of spicy, toasty oak. Wow, what a wonderful wine. Drinkable now. 2,500 cases made.", 13.0m, "1994", new Guid("880b4fed-ed82-4755-84a7-f51db012e979") },
                    { new Guid("0a859958-867b-4a60-9acb-f35187e11cb0"), 3, new DateTime(1995, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County San Lorenzo Reserve", "Dark, rich and complex, with splendidly ripe plum, black cherry and raspberry flavors that expand to reveal greater depth and concentration. Has a satisfying, full aftertaste. 622 cases made.", 18.0m, "1993", new Guid("880b4fed-ed82-4755-84a7-f51db012e979") },
                    { new Guid("adc42b21-3222-409e-8295-7630fb69ec21"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay En Champans", "Full-blown currant juice; a reduction of fruit. Wonderfully concentrated layers of raspberry character. Full-bodied with velvety tannins, sweet fruit and very long finish. Try in 2000.  ", 44.0m, "1993", new Guid("3dab064a-6e97-42db-88a6-0a413dc949bd") },
                    { new Guid("b56015be-a477-4e1d-b72d-6b2d8254f0ad"), 6, new DateTime(2002, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault Les Charrons", "Gorgeous from start to finish. An explosive wine that delivers lemon, ripe fruit, and toasted oak (in manageable doses). Sweet-tasting, but with an impressive mineral note. Beautiful, long, succulent finish. Drink now through 2010. 500 cases imported.", 36.0m, "2000", new Guid("c501525b-c3f1-4d92-8c35-8419bc6a03b4") },
                    { new Guid("6f374bc3-7983-4839-a0fa-df66cef5ce8d"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay-Santenots", "Like great sex: exciting at first and relaxing in the end. Full-bodied and very voluptuous, boasting loads of berry, smoky, chocolate character. Full, velvety tannins and a long finish. Better after 2000. 97 cases made.", 185.0m, "1993", new Guid("47dd87cb-0810-45b1-ba9c-f750ea68883e") },
                    { new Guid("e6d18edd-4c5f-4078-91e1-e43a431bfa67"), 3, new DateTime(2004, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alec's Blend Napa Valley", "A stunning effort. Wonderful fragrant aromas of violets, vanilla bean, espresso and tiers of black cherry and blackberry fruit. This is an unusually fruity and complex wine that is quite reliant on oak for some of its flavors, but also a wealth of rich, concentrated dark berry flavors, finishing with a long, luxurious finish. Syrah, Merlot and Cabernet Sauvignon. Drink now through 2012. 1,000 cases made.", 48.0m, "2002", new Guid("daa270fa-43a1-4306-8cb3-72538f3a2121") },
                    { new Guid("8a8eddd2-be76-491a-be8e-3cc6cf9e31c6"), 3, new DateTime(1995, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Oakville Ranch", "Has a wonderful sense of harmony and finesse, with tiers of currant, plum, cherry and tobacco flavors enhanced by hints of toast and vanilla from oak. Finishes with firm, fine tannins. Better still in 1998. 700 cases made.", 30.0m, "1992", new Guid("daa270fa-43a1-4306-8cb3-72538f3a2121") },
                    { new Guid("cafa060e-b713-420b-b80d-27274e48d9be"), 3, new DateTime(1995, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Mendoza Catena Agrelo Vineyard", "This classy wine takes Argentine Cab to another level. From the deep color to the complex aromas of black cherry, cassis, tobacco and tar through the rich, full-bodied flavors and long finish, this shows concentration, focus and depth. Drink now. 2,700 cases made.", 16.0m, "1992", new Guid("78458689-c373-448c-9b0a-bed642bb8f67") },
                    { new Guid("c78bed23-0ba0-4283-824d-461c85209a7e"), 6, new DateTime(2004, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Napa Valley Eisele Vineyard", "An exceptional bottling that offers a veritable explosion of hay, lemon blossom, grass, melon and nectarine notes. Tangy acidity, supple cream tones, anise and mineral nuances weave through a finish of superior depth and dimension. Doesn't get much better than this. Drink now through 2006. 1,300 cases made.", 32.0m, "2002", new Guid("c7ddf282-e909-4c6d-a6a7-c1f37345123e") },
                    { new Guid("f12576dd-9994-4cae-ac82-fab14caceac1"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Mendocino DuPratt Vineyard", "Beautifully crafted, rich and complex, featuring layers of spice, wild berry, cherry and toasty, buttery oak, an altogether supple and harmonious red that packs in flavor while maintaining its elegance. Picks up a peppery note on the finish. Delicious. Drink now through 1997. 1,100 cases made.", 18.0m, "1993", new Guid("f8be37d8-e6d1-438c-8680-0a28a48c539c") },
                    { new Guid("4201d200-99c2-41ad-83f0-d53a50321363"), 6, new DateTime(1999, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay California Steele Cuvée", "Lots of up-front fruit and spice make this white distinctive, with tiers of fig, pear, melon, apricot and nutmeg cascading to a long finish with an intricate aftertaste. Even better than last year's version, also a Spectator Selection. Drink now through 2001. 10,000 cases made.", 18.0m, "1997", new Guid("f8be37d8-e6d1-438c-8680-0a28a48c539c") },
                    { new Guid("5a21cdbc-58dd-4ea0-8b3c-5b8f2c558168"), 3, new DateTime(2003, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel San Francisco Bay Carla's Vineyards", "Dark and chocolaty, with zesty wild berry and blackberry fruit and lots of spice and espresso scents. The long, rich, concentrated aftertaste showcases the exotic fruit flavors. Drink now through 2008. 2,100 cases made.", 23.0m, "2001", new Guid("d85af72e-373f-48bb-a3c8-86da70d378dd") },
                    { new Guid("932142b4-b141-4de5-92d7-950b0c1108e6"), 3, new DateTime(1996, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Mount Veeder Brandlin Ranch", "This wine has a great sense of harmony and finesse. The texture is fleshy and polished, the flavors ripe and very complex tiers of plum, wild berry, cherry and currant that continue to unfold through the finish. 625 cases made.", 20.0m, "1994", new Guid("d85af72e-373f-48bb-a3c8-86da70d378dd") },
                    { new Guid("92084a0b-57ea-4618-912e-f8402b7920b0"), 6, new DateTime(1996, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chassagne-Montrachet En Remilly", "Seductive and charming, boasting tons of ripe pear and apple flavors. Rich honey, cream and dried herb complexity. Voluptuous mouth-feel and amazing silkiness. Needs time; try in 1999. 408 cases made.", 40.0m, "1994", new Guid("c8021e94-3d0b-4f5c-8b8b-ccd1646d7439") },
                    { new Guid("054b2b08-28aa-4bf2-b955-6c13413eecf8"), 3, new DateTime(1996, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa Old Block", "Rich, spicy and complex, its generous plum and red cherry flavors cradled in a nest of sweet cinnamon, nutmeg, vanilla and meaty, roasted notes. Full-bodied, but gentle enough in texture to be approachable now. Best after 2000. 1,500 cases made.", 27.0m, "1993", new Guid("dcc47d53-c4f3-4c6b-9a9f-cfdab7085d36") },
                    { new Guid("63679305-08bf-4470-a5a5-848a8d582b5e"), 3, new DateTime(1996, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Napa Valley Aldo's Vineyard", "A Zinfandel in a rich yet elegant style, showing nuances of ripe cherry, raspberry, mint and spice. The toasty oak flavors fold in nicely on the finish. The best yet from this winery. 780 cases made.", 19.0m, "1994", new Guid("29679bb3-cbe7-4627-9ab6-2ba8ef416a5d") },
                    { new Guid("5867b9af-1462-4485-8cfb-00f6f78ed0c5"), 6, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Chardonnay Hunter Valley Vat 47", "A distinctive white from Australia that's crisply focused, packing in its spicy, honeyed, buttery pear and apricot flavors that swirl around beautifully on the long, elegant finish. Drink now through 1998. 8,000 cases made.", 16.0m, "1994", new Guid("c8ed95ec-fdd7-4e67-8f8d-9040f9edebaa") },
                    { new Guid("a89a171a-d3ab-45a9-a783-2e9433d5b980"), 3, new DateTime(2002, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Grand Reserve", "Orchestrates an assortment of ripe cherry, blackberry and currant flavors in a smooth texture. Has fine tannins, picking up toasty, cedary oak on the finish, providing backbone. Best from 2004 through 2012. 4,500 cases made.", 20.0m, "1999", new Guid("601ded39-dea7-4028-9ba2-690329d00543") },
                    { new Guid("cf7f1031-6476-4841-b819-afc9a1b7d080"), 6, new DateTime(1998, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Maria Valley Bien Nacido Vineyard Grand Reserve", "Smooth, ripe, rich and creamy. A supple, elegant, complex wine, with tiers of toasty, spicy oak, hints of pear, nectarine and peach, and a long, refreshing finish. Drink now through 2002. 20,000 cases made.", 20.0m, "1996", new Guid("601ded39-dea7-4028-9ba2-690329d00543") },
                    { new Guid("24af4212-e422-4156-9f97-048507b6eb11"), 6, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay California Grand Reserve", "This reserve bottling is ripe, rich and full-bodied, with layers of creamy pear, fig, melon and honeysuckle flavors that slowly unfold, revealing ever-greater depth and concentration right through to the long, delicious aftertaste. The price is reasonable, too. 10,000 cases made.", 18.0m, "1995", new Guid("601ded39-dea7-4028-9ba2-690329d00543") },
                    { new Guid("2307445c-377d-4868-8778-a98677e93dd0"), 6, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Grand Reserve", "Rich, smooth and creamy, a complex and exotic style that offers lots of bold pear, spice, vanilla and hazelnut notes, picking up a citrus edge on the finish. Impressive flavor and texture. 3,000 cases made.", 16.0m, "1994", new Guid("601ded39-dea7-4028-9ba2-690329d00543") },
                    { new Guid("4a8381bd-b394-404a-aaa2-5e1dac278f19"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Barbara County Bien Nacido Vineyard", "Ripe, spicy, rich and complex young red delivering supple cherry, tar, currant and berry notes. Has a measure of grace and harmony, finishing in fine tannins. Ready now. 900 cases made.", 20.0m, "1994", new Guid("f8be37d8-e6d1-438c-8680-0a28a48c539c") },
                    { new Guid("dd43a803-4513-440e-85fc-fb74c16a53f5"), 3, new DateTime(1996, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vosne-Romanée Les Beaux Monts", "A deep, dark Burgundy that has concentrated, intense, ripe fruit notes, luscious oaky accents and a sense that it's still tight and youthful. Great balance between its firm acidity, tight tannins and ample hoard of flavors. Best to wait until 2000. 300 cases made.", 32.0m, "1993", new Guid("f5c52de9-09d9-442f-bcda-5da92a457c50") },
                    { new Guid("04f1105a-fd88-409c-8fb8-7a8677726c5c"), 2, new DateTime(1996, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sémillon Australia Noble One", "Deep gold color, deep aromas and deep flavors, centering around fig, honey, almond and orange zest. Spicy vanilla notes swirl through the elegant finish.  A remarkable wine with a character all its own. 8,000 cases made.", 28.0m, "1993", new Guid("7ed7444b-d21a-4783-accf-190d09e20fb5") },
                    { new Guid("a556363a-efe8-40fb-a9e2-b1869e545e9b"), 3, new DateTime(1996, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Oregon Claudia's Choice", "A profound, graceful Pinot Noir that presents a gamy character on a first whiff, then shows an amazing depth of fruit, expanding into its spicy plum, black cherry and anise flavors that swirl through the long finish. Needs until 1998 or even 2000 to show it all. 300 cases made.", 25.0m, "1994", new Guid("097c2243-10b4-48fa-95f5-9c2a3932e284") },
                    { new Guid("dc62e6db-c3a3-4e33-a485-691807f86023"), 3, new DateTime(2008, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Douro Vertente", "The aroma is redolent of berry and cherry, with rich, lush flavors of plum, raspberry, cherry and spice, flanked by veins of mineral. The long finish is filled with mocha and cream. Drink now through 2014. 1,740 cases made.", 32.0m, "2005", new Guid("37b25f6a-f809-49a8-b5ae-8693cbc254a7") },
                    { new Guid("05242ddc-cea0-4fc0-b2e4-874fc1b9e9d4"), 2, new DateTime(1996, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tawny Port Colheita", "Absolutely fabulous. One of the best tawnies we've had in a long time. An array of floral, dried fruit and nut character on the nose and palate. Medium in body and extremely smooth. Great balance. Mega-long finish. 2,224 cases made.", 34.0m, "1983", new Guid("37b25f6a-f809-49a8-b5ae-8693cbc254a7") },
                    { new Guid("8f1b69aa-cbdd-4298-a4a4-ef363b5154dd"), 3, new DateTime(2011, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Crozes-Hermitage La Guiraude", "This is very tight now, with a wall of iron-clad structure holding the core of blackberry, cassis and black cherry fruit in check. Notes of tar, iron and sweet tobacco lurk in the background. The long, long finish is all minerally cut. A brick-house Crozes that demands cellaring. Maybe the best vintage yet for this cuvée. Best from 2013 through 2020. 650 cases made.", 55.0m, "2009", new Guid("32df0f54-89da-4d96-93a0-932bd0585fad") },
                    { new Guid("8a196602-5a51-4a04-8a2a-bc1649a80d45"), 3, new DateTime(1996, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Crozes-Hermitage", "Pure, classic Syrah aromas of raspberry, licorice and game carry through on the rich palate, beautifully defined and balanced. Delicious now for its varietal character, it should improve through 2003. 4,000 cases made.", 15.0m, "1994", new Guid("32df0f54-89da-4d96-93a0-932bd0585fad") },
                    { new Guid("c0193f48-eb33-4d0b-9c45-325e9b6c0a82"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Coonawarra St. George Vineyard", "Ripe, smooth and opulent, a silky-textured Cabernet delivering rich black cherry, tar, spice and mint flavors bubbling around on the gorgeous finish. Approachable now, but youthful enough to want cellaring. Best after 2000. 7,400 cases made.", 22.0m, "1991", new Guid("e0ad1a15-21ee-494f-baf9-3a54610ca998") },
                    { new Guid("e887c37b-37e7-41db-906b-c2a89f2ecbf2"), 6, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Padthaway", "Tightly wound, a brilliantly flavorful white that unfolds slowly, displaying Burgundian hazelnut, honey and pear notes which linger on the finish. Drink now through 1997. 21,400 cases made.", 13.0m, "1994", new Guid("e0ad1a15-21ee-494f-baf9-3a54610ca998") },
                    { new Guid("c75a2454-24e3-4bdf-84f2-62c987b39602"), 6, new DateTime(1999, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Stellenbosch", "Wow. This is a richly concentrated, deftly made Chardonnay that's packed with ripe fruit flavors and layered with creamy, spicy accents that linger on the finish. Full-bodied, almost sweet in its ripeness, but still balanced. Drink now through 2001. 3,500 cases made.", 21.0m, "1997", new Guid("327f5610-a56f-47a8-958b-43911e108de8") },
                    { new Guid("286b56be-9202-4d9c-a392-0fb2ac6c0cd9"), 3, new DateTime(1995, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon-Merlot Stellenbosch", "Classy and refined mint, coffee, cedar and dried plum character. Full-bodied and elegant, adding fine tannins and a silky finish. Superbly made. Drink now. 1,200 cases made.", 20.0m, "1992", new Guid("327f5610-a56f-47a8-958b-43911e108de8") }
                });

            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("d00c1133-b157-46b6-b309-771db3d5240c"), 3, new DateTime(1996, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Amarone della Valpolicella Classico", "All the pieces fall together in this mature wine. It has mushroomy aromas and dried cherry flavors with plenty of leather to boot. Well balanced and almost chewy, with licorce and tea notes on the finish.  Drink now through 2000. 7,250 cases made.", 18.0m, "1988", new Guid("96dcccdc-daf4-4744-979d-910c059769b8") },
                    { new Guid("1428b64f-bb23-48aa-9aab-f0b796b00101"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Eisele Vineyard", "A classic California Cabernet that is well oaked, with distinct vanilla bean and toasty oak accents, but it also shows compelling cherry and berry flavors that develop complex nuances. The fruit shows a remarkable measure of finesse and harmony on the finish. 810 cases made.", 40.0m, "1992", new Guid("c7ddf282-e909-4c6d-a6a7-c1f37345123e") },
                    { new Guid("bbe93679-8519-4978-b494-4cb572fd66a8"), 3, new DateTime(2016, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Ueberroth Vineyard", "This potent red is loaded with personality, featuring aromas of smoky black raspberry and licorice that open to complex, dense flavors of dried cherry, cracked pepper beef and grilled herb. The tannins are big and framed by loamy mineral details. Drink now through 2028. 1,800 cases made.", 48.0m, "2013", new Guid("0a10cefa-0c76-4644-8e5f-fe2ba6a13229") },
                    { new Guid("070cd636-a77a-450c-beca-262980b42c35"), 3, new DateTime(2015, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Petite Syrah Howell Mountain Rattlesnake Ridge", "Bright, vivid and extremely aromatic, with lavender, white pepper and cinnamon notes up front, backed by a core of huckleberry, blackberry and blueberry flavors. The fleshy tannins have a rustic appeal, but never get in the way of the details on the long finish, accented by mineral, toasted herb and black tea elements. Drink now through 2030. 475 cases made.", 44.0m, "2013", new Guid("0a10cefa-0c76-4644-8e5f-fe2ba6a13229") },
                    { new Guid("efa4abe7-91b1-45a1-a4b5-11fefbcc70e9"), 3, new DateTime(1994, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Mesoñeros de Castilla Crianza", "Lush and velvety in texture, with focused flavors of black cherry and plum, accented by toast and chocolate notes from the oak barrels. Has a sense of grace despite the firm tannins, lasts a long time on the finish. Drinkable from now to 2000. 10,000 cases made.", 14.0m, "1990", new Guid("eea671ee-9261-49a5-8ce7-11abc1e3e6f2") },
                    { new Guid("bb33111e-1320-4406-9190-b358d3b193ab"), 6, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Laguna Ranch Vineyard", "The seductive, complex smoky oak and exotic fruit flavors turn more elegant and refined on the palate as the tiers of earthy pear, grapefruit and spice show through. Finishes with a delicate touch. The first Gallo Chardonnay from Laguna Ranch Vineyard. 6,195 cases made.", 16.0m, "1994", new Guid("5417651d-8162-4435-ac07-fa31bc510720") },
                    { new Guid("1948d2be-885a-44b0-9ef8-3ddfae0ce726"), 3, new DateTime(1994, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Dry Creek Valley Frei Ranch Vineyard", "Bright and lively, with spicy black cherry, raspberry and peppery notes that are deep and complex, finishing with a long, lingering aftertaste. Debut Frei Ranch Zin from Gallo. 3,600 cases made.", 14.0m, "1990", new Guid("5417651d-8162-4435-ac07-fa31bc510720") },
                    { new Guid("150365b9-c19e-4056-8b33-656ab8da804a"), 3, new DateTime(1994, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Morgon", "An intense, concentrated wine that adds meaty, smoky and peppery notes to the core of black cherry fruit, and achieves its intensity without new oak. It's dramatic, powerful and pays no mind to the idea that Beaujolais is a simple quaffing wine. Drink now through 1996.  ", 12.0m, "1991", new Guid("6a43f8b0-756e-46c6-8fe4-deea1d16e7a6") },
                    { new Guid("a130ee6e-68f9-43a1-847d-a61d098008c2"), 3, new DateTime(2015, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Altamont", "A seamless, fleshy expression of juicy, enticing plum, cherry blossom and rose petal, with black cherry flavors at the core. A remarkably charming and seductive effort. Drink now through 2022. 880 cases made.", 60.0m, "2013", new Guid("bfc87243-8f69-42f6-bc0d-ee68220fcfe6") },
                    { new Guid("d995b97c-97aa-4bf0-ad3c-d0c8891858d0"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Russian River Valley Goldridge", "This presents Syrah in a more tannic, structured style, with crushed rock, loamy earth, dark berry and cedar-tobacco leaf scents, ending with tannins that provide traction and a burst of black licorice. Drink now through 2022. 300 cases made.", 40.0m, "2010", new Guid("bfc87243-8f69-42f6-bc0d-ee68220fcfe6") },
                    { new Guid("93c11c4b-720c-40e7-bf7f-8dde99ac94b0"), 3, new DateTime(1994, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Kara's Vineyard", "A classy, elegant young wine that blends toasty, buttery oak with a supple core of bright, lively cherry and currant flavors. Finishes with a broad, complex aftertaste and fine tannins. Tempting now but sure to improve through 1997 and age into the next century. 1,200 cases made.", 20.0m, "1991", new Guid("1b2a1a2b-efb6-4d20-bc5a-c353dcbb7ea0") },
                    { new Guid("a2d2f44e-6c89-4ecf-aeb6-606d14a2ac22"), 6, new DateTime(1994, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay South Eastern Australia Mount Pleasant", "Ripe and generous; broad, with some depth and distinction, layering its spicy pear, melon and grapefruit notes nicely. Flavors echo on the long finish. Drinkable now. 18,000 cases made.", 10.0m, "1992", new Guid("540abcd4-56de-4e84-87de-b68c97648fd1") },
                    { new Guid("6f20a917-a3d3-4e05-b2dd-cef3176812a3"), 6, new DateTime(1997, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough", "Superb. Well crafted, it's steely dry but exuberant at the same time, with distinctive, bright passion fruit, nectarine and herb flavors that linger bracingly through the zingy finish. Delicious. Drink now. 7,000 cases imported.", 17.0m, "1996", new Guid("34cff6f0-8ce0-47e2-a5d3-83a3250174ad") },
                    { new Guid("c6b51c36-2a6a-4d73-b700-fef61ed13bfc"), 3, new DateTime(2015, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Imperial Reserva", "Smoky and tarry notes give this rich red an austere character, but plum, licorice, soy and mineral notes promise an expressive future. The tannins and acidity are balanced and unobtrusive. Best from 2016 through 2030. 1,500 cases imported.", 44.0m, "2010", new Guid("8a70cb00-8bdf-410a-a2d4-3d0e1649cffd") },
                    { new Guid("92e29acc-d8ed-41f4-8766-2c172371f67d"), 3, new DateTime(1997, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Val Sotillo Crianza", "This big, bold red from Spain delivers toasty, chocolate-scented oak character, and backs it up with ripe plum and blackberry flavors and marked gamy notes that are concentrated yet quite fresh. Big tannins don't get in the way of a long, spicy finish. Try in 1999. 6,000 cases made.", 19.0m, "1994", new Guid("eea671ee-9261-49a5-8ce7-11abc1e3e6f2") },
                    { new Guid("68eeb474-332c-4d23-a588-d68e0184dd35"), 6, new DateTime(2014, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja White Monopole", "This focused white shows bright, juicy flavors of white peach and Meyer lemon, with herbal and mineral accents. The texture is quite rich, but crisp, citrusy acidity keeps this lively. Drink now through 2017. 4,000 cases imported.", 15.0m, "2013", new Guid("8a70cb00-8bdf-410a-a2d4-3d0e1649cffd") },
                    { new Guid("a63910b5-0bf5-44b9-b7bb-f8c0a170004c"), 3, new DateTime(1994, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Viña Real Gran Reserva", "A big wine with plenty of fruit and lively accents of coffee and spice. It's balanced, concentrated and just beginning to show maturity. Drink now through 1998.  ", 18.0m, "1985", new Guid("8a70cb00-8bdf-410a-a2d4-3d0e1649cffd") },
                    { new Guid("c47b1a43-8466-4306-8251-4980dff300b5"), 3, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Napa Valley Carneros", "Brimming with juicy plum, strawberry and black cherry flavors, with decidedly earthy and herbal notes, and finishing with firm, tealike tannins. Hold a year to soften; try in 1998. 1,416 cases made.", 23.0m, "1995", new Guid("c88f1ee5-2a23-4f90-9612-d75b3db4831d") },
                    { new Guid("aa5f3631-e916-4e82-b529-f6ce88c6ac2b"), 6, new DateTime(1994, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Carneros", "Intense and lively, with an elegant band of spicy pear, apple, vanilla and nutmeg flavors that fold together nicely on the finish. Drink now. 682 cases made.", 16.0m, "1992", new Guid("c88f1ee5-2a23-4f90-9612-d75b3db4831d") },
                    { new Guid("60549fba-9693-4173-b069-6ab69b303ab6"), 5, new DateTime(2009, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Sonoma County Sonoma", "Lively and fun to drink, with creamy Asian pear and citrus aromas that lead to crisp, easygoing apple, yeasty cinnamon and mineral flavors. Drink now through 2012. 103,000 cases made.", 20.0m, "NV", new Guid("4f77598c-8f82-4420-96e5-b77f92d1d0b4") },
                    { new Guid("a32be599-b7db-472a-8da9-f29881c804cb"), 5, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Sonoma County", "An exceptional value. Round and full-bodied, with rich, ripe apple, pear and honeydew melon flavors. This complex sparkler has a nice touch of yeast and doughy aromas that give it added character, turning creamy with a touch of vanilla. Drink now through 2009. 65,000 cases made.", 18.0m, "NV", new Guid("4f77598c-8f82-4420-96e5-b77f92d1d0b4") },
                    { new Guid("2b540007-277a-4cff-89ff-c168a8133d22"), 3, new DateTime(1994, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Barbara County La Bauge Au-dessus Bien Nacido Vineyard", "A dead ringer for a fine red Burgundy. Shows this producer at its best, with the tight, focused, complex cherry, spice, earth and oak flavors that turn smoky and meaty on a long, full finish. Beautifully crafted. Try from now to 2002. 413 cases made.", 25.0m, "1991", new Guid("f464cda0-df95-4502-802c-c251056afb00") },
                    { new Guid("4fba2c6e-586e-4723-8ef4-8247fe374210"), 6, new DateTime(1994, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Barbara County Le Bouge D'à côté", "Rich and earthy, with complex, concentrated pear, pineapple, oak and spice notes that turn elegant, gaining finesse and grace on a long finish, where the honey and pear flavors shine through. Ready now. 1,113 cases made.", 25.0m, "1992", new Guid("f464cda0-df95-4502-802c-c251056afb00") },
                    { new Guid("3258d9ce-9b7d-45eb-a1f5-d654e8d766f7"), 3, new DateTime(2011, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Morgon Jean Descombes", "Light tannins and a smoky mineral note frame this lush red, which displays layers of black cherry, raspberry ganache and tea rose flavors. A spicy thread runs through the wine, leading to a fresh, firm finish. Drink now. 8,000 cases made.", 15.0m, "2009", new Guid("7de5673a-b9e6-49e1-b784-447b70b38320") },
                    { new Guid("f5db5779-5cd6-497d-9c62-191ddab288ae"), 3, new DateTime(2004, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Moulin-à-Vent Domaine des Rosiers", "Great core of blackberry and currant fruit, with ample chocolate, spice and briar notes. This is structured like its colleagues, but manages to end on a lush, creamy note. Drink now through 2005. 9,000 cases made.", 14.0m, "2003", new Guid("7de5673a-b9e6-49e1-b784-447b70b38320") },
                    { new Guid("03cb2bd3-fca2-4804-88a2-4b9e33fd5954"), 6, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fuissé Aged in Oak", "Lively, racy and full-bodied, this white Burgundy is packed with deftly created oak accents, showing a pronounced toasted character along with plenty of lemon notes, good fruit and impressive length on the finish. It's tempting now, but worth cellaring short-term. Best from 2000 through 2005. 5,000 cases made.", 17.0m, "1997", new Guid("7de5673a-b9e6-49e1-b784-447b70b38320") },
                    { new Guid("34c08e70-22ee-4698-b738-de06edd5b092"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Imperial Gran Reserva", "Firm and a bit austere, this red shows depth and drive, with chewy tannins supporting plum, tobacco, licorice and mineral flavors. The structure is solid but the wine remains fresh. Maturing now, this has a long life ahead. Drink now through 2024. 4,000 cases made.", 63.0m, "2004", new Guid("8a70cb00-8bdf-410a-a2d4-3d0e1649cffd") },
                    { new Guid("a4b845e8-d088-4177-bb86-44c57f6336cc"), 3, new DateTime(1995, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Montosoli", "One of <i>the</i> greatest Brunellos ever made. Very muscular with loads of polished tannins and fruit. Full-bodied and powerful, boasting gravel, mineral and berry character. Needs time. Best after 2000. 1,000 cases made.", 60.0m, "1990", new Guid("c1ef139c-8e1e-4b7e-b22a-82359d9eccc4") },
                    { new Guid("16a0b32d-284e-4609-b6b9-19ea6922d023"), 3, new DateTime(2017, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Our 40th Harvest", "Cherry, leather and iron notes mark this suave, elegant red. A bit reticent now, but the fruit is fresh and long on the finish. The dense tannins leave a mouthcoating impression, but this opens up beautifully over a few hours. Best from 2021 through 2035. 10,000 cases made.", 65.0m, "2012", new Guid("c1ef139c-8e1e-4b7e-b22a-82359d9eccc4") },
                    { new Guid("784746ae-cb3e-4f30-b3e4-42887e8480c0"), 3, new DateTime(1998, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Solengo", "Wild thing. This Italian red is even better than the wonderful debut '95. It offers exotic and complex aromas of blackberry, violet, crushed raspberry, is full-bodied, and though very tannic, the tannins are coated with ripe fruit, and the long and caressing finish delivers even more fruit. Sangiovese, Cabernet Sauvignon, Merlot and Syrah. Best after 2000. 2,000 cases made.", 52.0m, "1996", new Guid("07b47487-9ff9-4118-a927-e4a613b46957") },
                    { new Guid("248224a3-5e44-401d-a7e7-3659e35a7ad1"), 3, new DateTime(2014, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel California Juvenile", "Sleek, ripe and youthful, offering jammy raspberry aromas and flavors, with zesty notes of smoky spice and grilled anise. Drink now through 2019. 10,000 cases made.", 20.0m, "2012", new Guid("0a10cefa-0c76-4644-8e5f-fe2ba6a13229") },
                    { new Guid("e9a5f5c5-7262-4b04-8cc4-3f57572ad234"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Dusi Vineyard", "Rich and powerful, but focused like a laser, with wild berry and toasty briar aromas that lead to ripe but complex flavors of black cherry, licorice and spicy orange peel. The tannins are big but fleshy. Drink now through 2022. 900 cases made.", 42.0m, "2011", new Guid("0a10cefa-0c76-4644-8e5f-fe2ba6a13229") },
                    { new Guid("18dae6c0-38e1-4b9e-826d-d334292f0949"), 3, new DateTime(2012, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Pesenti Vineyard", "That rare Zin that shows both richness and a sleek sense of elegance. Briary and floral black raspberry aromas lead to delicately complex flavors of smoky cherry, wild anise and white peppercorn. Drink now through 2020. 900 cases made.", 35.0m, "2010", new Guid("0a10cefa-0c76-4644-8e5f-fe2ba6a13229") },
                    { new Guid("bdbb5e4b-d16a-4f5d-bca9-2c99d79f43c7"), 3, new DateTime(1995, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Napa Valley Hayne Vineyard", "A truly exciting, beautifully proportioned, rich and vibrant red. Has a delicious core of ripe black cherry, raspberry, anise and plum, finishing with depth and smooth, polished tannins. Drink now through 1999. 200 cases made.", 22.0m, "1993", new Guid("0a10cefa-0c76-4644-8e5f-fe2ba6a13229") },
                    { new Guid("9d94f771-9d6f-490f-b7d5-7b75e658e82c"), 3, new DateTime(1996, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino Vigna del Quercione Riserva", "Stunning concentration, with layers of berry, cherry and spice flavors. Full-bodied and very round, with loads of fruit and tannins. Drinkable now, but age it for decades to come. Even better than this producer's '90 norm, this is one of the greatest Brunellos ever made. 560 cases made.", 84.0m, "1990", new Guid("ca98d8a4-3fa9-43a7-81de-260b94134713") },
                    { new Guid("acc28caf-4263-4522-88cd-0e33d26d5a2e"), 3, new DateTime(2011, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sorella Horse Heaven Hills", "Impressive for its intensity and elegance, packed with black cherry, cherry, plum and spice flavors, hinting at roasted red pepper and a tarry minerality as the finish glides smoothly over refined tannins. Cabernet Sauvignon, Cabernet Franc and Merlot. Best from 2012 through 2020. 973 cases made.", 72.0m, "2008", new Guid("3f92c3ad-66f4-4c23-9c47-b2a289ffa35c") },
                    { new Guid("b3ead8be-0948-4300-840d-d7e7af7cae99"), 3, new DateTime(2008, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Champoux Vineyard Horse Heaven Hills", "Seamless and harmonious, with beautifully articulated, ripe black cherry, cassis and violet-tinged floral notes that mingle with hints of cedar and dark chocolate as the finish plays out against polished tannins. Cabernet Sauvignon, Merlot, Cabernet Franc and Petit Verdot. Best from 2009 through 2015. 1,185 cases made.", 58.0m, "2005", new Guid("3f92c3ad-66f4-4c23-9c47-b2a289ffa35c") },
                    { new Guid("0af17478-a2cf-4891-a444-bb63ea033096"), 6, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Preferred Stock", "Strikes a nice balance between its ripe, supple pear, vanilla and spice flavors and the light, toasty oak shadings. Turns smooth and creamy on a complex finish, where the flavors echo on. Ready now. 1,250 cases made.", 18.0m, "1992", new Guid("e0b9ce01-e15b-438c-9df9-c6e4c1a15983") },
                    { new Guid("777c70f7-f028-4a8a-b230-43ebaf77e936"), 3, new DateTime(1999, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Les Chaignots", "Sleek as a thoroughbred, the tannins are beautifully integrated with great minerally intensity, chalk, toast, spice and black cherry notes, and it has serious <i>terroir</i> etched all over it. Best from 2001 through 2005. 360 cases made.", 40.0m, "1996", new Guid("e2b7f341-3b4d-4df2-851a-b6c77ca46016") },
                    { new Guid("b61e33d2-5da7-4be9-aeef-122ea7b2caab"), 3, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Nuits-St.-Georges Les Vaucrains", "Dense and expansive, with a slightly herbal edge to the chunky raspberry, blackberry and spice aromas and flavors, a powerful red buried under a thick layer of chewy tannins. Tightly wrapped now. Best after 1999.  ", 39.0m, "1991", new Guid("e2b7f341-3b4d-4df2-851a-b6c77ca46016") },
                    { new Guid("e60bcfd0-a08c-4672-9594-54a916280eb3"), 3, new DateTime(2000, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Alexis Napa Valley", "Weaves together a complex array of rich black cherry, ripe plum, wild berry and floral notes, all sharply focused, finishing with firm tannins and wonderful length. Best from 2002 through 2010. 3,400 cases made.", 45.0m, "1997", new Guid("5b9f3c14-9c66-45c5-bba1-f21e3180bb51") },
                    { new Guid("52f79a65-f888-49f3-802d-be2318348f42"), 3, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sangiovese Napa Valley", "Ripe and generous, round and flavorful. Pours out its pure plum, berry and spicy vanilla character in a supple package that just doesn't quit. Beautifully made. Drinkable now. 1,492 cases made.", 18.0m, "1992", new Guid("5b9f3c14-9c66-45c5-bba1-f21e3180bb51") },
                    { new Guid("f003a1d8-b5c1-45c6-84b6-d9aaaabd08b4"), 6, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fumé Coteaux du Petit Boisgibault", "Fresh as a sea breeze. Intense and vivid, offering crisp lemon-lime and light herbal notes with a nervy acidity that gives it lift and elegance. It's fresh, clean and maintains good focus through the long finish. Drink now. 10,000 cases made.", 16.0m, "1992", new Guid("6814a2c4-2b22-4cd0-8fb0-7f9a9cea731a") },
                    { new Guid("86b67321-bba4-46ca-9061-ff195a21bde5"), 6, new DateTime(2004, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sancerre Le Chêne", "Subtle note of honeysuckle and straw, but the palate explodes with mineral, citrus peel, chive flower, flint and gooseberry flavors. Then fresh, racy acidity supports a long, flinty finish. Ideal with Crottin de Chavignol. Drink now through 2006. 1,250 cases imported.", 28.0m, "2002", new Guid("a23bad0e-79bb-4782-aeca-bd4ed988f5f8") },
                    { new Guid("1dce04c4-e8be-452f-ac13-a6594e671ad9"), 6, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sancerre", "Quintessential Sauvignon Blanc: high-intensity and classic flavors in an elegant, subtle style. The citrus, grapefruit and mineral elements are balanced and clean; the acidity is bracing without sharpness. Great with food. Drink now. 2,500 cases made.", 15.0m, "1992", new Guid("a23bad0e-79bb-4782-aeca-bd4ed988f5f8") },
                    { new Guid("18711c49-7c7f-4ba3-8a29-ecd4f83d76dc"), 3, new DateTime(2019, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Ribbon Ridge The Upper Terrace", "Seduces you immediately, with expressive violet and dark raspberry aromas that open to polished and brilliantly structured blueberry, savory anise and tea flavors that take on richness on a long finish. Drink now through 2026. 1,850 cases made.", 110.0m, "2017", new Guid("2ffa25a0-ddef-497a-ac3e-1ce81acc5322") },
                    { new Guid("dbfd9858-e341-427d-9707-3e83fa14bfe5"), 3, new DateTime(2016, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Ribbon Ridge The Beaux Frères Vineyard", "Supple, expressive and multilayered, exhibiting flavors of plum, currant, pomegranate and violet that come together harmoniously, persisting into the long and exceptionally well-balanced finish. Drink now through 2024. 2,405 cases made.", 90.0m, "2014", new Guid("2ffa25a0-ddef-497a-ac3e-1ce81acc5322") },
                    { new Guid("611724d5-7f91-47a7-9eb9-e3db38bb8b47"), 3, new DateTime(2004, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley The Beaux Frères Vineyard", "Big and ripe, a mouthwatering wine with tingly acidity bringing harmony to a wash of plum, blackberry, cherry and dusky spice flavors, which persist on the fine-grained, not terribly tannic finish. Drink now through 2012. 2,599 cases made.", 75.0m, "2002", new Guid("2ffa25a0-ddef-497a-ac3e-1ce81acc5322") },
                    { new Guid("4fecdf6f-c245-41fe-a9ad-0bc6582f808b"), 3, new DateTime(1996, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yamhill County The Beaux Frères Vineyard", "A bright, delicious wine packed with blackberry, black cherry, anise and smoke flavors that are just barely held back by the firm tannins. Needs to soften, and promises to be a real winner. Best after 1997. 1,000 cases made.", 40.0m, "1994", new Guid("2ffa25a0-ddef-497a-ac3e-1ce81acc5322") },
                    { new Guid("ac4c6f26-e8b3-4890-bbd0-c850ef42f4ba"), 3, new DateTime(1994, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley", "Elegant and refined, with ripe, smooth and silky flavors that echo cherry, raspberry and spice and finish with toasty, buttery oak. Not quite as concentrated as the wonderful '91, but worthy nonetheless. Perhaps with more bottle time it will show more complexities. Drink now. 1,000 cases made.", 34.0m, "1992", new Guid("2ffa25a0-ddef-497a-ac3e-1ce81acc5322") },
                    { new Guid("eb6322a9-5ffe-42e8-9b6b-70d44e05eefd"), 3, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Central Coast", "Smooth, rich and polished, with a complex core of herb-laced black cherry, currant, plum and raspberry flavors that soar on the finish, this California red offers an attractive combination of high quality and reasonable price, should be fairly available, too. Best from 2000 through 2004. 14,000 cases made.", 16.0m, "1997", new Guid("cc9798c6-4799-44c6-a1f9-cf0b9ec46716") },
                    { new Guid("abb31c0e-b34c-4878-a413-20b2ea0759e5"), 3, new DateTime(1994, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Santa Cruz Mountains Estate Reserve", "Bold, ripe and complex; tiers of spicy cherry, anise, tar and cola flavors pick up a nice earthy, mushroomy note. Smooth, supple texture going long into the finish. Drinkable now. 300 cases made.", 30.0m, "1990", new Guid("cc9798c6-4799-44c6-a1f9-cf0b9ec46716") },
                    { new Guid("1421ce89-b98b-49bc-97b4-a2250266d264"), 3, new DateTime(2009, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Non Confunditur", "Offers an interesting combination of currant, raspberry and meat on the nose. Full-bodied, with chewy tannins and a spicy dried fruit aftertaste. Cabernet Sauvignon, Merlot, Sangiovese and Syrah. Best after 2011. 9,200 cases made.", 25.0m, "2007", new Guid("07b47487-9ff9-4118-a927-e4a613b46957") },
                    { new Guid("21887648-6951-4295-b0c9-8730bbcb301d"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Richard Sauret Vineyard", "Intense and rich in flavor, with smoky, toasty oak wrapped snugly around a core of plum and blackberry fruit, turning complex as they fold together on the finish, ending with a chocolate espresso bean aftertaste. Drink now through 2009. 2,858 cases made.", 19.0m, "2002", new Guid("d85af72e-373f-48bb-a3c8-86da70d378dd") },
                    { new Guid("a6b442d9-5e66-4e36-b9d4-853ab7f19aad"), 3, new DateTime(1994, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Moulin-à-Vent Aged in Oak", "Here's a winner. Bottle age has given this wine rich, exotic mature flavors of tobacco, cedar, plums and prunes; it's big but silky, firm but well-integrated, with a core of tannins that support but never intrude. Though it lacks the grapey freshness characteristic of young Beaujolais, it has only grown over time. Drink now.  ", 11.0m, "1991", new Guid("7de5673a-b9e6-49e1-b784-447b70b38320") },
                    { new Guid("240a5f86-64a3-4824-9f23-68d9f4d40ac8"), 3, new DateTime(2005, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Rockpile Rockpile Road Vineyard", "Ripe, delicious and exuberant; rich with layers of wild berry, blackberry and raspberry shaded by light, toasty oak. Finishes with a fantastic burst of fruit and ripe, integrated tannins. Drink now through 2009. 4,973 cases made.", 29.0m, "2003", new Guid("d85af72e-373f-48bb-a3c8-86da70d378dd") },
                    { new Guid("b6890fef-de36-4874-96a7-972177114600"), 3, new DateTime(1996, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay Premier Cru", "Great fruit combines with intriguing, spicy accents for a full-flavored, complex, interesting Burgundy sporting lingering aftertaste. Elegant but concentrated, firmly tannic but not harsh. Tempting now, but best in 1999. 400 cases made.", 30.0m, "1993", new Guid("fc9ad710-4ea3-470c-ae36-0e4e3e83e25b") },
                    { new Guid("cbbbdf42-55ee-411d-9d8c-7f886f7c2ef3"), 6, new DateTime(2003, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chassagne-Montrachet Morgeot Vieilles Vignes", "Fat and rich, with a botrytislike dried apricot, dried peach, fig and honey character. Has the opulence of a grand cru. Silky. Full-bodied and harmonious, but not as long as it could be on the finish.  Drink now through 2010. 700 cases made.", 49.0m, "2001", new Guid("940f8d82-3c80-4cc6-bdc6-5e09724f5510") },
                    { new Guid("18dbbd51-43e0-48f7-b836-1744faac7ff7"), 3, new DateTime(1997, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay Clos des Chênes", "Great effort in this appellation. A delicious red that fills the mouth with rich black cherry flavors and spicy notes. The richness and concentration last through the lengthy finish. Give it time to integrate. Best after 1999. 275 cases made.", 35.0m, "1995", new Guid("940f8d82-3c80-4cc6-bdc6-5e09724f5510") },
                    { new Guid("48799180-097a-4446-8ae7-941f37347c5c"), 6, new DateTime(2008, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Clare Valley Magnus", "Impressive for its harmony and seamless structure, offering a joyous mouthful of lemon curd, quince and lime on a steely frame. The flavors just don't quit. Drink now through 2015. 2,500 cases imported.", 12.0m, "2007", new Guid("51d06f98-e7b9-47e9-a41e-302faf3d7525") },
                    { new Guid("87e5adbc-adc5-4791-9451-ab2608d13dc6"), 3, new DateTime(2001, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Clare Valley Bin 61", "This rich, complex red drips with juicy blackberry, cherry and exotic spice flavors, weaving in hints of game and pepper as everything lingers on the long finish. Tannins are soft, making the wine seductive. Drink now through 2010. 15,000 cases made.", 15.0m, "1998", new Guid("51d06f98-e7b9-47e9-a41e-302faf3d7525") },
                    { new Guid("07d112a9-5b4b-4d1e-9630-6e5d2dfffad8"), 3, new DateTime(1999, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Clare Valley Classic Clare", "This firm, beautifully framed, elegantly styled Shiraz fleshes out with berry, plum, white pepper and licorice flavors that last and last on the focused finish. Has style and the ability to age. Drink now through 2011. 5,000 cases made.", 29.0m, "1996", new Guid("51d06f98-e7b9-47e9-a41e-302faf3d7525") },
                    { new Guid("1065b4f2-d938-451d-bc0b-f169dff01feb"), 3, new DateTime(1997, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Clare Valley Domaine", "Elegantly built, remarkable for its rich flavor profile, with cascades of lovely cherry, blackberry, raspberry and spice flavors that swirl together smoothly on the taut finish. Delicious. Tempting now. Best after 1999. 5,000 cases imported.", 13.0m, "1995", new Guid("51d06f98-e7b9-47e9-a41e-302faf3d7525") },
                    { new Guid("be2ba88f-cd6c-4936-a18e-2caa82af40a1"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Georges de Latour Private Reserve", "An extraordinary wine, dense, rich and earthy, with broad, plush tannins and tiers of complex currant, earth, cedar, black cherry, mineral and anise flavors. Keeps a tight focus on the long, rich, detailed aftertaste. Best from 2002 through 2010. 17,000 cases made.", 100.0m, "1997", new Guid("5dc33faf-6068-4414-998e-5839ea0043ea") },
                    { new Guid("5cf39cb7-2095-4d35-a80c-9af15db626d1"), 3, new DateTime(1999, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tapestry Reserve Napa Valley", "Tight in structure while plush in texture, offering rich plum, dark chocolate, cinnamon, spice and earth flavors that linger deliciously on the long, complex finish--this is one seductive California red. A blend of Cabernet Sauvignon, Merlot, Cabernet Franc and Petit Verdot. Drink now through 2005. 14,500 cases made.", 35.0m, "1996", new Guid("5dc33faf-6068-4414-998e-5839ea0043ea") },
                    { new Guid("b1e0621c-2fe7-4bfd-925f-d235ca4679e2"), 3, new DateTime(1997, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Rutherford", "A very impressive BV Rutherford bottling, this California red is deeply concentrated, with a rich core of plum, currant and cherry flavors. It shows remarkable finesse and finishes with a long, full aftertaste. Approachable now, will soften nicely in a few years. 85,000 cases made.", 15.0m, "1994", new Guid("5dc33faf-6068-4414-998e-5839ea0043ea") },
                    { new Guid("9c44917f-f0a9-4514-aa4d-4dabb4cb8f49"), 6, new DateTime(1997, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Pfalz Ungsteiner Bettelhaus", "A real standout for this vintage, this shows lovely apricot and honey aromas and flavors, with a hint of spice and pie crust. Medium-bodied, medium-sweet, with a long, flavorful finish. Drink now. 233 cases made.", 11.0m, "1996", new Guid("6240557f-b884-49a4-b695-35fbd9721785") },
                    { new Guid("e08db4d8-bd56-493b-a323-798b9e6c8b6f"), 3, new DateTime(2005, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gevrey-Chambertin Vieilles Vignes", "A pure, elegant Gevrey. Medium-bodied and bursting with fresh cherry, berry and earth notes. Concentrated, with a sense of refinement. Excellent length, with fine tannins. Best from 2007 through 2018. 400 cases imported.", 47.0m, "2002", new Guid("940f8d82-3c80-4cc6-bdc6-5e09724f5510") },
                    { new Guid("85f97794-d1bf-46c0-ae61-6cd30cfa4be0"), 6, new DateTime(1997, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Schlossberg Cuvée Ste.-Cathérine", "Broad and rich, this Alsace Riesling serves up a generous ripe apple, honey and peach character on a racy, powerful structure. It's intense and deep, with a long, apple-flavored finish. Would make a nice complement to food, just needs until 1999. 360 cases made.", 61.0m, "1996", new Guid("837ef7fc-2d7f-4be5-840f-2b691dc2fe25") },
                    { new Guid("37540af6-e035-40cc-8d8d-97ea639b8019"), 3, new DateTime(2010, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinotage Simonsberg-Stellenbosch", "Dark and alluring, with mulled black currant, Turkish coffee, bittersweet cocoa and licorice snap notes framed by solid structure. Shows roasted notes, but stays polished and focused on the grippy finish. Drink now through 2012. 750 cases imported.", 35.0m, "2008", new Guid("913b48d3-f365-47f1-906e-d2abd4abac00") },
                    { new Guid("5e99ef56-a97f-4fc2-ab6a-35e7e527035d"), 3, new DateTime(2004, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Paul Sauer Simonsberg-Stellenbosch", "Great character in this throwback wine, with a thicket of dark currant, blackberry, tar, beef, chestnut, mineral and sanguine flavors. Long finish is etched with bittersweet chocolate and another layer of really ripe blackberry fruit. Impressive. Cabernet Sauvignon, Cabernet Franc and Merlot. Drink now through 2008. 3,080 cases made.", 35.0m, "2000", new Guid("913b48d3-f365-47f1-906e-d2abd4abac00") },
                    { new Guid("bafae08b-2886-4000-ae0f-da659dcb1e74"), 3, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinotage Stellenbosch", "Here's a rich and remarkable South African red, impressively structured, with plenty of jammy flavors in the midpalate framed by chocolate, leather and smoke notes. Has good acidity as well. Drink now through 1999. 14,500 cases made.", 20.0m, "1995", new Guid("913b48d3-f365-47f1-906e-d2abd4abac00") },
                    { new Guid("68d81722-93c4-4ef3-a992-e2ca47893cff"), 6, new DateTime(1999, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Coast Russian River Ranches", "Tight and crisp, with a flinty edge to the narrow band of pear, spice and apricot, slowly unfolding to reveal more nuance and complexity. Best from 2000 through 2004. 75,000 cases made.", 17.0m, "1997", new Guid("a80a4d05-b0bb-401f-98e3-26192f3e73f3") },
                    { new Guid("94dc9e22-c4c4-4df9-9514-a6cd9a111243"), 6, new DateTime(1997, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Coast Cutrer Vineyard", "Overflowing with complex fig, pear, melon and butterscotch notes, smooth, ripe, rich and creamy. Reveals yet more depth and richness on the finish, while the flavors linger. Looks like Sonoma-Cutrer is back on track. 10,000 cases made.", 25.0m, "1994", new Guid("a80a4d05-b0bb-401f-98e3-26192f3e73f3") },
                    { new Guid("d509beac-9085-4310-81b1-1f7170bcc14e"), 3, new DateTime(2010, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Horse Heaven Hills H3", "Dark, supple and generous with its blackberry and black currant flavors, playing against superfine tannins on an open-textured finish. Floral, savory overtones add depth. Best from 2011 through 2017. 30,000 cases made.", 15.0m, "2007", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("a21471bc-8738-41e6-b2f0-f815b0235825"), 3, new DateTime(2005, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Walter Clore Private Reserve Columbia Valley", "Polished, lithe and seductive, its ripe currant and blueberry fruit shaded nicely with peppery, creamy oak notes that still let the fruit persist beautifully through the long, silky finish. Cabernet Sauvignon and Merlot. Drink now through 2012. 5,250 cases made.", 35.0m, "2002", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("66834bba-2995-4395-9755-a40a9cc6f46b"), 3, new DateTime(2004, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Columbia Valley Grand Estates", "Ripe, plump and generous with its fresh cherry, dried cherry, dark plum and sweet spice flavors, nicely framed with toasty oak as the fruit persists impressively on the long finish. Drink now through 2009. 310,000 cases made.", 11.0m, "2001", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("1ab4afe9-b31b-4d27-9ae4-6f1ed63ddfba"), 3, new DateTime(2004, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley Reserve", "Ripe, polished, opulent style teems with spicy black cherry, raspberry, black olive, bay leaf and sweet spice flavors, which echo against the superfine tannins on the long, generous finish. Drink now through 2013. 4,000 cases made.", 30.0m, "2001", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("7c57e544-8c90-4752-a120-0c93c6e4bb9e"), 3, new DateTime(2002, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Columbia Valley Reserve", "Plump and generous, dark-colored and amazingly supple, offering pretty plum, blackberry and sweet spice flavors that linger on the polished finish. Drink now through 2005. 3,000 cases made.", 28.0m, "2000", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("0564f51f-40e2-4568-b3a5-45b7c05a3629"), 5, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Millésimé", "Complex and mature in style, this is a full-bodied but appealingly austere-tasting Champagne that expands on the finish. The perfect bubbly for those who value reserve and maturity. 23,000 cases made.", 40.0m, "1989", new Guid("d2cf44b3-5bca-4479-8ea8-82cc4b8f8163") },
                    { new Guid("166024eb-7321-4b12-ada6-dc9d43f4e93d"), 6, new DateTime(2001, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley Grand Estates", "Rich and vibrant, beautifully focused to show off its plush honey, spice and smoke-accented pear and apricot flavors. Finish is seductively polished and as smooth as a Jean Arp sculpture. Drink now through 2005. 120,000 cases made.", 11.0m, "1999", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("d0ef3590-225a-485d-8555-0bdc38ea2fe1"), 3, new DateTime(2011, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Moulin-à-Vent Domaine de La Tour du Bief Clos de la Tour", "Lush fruit flavors of blackberry and blueberry mix with a core of spice and dried herb notes in this fresh and silky red, which has lightly chewy tannins, followed by lingering hints of dark chocolate and cardamom on the finish. Drink now through 2016. 1,000 cases imported.", 22.0m, "2009", new Guid("940f8d82-3c80-4cc6-bdc6-5e09724f5510") },
                    { new Guid("5873affd-47e7-4a2e-9bc7-1ce25ee90cb5"), 6, new DateTime(1997, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Bourgogne Côtes d'Auxerre White Domaine du Corps de Garde", "Stupendous. Silky in texture, full in body, with polished flavors, long finish, it's a seamless marriage of harmony and power. The spice, oak and fruit flavors show the smooth yet intense complexity of a <i>premier cru</i> Meursault. From one of Burgundy's rising stars in the field of white wine making. Drink now through 2002. 1,500 cases made.", 15.0m, "1995", new Guid("517c0abd-c9ee-4656-96c5-d54f5edcc1af") },
                    { new Guid("d1aa8d51-e1da-4269-a01f-c2a23da93038"), 3, new DateTime(1998, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat L'Ermita", "Such a powerful mouthful of wine that after swallowing, it's almost hard to talk. Explosively aromatic, with jam, tar, licorice and exotic spices, it leads with concentrated fruit, then follows with a sledgehammer of polished tannins that somehow dissolve on the finish, where the fruit reemerges like a rainbow. With a slab of well-aged, chargrilled beef--paradise. Drink now through 2005. 450 cases made.", 135.0m, "1995", new Guid("91e8215a-2f4b-4acf-877c-563a40c103b2") },
                    { new Guid("519e3f2a-136d-4e00-8faa-3624ab22d220"), 6, new DateTime(1998, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Vorbourg Clos St.-Landelin", "Melts in your mouth despite the laserlike acidity. Passion fruit, mineral and floral aromas and flavors give way to mouthwatering acidity and a finish on which the flavors have a featherlike touch. Best from 2000 through 2006. 650 cases made.", 22.0m, "1996", new Guid("ea0802de-c3a2-4f12-bb97-86a164a3ab56") },
                    { new Guid("636a8ac7-81b0-4794-ac0d-4d64cc54e933"), 3, new DateTime(2013, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Carneros Hyde Vineyard", "Combines deliciously ripe dark berry fruit with a graceful texture and deft balance, featuring juicy wild berry and blackberry at the core. Floral, spice and anise touches linger on the finish. Drink now through 2022. 404 cases made.", 65.0m, "2010", new Guid("992c262e-88b3-4c3a-90b5-a34697d7d02f") },
                    { new Guid("7634b878-1a06-463f-9982-5200ff25196d"), 5, new DateTime(1998, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Blanc de Blancs Champagne", "Just gorgeous. Mouthfilling and wonderfully flavorful but not overpowering, it blends abundant ripe fruit flavors with a subtle mousse and weaves great complexity with an elegant restraint. Like a classic Meursault that sparkles. Drink now through 2003. 2,500 cases made.", 50.0m, "1990", new Guid("803da87d-a883-4109-ace2-ae719bbc2b89") },
                    { new Guid("9266fb9e-b489-4cb0-b50a-1e50b3fea693"), 6, new DateTime(2005, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Mont de Milieu", "Compact, yet dense and powerful, exhibiting honey, mineral and lemon aromas and flavors, all balanced with a vibrant structure and supple texture. Fine length, but needs time. Should age well over the next decade. Best from 2007 through 2015. 3,000 cases made.", 32.0m, "2002", new Guid("bc690bd9-16f3-4989-97f7-c039a8d7c139") },
                    { new Guid("af915232-2cf0-4a7a-96e4-507b4c5d6d95"), 6, new DateTime(2004, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Grande Cuvée", "Very refined, with a lemony cast to this firmly structured, minerally white. It's concentrated, elegant and beautifully balanced. It requires time, however, to integrate all its components. Best from 2006 through 2012. 7,500 cases made.", 28.0m, "2002", new Guid("bc690bd9-16f3-4989-97f7-c039a8d7c139") },
                    { new Guid("30851c7b-b30d-4c57-879e-93c88703cab0"), 6, new DateTime(2001, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Premier Cru", "Amazing. This is a beautiful Chablis, tightly structured and very refined, expanding its mineral base to encompass some vanilla, ripe fruit, honey and slightly spicy oak. Medium-bodied and very long on the finish, it just keeps riding into the sunset. Best from 2003 through 2010. 16,000 cases made.", 24.0m, "1999", new Guid("bc690bd9-16f3-4989-97f7-c039a8d7c139") },
                    { new Guid("5e7bc0f0-b0dc-4c53-9a8f-249848716185"), 6, new DateTime(1998, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Fourchaume", "A beauty. Ultrarich but also very elegant, thanks to loads of fresh, citrusy acidity. Balanced and full-bodied, with marvelous pear, honey, mineral, spice and tropical character that you expect from '96. Tempting now. Best after 2000. 10,000 cases made.", 27.0m, "1996", new Guid("bc690bd9-16f3-4989-97f7-c039a8d7c139") },
                    { new Guid("51edb013-6547-40ae-aad5-1a5ebe169e1e"), 2, new DateTime(1997, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tokaji Aszú 5 Puttonyos", "Modern-style Tokay. A laser-sharp, pure and clean sweetie, showing great elegance and refinement. Its superb citrusy, crisp structure counterbalances the honey, dried fruit and pineapple character that oozes from start to finish in this full-bodied beauty. Long, spicy and smoky finish makes it tempting now. Drink now through 2010. 10,000 cases made.", 32.0m, "1993", new Guid("872cf7ab-d6ec-4d3f-8774-fd4fd51d03ea") },
                    { new Guid("e8a2e0d5-baa2-48d9-a878-aa7d5fc43c20"), 2, new DateTime(1998, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Late Harvest California", "Smooth, silky, supple-textured and sweet, this dessert white overflows with lovely honey-scented pear and peach flavors that linger delicately on the finish. It's delicious now, but will deepen and enrichen further by 2000, so try to hold off until then. 1,000 cases made.", 55.0m, "1994", new Guid("0e26afa2-7c42-45d9-9c94-cf308d553cba") },
                    { new Guid("271f341b-4751-41d1-b7e4-bec964faa8f3"), 3, new DateTime(2019, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Heritage Vines", "Briary and vibrant, with zesty raspberry, toasted sage and cracked pepper flavors that take on richness toward polished tannins. Drink now through 2025. 15,000 cases made.", 26.0m, "2017", new Guid("578761c0-eb36-4b5d-a7d4-ddcbf8195293") },
                    { new Guid("d48ff231-8a74-461e-811a-062e4efa79b6"), 6, new DateTime(2015, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Ste.-Claire", "A textbook flinty aroma gives way to green apple, lemon and mineral flavors in this white, which shows fine depth and richness, offset by vibrant acidity. Excellent length. Drink now through 2020. 50,000 cases made.", 22.0m, "2014", new Guid("d895fafb-b33b-4338-9955-083a227265b5") },
                    { new Guid("4e2c497e-8be4-42a8-92c9-5c98a31086f8"), 3, new DateTime(1998, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cornas", "Impressively complex, mingling wonderful fruit and spice accents with black pepper, tar, cassis and olive. Dark and brooding, this full-bodied Northern Rhône red is amazingly ripe, sweet-tasting, seductive--just plain delicious. So balanced, it's tempting now, but it will keep for years. Drink now through 2015. 1,330 cases made.", 35.0m, "1996", new Guid("bcaf32c2-978a-429c-8eb5-c6105a887676") },
                    { new Guid("06265acf-9108-4140-949b-a65ba69905cb"), 2, new DateTime(1997, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tokaji Esszencia", "One of the world's finest dessert wines. Sensational, ultrasweet Tokay, yet with a lovely citrus underpinning that keeps it from being heavy. Like melted honey, with lime and pineapple, this is one of those marvelous gems that makes you swoon in delight. Tempting now into the 21st century. A collector's item to drink in small portions, by itself, to celebrate life. 165 cases made.", 400.0m, "1993", new Guid("911920ed-6490-49f0-a7dd-d7d5e7473d8a") },
                    { new Guid("aa9d763b-2620-4e3b-964c-f7d251cc3698"), 2, new DateTime(1998, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Trockenbeerenauslese Neusiedlersee Nouvelle Vague No. 13", "A white chocolate dream. Incredibly concentrated and viscous, lively and complex at the same time. The white chocolate is augmented by lemon peel, vanilla custard and apricot flavors that pulse through the finish. Best after 2000. 133 cases made.", 100.0m, "1995", new Guid("b4821d36-90af-488f-90c8-4f8d7d923508") },
                    { new Guid("11d41d9d-264e-48fb-bafd-f1a55a39ecc1"), 6, new DateTime(2017, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Qualitätswein Trocken Rheingau Kiedricher Gräfenberg GG", "Very rich and complex, with enveloping flavors of baked apple, ripe citrus, nectarine and dried pear supported by a powerful structure. Lush midpalate, with a notes of dried guava, followed by unctuous spiciness on the finish, with accents of white chocolate, chamomile and dried herbs. Best from 2018 through 2026. 2,500 cases made.", 92.0m, "2015", new Guid("c46dd297-7c7a-4d64-a1f5-a8375727091c") },
                    { new Guid("7b7a2714-4b57-4b8a-837e-12e56643af35"), 6, new DateTime(2010, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Qualitätswein Trocken Rheingau Kiedrich Turmberg", "A pungent, aromatic white, with gooseberry, peony and spice on the nose. Racy and vividly flavored, with citrus intensity and a laserlike finish. A bit extreme, yet full of personality and a sense of place. Best from 2013 through 2022. 1,700 cases imported.", 48.0m, "2009", new Guid("c46dd297-7c7a-4d64-a1f5-a8375727091c") },
                    { new Guid("cd279149-6cf0-4f52-9d15-4918f016488a"), 6, new DateTime(1997, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Rheingau", "Plenty of apple, spice and mineral character in this wine. Medium bodied, medium sweet with a long flavorful finish. Tastes more like a Spätlese. Drink now. 2,000 cases made.", 19.0m, "1996", new Guid("c46dd297-7c7a-4d64-a1f5-a8375727091c") },
                    { new Guid("5c54e69b-c20b-4f16-a500-046c0842a907"), 5, new DateTime(1997, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Antique", "Definitely something special. This Champagne has riper fruit, a richer texture and longer finish than most non-vintage bottlings, with a soft, lush mouthfeel and subtle, seductive flavors. Delicious, and reasonably priced at this score. 12,000 cases made.", 30.0m, "NV", new Guid("53826f92-fdaf-4c89-b0a2-5131ba1cc2da") },
                    { new Guid("7f6ad274-eb58-44b1-912e-a088e7734777"), 3, new DateTime(2004, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Madiran Château Montus", "Full-bodied, with loads of dark plum, blackberry, dark cherry and spice flavors. This is a finely sculpted red that packs plenty of punch as well. Espresso and dark chocolate notes linger on the finish. Tempting now. Best from 2005 through 2009. 20,950 cases made.", 24.0m, "2001", new Guid("84fa2166-6472-4b3b-88e1-29b8c1bbab82") },
                    { new Guid("219d1d4a-e715-4743-ad95-b1481e1815bb"), 3, new DateTime(1997, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Madiran Château Bouscassé Vieilles Vignes", "Gorgeous aromas of roasted coffee and vanilla yield to flavors of cassis, chocolate and tobacco in this massive, backward wine. Its wall of tannin needs to be resolved, but the concentration and sweet fruit on the finish suggest it will come together in time. Try in 2001. 6,500 cases made.", 25.0m, "1995", new Guid("84fa2166-6472-4b3b-88e1-29b8c1bbab82") },
                    { new Guid("7b04cfc4-51d2-4091-bac7-0af3b057f7b9"), 3, new DateTime(1997, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Crianza", "Very young and quite closed now, this rich Spanish red nonetheless promises a bright future. It shows concentration and balance, and offers an intriguing range of coffee, plum, licorice and tobacco flavors. Oaky notes are dominant, but the wine should improve with time. Best after 1999. 30,000 cases made.", 17.0m, "1995", new Guid("00d16ba5-0e0f-48b3-97a9-faa82d4a9cfb") },
                    { new Guid("275d0b5d-c129-4003-9d2c-cdc437ee2d31"), 6, new DateTime(1998, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Beauregard", "Fantastic. Deep and complex, subtle and reserved on the nose, with a classy, creamy, silky texture on the palate. Lots of fruit and soil substance stimulate the senses and alert you that this is a serious Chardonnay. Long, subtle, oil-like finish. Tempting now, but will improve. Drink now through 2007. 2,500 cases made.", 25.0m, "1996", new Guid("d895fafb-b33b-4338-9955-083a227265b5") },
                    { new Guid("1af1c349-9fe9-4208-89c4-c10c72ced473"), 3, new DateTime(2000, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Columbia Valley Estate Vineyards", "Beautifully proportioned to balance the fruit and oak, generous without being weighty, this Washington red serves up a lush mouthful of ripe berry and spice flavors, dripping with chocolate and sage notes on the polished finish. Drink now through 2007. 20,000 cases made.", 16.0m, "1997", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("38d247bc-ae3e-41da-85e6-1432fe03c1f7"), 3, new DateTime(2000, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley Estate Series", "Round and cedary, this Washington red deliversdepth, length and distinctive flavor. Ripe, plushand generous with its exotic, seductive aromas andflavors, layering rich plum, currant and clove ona bed of fine-grained tannins. Great price/scoreratio. Drink now through 2011. 15,000 cases made.", 16.0m, "1996", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("4a1556be-24b0-4ac3-8c65-281feac19911"), 6, new DateTime(1997, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley Estate Series", "An elegant wine, silky smooth and nicely layered with nutmeg, nectarine and apple flavors that linger enticingly on the beautifully balanced finish. This reasonably priced Washington Chardonnay is delicious now, will be best from 1998. 20,000 cases made.", 14.0m, "1995", new Guid("d64e297f-2c13-465a-9b75-869c0de03b23") },
                    { new Guid("d0c92663-47b0-4cdf-bb52-67fa0a6d2775"), 3, new DateTime(2003, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos Apalta Rapel Valley", "A stunner. This coats the palate with cassis, blackberry and boysenberry fruit, but stays focused and pure, with vanilla, spice and toast notes chiming in on the palate for added effect. The finish then resonates with purity. A thoroughly seductive wine that glides effortlessly, despite its obvious power and depth. Carmenère, Merlot and Cabernet Sauvignon. Drink now through 2009. 3,000 cases made.", 55.0m, "2000", new Guid("dc8bed2d-dabb-4a1f-8666-74625602322a") },
                    { new Guid("fdc11727-a271-4626-aeab-1d4995e578a8"), 3, new DateTime(1996, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Rapel Valley Cuvée Alexandre", "Rich and well-defined. Powerful aromas of plum and toast give way to round, lush tannins and ripe fruit, as chocolate and sweet fruit flavors linger on the finish. Drink now through 1997. 3,000 cases made.", 15.0m, "1994", new Guid("dc8bed2d-dabb-4a1f-8666-74625602322a") },
                    { new Guid("76091671-f682-4782-a567-4ace82ab3284"), 6, new DateTime(1996, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Monterey County", "A bold, ripe and full-bodied white from California that offers lots of rich pear, spice, honey flavors, all presented with a light shading of hazelnut. This has a sense of elegance and grace that goes on through the finish. 22,000 cases made.", 15.0m, "1994", new Guid("37a76cc1-1a3f-4cd8-9626-a6aa4bde2cd3") },
                    { new Guid("bc18eaf3-7c1d-42b8-b51a-fb7a73585e4d"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Barossa", "Smooth, polished and positively glowing with fruit and spice flavors, bursting across the palate with gorgeous blackberry, black cherry, anise and cinnamon flavors, swirling in some sweet vanilla and pepper on the long finish. Best from 2002 through 2020. 12,000 cases imported.", 18.0m, "1998", new Guid("304ef0ef-5a6c-4fdb-927e-5b116eb16972") },
                    { new Guid("813d1cf1-e40f-455e-a76c-e9e5506c2e74"), 3, new DateTime(1999, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clancy's Barossa", "A big red, ripe, rich and round, with lots of spicy, earth-scented black cherry and berry flavors, hinting deliciously at chocolate on the smooth finish. This distinctive Shiraz blend is drinkable now, yet has the depth to repay cellaring through 2007. 8,000 cases made.", 20.0m, "1997", new Guid("304ef0ef-5a6c-4fdb-927e-5b116eb16972") },
                    { new Guid("f4f4dc19-6513-49eb-8896-e597d1185231"), 3, new DateTime(1997, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clancy's Gold Preference Barossa Valley", "Rich, spicy and complex, disarmingly seductive with plum, black cherry and exotic spices mingling on the plush finish. Has style and power, but finishes with welcome harmony. Absolutely stunning now, but should keep developing. Shiraz, Cabernet Sauvignon, Merlot and Cabernet Franc. Best from 2000 through 2005. 5,000 cases made.", 18.0m, "1994", new Guid("304ef0ef-5a6c-4fdb-927e-5b116eb16972") },
                    { new Guid("c2d4940e-7b43-42c8-bcb4-ac1470869531"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Barossa Valley", "Ripe and generous, boasting a subtle, spicy edge to the blackberry and currant flavors packed between its layers of tannin. Shows enough promise to make it worth waiting until 2002 to 2005. 10,000 cases made.", 14.0m, "1993", new Guid("304ef0ef-5a6c-4fdb-927e-5b116eb16972") },
                    { new Guid("676d3104-a029-4dc2-af76-3c26e481e995"), 5, new DateTime(1996, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Early Muscat Oregon Semi-Sparkling", "Light, sweet and utterly beguiling for its delicately spicy litchi and pear flavors that dance like ballerinas on the finish. Just delicious to drink fresh. 1,200 cases made.", 12.0m, "1994", new Guid("a7b4300c-5857-4dc1-926a-ac57a77f424b") },
                    { new Guid("ae2b1fd5-22fa-4379-83ff-908e67f2b89d"), 6, new DateTime(1996, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Monterey Riverstone", "A complex array of elegant pear, spice, honey and hazelnut notes, leading up to a nicely, smoky, toasted oak finish. Combines richness with depth, concentration and finesse. Has the flavor and complexity of wines two to three times its price. 86,000 cases made.", 12.0m, "1994", new Guid("63798649-c3a7-4600-aec5-1c66478678be") },
                    { new Guid("853a721d-0d2e-4155-ad92-152851ca7b11"), 3, new DateTime(1996, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Migration Red Napa Valley", "Ripe, well-focused, spicy black cherry, herb and cedar flavors are bright and lively. Well proportioned and moderately tannic, it's approachable now but better in 1997 after softening. Deliciously complex and terrific at this price. 4,900 cases made.", 12.0m, "1993", new Guid("7ba8bcf6-eefc-4207-8822-52f2c3223c73") },
                    { new Guid("79e8a078-6f24-42f8-b05f-df97b6c0e9ba"), 3, new DateTime(2004, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Clos Apalta Colchagua Valley", "Stunning purity, with concentrated plum, boysenberry and raspberry fruit that just glides across the palate, buttressed by superfine yet muscular tannins. Suave toast, coconut butter and spice notes along with alluring chocolate and black tea hints chime in on the long finish. A superb combination of fruit and elegance. Drink now through 2009. 4,885 cases made.", 55.0m, "2001", new Guid("dc8bed2d-dabb-4a1f-8666-74625602322a") },
                    { new Guid("d2592bb8-a5b1-418a-bb2b-205cab144f07"), 6, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Victoria", "Very fresh and spicy, packed with nectarine and vanilla flavors that linger enticingly on the finish. Big and refreshing at the same time. Drink now.  ", 10.0m, "1995", new Guid("b6a78fba-9443-4a38-b9c5-08226d8ee04b") },
                    { new Guid("64efb6cc-4ea0-4496-9fb9-e47105d43db4"), 6, new DateTime(1996, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Les Fourneaux", "Great winemaking: mellow and lush, packed with flavors ranging from citrus to honey, mineral to cream. Comes together beautifully on a silky yet long and refreshing finish. Drinkable now through 2000. 580 cases made.", 22.0m, "1994", new Guid("e8883f80-5792-4ba2-8499-ee26d4e2cb2c") },
                    { new Guid("173855f9-8c26-43ec-bea2-5cacd61cb00b"), 6, new DateTime(1996, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mâcon-Chaintré Vieilles Vignes", "A work of art. This blends its crisp acidity nicely into creamy, butterscotch flavors. Fat yet restrained, seductive and full-bodied. Extremely long finish. Delicious now, but even better in 1999. 1,200 cases imported.", 15.0m, "1994", new Guid("92d49212-af1b-4a95-8955-fb8b0430705c") },
                    { new Guid("09f94803-6c36-46c8-ad4b-0723bb50a38a"), 6, new DateTime(1996, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Victoria Reserve", "Supple, generous and beautifully proportioned, this is a sleek wine with lovely buttery, spicy pear and apple flavors that extend into a long and glorious finish. Has depth and harmony. Drink now. 5,000 cases made.", 16.0m, "1994", new Guid("eccd79d3-2a19-4ecd-bcf8-0810096debc3") },
                    { new Guid("714e1918-226d-4823-8918-f5a10b931e32"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Australia Personal Reserve", "Packs a lot of beautiful currant and blackberry between the layers of anise and vanilla, wrapped in velvety tannins, harmonious and elegantly proportioned. Tempting now. Best from 2000 through 2002. 1,000 cases made.", 15.0m, "1993", new Guid("5e6272ca-b9aa-4bde-934e-3051a17020e9") },
                    { new Guid("bb70f499-35f0-4b98-a295-4ffa2ea75a65"), 6, new DateTime(1996, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Valley Carneros Sangiacomo Vineyard", "A touch earthy, but has enough ripe pear, citrus, honey and light oak shadings to balance it out. Turns complex on the finish, where the flavors expand and intensify. Serves up a lot of intriguing flavors. 7,106 cases made.", 16.0m, "1993", new Guid("02e3f9ad-e6e2-4c21-aea2-489f4647521c") },
                    { new Guid("1cd7238f-e3e5-4e0b-9901-2c20bc81f876"), 3, new DateTime(2009, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Sonoma Valley Steel Plow", "Shows loads of pepper, beef, dark berry, teriyaki, mineral and spice. Intense, distinctive, full-bodied and focused, this is so complex and well-proportioned that all the flavors work together seamlessly. Drink now through 2015. 1,600 cases made.", 30.0m, "2006", new Guid("89d327e1-eac2-4ec0-b29e-42967f548e73") },
                    { new Guid("2ede0727-6fe3-4d6f-9d68-f2d75db6503a"), 6, new DateTime(2003, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma-Monterey Counties Overlook", "Loads of rich flavors yet with a sense of elegance and finesse, with layers of ripe, spicy, apple, pear and buttery notes, finishing with lively, well-integrated acidity and a burst of flavor on the aftertaste. Drink now through 2005. 18,000 cases made.", 25.0m, "2001", new Guid("89d327e1-eac2-4ec0-b29e-42967f548e73") },
                    { new Guid("fe940ff1-962a-46de-9738-5919ca21cb07"), 6, new DateTime(1999, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma-Santa Barbara-Monterey Counties Overlook", "Ripe, smooth, rich and creamy, with layers of pear, vanilla, anise and fig flavors that are deep, complex and concentrated. Finishes with a long, delicious aftertaste. Drink now through 2004. 15,000 cases made.", 22.0m, "1997", new Guid("89d327e1-eac2-4ec0-b29e-42967f548e73") },
                    { new Guid("05a6892f-d412-408f-b90f-dc7514411e5d"), 6, new DateTime(1996, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Overlook", "A fabulous California Chardonnay. Well oaked, rich and creamy, with a pretty core of melon, fig, honey and pear flavors that hold on through the finish. This builds on the palate, offering more complexity as it goes. 17,000 cases made.", 16.0m, "1994", new Guid("89d327e1-eac2-4ec0-b29e-42967f548e73") },
                    { new Guid("4bd31ae4-0252-4994-9be1-5139fc8ddbb6"), 3, new DateTime(2019, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay Champans", "Pure and intense, featuring cherry, black currant and violet aromas and flavors, this red is seductive and approachable despite its youth. The solid structure is seamlessly integrated and bodes well for future development. Shows terrific harmony and expression. Best from 2023 through 2042. 145 cases imported.", 150.0m, "2016", new Guid("fc9ad710-4ea3-470c-ae36-0e4e3e83e25b") },
                    { new Guid("e695b701-f119-44a5-a55b-481ee2681dfc"), 6, new DateTime(1996, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Russian River Valley", "Beautifully structured to highlight its pretty floral and grapefruit flavors and luscious undertone of apricot and mineral notes--all of which persist through the off-dry finish. Ready right now. 585 cases made.", 10.0m, "1995", new Guid("49e38b28-4fea-4d56-88e6-6391e6251bb3") },
                    { new Guid("833a0be0-fc77-44a1-b67b-07ccac30ae36"), 3, new DateTime(2012, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Colchagua Valley Cuvée Alexandre Apalta Vineyard", "This dark red balances racy wild berry and cassis flavors with full yet fine tannins, showing layers of toasty oak, smoke and baking spice. Polished, with a long, savory aftertaste of spice, cured olive and underbrush. Drink now through 2017. 10,700 cases made.", 25.0m, "2010", new Guid("dc8bed2d-dabb-4a1f-8666-74625602322a") },
                    { new Guid("cd62b747-8afd-4357-aa2a-5300d63c9aad"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Stellenbosch Private Collection", "Gorgeous, deeply flavored red sporting the exuberance of an Aussie Shiraz but the elegance of a Northern Rhône. Has enticing raspberry and violet aromas, rich, peppery tones and lingering finish. Drink now through 1999. 800 cases made.", 16.0m, "1993", new Guid("75dbf19f-23b1-4b32-b54d-ea2e62be0bf8") },
                    { new Guid("2aad55a0-f694-47ed-bc94-baf37cc888cc"), 3, new DateTime(2013, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Generations", "Shows striking intensity, density, focus and layers of flavor, with tiers of mocha, blackberry, mineral, black licorice and melted chocolate. The finish sails on, retaining focus and persistence. Drink now through 2023. 1,000 cases made.", 100.0m, "2009", new Guid("e37ecf4a-af5f-4fc0-afe3-fb6b9be62a76") },
                    { new Guid("f53a349e-61f2-44ec-87ff-54f7cacd21cd"), 3, new DateTime(1997, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Clos de L'Obac", "Showing off pure fruit and the essence of blackberries, this big red is rich, smooth and irresistible. Sure, it has tannin, acidity and alcohol--all in spades--but they are seamlessly integrated and subordinate to that ripe, sweet fruit. Much better than previously reviewed. Drink now through 2002. 1,250 cases made.", 45.0m, "1995", new Guid("ca3f08a4-23f6-43b8-9ad7-54171cc1f3a3") },
                    { new Guid("ed7804b3-a709-4071-a5e4-f7f2b58e4b27"), 3, new DateTime(1997, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Mendocino County", "A bold and juicy California red, with black cherry, plum, wild berry and raspberry flavors that are ripe, plush and concentrated and finish in a complex aftertaste. A great find at this price and score. 2,000 cases made.", 12.0m, "1994", new Guid("2998cc99-31dc-455c-813a-f83f391fc7a8") },
                    { new Guid("c58d50c2-c4ce-42f1-b48c-eea5abed741f"), 3, new DateTime(2015, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Oakville Au Paradis", "Deliciously pure and supple, with explosive dark berry and red berry flavors and a silky, polished texture. The tannins become more evident midway through, with a gravelly crushed rock profile that adds a dimension of dust and mineral. Drink now through 2030. 1,785 cases made.", 195.0m, "2012", new Guid("1377912b-6593-4f60-b850-d15285bd62ed") },
                    { new Guid("cce3a28b-f5dd-4b98-b8f2-ccf899d59462"), 6, new DateTime(2014, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Knights Valley Ma Belle-Fille", "Captivating, sleek, elegant and graceful, this is rich without weight, offering a mix of ripe pear, fig, apricot and melon flavors, with subtle touches of nutmeg, hazelnut and toasted almond. For all its flavor and depth, this displays wonderful lift on the finish. Drink now through 2020. 2,080 cases made.", 90.0m, "2012", new Guid("1377912b-6593-4f60-b850-d15285bd62ed") },
                    { new Guid("9882b6b7-2bea-4d46-9201-d89e9bade307"), 6, new DateTime(2009, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County Ma Belle-Fille", "Deliciously complex, with a rich, elegant, layered presentation of citrus-infused green apple, spice and toasty oak. Subtle honeydew melon flavors emerge on the finish. A shade lighter on its feet than Mon Plaisir. Drink now through 2013. 2,780 cases made.", 85.0m, "2007", new Guid("1377912b-6593-4f60-b850-d15285bd62ed") },
                    { new Guid("0ad2ccc0-e3aa-40a6-a273-e3623ca91f0f"), 3, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Les Pavots Knights Valley", "Wonderful complexity, depth and concentration, with layers of ripe, juicy plum, currant and black cherry flavors tinged with spice, smoke, toast and buttery oak notes. Delicious aftertaste keeps pumping out flavors. A blend: 68 percent Cabernet Sauvignon, 16 percent Merlot, 16 percent Cabernet Franc. Best from 1999 through 2005. 3,000 cases made.", 35.0m, "1994", new Guid("1377912b-6593-4f60-b850-d15285bd62ed") },
                    { new Guid("7ad9cb5d-5148-46b8-b3ab-c50796f72876"), 6, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chablis Montée de Tonnerre", "A good example of the spectacular quality of the '96 vintage in Chablis, this ripe and balanced <i>premier cru</i> delivers gorgeous mineral, lime, honey, tropical fruit, flint, stone and floral notes. Full-bodied, with smoky overtones on the long, succulent finish. Tempting now, but wait until after 2000, when the palate-coating mineral character should be fully developed. Drink now through 2004. 1,000 cases made.", 18.0m, "1996", new Guid("9bf8ad0f-642e-4796-99f1-9062f6a19ae2") },
                    { new Guid("2f7fbd68-a609-4968-a55b-b6c50f7597bd"), 3, new DateTime(2001, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Verona Palazzo della Torre", "This Veneto red is glorious and rich, yet sophisticated. Dark ruby color, it emits aromas of raisins and berries, and hints of cocoa and spice. It's full-bodied, with chewy yet velvety tannins and a long, long finish. Best after 2001. 11,000 cases made.", 18.0m, "1997", new Guid("8e25e733-6851-403c-bf2d-db7e98496255") },
                    { new Guid("5344d838-d5fa-498a-b598-f2a9df204a7f"), 3, new DateTime(2000, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gevrey-Chambertin Lavaux St.-Jacques", "Blockbuster style of red Burgundy. Inky-black in color, full in body, packed with flavors, but also delivering great finesse with its silky tannins. Bold but focused blackberry, wild raspberry, smoke, petrol, and tar complexity. A winner, with its silky texture that endures through a long, balanced finish. Best from 2003 through 2015. 405 cases made.", 81.0m, "1998", new Guid("a994fabe-be96-47ac-b6a9-3c724a771408") },
                    { new Guid("f4db687d-889b-4ae6-b328-ef928fa73111"), 3, new DateTime(1997, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gevrey-Chambertin En Motrot", "Gorgeous purity of clean, scrumptious red- and blackberry flavor shines through in this full-bodied, ripe, seductive, perfumy and racy red Burgundy. Notes of spice, mocha, chocolate, toasted and smoky oak play subtly in the background, giving depth to a complex wine. Near-perfect finish. Tempting now through 2010. 240 cases made.", 47.0m, "1995", new Guid("a994fabe-be96-47ac-b6a9-3c724a771408") },
                    { new Guid("00c09e77-bd3d-45db-8d7b-feecb8f4cc96"), 6, new DateTime(1997, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Les Combettes", "Fantastic--so ripe, so balanced, so fresh--you just can't get enough. Brilliant success here, even by the high standards of this vintage. Offers loads of sweet-tasting tropical flavors, mingled with toasted coconut, mineral and pear tart notes. Perfect, velvety texture, with a long, clean finish. Drink now through 2010. 268 cases made.", 60.0m, "1995", new Guid("3c3bbdbf-ff16-40bc-8d8d-5c3e55c9a01e") },
                    { new Guid("dd3833a6-d55e-438d-b5b8-b1cd9913a69c"), 6, new DateTime(2010, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fumé Silex", "Weighty, but lithe and balanced, with gorgeous, alluring notes of honeysuckle, macadamia nut, white asparagus, lemon curd, quinine and straw all carrying through seamlessly on the long, invigorating finish. This beautiful wine transcends the vintage. Drink now through 2012. 2,500 cases made.", 125.0m, "2007", new Guid("d25bda46-6fc2-4931-a6e4-efb788ff3f91") },
                    { new Guid("be3a9758-fb37-4475-962f-902f68fdb659"), 6, new DateTime(2006, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fumé", "Tight on the nose, but uncommonly rich, with a tightly wound core of lemon zest, white flowers, chalk and freshly baled hay. Long, riveting finish. Best from 2007 through 2011. 2,500 cases made.", 50.0m, "2004", new Guid("d25bda46-6fc2-4931-a6e4-efb788ff3f91") },
                    { new Guid("e3283196-6656-4b03-8980-9bb010cfbc77"), 6, new DateTime(1997, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fumé Pur Sang", "Unique and delicious, this Loire Sauvignon Blanc is reminiscent of a great white Bordeaux, such as Haut-Brion, thanks to the vanilla and toast flavors of barrel fermentation. With enough acidity for balance and ripe fruit flavors that give it concentration and complexity, it's head and shoulders above the rest. Drink now through 2001. 2,000 cases made.", 35.0m, "1995", new Guid("d25bda46-6fc2-4931-a6e4-efb788ff3f91") },
                    { new Guid("f432d656-f64a-4ca9-a17b-4d9f4db58fee"), 2, new DateTime(1998, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vintage Port Quinta da Cavadinha", "This young Port sneaks up on you and then--bam! Aromas of grapes, flowers and minerals pave the way for a slowly building palate that finishes with a burst of ripe, exotic fruit. Full-bodied, yet very tight and held back. Better than when previously reviewed as a barrel samples. Best after 2008. 7,000 cases made.", 33.0m, "1995", new Guid("894d441f-cd4b-4b78-b556-bb18e2ff3124") },
                    { new Guid("b37b252a-0278-46f2-84ab-d23645791665"), 6, new DateTime(2017, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Savennières", "Alluring, with quince, creamed pear, verbena and green almond flavors allied to a creamy texture, all offset beautifully through the finish by light quinine and mineral hints. Approachable, but this seems built for the cellar. Best from 2018 through 2030. 1,500 cases imported.", 30.0m, "2015", new Guid("694d1cfb-c7d8-4cd5-bf23-c248a75983a6") },
                    { new Guid("3af8b535-8d46-4200-831d-de5024b44d63"), 6, new DateTime(2004, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Savennières Clos du Papillon", "Plump, with lots of fig and quince fruit; pretty hints of spice and minerals in the background while the fruit takes center stage. Nice dry, minerally finish keeps it honest and bodes well for cellaring. Drink now through 2015. 2,330 cases made.", 27.0m, "2001", new Guid("694d1cfb-c7d8-4cd5-bf23-c248a75983a6") },
                    { new Guid("038e5d19-f304-4e1d-8c22-271fac746b7e"), 2, new DateTime(1997, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Quarts de Chaume", "This gorgeous Loire white manages to be both extremely complex and utterly harmonious. Aromas of spice, crème brûlée and mango give way to mouthfilling flavors of honey, minerals, dried apricots and orange peel, very sweet yet kept vivid by crisp, lime-scented acidity. Irresistable now, better--probably much better--in years to come. Drink now through 2020. 1,000 cases made.", 35.0m, "1995", new Guid("694d1cfb-c7d8-4cd5-bf23-c248a75983a6") },
                    { new Guid("efc2571b-738f-4b73-a8cd-89b136604626"), 3, new DateTime(1997, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Ribera del Duero Teófilo Reyes", "Gorgeous. Seriously aromatic, this Spanish red explodes with toast, smoke, coconut and vanilla aromas and flavors, with plenty of ripe fruit notes beneath. The tannins are big but beautifully framed, the wine is concentrated but completely harmonious and should improve for a decade. 9,000 cases made.", 25.0m, "1994", new Guid("35bd86e2-11d0-466f-b5f2-6460e28640f6") },
                    { new Guid("1488929e-ddfa-4001-b78a-10bc7a6acf89"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Yamhill-Carlton District Savoya Vineyard", "A focused, harmonious and distinctive red, offering blackberry, anise seed and charred meat flavors, with delicate tannins complementing a finish of intensity and finesse. Best from 2015 through 2020. 892 cases made.", 55.0m, "2010", new Guid("4a2c89c6-5440-4a7d-b1f1-f2b9e269a970") },
                    { new Guid("13a248ea-33b9-4760-bee8-5ba8ac67298f"), 3, new DateTime(2006, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Willamette Valley Canary Hill Vineyard", "Lush and velvety, but beautifully restrained, the better to let the plum, blackberry and minty cream flavors spill gently over the tongue. The flavors linger against ultrarefined tannins on the long, delicate finish. Drink now through 2014. 700 cases made.", 45.0m, "2004", new Guid("4a2c89c6-5440-4a7d-b1f1-f2b9e269a970") },
                    { new Guid("c1af0b13-6aca-401d-92b5-ad69f8eee95b"), 3, new DateTime(1996, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley Three Vineyards at Red Mountain", "Rich, generous and spicy, filling the mouth with ripe blackberry, currant, tar and tobacco flavors that don't let go even when met by the chewy tannins on the finish. Give until 2000 to 2002 for best drinking. 1,000 cases made.", 18.0m, "1994", new Guid("f916bd15-e2d0-4bb0-b9e3-019693bd4cd9") },
                    { new Guid("f4a905f3-12d7-4307-bfd9-44c2c1e1728a"), 3, new DateTime(2004, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Mélange Columbia Valley", "Ripe and plump, generous with its spicy, mocha-scented cherry and blackberry flavors, persisting smoothly through the long, harmonious finish. Cabernet Sauvignon, Sangiovese, Merlot, Syrah and Cabernet Franc. Drink now through 2010. 19,000 cases made.", 13.0m, "2003", new Guid("68beb4ad-f1b9-4811-ba0a-3fbda4c5656a") },
                    { new Guid("a6948679-ce44-4e7d-a9c5-c6174b830a85"), 3, new DateTime(2005, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Richard Sauret Vineyards", "A delicious young Zin, with layers of ripe, zesty blackberry, boysenberry and wild berry shaded by toasty oak. Plush, rich aftertaste. Drink now through 2009. 7,029 cases made.", 19.0m, "2003", new Guid("d85af72e-373f-48bb-a3c8-86da70d378dd") },
                    { new Guid("44c67b51-f59f-47f8-b68c-9e4e307016ba"), 3, new DateTime(1994, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brunello di Montalcino La Casa", "Fabulous, immensely appealing, with vibrant, plush fruit character and a hint of oak. Full-bodied with masses of fruit and tannins, but completely harmonious. Fresh and clean finish. Tasted four times. Approachable now, but better after 1997. 1,250 cases made.", 60.0m, "1988", new Guid("2a3e8079-5a01-44e4-8cbc-9c2344ad52bc") },
                    { new Guid("a6d76222-321c-4700-9ab7-58b0def8b3a0"), 3, new DateTime(2013, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Coonawarra Black Label", "Refined, showing finesse, this plays its black currant and blackberry fruit against hints of mineral and herb. The finish is wrapped in fine tannins. Best from 2015 through 2020. 1,500 cases imported.", 35.0m, "2010", new Guid("e89a635f-4f9a-47e6-9cfb-185b5c1d352e") },
                    { new Guid("5190d2c1-799d-4186-9b29-0470ed93702d"), 6, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Coonawarra", "Ripe and distinctive, expanding and accelerating to the finish. It elevates its spicy, smoky pear and apricot flavors onto another plane on the long finish. Terrific. Drink now through 1997.  ", 12.0m, "1992", new Guid("e89a635f-4f9a-47e6-9cfb-185b5c1d352e") },
                    { new Guid("736de8bf-0749-4d14-8db0-9d9576b55174"), 3, new DateTime(1992, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pommard Premier Cru", "For being a blend of vineyards, this is unbelievable. Ripe, rich and firm, showing terrific breeding, with lovely, elegant, focused red berry flavors and superfine tannins. Drinkable now.  ", 39.0m, "1990", new Guid("3a95d042-fb87-4ffd-87e8-6f3d8f7282c2") },
                    { new Guid("d10e4a28-1815-455f-b6ca-2cf31742efcf"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tokay Pinot Gris Alsace Cuvée Réserve", "An excellent Tokay Pinot Gris; like a great white Burgundy. Rich, yet delicate and refined. Full-bodied and dry, with vanilla ice cream and fruit aromas. Overflowing with tropical fruit, vanilla, almond and cream flavors and a smoky finish that goes on and on. Drinkable now.  ", 11.0m, "1990", new Guid("90689d13-1d3b-4933-bc78-8a3f107ebc4e") },
                    { new Guid("b2b8ce9a-4ca5-4434-a133-f79cc79e18af"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Rosacker", "Offers great depth and complexity. Has enticing coconut, honey, apple and spice aromas. Medium-bodied and dry, with super-ripe apple and coconut flavors and an intensely rich finish. Drinkable now.  ", 22.0m, "1990", new Guid("e65c886b-f552-492f-bc03-42798123c46b") },
                    { new Guid("caa75f64-21b6-4ce5-bdc8-3515f21116af"), 3, new DateTime(1992, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pommard Les Boucherottes", "Ripe but focused, this has a massive structure yet manages to remain elegant. A wine to put away for decades. From an exclusive vineyard owned only by Coste-Caumartin. Drinkable 1997 to 2000. 650 cases made.", 37.0m, "1990", new Guid("02b5897a-6592-489e-8021-50823e76e102") },
                    { new Guid("dde23d98-ba38-43b6-a136-07eba501d549"), 3, new DateTime(1992, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Moulis", "Luscious, with all the gorgeous raspberry and berry flavors you could want. Shows lovely, rich, ripe fruit. Perhaps the best ever at this underrated property. A superb value. Best after 1996. 25,000 cases made.", 30.0m, "1989", new Guid("388241ec-bb0b-4a71-a2c8-7290daccded9") },
                    { new Guid("81b67429-2bc2-4718-a1be-54d90914b1e5"), 6, new DateTime(2012, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Veneto White San Vincenzo", "Smoke and straw notes mix with tropical mango and pineapple fruit and an exotic hint of jasmine in this exuberant white, which is medium- to full-bodied without being weighty, offering good cut throughout. Drink now through 2017. 40,000 cases made.", 13.0m, "2010", new Guid("23874d96-a214-412d-bdd2-e338ea3e2f88") },
                    { new Guid("55457a58-95ba-4d0e-aa35-28966a489c51"), 2, new DateTime(1992, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Recioto di Soave I Capitelli", "Fasten your seat belts, because this wine takes you for an exciting ride. Packed with apricot, honey and pear flavors and tobacco and cedar notes, it's smooth, subtle and velvety. Drink now through 1996. 400 cases made.", 34.0m, "1988", new Guid("23874d96-a214-412d-bdd2-e338ea3e2f88") },
                    { new Guid("6fc00716-7323-44e3-89be-1aabafcca36a"), 3, new DateTime(1992, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Musigny Cuvée Vieilles Vignes", "A benchmark wine for Burgundy. Defines harmony and class, with layer upon layer of ripe earth and raspberry flavors and a great deal of refined new oak. Has a deep, complex, velvety finish. Best from 1998 through 2000. 1,500 cases made.", 140.0m, "1990", new Guid("6dcc5974-36db-492e-80ba-63572c36a1c3") },
                    { new Guid("ae9d84e6-863a-4e56-b0f4-9b96b946d010"), 5, new DateTime(1991, 3, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Blanc de Blancs Mendocino County", "Delicate, dry and creamy, with plenty of flavor to go along with the finesse. Lemon, vanilla and fig aromas and flavors are harmoniously balanced and shaded by just enough hazelnut to keep it interesting. 3,000 cases made.", 20.0m, "1986", new Guid("b59aa619-e7e3-41ad-b404-54f8b4b80fbe") },
                    { new Guid("58d571cb-d1eb-4145-bd3a-e9fcdf044454"), 3, new DateTime(1991, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Estate Reserve", "Rich and supple, it's nicely shaded by oak aging, with velvety tannins and warm currant, chocolate, anise, cherry and nutmeg flavors. The fruit and spice linger nicely on the finish. Well balanced, with firm acidity and gentle tannins. Drink now. 1,371 cases made.", 24.0m, "1988", new Guid("eb1469a0-61dc-4688-8bb4-4b8384348780") },
                    { new Guid("75976099-3ac2-4007-ac2c-eabe8c0262a6"), 3, new DateTime(1992, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay Caillerets Ancienne Cuvée Carnot", "Has a fantastic concoction of rich, focused Pinot Noir flavors that takes on a racy edge. Ultra-fine tannins, smooth-textured and polished but the fruit is deep, complex and persistent. An outstanding wine from a highly-rated year. Should get even better if aged. Best after 1995. 1,500 cases made.", 52.0m, "1989", new Guid("3a95d042-fb87-4ffd-87e8-6f3d8f7282c2") },
                    { new Guid("128c1241-010e-4c60-bfb0-e2d27d237e2d"), 3, new DateTime(2004, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Napa Valley-Carneros", "Intense and vibrant, with a firm tannic backbone to the core of earthy blackberry and wild berry fruit. Tightens on the finish, with a hint of clove and cedary oak, at times flexing its muscles yet also offering concentration and depth. Drink now through 2009. 20,400 cases made.", 20.0m, "2002", new Guid("f7847e44-959a-4c75-9661-dd365626873f") },
                    { new Guid("f8fd31b4-e292-457b-93c0-03699a6fb1c6"), 3, new DateTime(1991, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tierra de Barros Gran Reserva", "Distinctive, rich, mature and elegant, with plenty of spicy brown-sugar charm around a core of strawberry and raspberry flavors. Hints at leather at first, but echoes fruit on the finish. Refined and elegant. 3,500 cases made.", 14.0m, "1982", new Guid("83b6ecd8-e18e-4377-82a7-78c5583be9cb") },
                    { new Guid("f94aac63-e531-44b6-b3e5-2a8570b54770"), 3, new DateTime(1991, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah California EXP", "Ripe, rich and opulent, with a luscious concentration of black cherry, plum and strawberry aromas and flavors and hints of pepper and smoke on the long finish. A dense, profound wine that's round in texture. Best after 1994. 835 cases made.", 15.0m, "1988", new Guid("94cfe17f-18dd-414f-a8fa-13cff3d6a6c0") },
                    { new Guid("eb6a58ce-6d6f-4b47-aae6-18283a558eb7"), 5, new DateTime(2012, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Green Valley of Russian River Valley Classic Vintage", "Seamless from start to finish, both rich and elegantly sleek. Floral Asian pear and creamy almond aromas lead to complex and delicately balanced flavors of lively lemon and raspberry, plus ginger and spice. The finish lingers beautifully. Drink now through 2016. 1,000 cases made.", 38.0m, "2007", new Guid("8c3a8e22-843a-4b7a-8e61-91ddea712a4e") },
                    { new Guid("baa8bb38-51d9-4c02-9c87-3c60da353e89"), 5, new DateTime(1991, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Sonoma County Green Valley L.D. Vrais Amis", "A classy, complex sparkling wine. Bracing and crisp, but backed by lovely green apple and spice flavors that are shaded by doughy, toasty notes. Has good length on the finish, too. 700 cases made.", 25.0m, "1986", new Guid("8c3a8e22-843a-4b7a-8e61-91ddea712a4e") },
                    { new Guid("6706879a-d3e6-4a11-ab34-03a98425ad0e"), 3, new DateTime(1999, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Trilogy Napa Valley", "An impressive red blend, tightly structured, with a rich array of currant, black cherry, anise, cedar, tar and spice flavors of remarkable harmony and finesse, ending in a long, complex aftertaste. A blend of Cabernet Sauvignon, Merlot and Cabernet Franc. Best from 2000 through 2009. 4,100 cases made.", 45.0m, "1996", new Guid("b36557f9-e285-4c97-9459-cf34f173268b") },
                    { new Guid("08c105a6-2361-4719-927b-3e7d43da9f89"), 3, new DateTime(1995, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Rutherford Reserve", "Serves up a broad range of complex flavors—tiers of currant, plum, black cherry and spice—then finishes with prune and anise nuances that add some exciting dimensions. Finishes long and rich, with smooth, polished tannins. Better in 1998 or 1999. 500 cases made.", 40.0m, "1992", new Guid("b36557f9-e285-4c97-9459-cf34f173268b") },
                    { new Guid("853e275b-3505-496e-a499-69c4d219fc28"), 6, new DateTime(1992, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Barrel Fermented", "Smooth, ripe and generous, with rich, complex, polished pear, spice, hazelnut and honey notes. The fruit zooms across the palate. A stylish wine that's delicious; drinkable now. 6,500 cases made.", 24.0m, "1990", new Guid("b36557f9-e285-4c97-9459-cf34f173268b") },
                    { new Guid("d948ba0f-273c-4e68-9a5b-b2e69c913751"), 6, new DateTime(1996, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viognier Sonoma Valley", "Rich and flavorful, showing more depth and range of flavor than most Viogniers, with tiers of peach, pear, honey and spice. An altogether complex and alluring wine. Drink now through 1997. 2,200 cases made.", 18.0m, "1995", new Guid("40710305-c4f1-4c73-86e7-928c65e10c05") },
                    { new Guid("d989f9bf-7ca9-4a90-a657-b56dd4b1d2ea"), 6, new DateTime(1992, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Valley Reserve", "Intensely rich and fruity, with smoky, buttery oak shadings that add complexity and depth to the ripe pear, spice and apple flavors. Sharply focused, long and lingering on the finish. A real beauty. Drink now through 1994. 2,900 cases made.", 19.0m, "1990", new Guid("40710305-c4f1-4c73-86e7-928c65e10c05") },
                    { new Guid("3b977381-6862-4a4e-a830-e6bd87cc6bf3"), 6, new DateTime(1991, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma Valley", "Ripe, bold and buttery, with layers of honey, pear, peach and nutmeg flavors that are rich and silky-smooth. Oak adds dimension to the flavors. A harmonious wine. Drink now. 7,800 cases made.", 12.0m, "1990", new Guid("40710305-c4f1-4c73-86e7-928c65e10c05") },
                    { new Guid("9ef61c45-7da8-494b-978c-159620f28e4d"), 3, new DateTime(1991, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Napa Valley Carneros St. Clair Vineyard", "Very firm and concentrated, with nicely modulated plum, cherry and currant aromas and flavors and sufficient tannins to hold the wine without getting in the way. Concentrated enough to develop well. Drink now through 1995. 2,588 cases made.", 20.0m, "1988", new Guid("f7847e44-959a-4c75-9661-dd365626873f") },
                    { new Guid("edf47ab2-18b1-44dd-a17f-17cbd4e0e004"), 6, new DateTime(1991, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Washington Barrel Fermented", "Full-bodied, complex, earthy and oaky, but the grapefruit and pear flavors come through on the rich finish. A stylish wine that gets better with each sip. Drinkable now, but should be better around 1993. 700 cases made.", 12.0m, "1989", new Guid("dddbb3fe-d791-4e94-8776-78bd224ad704") },
                    { new Guid("132e55f9-39d1-4f80-a150-0732edf7f008"), 6, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chevalier-Montrachet", "It flies over the palate as delicately as a glider in the sky, leaving in its wake a trail of subtle, seamless flavors. Delicate with plenty of depth, offering focused lime, lemon, pear and toast flavors, with a creamy yet solid finish. 550 cases made.", 81.0m, "1992", new Guid("3a95d042-fb87-4ffd-87e8-6f3d8f7282c2") },
                    { new Guid("f0af7255-adab-4864-abb8-5c2b3e4ed10b"), 3, new DateTime(2015, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Beaune Teurons Domaine", "Vibrant and juicy, this red offers black cherry flavors, with accents of licorice, spice and floral. Silky and rich, with fine balance and a lingering aftertaste of fruit and spice. Drink now through 2024. 200 cases imported.", 55.0m, "2012", new Guid("3a95d042-fb87-4ffd-87e8-6f3d8f7282c2") },
                    { new Guid("bc4e10a5-2902-4535-a52e-c8db24d24d39"), 6, new DateTime(1994, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace Grand Cru Gueberschwihr Goldert Vendange Tardive", "Compelling, undeniable Gewürztraminer. Sweet and rich, abundant flavor and a thick, pleasingly unctuous texture with great acidity to keep it fresh on the palate and the finish. Delicious now, but it's still a baby, and will be more interesting if cellared until about 2000. 300 cases made.", 45.0m, "1990", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("d8bfb783-f46d-4148-8120-51ca7747a2a9"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Turckheim Brand", "An electrifying wine, with super-charged flavors. Smooth and superb, with great acidity and ripe fruit flavors. Drink now.  ", 35.0m, "1990", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("4ff46f5c-36d6-4ea8-bc7b-ba014ee9aa06"), 3, new DateTime(2019, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Finca Ygay Reserva", "Cedar, tobacco, dried cherry and spice flavors mingle in this red. Features a polished texture, with well-integrated tannins and orange peel acidity. Supple, harmonious and graceful. Tempranillo, Graciano, Mazuelo and Garnacha. Drink now through 2025. 100,000 cases made, 9,067 cases imported.", 29.0m, "2015", new Guid("b54d6f6e-776a-4a9f-95f5-28a341c83346") },
                    { new Guid("a849f38a-104b-4e0b-92fd-051b8b0d87ea"), 6, new DateTime(2015, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viura Rioja Capellanía", "Aromatic notes of beeswax, chamomile and orange blossom frame the flavors of baked apple, coconut, ginger and tarragon in this generous white, whose fresh acidity and light tannins keep it structured, with the spicy accents lingering on the finish. A traditional style. Drink now through 2020. 3,244 cases made.", 28.0m, "2010", new Guid("b54d6f6e-776a-4a9f-95f5-28a341c83346") },
                    { new Guid("0f5a878d-9dd7-484f-b402-6b377ae69ea0"), 3, new DateTime(2014, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Castillo Ygay Gran Reserva Especial", "Bold and rich, this red fills the palate with blackberry, plum sauce, soy, chocolate and spice flavors. Broad and deep, in a modern style, with plenty of oak and enough backbone to stay focused. Drink now through 2020. Tasted twice, with consistent notes. 750 cases imported.", 89.0m, "2005", new Guid("b54d6f6e-776a-4a9f-95f5-28a341c83346") },
                    { new Guid("e0d65d3b-8282-4006-bbf7-258249940b84"), 6, new DateTime(1992, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja White Reserva", "Maturing, but still monumental, this is rich with honey, vanilla, pear and melon flavors and still has a long life ahead of it.  ", 20.0m, "1986", new Guid("b54d6f6e-776a-4a9f-95f5-28a341c83346") },
                    { new Guid("e57498d1-78a3-4ab1-82d1-3083bfbb05fb"), 3, new DateTime(1992, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Gran Reserva", "A gorgeous wine, brimming with plum, cedar and licorice flavors and young, fresh fruit braced by firm, spicy tannins. Perfectly proportioned, with plenty of time ahead. Drinkable now.  ", 35.0m, "1975", new Guid("b54d6f6e-776a-4a9f-95f5-28a341c83346") },
                    { new Guid("3cde3112-c773-4198-9503-a58541c74222"), 3, new DateTime(1996, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley West Block Reserve", "Stunning, with an amazing core of ripe, rich, complex and concentrated fruit--tiers of brilliant black cherry, plum, currant and raspberry flavors--and just the right amount of oak shading, giving it a light, toasty vanilla accent that adds dimension. A sensational wine from a winery that is right on target. 243 cases made.", 48.0m, "1994", new Guid("f9779b7c-4d03-4509-b425-76757e9d3a07") },
                    { new Guid("80339542-954e-47a8-a4a4-050d5e30ab10"), 3, new DateTime(1992, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Russian River Valley Reserve", "Deep, dark and complex, with a pretty, concentrated core of currant, cherry, spice and oak flavors. Crisp and lively, finishing with a delicate aftertaste that echoes fruit and oak. Has wonderful balance and depth. Drink now. 154 cases made.", 30.0m, "1990", new Guid("f9779b7c-4d03-4509-b425-76757e9d3a07") },
                    { new Guid("9cb4e7e9-efa1-41c8-86f6-b94c80bd7726"), 3, new DateTime(1997, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Napa Valley Peter Mondavi Family", "Delivers plenty of detailed flavors, with pretty currant, plum and cherry notes complemented by lovely toasted oak shadings, all coming together quite nicely on the finish where the texture is polished. An impressive offering, it's this winery's best Merlot yet. 8,700 cases made.", 15.0m, "1995", new Guid("0f723162-8658-44e0-b8ed-ce0ffcb3e3be") },
                    { new Guid("8f6f1c9c-e988-409a-a93a-1bdc34419c9a"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay Caillerets Ancienne Cuvée Carnot Domaine", "There's beautiful purity to the raspberry, black currant and violet aromas and flavors as this compelling red moves across the palate. There's depth and detail, with a long, fruit- and mineral-filled aftertaste. Best from 2012 through 2024. 300 cases imported.", 61.0m, "2008", new Guid("3a95d042-fb87-4ffd-87e8-6f3d8f7282c2") },
                    { new Guid("8b0f07d4-402b-42f0-9b56-693a62b2aa1e"), 3, new DateTime(1992, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Rioja Conde de Valdemar Reserva", "Very appealing, perfumed and pretty, with bright, exuberant blackberry and boysenberry flavors on a bed of violets and vanilla. Has good concentration. Drink now.  ", 9.0m, "1985", new Guid("4e6bfb3d-8d61-43b9-9d49-f93194c3f233") },
                    { new Guid("45b9c385-7117-4899-b245-9837d9d5c6d2"), 3, new DateTime(2009, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Horse Heaven Hills Canoe Ridge Estate", "Sturdy, ripe and generous, with a surprisingly soft edge to the explosive black cherry, currant, black olive and smoke flavors, lingering effortlessly on the big, round finish. Best from 2010 through 2018. 5,800 cases made.", 28.0m, "2006", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("dd589688-3075-4216-9bab-895fed780d4e"), 6, new DateTime(2006, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley Canoe Ridge Estate", "A fruit-centered wine, this sings with pear, grapefruit and peach aromas and flavors, perched enticingly on a lithe frame, with subdued oak notes lingering around the periphery. It's the texture, though, that makes it so attractive, polished and refined. Drink now through 2010. 9,000 cases made.", 20.0m, "2004", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("d32c2bec-0032-49c0-aeda-adf2cb31ffbe"), 3, new DateTime(2002, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Columbia Valley Canoe Ridge Estate Vineyard", "Rich, round and generous with its ripe blackberry, cherry and sweet spice flavors, which echo beautifully on the velvety finish. Drink now through 2006. 10,000 cases made.", 23.0m, "1999", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("d998f43d-efce-4152-ba80-14903b2e0e01"), 3, new DateTime(2000, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Columbia Valley Cold Creek Vineyard", "Rich, ripe and focused, this Washington Cabernet shows layers of currant, plum and vanilla flavors, with hints of pepper and bay leaf sneaking in on the long, supple finish. Fine-grained tannins. Best from 2001 through 2008. 5,000 cases made.", 25.0m, "1996", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("0267dfd9-8a84-47ce-9743-bc2ead2590d7"), 6, new DateTime(1995, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley Cold Creek Vineyard", "Subtle, harmonious and beautifully balanced, blending together pear, spice, vanilla, honey and toast flavors right through to the smooth finish. An intriguing Washington white that's drinkable already, but should improve through 1997. 3,600 cases made.", 25.0m, "1993", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("06b819df-bb2e-4b03-a6c1-4d9101259dcf"), 2, new DateTime(1995, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "White Riesling Columbia Valley Late Harvest Chateau Reserve", "A ripe, sweet, generous and elegant dessert wine from Washington. The silky mouth-feel and gorgeous honey, apricot and spice flavors blend beautifully, joined by a touch of almond on the lovely finish. Wonderful now, and it should last through 1999 and beyond.  1,000 cases made.", 9.0m, "1991", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("5ad2389b-33f0-4974-899d-92f891ca26aa"), 6, new DateTime(1994, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Columbia Valley Chateau Reserve", "Spicy, ripe, generous and silky, with layers of pear, apple, peach and honey aromas and flavors, deftly balanced between ripe fruit and spicy oak. Pretty notes linger on the long finish. Delicious now, so why wait? 1,200 cases made.", 22.0m, "1992", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("f6146a16-ebb7-4d46-abfa-aa5e16da98d0"), 6, new DateTime(1992, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Columbia Valley", "A real zinger, combining great value and outstanding quality. Crisp and lively, with a creamy richness that lifts it out of the ordinary. Citrus, floral and pear aromas and flavors slip in and out of the spotlight, and it remains refreshing through the long finish. Drink now. 11,700 cases made.", 9.0m, "1990", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("fbb42cc7-f838-431e-b3fe-baabcd3a2efc"), 3, new DateTime(1992, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Napa Valley Dickerson", "A delicious young Zin that is ripe, rich tannic and tasty, with tiers of fresh berry, raspberry and cherry flavors that zoom across the palate. Fresh and lively. A mouth-watering wine that drinks well now, but can hold. Drink now through 1997. 1,000 cases made.", 16.0m, "1990", new Guid("a6d585f1-455a-4733-816a-21ede3d06e6b") },
                    { new Guid("db41e9a7-9c2a-48ae-9b93-04e5a1f91718"), 6, new DateTime(1992, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chassagne-Montrachet Les Grandes Ruchottes", "Rich, creamy and concentrated around a racy core of zingy, lemony acidity, packing in ripe pear, peach and honey aromas and flavors and hinting at hazelnut on the long finish. Jams in so much flavor it needs at least until 1997 to sort everything out, although it should be delicious anytime. 55 cases made.", 49.0m, "1990", new Guid("6907b528-3a29-4153-a619-5137a90ec2df") },
                    { new Guid("ec00e3d3-ab39-4cc5-a64d-90f8b517ae2f"), 6, new DateTime(2013, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Horse Heaven Hills Horse Heaven Vineyard", "Bright, vibrant, expressive and juicy, with quince, pear, floral and spice flavors that dance through the long finish. Drink now. 53,000 cases made.", 15.0m, "2012", new Guid("8b9eca78-b117-44b4-b3e6-15d1917a4f27") },
                    { new Guid("2060b20a-5897-4aa1-92eb-6dcfacbac4e4"), 3, new DateTime(2012, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Central Coast Thirty-Fifth Anniversary Vintage", "A classy art-of-the-blend cuvée that's very ripe but still complex, relying on rich dark berry, plum, crushed rock and cedar. So young and vibrant at this stage, yet also so deep and tight, that it's both tempting to drink now but looks like a great cellar choice as well. Drink now through 2021. 7,688 cases made.", 24.0m, "2010", new Guid("30e29216-f10c-4f64-ba95-23d02c9b4596") },
                    { new Guid("13aa5901-b357-4033-92a4-8aa13b2c5076"), 3, new DateTime(1991, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Mount Harlan Jensen", "Packs in a load of fresh, ultraripe, rich black cherry, currant, herb and spicy earth overtones. Deeply flavored and very concentrated, with smooth, supple tannins and a long, full, fruity finish. A distinctive wine. Drink now through 1997. 437 cases made.", 35.0m, "1988", new Guid("30e29216-f10c-4f64-ba95-23d02c9b4596") },
                    { new Guid("9edf56bc-956f-46c7-ad8f-be362a21bd9c"), 6, new DateTime(1995, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Corley Estate Reserve", "Bold, rich and concentrated, with attractive pear, honey, spice and apple flavors that turn smooth and creamy as the toasty oak nuances chime in. 492 cases made.", 26.0m, "1992", new Guid("6d49b22f-3be8-4a27-87d6-6943b88b3023") },
                    { new Guid("1b5f1a63-8bb0-4f66-ba4c-432af78c2e16"), 3, new DateTime(2000, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Colli della Toscana Centrale Flaccianello", "This builds and builds and builds on the palate. Gorgeous aromas of ripe berries, meat and spices. Full-bodied, with polished, well-integrated tannins and a long, long finish. Give it time. Best after 2005. 5,000 cases made.", 68.0m, "1997", new Guid("4057b80d-39a7-45e4-ae36-435fa7799017") },
                    { new Guid("eab58fc8-d7e1-4199-aec9-8a156648a531"), 3, new DateTime(1991, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Riserva", "A wine supercharged with fine fruit and an excellent backbone of tannins and acidity. Has stunning aromas of blackberry, cedar, cassis and mint, and a crisp finish. Ready to drink now, but it will improve.  ", 16.0m, "1985", new Guid("4057b80d-39a7-45e4-ae36-435fa7799017") },
                    { new Guid("ba3579ea-f7b5-4764-adcc-a46384ea6a1e"), 3, new DateTime(2008, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pritchard Hill Estate Vineyard Napa Valley", "Big, ripe and complex, but light on its feet. An elegant, spicy mix of rich currant, anise, toasty oak, espresso  and subtle herbal scents, all of which fold together nicely, ending with a long, persistent finish. Cabernet Sauvignon, Petit Verdot and Malbec. Best from 2010 through 2016. 1,426 cases made.", 125.0m, "2005", new Guid("dfd12f3e-02cf-4cc1-87d6-5705a01d18e3") },
                    { new Guid("bfaf6c63-3fe2-4268-abfd-74fde3c4a932"), 3, new DateTime(2007, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Signature", "A supple, graceful style that serves up a complex mix of cherry, currant, herb and plum-scented fruit, picking up a light toasty oak and mocha note midpalate and finishing with a long, lingering aftertaste and round tannins. Drink now through 2013. 6,200 cases made.", 46.0m, "2004", new Guid("dfd12f3e-02cf-4cc1-87d6-5705a01d18e3") },
                    { new Guid("b58d0cd1-529e-4e73-8db7-c809ba4cbb99"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Signature Reserve", "The best Chappellet Cabernet in years. Focused and chewy, with powerful currant and berry aromas and flavors shaded by hints of cinnamon, nutmeg and bay leaf, all wrapped in a tight package that should require until 1998 to 2000 to settle all the tannins. 4,900 cases made.", 18.0m, "1987", new Guid("dfd12f3e-02cf-4cc1-87d6-5705a01d18e3") },
                    { new Guid("937c5223-5dca-42e1-9cfd-d2a8a57a74dd"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Vieilles Vignes", "A huge wine. Deep colored, with chocolate-covered cherry aromas and bitter chocolate and cassis flavors. Full-bodied and amazingly concentrated, like crushed grapes. Still like a barrel sample; drink next century.--Châteauneuf-du-Pape retrospective. Best from 2000 through 2010.  ", 30.0m, "1989", new Guid("433326db-a60e-48fe-8240-cfcb3d108d85") },
                    { new Guid("13affe9c-0547-4835-b102-1c212f3816cb"), 6, new DateTime(1991, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Les Folatières", "There isn't much more you could want in white Burgundy; lemon, butter, toast, hazelnut, ripe apple--the list of characteristics goes on and on. Massive, yet superbly balanced, with great finesse. From Lalou Bize-Leroy. Drink now to 1998.  ", 95.0m, "1989", new Guid("8679fe95-7aff-4596-82c6-856590b6e849") },
                    { new Guid("dfd808c2-0a5f-4aac-961c-be32662aba0f"), 6, new DateTime(1991, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault Les Narvaux", "This is to beg or kill for. Tastes like nectar, with captivating aromas of lilacs, honey, butter and melon. Full-bodied, with multidimensional flavors and toasted notes. Superbly long on the finish. From Lalou Bize-Leroy. Drink now to 1996.  ", 60.0m, "1989", new Guid("8679fe95-7aff-4596-82c6-856590b6e849") },
                    { new Guid("e48baed3-0ee4-49a8-b50f-004461de0707"), 6, new DateTime(1999, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Pfalz Forster", "Gorgeous. Exhibiting peach, violet and tropical fruit nuances, this white is concentrated and solidly supported by bracing acidity and power that carries through the finish. Young and unevolved, this shows great potential. Best from 2001 through 2008. 1,200 cases made.", 12.0m, "1997", new Guid("bf7bf553-44fd-4187-90f6-2b55218781b0") },
                    { new Guid("82ce0b12-b0cf-40dc-b816-d8cf55d5ab99"), 6, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Auslese Pfalz Forster Pechstein", "Undeveloped now, but very rich nonetheless. Light apricot and lemon aromas; full-bodied, with honey, lemon, spice and botrytis flavors. Excellent acidity and finish.  ", 25.0m, "1989", new Guid("bf7bf553-44fd-4187-90f6-2b55218781b0") },
                    { new Guid("898c7f19-95c8-4bd3-95c7-c3a5565e4f0d"), 3, new DateTime(2018, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Vigna del Sorbo Gran Selezione", "This is concentrated and monolithic, with blackberry and black cherry flavors augmented by tar, tobacco and iron notes. Offers excellent potential, but needs time to absorb the tannins. Powerful, showing fine length. Best from 2023 through 2040. 1,800 cases made.", 89.0m, "2015", new Guid("4057b80d-39a7-45e4-ae36-435fa7799017") },
                    { new Guid("ac69919d-4134-4ef6-933a-89ae7d0bb5bf"), 6, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Auslese Pfalz Wachenheimer Gerümpel", "Very sweet and rich, like syrup. Beerenauslese quality. Light gold in color; honey, spice and nectarine aromas; full-bodied and sweet, with syrup, honey, spice and nectarine flavors, medium acidity and a spicy finish. Drinkable now.  ", 34.0m, "1989", new Guid("bf7bf553-44fd-4187-90f6-2b55218781b0") },
                    { new Guid("0262c829-dd2f-4aa2-bbb2-6d235a597374"), 3, new DateTime(2010, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Hommage à Jacques Perrin Grande Cuvée", "This has all the heady, dense crushed fig, linzer torte, currant confiture and melted licorice flavors of the vintage, but carries them effortlessly, thanks to perfectly embedded tannins and gorgeous, creamy layers of tar, roasted mesquite, braised chestnut, maduro tobacco and iron. Offering amazing mouthfeel, a stunning array of flavors and awesome density, purity and length, this shows the glory of Mourvèdre in 2007. Best from 2012 through 2035. 580 cases made.", 535.0m, "2007", new Guid("6fb0f931-bf8d-46f4-83e2-42d86deb8b68") },
                    { new Guid("70837322-aa6d-48e3-bf70-168d9dfd59f9"), 6, new DateTime(1998, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape White", "This classy, distinctive white shows wonderful personality and fantastic balance. The smooth mouthfeel has a tight grip that explodes with cedar, spice, tropical, iodine, lemon and mineral notes, all swirling around beautifully. An ager for sure, but tempting now because of its full body. The ripe flavors linger on the bitter almondlike finish. Drink now through 2010. 1,665 cases made.", 40.0m, "1997", new Guid("6fb0f931-bf8d-46f4-83e2-42d86deb8b68") },
                    { new Guid("ce5f3439-b7e0-4f75-a023-28e3f7bc8ab5"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape", "Perhaps the greatest Beaucastel ever produced. Has the class and structure of a great vintage of Mouton-Rothschild. Deep, inky in color, with intense herb, plum, game and spice aromas, this full-bodied wine has an explosion of fruit and an iron backbone. Try the beginning of next century.--Châteauneuf-du-Pape retrospective. Best from 1995 through 2005. 25,000 cases made.", 35.0m, "1989", new Guid("6fb0f931-bf8d-46f4-83e2-42d86deb8b68") },
                    { new Guid("1f3737ac-0dfc-4ef3-8949-25dcd9821d85"), 6, new DateTime(1990, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Savennières-Coulée de Serrant Clos de la Coulée de Serrant", "Distinctive, unusually ripe and complex, offering loads of apricot, pineapple and almost plummy aromas and flavors that just sail on and on through the long finish. Not steely and austere, like mnay Savennières, but almost silky and honeyed. Tempting now, but better with time. Drink now through 2005. 2,500 cases made.", 33.0m, "1989", new Guid("5512b7b0-37de-417e-926f-d13af58286f5") },
                    { new Guid("637d3f53-60ac-4fa3-a345-409bab6b61d8"), 6, new DateTime(2012, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Gris Alsace Les Princes Abbés", "Finely knit, with a vibrancy to the refined acidity that focuses the flavors of ripe peach, pear, guava and kumquat. Silky in texture, with a core of smoky minerality that pushes through on the lasting finish. Drink now through 2022. 2,100 cases imported.", 20.0m, "2010", new Guid("d3cfcd83-a5ef-4532-b185-fb466b897c74") },
                    { new Guid("6803364c-6802-4d28-a48e-3f96a08b1750"), 6, new DateTime(2004, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Les Princes Abbés", "This is lush and though it starts out off-dry, the racy structure emerges midpalate, driving the floral, peach and mineral flavors to a long, dry conclusion. It really sings on the finish today, with a long, smoky aftertaste. Drink now through 2010. 2,000 cases imported.", 16.0m, "2000", new Guid("d3cfcd83-a5ef-4532-b185-fb466b897c74") },
                    { new Guid("53257840-525f-48c5-a1ed-09cfa7cba70a"), 6, new DateTime(1996, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace Fleur de Guebwiller", "Unusually elegant for a Gewürz--light-bodied yet intense, with alluring honey, smoke and dried peach aromas and flavors. The perfect balance of sweet and tart makes this a delicious accompaniment to assertive foods. 8,000 cases made.", 18.0m, "1993", new Guid("d3cfcd83-a5ef-4532-b185-fb466b897c74") },
                    { new Guid("cf29339d-f557-44b2-9a05-329e9aab646a"), 6, new DateTime(1994, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace Grand Cru Kessler", "Exciting and rich, stuffed with fruit, yet still has plenty of acidity for balance. Harmonious, complex, and its toasty, nutty flavors of vanilla, peach and apricot bloom on the palate and echo on the long finish. It's delicious now, on its own or with food. 2,000 cases made.", 25.0m, "1989", new Guid("d3cfcd83-a5ef-4532-b185-fb466b897c74") },
                    { new Guid("30d1fd54-59e0-46e8-a694-3a80f4500523"), 6, new DateTime(1989, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace Grand Cru Kitterlé", "  ", 14.0m, "1988", new Guid("d3cfcd83-a5ef-4532-b185-fb466b897c74") },
                    { new Guid("8bc33426-9388-416e-abcf-8fe0595fcb76"), 6, new DateTime(2013, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fuissé Marie-Antoinette", "Medium-weight and linear, exuding mineral and spice flavors, with a hint of lemon underneath, this bright white finishes long and spicy. Drink now through 2017. 6,000 cases imported.", 25.0m, "2011", new Guid("460d9443-93ad-4156-ad89-81f78b0d7b83") },
                    { new Guid("f42d3f68-7cab-4415-91b2-7cf2e8a1055d"), 3, new DateTime(1991, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Julien Les Fiefs de Lagrange", "Elegant and restrained, with pretty, rich, concentrated currant, herb, vanilla and chocolate flavors that gently unfold on the palate, finishing with rich, round, smooth and intense tannins and flavors that linger. Best from 1998 through 2004.  ", 17.0m, "1988", new Guid("0c906829-d599-4602-9240-f8bdbeb1dd4f") },
                    { new Guid("d67de4f0-36dc-4277-87a5-e98b50c1ed44"), 3, new DateTime(1991, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Paso Robles", "A great value. Smooth and generous, this ripe wine has marvelous currant and plum flavors and an overlay of rich chocolate and spice notes. Supple, flavorful and utterly charming, with flavor to burn. Drinkable now. 4,000 cases made.", 12.0m, "1988", new Guid("54c8d459-3543-4fc7-9844-131630327dd3") },
                    { new Guid("c6dc0094-c789-46b7-90c2-5ce8f9736b2b"), 3, new DateTime(1991, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Paso Robles", "Meridian's first Syrah is dense, dark and rich, with spicy, cedary, and focused blackberry and wild raspberry aromas and flavors. A tough wine with a soft heart. Long and elegant, turning complex and especially delicious on the finish. Drinkable now, but cellaring until 1994 couldn't hurt. 4,000 cases made.", 14.0m, "1988", new Guid("54c8d459-3543-4fc7-9844-131630327dd3") },
                    { new Guid("9c74e8cc-6c43-4bfd-9220-d71cbc7a26a0"), 6, new DateTime(1998, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Edna Valley Coastal Reserve", "Beautifully crafted, this California white is rich, complex, concentrated and sharply focused, with layers of ripe pear, fig, peach and honeydew melon. Turns elegant and spicy on a long, lingering finish that echoes fruit and vanilla. Hard to go wrong at this price and score. 15,000 cases made.", 14.0m, "1996", new Guid("54c8d459-3543-4fc7-9844-131630327dd3") },
                    { new Guid("ce73ba78-8082-44a2-8f6a-c14770c63624"), 6, new DateTime(1991, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Napa Valley Corley Reserve", "A beautiful combination of fruit and oak. Has intense, concentrated, elegant apple, pear and peach flavors neatly shaded by sweet vanilla and honey notes. Polished, long and full on the finish. A real treat. Drink now to 1994. 3,700 cases made.", 18.0m, "1988", new Guid("6d49b22f-3be8-4a27-87d6-6943b88b3023") },
                    { new Guid("05db26eb-dc96-49a5-8a57-c31720e76d56"), 2, new DateTime(2013, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tawny Port 20 Year Old", "A complex and full-bore tawny, with deep, intense flavors of orange cream, glazed apricot, baked peach and roasted pineapple that are balanced by concentrated chocolate and toffee notes. The bright finish is filled with spice and supported by fresh acidity. Drink now. 25,000 cases made.", 60.0m, "NV", new Guid("02c81a67-e315-457f-bd76-19f334457861") },
                    { new Guid("bb7d2b4d-ddeb-4e99-85b2-f283b1bf3e8f"), 2, new DateTime(1991, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vintage Port Malvedos", "Ripe, opulent and delicious. Beautifully peppery and fruity in flavor, with a silky smooth texture that belies its high alcohol and young age. A very long finish echoes black pepper and chocolate. Tempting to drink now for its fruitiness, but probably best after 2000. 7,000 cases made.", 26.0m, "1988", new Guid("02c81a67-e315-457f-bd76-19f334457861") },
                    { new Guid("289edc39-7f62-417c-863a-0de62e9d0639"), 3, new DateTime(2000, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana San Martino", "Big and powerful Tuscan red. Quite dark in color, with an intense blackberry, raspberry and floral character. Full-bodied, with very chewy tannins. Very tight now. Best after 2005. 1,077 cases made.", 50.0m, "1997", new Guid("3a3cf066-44c3-4b21-b6c1-92e6b10eb943") },
                    { new Guid("c50671e2-7fbb-476a-96c2-04571d8e44cd"), 6, new DateTime(1991, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Willamette Valley", " 448 cases made.", 10.0m, "1988", new Guid("b46f90b5-83d0-49d7-a22c-a37830263d99") },
                    { new Guid("7f3208ba-a056-412d-99f4-758e8742ab19"), 3, new DateTime(1991, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chianti Classico Giorgio Regni Riserva", "A joy to drink, with exuberant fruit and a lovely texture. Superbly balanced, with intense cassis, violet and berry aromas and an excellent balance of intense cherry, cassis and black cherry flavors. Medium-bodied, with a silky texture. Drink now.  ", 20.0m, "1985", new Guid("a00b81b9-1071-48cb-b108-a114c254fc17") },
                    { new Guid("41be7eab-d780-4ec1-b60a-67c4d3039db6"), 2, new DateTime(1991, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Johannisberg Riesling Sonoma County Late Harvest Reserve", "Very ripe, lush and opulent, with rich apricot, spice and honey aromas and flavors that are long, fresh and balanced on the finish despite the high level of sweetness. Drinkable now, but should be best after 1993. A good value.  ", 10.0m, "1988", new Guid("7d992193-1563-4a3f-a090-375bb520c583") },
                    { new Guid("f05d6a63-a201-46d2-addb-9b7701807449"), 6, new DateTime(2008, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Mosel Piesporter Goldtröpfchen", "This is unevolved, revealing hints of floral and black currant aromas and flavors, but also yeast and carbonic gas elements. Yet it's vibrant and juicy, with a lingering finish. Best from 2009 through 2022. 2,500 cases made.", 24.0m, "2007", new Guid("e6046340-9ee8-457e-b849-2c9753b9531e") },
                    { new Guid("971ed8ee-9859-428a-8baa-684bedfb2bc9"), 6, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Auslese Mosel-Saar-Ruwer Oberemmeler Karlsberg", "  ", 32.0m, "1989", new Guid("e6046340-9ee8-457e-b849-2c9753b9531e") },
                    { new Guid("9dbcc94a-7bb2-42eb-825c-5c2f259683a4"), 6, new DateTime(1991, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault", "Rich, bold and flavorful, showing lots of concentration, but maintaining smoothness and elegance, too. Earthy, creamy butterscotch notes accent the ripe pear and apple flavors. Mature and delicious now, but could be aged until at least 1996. 2,500 cases made.", 55.0m, "1986", new Guid("04a3436c-6fff-4a38-bf51-e5b9ef86cadf") },
                    { new Guid("23616b85-a0d5-4f10-b30e-81b012066991"), 6, new DateTime(1991, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vouvray Domaine Le Peu de la Moriette", "Strikingly fruity and grassy, with rich, concentrated flavors of apple, custard and vanilla. Off-dry, but balanced, with remarkably lively, complex flavors that hint at grapefruit and honey on the finish. Drink now through 1993. 5,000 cases made.", 12.0m, "1989", new Guid("025102d5-2308-464d-b344-6a0f9a9c91bd") },
                    { new Guid("de13e6ab-345a-4008-a7c9-5996c0fe26c4"), 3, new DateTime(1999, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Toscana Cepparello", "This blockbuster of a Sangiovese shows layers of ripe fruit on the palate, with loads of ripe tannins and a long, fruity finish. Truly monumental for this producer and appellation. Breathtaking. Best after 2002. 3,900 cases made.", 38.0m, "1997", new Guid("438db650-1f59-4b4e-9238-0444c456d226") },
                    { new Guid("debec30a-6d3f-477f-a7df-440990f8f351"), 3, new DateTime(1991, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cepparello", "Subtle on the nose, with elegant blackberry, cherry and licorice aromas, but chewy and powerful on the palate. Excellent potential for cellaring. Full-bodied, with ripe, rich fruit and highly extracted tannins, but shows great elegance. Made from Sangiovese. 2,000 cases made.", 32.0m, "1988", new Guid("438db650-1f59-4b4e-9238-0444c456d226") },
                    { new Guid("5aa821c3-d3d5-45f6-8185-4a4e61e96866"), 3, new DateTime(1991, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vougeot", "Ripe and generous, with lots of blackberry and black cherry aromas and flavors shaded by smoke, nutmeg and a delicate touch of earthiness. Has the intensity and complexity to age well; keep until 1995 to '97. Reasonably priced; not the grand cru. 200 cases made.", 32.0m, "1988", new Guid("55540277-f2bb-4b43-a15a-815a81e17809") },
                    { new Guid("06472f74-52b6-4483-87db-b12546b5f973"), 3, new DateTime(1991, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Volnay", "Tight, lean, intense and concentrated, with pretty aromas and a solid core of black cherry, currant, berry and raspberry flavors tightly wrapped in firm tannins. A keeper; will probably reach maturity in 1995 or '96. 185 cases made.", 32.0m, "1988", new Guid("7677f1c7-6cde-4912-b54c-e0fd2af51fc5") },
                    { new Guid("e5dc9346-539a-4950-9b08-a7f13b98ca41"), 3, new DateTime(1991, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chambolle-Musigny Les Noirots", "A seductive style, with deep, rich, complex raspberry, strawberry, anise and toasty oak flavors that are engagingly silky and long on the finish. Drink now through 1996. 200 cases made.", 30.0m, "1989", new Guid("444ecad8-1d6e-47e2-8ad4-ba478f2146b7") },
                    { new Guid("9443007b-7984-4aa2-9272-fbef4084cd61"), 6, new DateTime(1991, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Condrieu", "Ripe, rich and seductive, a plush pillow of a wine, with all sorts of apricot, honey, hazelnut, vanilla and floral aromas and flavors that keep taking turns as it extends into a long, long finish. An exotic, substantial wine that offers plenty of pleasure.  ", 45.0m, "1989", new Guid("273935d8-1aff-48a4-bf8d-5d560dcaf252") },
                    { new Guid("e43b3f13-ec6d-484a-8234-dc7d6bf14444"), 3, new DateTime(2012, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Marlborough Icon", "Generous, with vanilla, plum and black cherry flavors that are rich and lush, joined by plenty of echoes of cedar, tobacco and spice notes in the background. The finish reveals plenty of floral, herb and mineral details. Drink now through 2019. 6,100 cases imported.", 22.0m, "2010", new Guid("4cf514d0-065e-4024-b129-f87185cf197b") },
                    { new Guid("3acf1cb5-0c19-4323-8940-1ac78ac128a4"), 6, new DateTime(2003, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Marlborough Icon Series", "Brisk, floral style brims with pear, grapefruit and passion fruit aromas and flavors, which linger effortlessly on the vibrant finish. Drink now. 14,000 cases made.", 19.0m, "2002", new Guid("4cf514d0-065e-4024-b129-f87185cf197b") },
                    { new Guid("fea23d24-d935-4aac-a92b-1db53d7a3a7c"), 6, new DateTime(1991, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Gisborne Tietjen Vineyard Reserve", "Buttery and flavorful, with a smooth, almost unctuous texture, dripping with honey and butterscotch notes but weighing in with distinctive pear and pineapple flavors that persist into the finish. This wine evolves on the palate, offering different dimensions of flavor. 4,800 cases made.", 18.0m, "1989", new Guid("4cf514d0-065e-4024-b129-f87185cf197b") },
                    { new Guid("a8711680-7371-4462-9d20-5e4f97c07610"), 2, new DateTime(1991, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Johannisberg Riesling Mendocino County Late Harvest Selected Dried Berry", "Ripe, spicy and balanced, an elegant dessert wine just oozing with grapefruit, honey, apricot and spice aromas and flavors that keep echoing on the long, generous finish. Drinkable now. 300 cases made.", 13.0m, "1990", new Guid("bd7197b8-1e54-400d-9036-2d6cc607f64e") },
                    { new Guid("61995775-fdb3-4dd5-9e9c-5b126b42cdff"), 6, new DateTime(1990, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Scharzhofberger", "  ", 31.0m, "1989", new Guid("65ef1893-d9f4-4799-b2c0-16c71dd5512d") }
                });

            migrationBuilder.InsertData(
                table: "Wines",
                columns: new[] { "Id", "Color", "IssueDate", "Name", "Note", "Price", "Vintage", "WineryId" },
                values: new object[,]
                {
                    { new Guid("8c652bff-3bd2-4620-9d97-b0c688f1dee4"), 3, new DateTime(1991, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape Les Cailloux", "Impressively well crafted and subtle. Like Cheval Blanc; has great finesse and pure fruit. Grapey, raisiny and earthy on the nose and palate, with well-integrated tannins and a focused, grapey, gamy finish.--Châteauneuf-du-Pape retrospective. Best from 1994 through 1996.  ", 16.0m, "1989", new Guid("49a6cb6a-5fa8-4b78-b06a-89a55a1e62c9") },
                    { new Guid("d2b071f0-cefa-4ee5-b3fc-6569757aa0f2"), 6, new DateTime(1995, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace Grand Cru Gueberschwihr Goldert", "Bold and opulent but dry, combining smoky aromas, ripe pear and honey flavors and a long-lasting nutty, mineral character on the finish. Very full-bodied and assertive, yet well balanced by acidity. Drinkable now through 2000. 300 cases made.", 35.0m, "1993", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("d30aae6f-ed7d-4494-8cc0-5ffa297bd3a0"), 6, new DateTime(1999, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot d'Alsace Alsace", "Delicious from start to finish, this gorgeous white bursts with ripe peach, smoke, nut and citrus character. Complex, fresh and beautifully balanced, this is textbook Alsace, a perfect introduction to the region. Drink now through 2001. 2,000 cases made.", 22.0m, "1997", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("1c863555-3807-483b-9ac6-a1cd201112f4"), 6, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace Turckheim Herrenweg", "Lovely richness, fleshing out the honey, litchi and grapefruit peel notes. It gets good support from a moderately firm structure, with a touch of bitterness shoring up the long finish. Best from 2004 through 2008. 1,600 cases made.", 34.0m, "2001", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("dbd8ab1d-48e8-41e4-a741-4035f830f9ad"), 6, new DateTime(2008, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Alsace", "A dry white that's well put together, with integrated acidity and a compact structure that mesh nicely. A pleasant orange blossom note accents flavors of quince and honey, while the lingering finish shows a hint of minerality. Drink now through 2020. 1,000 cases made.", 24.0m, "2006", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("0e65ff77-84a8-45b6-a68a-156ec98382fa"), 5, new DateTime(1993, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Carte d'Or", "Packs in lots of concentrated flavors, yet maintains its elegance and finesse, with spicy pear, vanilla, toast and light cherry notes, finishing with a smooth, creamy texture. Drink now through 1996. 4,000 cases made.", 35.0m, "1989", new Guid("1065ca21-78f6-40b9-aa80-2a968aabb2cd") },
                    { new Guid("127f2ea5-d13d-430c-948f-955f358b459e"), 6, new DateTime(1993, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Northern Sonoma Estate Bottled", "Gallo hits the big time with this outstanding new estate Chardonnay. Elegant, rich and flavorful, with beautiful detail. Has wonderful balance and finesse. Picks up a smoky edge on the finish. Drink now through 1995. 3,520 cases made.", 30.0m, "1991", new Guid("665f85d6-38d3-4a11-bf5c-cdf9964bb68c") },
                    { new Guid("dfaeab5b-83b3-45de-8633-0300c6a1bbc3"), 3, new DateTime(1993, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meritage Alexander Valley", "Ripe and distinctive, with a toffee, spice-scented tone to the supple currant and blackberry flavors, finishing rich and elegant. Has the style and concentration to mature through 1998. 70 percent Cabernet Sauvignon, 25 percent Cabernet Franc and 5 percent Merlot. 8,000 cases made.", 14.0m, "1990", new Guid("5c4edacc-ec29-49fd-9505-83400ae2ab77") },
                    { new Guid("cb626136-8aef-4f8b-ab4c-c536cf669339"), 6, new DateTime(1993, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Santa Ynez Valley Barrel Fermented", "A graceful, harmonious California Chardonnay, with plenty of style. Crisp, focused and flavorful, with spicy, toasty apple, pear and vanilla aromas and flavors that swirl around on the finish. Drinkable now. 16,000 cases made.", 13.0m, "1991", new Guid("98931bea-5ba0-43c5-afcc-c5c529783f1f") },
                    { new Guid("427e09f6-3b55-4c42-babc-12ca4cfe20cb"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Brunate", "Smooth and polished, with an elegant beam of raspberry, cherry, spice and rose petal lighting up the aromas and flavors, sweet tannins swarming over the finish. Still, the fruit shines through. Best to drink from 1997. 1,800 cases made.", 25.0m, "1989", new Guid("40a6e175-da0f-4665-80a7-6f5f6a79c913") },
                    { new Guid("9b06ffb2-042d-4fb0-900e-5ae5afe54ad6"), 6, new DateTime(1993, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault Clos de la Baronne Domaine René Manuel", "Wonderfully ripe, with toasted oak, vanilla, honey and tropical fruit; a full body and a long, flavorful, fresh finish. Drink now. 800 cases made.", 21.0m, "1991", new Guid("d8171d1e-0ac9-437c-9876-c2ae54bc2407") },
                    { new Guid("d9b5a8d2-8b06-4281-a542-8acd1ec23e37"), 6, new DateTime(2017, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Halbtrocken Mosel Zeltinger Himmelreich", "Pungent, with aromas of slate and ripe orchard fruit, this sleek white displays complex flavors of flowers, peach, quince and slate. A hint of chive works into the mix as this remains persistent on the finish. Drink now through 2024. 140 cases imported.", 20.0m, "2015", new Guid("fbebf4e3-8a76-46cd-a4fd-339189a7815c") },
                    { new Guid("0665633a-e3af-442e-bed7-669be1e41a21"), 6, new DateTime(1993, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Zeltinger Sonnenuhr", "This has what many '92 Mosel Rieslings are lacking: structure and concentration. A bit shy now, but the peach, floral and mineral flavors become fuller and more complex after a while in the glass. Try now.  ", 11.0m, "1992", new Guid("fbebf4e3-8a76-46cd-a4fd-339189a7815c") },
                    { new Guid("1bdcaa15-f53d-4a99-b7aa-307f57cc5f18"), 6, new DateTime(2003, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Winemaker's Reserve", "A rich, flamboyant style, from its wonderful aromatics to its opulent display of fruit and oak flavors. The mix of fleshy pear, fig and smoky oak combine to open all kinds of flavor doors. Finishes with mineral and lemon-citrus scents. Drink now through 2006. 1,958 cases made.", 25.0m, "2001", new Guid("fd1e40ed-5d9d-460f-a378-03efdfdb0fb4") },
                    { new Guid("afaaaa2b-62c8-41cd-b408-1124abf5e35d"), 3, new DateTime(1993, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Dry Creek Valley Gold Leaf Cuvée", "Crisp and lively, with a rich, focused band of raspberry, cherry and currant flavors flanked by firm, drying tannins. Packs in lots of fresh, lively fruit and is elegantly balanced. The tannins suggest you can cellar this through 1998. 2,050 cases made.", 10.0m, "1990", new Guid("674a37a5-5efb-4184-8a36-11cd453b615b") },
                    { new Guid("3f56063b-1de2-4028-91eb-042acca39568"), 5, new DateTime(1996, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Carte Blanche", "Powerful and fruity, young and lively in texture, this offers lots of pear, strawberry and lemon flavors, with good depth and length. Nicely balanced, too. Should improve through 1998. 4,000 cases made.", 30.0m, "NV", new Guid("1065ca21-78f6-40b9-aa80-2a968aabb2cd") },
                    { new Guid("1a6c5808-ffd2-4a2c-97c6-82c65478a79f"), 3, new DateTime(1993, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Fronsac", "A classy wine that shows great winemaking. Fills your mouth with plum and black cherry flavors and silky tannins. From Michel Rolland. Drinkable now. 3,500 cases made.", 17.0m, "1990", new Guid("424b104b-be9a-4082-9d6b-5849a48f9541") },
                    { new Guid("2fca5a0d-cefb-47e9-9a09-f86fdb511080"), 6, new DateTime(1993, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sancerre La Moussière", "Ripe, round and spicy, this uniquely balanced wine offers hazelnut, butter and toast nuances to the basic green apple and honeydew melon aromas and flavors that echo on the long finish. A stylish wine that shows all the signs of growing more complex. Drink now through 1995. 20,000 cases made.", 16.0m, "1991", new Guid("eabfff40-ffff-43eb-88c7-c2fdae178a10") },
                    { new Guid("5c095057-ab4a-4bc8-865d-bfa02d5674eb"), 6, new DateTime(1993, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Carneros", "Ripe, rich and packed with flavor, with tiers of honey, pear and butterscotch. The texture is smooth and silky, and flavors run on and on, gaining nuances on the finish. Best Rombauer Chardonnay in years. Delicious now through 1995. 4,000 cases made.", 15.0m, "1991", new Guid("2273e909-7189-41b1-bef6-41d7eb0f3869") },
                    { new Guid("548a8fcd-9662-4418-8db2-a0e54132f453"), 3, new DateTime(2000, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon North Coast", "Ripe and fleshy, with blackberry, chocolate and toffee flavors supported by round tannins that linger on an intense finish. Drink now through 2005. 14,382 cases made.", 17.0m, "1997", new Guid("2ad1ff11-43fa-4e23-9044-afa6f3b131c1") },
                    { new Guid("ce3def9c-7ada-43c2-b414-cfef2e3d5b9f"), 3, new DateTime(1994, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Napa Valley Beckstoffer Vineyard Reserve", "Young and a bit austere at first, it is well focused, concentrated and serves up complex herb, currant and black cherry flavors that turn supple and elegant on the finish. Shows a measure of finesse and restraint. Ready in 1998. 1,311 cases made.", 35.0m, "1991", new Guid("2ad1ff11-43fa-4e23-9044-afa6f3b131c1") },
                    { new Guid("aa65e57d-4f6a-4e70-89f8-397fbc4606af"), 3, new DateTime(1993, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meritage Lake County", "Comes alive with rich, complex fruit that echoes currant, spice and cherry, with a pretty toasty, buttery character from new oak. Balanced and concentrated, adding a long, full finish that keeps pumping out the flavor. 66 percent Cabernet Sauvignon, 18 percent Cabernet Franc, 15 percent Merlot. 4,672 cases made.", 15.0m, "1990", new Guid("2ad1ff11-43fa-4e23-9044-afa6f3b131c1") },
                    { new Guid("9dca8725-af6e-4059-b702-654f73f14a17"), 6, new DateTime(1993, 4, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Sonoma County", "Ripe, lush and spicy, with a rich core of pear, peach and nectarine flavors kept alive by crisp acidity. Has a smooth, creamy texture and a long, full, lingering aftertaste. Drink now. 3,470 cases made.", 13.0m, "1991", new Guid("bc312428-8092-44c4-894a-690498e7f8cb") },
                    { new Guid("ea84c0f5-a588-4f49-9ff3-06dff4121e3d"), 3, new DateTime(1994, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Anderson Valley Roederer Estate Vineyards", "Deep color, with ripe, rich, well-focused black cherry, wild berry and raspberry flavors that are complex and concentrated. Distinctive and makes a strong statement about the quality and potential of Pinot Noir from this California appellation. 750 cases made.", 15.0m, "1992", new Guid("8903a5f3-15b9-4fb6-af26-65e93f986ec3") },
                    { new Guid("dfc19142-9b06-4077-8d00-bebfec1025de"), 3, new DateTime(1993, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Sonoma County Scherrer Vineyards", "Delicious, pure and ripe, offering cherry, raspberry, plum and spice flavors. Intense, complex, wonderfully balanced and harmonious. Try a bottle now and enjoy for its vitality, and keep a few to see what happens. Best from 1994 through 2000. 531 cases made.", 13.0m, "1991", new Guid("8903a5f3-15b9-4fb6-af26-65e93f986ec3") },
                    { new Guid("d22667cb-914c-484b-b51c-cca79221c9e7"), 3, new DateTime(2012, 2, 29, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera d'Asti Superiore Le Orme", "Sweet blackberry, plum and pomegranate fruit is fresh and vibrant as this red unfolds across the palate. Hints of flowers and spices intermingle, but this is more about the juicy fruit. Drink now through 2018. 19,000 cases imported.", 15.0m, "2009", new Guid("322b6275-2992-4592-8432-28deffc482d6") },
                    { new Guid("a2e92b77-a910-4d46-9f0c-73e75888dfc4"), 3, new DateTime(2004, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Tortoniano", "Very fresh on the nose, with sliced plums and hints of blackberries. Full-bodied, with a solid core of ripe yet clean fruit and very silky tannins. Very polished. Best after 2007. 5,000 cases made.", 40.0m, "2000", new Guid("322b6275-2992-4592-8432-28deffc482d6") },
                    { new Guid("f60745ec-58a1-41be-ad0e-c4c4f9caa8fc"), 6, new DateTime(1993, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Yakima Valley Reserve", "A great combination of ripeness and delicacy, with honey, hazelnut, pear and butter flavors. True Chardonnay flavor comes through in this Washington wine, with complexity and length. Beautifully balanced, showing richness without weight. Drink now through 1995. 2,060 cases made.", 16.0m, "1990", new Guid("e445cf03-6fac-4c65-a23f-1908c0d1027c") },
                    { new Guid("e48a8a1c-b90b-433b-8df6-0eba3e94cdd4"), 5, new DateTime(1993, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Anderson Valley L'Ermitage", "A wonderful example of just how good California bubbly can be. Creamy and rich, a toasty wine with pear and hazelnut notes that keep singing on the finish. A graceful bubbly with style and plenty of flavor, a lot like a fine Champagne. 4,600 cases made.", 35.0m, "1989", new Guid("5e07525c-e32a-4957-9e81-46ac59661dd5") },
                    { new Guid("962fe80c-8a0c-4659-add5-344bd5135e17"), 5, new DateTime(1994, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Anderson Valley", "A spicy California sparkler, with ripe pear, hazelnut, honey and butter notes. It serves up a wide range of complex flavors and finishes with an elegant touch. 56,000 cases made.", 16.0m, "NV", new Guid("5e07525c-e32a-4957-9e81-46ac59661dd5") },
                    { new Guid("98b2afd7-0c13-4d67-bc91-28e61f35c1fa"), 5, new DateTime(1997, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Rosé Anderson Valley", "Wonderful complex aromas and flavors, with smooth creamy vanilla notes, lots of delicate black cherry, herb, spice and pear flavors, showing lots of finesse. Long and complex on the finish. Drink now. 3,000 cases made.", 21.0m, "NV", new Guid("5e07525c-e32a-4957-9e81-46ac59661dd5") },
                    { new Guid("41f0513d-1fe6-44cd-835c-da441ea4f2a0"), 3, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Coonawarra John Riddoch Limited Release", "Chewy, focused and impressively deep in flavor. It's plush and pours out blackberry, currant and spicy-herbal flavors. Finishes with an echo of the minty notes that also sneak in on the aromas; this has style and great potential. Tempting now, but best from 1998. 3,900 cases made.", 22.0m, "1990", new Guid("e89a635f-4f9a-47e6-9cfb-185b5c1d352e") },
                    { new Guid("8e2416b8-016b-41c8-9190-d668bbf64fa2"), 6, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Savennières Cuvée Spéciale", "Imagine an apple tart glazed with honey and lemon. This wine has the same richness and intensity of flavor, but it's still tight and young. It's concentrated, balanced and long, with a trace of sweetness to balance the firm acidity, and a long beeswax and honey finish. Drink now. 2,600 cases made.", 14.0m, "1992", new Guid("f9ff03de-6e82-42b7-93dd-4fc0949b86ed") },
                    { new Guid("305bac8c-9858-4452-99df-818fafec11dd"), 3, new DateTime(2001, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah McLaren Vale Basket Pressed", "Crisp and juicy Shiraz, rich with fruit, bursting with flavor, offering a cascade of bright berry, guava, plum and anise flavors, echoing with a distinctive black pepper note on the spicy finish. Fine-grained tannins don't get in the way. Drink now through 2012. 8,000 cases made.", 29.0m, "1998", new Guid("bc18dd59-0366-4bf1-9da5-e3f2a25cb175") },
                    { new Guid("d2c0325e-5b6f-4097-ab81-81efed4a856d"), 3, new DateTime(1996, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz McLaren Vale Basket Pressed", "A generous mouthful of brilliant berry, black pepper, sweet spice, coffee and chocolate flavors that swirl through the velvety finish. Delicious now; best in 1997. 4,000 cases made.", 12.0m, "1994", new Guid("bc18dd59-0366-4bf1-9da5-e3f2a25cb175") },
                    { new Guid("e33ad230-e16c-4e93-ab7f-424acb6928ab"), 2, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tawny Port Australia Old Cave Fine Old", "A rich, round and generous wine that cascades its spice, leather, raisin, plum and toast flavors into a long and elegant finish. Delicious to drink by itself. 2,000 cases made.", 12.0m, "NV", new Guid("bc18dd59-0366-4bf1-9da5-e3f2a25cb175") },
                    { new Guid("b261fe71-fe89-4be0-b0c6-411652f10295"), 6, new DateTime(2018, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viognier South Australia The Y Series", "Lemon, chamomile and spicy nectarine flavors are fresh and vibrant, set on a crisp body, finishing spicy and refreshing, with plenty of intensity. Drink now. 19,000 cases imported.", 13.0m, "2017", new Guid("81dcfe12-b4c0-47e6-a3b8-9eaa667e4b63") },
                    { new Guid("9c70cc48-daea-4b85-af35-6edc11a2a0d2"), 6, new DateTime(2008, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Viognier Eden Valley", "Lithe and appealing for its gorgeous apricot, pear and floral flavors, gliding smoothly over a silky frame and lingering effortlessly. Drink now through 2011. 1,600 cases imported.", 19.0m, "2007", new Guid("81dcfe12-b4c0-47e6-a3b8-9eaa667e4b63") },
                    { new Guid("e1220905-fc41-4fae-acf6-0eb174c88e0d"), 2, new DateTime(1998, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscat Victoria Museum Release", "This Australian \"sticky\" rivals the quality of many more-prestigious versions, at a fraction of the price. It's a gorgeous mouthful of spice and walnut, sweet and opulent, and is deep with clove, coffee, dried litchi and berry flavors that linger with hints of tangerine peel on the finish. Yummy. Drink now. 3,500 cases made.", 15.0m, "NV", new Guid("81dcfe12-b4c0-47e6-a3b8-9eaa667e4b63") },
                    { new Guid("9b4572c6-9ce2-47f4-a4c8-e80ce7bdc486"), 2, new DateTime(1994, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Muscat Rutherglen Museum Show Reserve", "Amazingly rich, ripe and powerful, gorgeous and unctuous, with layers and layers of spice, tar, coffee and molasses around a core of prune and apricot flavors. A great dessert wine. 1,000 cases made.", 12.0m, "NV", new Guid("81dcfe12-b4c0-47e6-a3b8-9eaa667e4b63") },
                    { new Guid("4a10850a-959b-4d84-a3b1-fc58692eaf73"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Il Pareto", "A powerhouse, with deep color and super ripe, exotic berry, chocolate and plum aromas and flavors. Full-bodied, with full, soft tannins. Made from Cabernet Sauvignon. Try after 1998. 1,450 cases made.", 41.0m, "1990", new Guid("e53f4315-3031-4f04-985b-b96cf11aacfc") },
                    { new Guid("0da08035-e30f-4fd4-b26d-d52ec2ef16c3"), 6, new DateTime(1999, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pouilly-Fuissé Tête de Cuvée", "What a beauty. Ultrasmooth, yet racy from a lemony texture laced with honey and lovely ripe fruit, this one of the cleanest Pouilly tasted from '97, displaying a minerally concentration on the finish, and not even the slightest astringency, it's that balanced. Drink now through 2005. 2,166 cases made.", 18.0m, "1997", new Guid("0e031ef4-dad3-4600-9660-bab0613dd32c") },
                    { new Guid("dff6cb34-8e05-4174-be8e-b0683842b626"), 6, new DateTime(1997, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Les Enseignères", "Takes off like a rocket on the palate, with amazing intensity of fruit, acidity and toasted oak. Wonderful mineral flavors, too. Don't touch until 2000 to 2010. 650 cases made.", 35.0m, "1995", new Guid("0e031ef4-dad3-4600-9660-bab0613dd32c") },
                    { new Guid("1e6acc58-06a4-4787-a2bf-f97e54205e59"), 6, new DateTime(1994, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Sous le Puits", "Stylish, exuberant and lively, with tons of toast, butter and pear flavors. Its vibrant lime and lemon character keeps it in check, leading to a crisp finish that gives it length and certainly, a long life in the cellar. Drink now through 1998. 600 cases made.", 38.0m, "1992", new Guid("0e031ef4-dad3-4600-9660-bab0613dd32c") },
                    { new Guid("8ce2ed69-7805-4431-a447-fa863c9dfa66"), 2, new DateTime(1994, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vintage Port Guimaraens", "Wonderful and classy, with superb balance. Very dark and inky, featuring thick berry, tar and violet aromas and flavors. Sweet and silky with a long finish. Best after 2002. 3,000 cases made.", 35.0m, "1991", new Guid("470d70df-f669-4629-99b0-ba901085a05e") },
                    { new Guid("5c8aedfa-e5ed-477c-b574-3df56ed6e13d"), 5, new DateTime(2019, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Cuvée", "Lithe and vibrant, this graceful Champagne is driven by well-knit, racy acidity, carrying flavors of ripe black cherry, grated ginger, toast point and mineral on the satiny mousse. Drink now through 2022. 222,500 cases made, 29,000 cases imported.", 47.0m, "NV", new Guid("0d47e761-54f0-4fab-bd1a-924910869d37") },
                    { new Guid("40f6891b-0bc0-4b55-8a75-c49fb728e3e7"), 5, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Rosé Champagne", "Has the taste of authenticity, with a provocative style that reminds us of an aged red Burgundy. Earthy, leathery aromas, well-seasoned, meaty flavors and echoes of cherry and spice on the long finish. Exciting and unique. 9,000 cases made.", 30.0m, "NV", new Guid("0d47e761-54f0-4fab-bd1a-924910869d37") },
                    { new Guid("2a4c9641-1c15-4e1d-b2c0-ff0dbb6bd3af"), 2, new DateTime(1994, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vintage Port Quinta de Vargellas", "The greatest Vargellas ever produced. Offers an explosion of fruit flavor. Deep dark purple color, with amazing cassis and chocolate aromas. Thick and rich, medium-sweet; full tannins but racy and fine. Try after 2000. 4,000 cases made.", 37.0m, "1991", new Guid("e89a586e-7801-4008-8296-6a2ff2585d42") },
                    { new Guid("8d0c084d-4fbc-45ee-bb05-d0ad4ec71def"), 3, new DateTime(1994, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Ornato", "Deep in color and tremendously rich in aroma and flavor, a supple package that oozes with licorice, plum, berry and black cherry notes. The tannins are beautifully integrated, gently supporting the whole show of fresh flavors. Awfully tempting to drink now, but try to hold off until 1997 to 2001.  ", 41.0m, "1990", new Guid("aa144935-89a1-4f52-a324-3ba9a3ad25cd") },
                    { new Guid("a79a6dd3-b66d-4ab3-ac7f-d9f7570629dd"), 3, new DateTime(1994, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Oroppas Napa Valley", "A solid wine with a rich core of ripe plum, black cherry, currant and new-oak flavors, all of which are bright and well focused. Finishes with a long, full, rich aftertaste. Has the depth and concentration to age well into the next decade. Delicious and merits special attention.  650 cases made.", 25.0m, "1992", new Guid("2d3cad67-3d0c-4471-8edc-7da2134b4600") },
                    { new Guid("c3af4362-f5f9-4463-b0b1-e2a706929241"), 3, new DateTime(1994, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Sorì Ginestra", "Rich, ripe and generous, a huge mouthful of seductive, pure fruit, balanced and supple right through the finish. Tannins are smoothly integrated into the berry, black cherry, anise, rose petal and violet flavors that seem to fan out over the palate. Delicious now, but has a great future; target 2000 to 2005.  ", 40.0m, "1990", new Guid("0bc06c07-1ab2-4474-82c3-637fd7417eab") },
                    { new Guid("e75dfbed-ecdc-4e28-9366-0f6e9dba02da"), 2, new DateTime(1994, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Vintage Port", "Even better than I remember from prior tastings. The best Croft since 1966 and easily one of the best of the vintage. This shows terrific cassis and black fruit aromas and flavors. Full-bodied, thick and medium-sweet, massive, with loads of tannins. Best after 2002. 9,000 cases made.", 32.0m, "1991", new Guid("22ad21e1-83a9-4c9d-abb7-a4af2b3cdbff") },
                    { new Guid("c1faf302-ce77-43f7-a300-33eea4c5d6e0"), 3, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Oregon Laurène", "Smooth and generous, glowing with blackberry and currant flavor that echoes on the supple finish. A fruit-centered Pinot Noir that is graceful enough to drink now. 3,000 cases made.", 40.0m, "1992", new Guid("fd1d78e2-402a-479f-96b6-fbdb1a36367a") },
                    { new Guid("ff1e6d55-8c5a-46ea-abe7-04451dd832cb"), 3, new DateTime(1993, 1, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Pinot Noir Oregon", "Smooth, ripe and focused, with supple cherry, plum and cedary oak flavors that run deep and gain complexity on the finish. Drinks well now, but has elegance, balance and fine tannins to age. Drink now through 1997. 2,350 cases made.", 30.0m, "1990", new Guid("fd1d78e2-402a-479f-96b6-fbdb1a36367a") },
                    { new Guid("c8802cb0-fa4e-402c-9857-1851a913652b"), 3, new DateTime(1993, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera d'Asti", "Peppery, spicy notes invigorate this smooth-textured, nicely balanced wine, berry at the core, hinting at tobacco and tar on the rich finish. Drinkable now through 1996 and beyond. 11,000 cases made.", 9.0m, "1990", new Guid("322b6275-2992-4592-8432-28deffc482d6") },
                    { new Guid("add028b8-a798-44ba-bb2b-ca18a28fd5c3"), 3, new DateTime(2000, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Priorat Finca Dofí", "Seamless. This red is a beautiful combination of ripe fruit, powerful structure and polished texture. Lush flavors of blackberries and plums are supported by firm, ripe tannins and give way to coffee and mineral notes on the long finish. Drink now through 2010. 1,200 cases made.", 49.0m, "1997", new Guid("91e8215a-2f4b-4acf-877c-563a40c103b2") },
                    { new Guid("489bf6e9-35f7-4cf3-991b-f7ccd436d4cd"), 6, new DateTime(1993, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault Les Tessons Clos de Mon Plaisir", "A blockbuster, with vanilla, honey, butterscotch and ripe fruit aromas and flavors. Full-bodied and immensely appealing. Best after 1996. 475 cases made.", 34.0m, "1990", new Guid("9970eaa3-b281-45d0-923e-8bda4dcc4e1c") },
                    { new Guid("d76ecab3-91c3-4b84-b91f-27030342f7c9"), 5, new DateTime(1993, 12, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne La Grande Dame", "Like a wild horse. Tons of potential. It's extremely exuberant and lively. Full bodied with masses of lemon, toast and apple character. It shows great acidity and long finish.  ", 100.0m, "1985", new Guid("ce152b2f-f732-401f-97e3-0d1e01bf41d9") },
                    { new Guid("ce8a6e13-a42d-4547-966a-7be7478322a2"), 6, new DateTime(1992, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Willamette Valley Barrel Selected Private Reserve", "Crisp at the center, but nicely rounded and polished around the edges, featuring toasty hazelnut, pear and honey aromas and flavors that extend into a long finish. Reminiscent of a good Puligny-Montrachet. Has style and grace. 700 cases made.", 20.0m, "1989", new Guid("27cb6992-4435-4489-80f2-b98f34261ee0") },
                    { new Guid("ad9a73f9-2c37-41bc-ac5f-67163137f893"), 6, new DateTime(1992, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Margaret River", "Crisp and focused, with sharply defined peach, pear and spice aromas and flavors, hinting at toast and butter on the long finish. The structure is tight, suggesting this will improve with cellaring. Drink now through 1995. 21,000 cases made.", 20.0m, "1990", new Guid("577b6557-c9f7-49ca-b419-938e38bb397d") },
                    { new Guid("b99c93c0-514b-4656-bb32-aaf27a1a82db"), 6, new DateTime(1996, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sauvignon Blanc Chalk Hill", "Has a welcome subtlety and depth that's missing from many other California Sauvignons. Offers plush pear, tobacco and spice flavors and delicate touches of citrus and parsley on the finish. 12,950 cases made.", 14.0m, "1994", new Guid("3352e4a4-c1da-4e15-be7a-54662aad3a32") },
                    { new Guid("d42ce918-8f8c-4d2f-8907-1461ff301e3e"), 6, new DateTime(1995, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Chalk Hill", "A bold, ripe and spicy California Chardonnay, with an intriguing earthy edge to the rich, creamy fig and apple flavors. There are notes of vanilla and toast, followed by a complex aftertaste. Drinkable now, but should develop through 1997. 15,938 cases made.", 19.0m, "1993", new Guid("3352e4a4-c1da-4e15-be7a-54662aad3a32") },
                    { new Guid("5854755b-1546-47b0-8336-b1f1ea5eddd3"), 2, new DateTime(1992, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sémillon Chalk Hill Late Harvest", "Would give most Sauternes a run for their money. The salty, nutty, tobaccolike aromas and rich, ripe flavors are full and satisfying. Sweet, unctuous and long on the finish. Mature now and ready to drink. 2,262 cases made.", 10.0m, "1986", new Guid("3352e4a4-c1da-4e15-be7a-54662aad3a32") },
                    { new Guid("b0b20150-6fc5-4286-ba01-786544548471"), 3, new DateTime(2013, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Alexander Valley Rockaway Single Vineyard", "Exhibits gorgeous dark berry flavors in a big yet graceful style, turning elegant and supple on the finish, where touches of spice, mocha and light oak come into play. Drink now through 2026. 1,356 cases made.", 75.0m, "2010", new Guid("fcc0ba31-195d-4368-9011-6d4a76b728cf") },
                    { new Guid("0e79ed93-e2d5-47dd-9221-532247fa58ae"), 6, new DateTime(2009, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Russian River Valley Reserve", "Wonderful perfumed aromas fold over to the palate, which is full-bodied and ultrarich without being heavy, delivering tiers of ripe fig, apricot and green apple, shaded by spicy, toasty oak. Better than previously reviewed. Drink now through 2012. 2,448 cases made.", 40.0m, "2006", new Guid("fcc0ba31-195d-4368-9011-6d4a76b728cf") },
                    { new Guid("46bcb63f-8d0a-4a24-a427-35e6f64c744e"), 3, new DateTime(1992, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Northern Sonoma Alexander's Crown Vineyard", "Smooth and generous, with layers of ripe currant, blackberry and buttery oak flavors. Finishes with a sense of polish and elegance. Almost drinkable now, and has the intensity to improve through 1997 to 2000. 6,000 cases made.", 18.0m, "1988", new Guid("fcc0ba31-195d-4368-9011-6d4a76b728cf") },
                    { new Guid("945d2f87-31a0-4424-a92c-12e279bd8421"), 3, new DateTime(1996, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Padthaway-McLaren Vale-Clare Valley Eileen Hardy", "Dark, almost black in color, densely packed with blackberry, black cherry, pepper and licorice flavors that just don't quit, ending in a smoky note. Has great depth and a long, fat finish. Tight enough to want cellaring. Best from 2000 through 2005. 2,000 cases made.", 20.0m, "1993", new Guid("52206633-8268-4cb6-a19d-f534235cf595") },
                    { new Guid("b84eb0c3-f255-4697-a28b-2cda52b8811d"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay South Australia Eileen Hardy", "A ripe, buttery wine that's deeply colored and deeply flavorful. The solid core of pineapple and pear flavor is shaded by hints of nutmeg, caramel and vanilla notes. A broad style that already shows finesse. Drink now. 3,500 cases made.", 19.0m, "1989", new Guid("52206633-8268-4cb6-a19d-f534235cf595") },
                    { new Guid("be20339c-5659-4247-8f71-16233dc5f4f6"), 3, new DateTime(1992, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barbera d'Alba Conca Tre Pile", "Tart, flavorful and spicy, with a generous, focused beam of juicy berry and currant flavors and a touch of oak on the finish, ending up smooth and balanced. Drinkable now.  ", 21.0m, "1989", new Guid("1c29bc35-e131-4907-97aa-4a4f12c7eb95") },
                    { new Guid("eb3996d9-cfb9-4c48-a8dc-e1bd39936da5"), 3, new DateTime(2018, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Carmenère Peumo Serie Riberas Gran Reserva", "This boasts richly spiced aromas, with flavors to match, as well as dried raspberry, portobello mushroom and currant elements. Dried beef notes on the powerful finish. Drink now through 2021. 33,000 cases made.", 17.0m, "2016", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("a0e01ed7-f17a-444d-945a-e29d22ca8143"), 6, new DateTime(2012, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Limarí Valley Marqués de Casa Concha", "A fresh, harmonious white, featuring pineapple, citrus and spice notes backed by juicy acidity and creamy texture. Well-integrated, with a lingering aftertaste of fruit and chalk. Drink now. 8,000 cases made.", 18.0m, "2010", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("6a62301d-97d0-4b6b-9bf1-b41deb1f4d4f"), 3, new DateTime(2009, 9, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Carmenère Peumo Terrunyo", "Dark in profile, with plum and currant fruit, but very pure, with silky tannins and a long, stylish finish that lets incense, spice and mineral notes play out. Drink now through 2011. 8,500 cases made.", 41.0m, "2006", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("9c9f1db9-5988-4608-ad13-95583c1abdd8"), 3, new DateTime(2004, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Puente Alto Don Melchor", "Terrific personality, with prune, cocoa, loam, mineral, dark currant, blackberry, tar and sanguine flavors that don't quit. Well-structured, with fine balance and harmony already and a very long finish. Best from 2005 through 2010. 15,200 cases made.", 41.0m, "2000", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("25325c9f-3fff-42c3-9c50-ab98b73db490"), 3, new DateTime(2003, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Merlot Peumo Marqués de Casa Concha", "Smoky and ripe, with black currant and plum fruit, hints of herbs, cocoa and toast, and fleshy, mouthfilling finish. Feels like it has the stuffing to cellar a bit too. This label is really strong these days. Drink now through 2005. 20,000 cases made.", 14.0m, "2001", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("a482473b-43a9-4564-ae48-68aa33c53973"), 3, new DateTime(2002, 11, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Puente Alto Marqués de Casa Concha", "Delicious Old World-style Cab, with ripe currant and cassis fruit accentuated by mineral, loam, smoke and earth notes. Ripe and juicy, with lovely mouthfeel, and the tug of <i>gout de terroir</i> on the finish. Back-to-back outstanding efforts for this wine. Drink now through 2005. 24,500 cases made.", 14.0m, "2000", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("4ee878fb-efa9-4698-a42a-35c4b13d2e67"), 3, new DateTime(1992, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Maipo Valley Puente Alto Vineyard Don Melchor Private Reserve", "Powerful and intense, pouring on the spice, with smoke, tobacco and cedar aromas backed by rich, ripe plum, prune and pepper flavors. A real mouthful, graced with chocolate and spice notes. Full-bodied and long on the finish. Perhaps Chile's best red wine. Drink now through 1995. 4,000 cases imported.", 14.0m, "1988", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("3c618d1f-5efb-4f57-89eb-bf66bed3aae7"), 3, new DateTime(1992, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Dry Creek Valley Saunders Vineyard", "Ripe and inviting, a firm-backboned, creamy-textured, oak-scented masterpiece, offering opulent raspberry, cherry, vanilla and spice flavors and nice hints of pepper and butter on the long finish. Drink now. 150 cases made.", 13.0m, "1990", new Guid("f3ee95b5-e980-409c-b07f-a325c73259bc") },
                    { new Guid("1e99e1fe-d926-442f-af12-71bc7990e446"), 3, new DateTime(1992, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel Paso Robles Westside", "Ripe and bold, with generous blackberry, plum and slightly pruny, spicy aromas and flavors that seem to jump out of the glass. Supple and bursting with character. Drink now through 1996. 1,000 cases made.", 12.0m, "1990", new Guid("34128478-c5e5-4481-b293-87bb307be9ea") },
                    { new Guid("a759eb24-0a50-4e0e-b7f5-814d3dcb000e"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Tokay Pinot Gris Alsace Jubilée Réserve Personnelle", "An exotic style of wine, with the power of a top white Burgundy. Concentrated aromas of tangerine and coconut; full-bodied and dry, with super-rich fruit flavors and a long, balanced finish of acidity. Very harmonious. Drink now.  ", 30.0m, "1990", new Guid("c09b56f1-6178-4b52-8e8a-dd363ecfd3b5") },
                    { new Guid("142246c5-0951-42d0-a00e-6bf30d971c22"), 6, new DateTime(2011, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Gewürztraminer Alsace L170", "Crackling acidity sets up this dry, aromatic version, with layered flavors of lychee, fleur de sel, orange peel and smoke, joined by a hint of cantaloupe. Finely meshed, building in intensity toward the minerally finish, with lots of lingering white pepper notes. The L170 in small print in the lower left corner of the label distinguishes this from the otherwise identical label of the L17M. Drink now through 2024. 3,000 cases made.", 23.0m, "2009", new Guid("4c493ad3-efa5-4f99-a180-065df03066f7") },
                    { new Guid("a774e4ef-b4a9-4f06-a2da-71e806ff14e2"), 3, new DateTime(2013, 4, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Syrah Buin Marqués de Casa Concha", "This compact red needs aeration, featuring layers of blackberry and dark plum notes wound around fine tannins, with the long finish evoking savory grilled game, cured olive and mocha. Best from 2014 through 2018. 5,500 cases made.", 22.0m, "2010", new Guid("d6efb15f-0373-4e3b-a10f-a0555af2c154") },
                    { new Guid("0fda3cb2-4aa7-435f-9037-2199c6611431"), 3, new DateTime(1997, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Cicala", "Barolo of the vintage. Beautifully balanced, from one of the region's finest vineyards, it sings with the purity of the Piedmont soil. Full-bodied but also elegant, with lots of ripe, sweet plum, jamlike currant and licorice flavors, and a superb minerally lead-pencil thread that gives extra dimension. As subtle as a top <i>grand cru</i> Burgundy, like Richebourg or perhaps Romanée St.-Vivant. Drink now through 2005. 567 cases made.", 125.0m, "1993", new Guid("1c29bc35-e131-4907-97aa-4a4f12c7eb95") },
                    { new Guid("bff58be9-41ee-48bb-bed5-fba145d31cd2"), 3, new DateTime(1992, 9, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Breganze Fratta", "Rich, balanced and deep, with great harmony. At this stage you taste a lot of new oak, but the generous vanilla, violet and toast notes complement the rich plum and black currant flavors. Best after 1995.  ", 39.0m, "1990", new Guid("b718bb78-3795-4b1c-8029-11f11d2ae63d") },
                    { new Guid("da0098c8-9c65-447d-8fbb-7a4fd38a8176"), 3, new DateTime(1992, 5, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Keyneton Hill of Grace", "One of Australia's finest red wines. Extremely ripe in flavor and plush in texture, with a great combination of fruit, spice, chocolate and tobacco. Complex, intense and satisfying, showing enough oak. Resembles a Côte-Rôtie. Tempting to drink now, but s 2,000 cases made.", 27.0m, "1987", new Guid("72c54b93-6b60-437a-833e-c8ace6adcf5e") },
                    { new Guid("1ab83b80-de47-4f2b-8c0b-9a3f3098c9dd"), 6, new DateTime(2003, 2, 28, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Kabinett Mosel-Saar-Ruwer Wehlener Sonnenuhr", "Gorgeous balance and grace in this peach- and mineral-flavored Riesling. Great clarity and delicacy defy the amount of flavors deftly positioned on its lightweght frame. Plenty of snap and a chalky texture on the finish. Drink now through 2012. 1,000 cases made.", 18.0m, "2001", new Guid("e1af523f-4e4e-4125-8b7f-6bfea5346abd") },
                    { new Guid("600a5f0b-aee5-49c1-863b-09e9013d23d4"), 6, new DateTime(1994, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Mosel-Saar-Ruwer Ürziger Würzgarten", "Very impressive for the vintage showing vivacious fruit character throughout. Medium-to full-bodied with sweet, ripe apple and apricot flavors, fine acidity and a long aftertaste. Delicious now.  ", 25.0m, "1993", new Guid("e1af523f-4e4e-4125-8b7f-6bfea5346abd") },
                    { new Guid("d01561c8-5c4e-4f7c-a940-a2d325613af8"), 6, new DateTime(1998, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Monterey Sleepy Hollow Vineyard", "An outstanding California Chardonnay, ripe, rich and creamy, with layers of pear, pineapple, fig and spice and a pretty dose of smoky, toasty oak folding in on the long, luxurious finish. Drink now through 2002. 7,000 cases made.", 30.0m, "1995", new Guid("07cbaf47-115b-4f4e-be3f-d6bb7136c89b") },
                    { new Guid("9eec0906-6a64-4503-a19f-d725705a63a0"), 6, new DateTime(1993, 6, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Chardonnay Monterey", "Ripe, intense and concentrated, with rich, spicy pear, peach and pineapple flavors that are smooth and polished. Reveals more depth and finesse with each sip and gains complexity and grace on the long, smoky finish. Simply delicious to drink now. 6,000 cases made.", 24.0m, "1990", new Guid("07cbaf47-115b-4f4e-be3f-d6bb7136c89b") },
                    { new Guid("f9fe6415-f8b5-4c5c-8edd-58ff7aa892bc"), 6, new DateTime(1995, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Puligny-Montrachet Clavoillon", "A flamboyant, ripe and seductive style of Chardonnay with plenty of fruit flavor accented by intriguing notes of butter, vanilla, nutmeg and toast. Firm with acidity, full-bodied and long on the finish. Appealing now but better if cellared until 1997. 300 cases made.", 65.0m, "1992", new Guid("aeb45d06-28c7-4853-9580-1e0c28143416") },
                    { new Guid("a63931bc-17c4-40f0-ae75-25dfd52ed918"), 6, new DateTime(1993, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Meursault Désirée", "Fantastic, with intense aromas of white truffles, apples and fruit. Full-bodied and concentrated, with great acidity. Drink now. 300 cases made.", 71.0m, "1990", new Guid("06a5e54d-777e-4556-975a-eed253786c7d") },
                    { new Guid("fa273bf4-227b-4ca9-96ae-16e767c491e5"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo La Corda della Briccolina", "Totally disarming, a gorgeous array of rose petal and anise-scented raspberry, vanilla and strawberry aromas and flavors extending through a supple finish that wraps its fine tannins smoothly around the warm fruit and spice. Drink now. 725 cases made.", 38.0m, "1989", new Guid("faf1df0d-35e6-4215-8ad4-dd266b8be724") },
                    { new Guid("31efbd1d-b99f-401e-9637-eb53aa0e5f37"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Sangioveto", "The greatest Sangioveto ever made. Massive wine with layers of tobacco, chocolate and violet character; full bodied and complete with perfect tannin structure and excellent acid. Best after 1997. 500 cases made.", 40.0m, "1988", new Guid("29af5a37-0ca6-41ca-8c44-a1d0b6965098") },
                    { new Guid("a506e7f2-a0e0-452c-8705-bfa53d9f5463"), 3, new DateTime(2002, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Carobric", "Very well-done. Bright fruit, with hints of dry porcini that turn to blackberries. Full-bodied, with loads of silky tannins and a medium finish. Best after 2005. 1,600 cases made.", 80.0m, "1998", new Guid("b6bf6f8a-9cf1-4c96-af2c-036c41b124c1") },
                    { new Guid("94275123-abaa-4511-9820-4d0cac5b05cb"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Bric dël Fiasc", "A luscious mouthful of wild berries, black cherry, toast and rose petal. Concentrated and exotic, a powerful wine with totally disarming aromas and flavors and remarkably supple texture. Layers and layers of personality make this a stellar candidate for cellaring. Best after 1997. 800 cases made.", 45.0m, "1989", new Guid("b6bf6f8a-9cf1-4c96-af2c-036c41b124c1") },
                    { new Guid("2af42f17-fc55-4670-9282-2a4c23993aa2"), 3, new DateTime(1993, 10, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Barolo Cannubi Ca' dei Gancia", "This stunning wine is rippling with layers and layers of dense, exuberant black cherry, violet, blackberry, tar and vanilla aromas and flavors. Muscular at the core, but sweet and supple through the extraordinary finish. A great wine in any context. The tannins are so refined you could drink it now, but has the stuff to age past 2000. 825 cases made.", 65.0m, "1989", new Guid("03910dc6-4b14-4777-9a98-a62dc67139a9") },
                    { new Guid("cdd48743-e27b-4eea-9e0e-30cd78ebf653"), 6, new DateTime(1992, 2, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Fumé Blanc Barrell-Fermented Reserve Alexander Valley", "Stands out from the pack. A rich-tasting, rich-textured Fumé Blanc, with butter and vanilla overtones to the citrus and pear flavors. Has depth, elegance and a long finish. 24,000 cases made.", 13.0m, "1990", new Guid("2d60e93b-a0ea-4daf-a686-496e68ce75c3") },
                    { new Guid("3363262d-5852-4b66-bbe8-642fd4f6c15a"), 3, new DateTime(1992, 10, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Zinfandel El Dorado Walker Vineyard", "Round and ripe, with a fine line of sweet currant and plum aromas and flavors running through it from the first sip to the long finish. Beautifully crafted fruit flavors are immediately appealing, and the texture is plush and smooth. A real winner. Taste 750 cases made.", 10.0m, "1990", new Guid("16a6dd19-d671-4cbf-9250-7ee7b60cd2bb") },
                    { new Guid("97f807f9-14a0-4db7-a591-11b669818fa6"), 5, new DateTime(2009, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Premier", "Light and lively, featuring apple, toast, ginger and light vanilla flavors. Balanced and on the dry side, with a creamy texture and a lingering butter pastry aftertaste. Drink now through 2011. 42,000 cases imported.", 43.0m, "NV", new Guid("29bc4513-611c-47fe-851e-166cc2eedbe7") },
                    { new Guid("5e350767-fe3e-4702-a5a0-fe5663e25b1c"), 5, new DateTime(1992, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Brut Champagne Brut Premier", "Creamy, elegant and stylish, offering complex layers of pear, spice, honey and vanilla flavors that are smooth and polished, finishing with a long, complex aftertaste. Subtle and refined; a treat to drink.  ", 38.0m, "NV", new Guid("29bc4513-611c-47fe-851e-166cc2eedbe7") },
                    { new Guid("293bc8ec-59dd-43be-8477-62721bf86b74"), 6, new DateTime(2015, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Hermitage White Chante-Alouette", "Gorgeous, showing remarkable range, with jasmine, honeysuckle, acacia and chamomile notes leading the way, followed by star fruit, yellow apple and green fig fruit flavors. The long, mineral-edged finish adds just the right amount of tension, while the fruit and floral elements cascade over one another. Drink now through 2029. 500 cases imported.", 100.0m, "2014", new Guid("3b8466a4-7918-45ff-8489-1304244a782f") },
                    { new Guid("019a467f-b966-4969-827c-4c799330dd92"), 3, new DateTime(2009, 12, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Côtes du Roussillon-Villages Les Vignes de Bila-Haut", "This muscular red shows concentrated flavors of dark cherry, plum, raspberry tart and grilled fig. The dense finish of dark chocolate is firm and focused, with smoky notes. Best from 2010 through 2014. 15,000 cases made.", 14.0m, "2008", new Guid("3b8466a4-7918-45ff-8489-1304244a782f") },
                    { new Guid("99e8cff9-8720-4e0e-8bec-4528077a16c4"), 3, new DateTime(2006, 5, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "St.-Joseph Les Granits", "Dark and beefy, with a note of game running through the black currant, coffee and tar flavors. This unwinds more on the finish, with a fine mineral thread running through currant and cocoa notes. Drink now through 2010. 650 cases made.", 65.0m, "2003", new Guid("3b8466a4-7918-45ff-8489-1304244a782f") },
                    { new Guid("7a0e0481-5c8e-44e6-b0c8-16a25e7c139c"), 3, new DateTime(1992, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Châteauneuf-du-Pape La Bernardine", "Shows uncommon balance, proportion and finesse, offering lovely currant, cherry, chocolate and smoky oak flavors that are complex and luscious. The finish is long and complex, with soft but firm tannins. Tasty now, but can age through 1998. 8,000 cases made.", 16.0m, "1990", new Guid("3b8466a4-7918-45ff-8489-1304244a782f") },
                    { new Guid("cb711ae2-bb11-4d97-b41f-001d96f83220"), 3, new DateTime(1992, 8, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Hermitage Monier de la Sizeranne", "Ripe, rich and complex, with deep currant, earth, oak and spice flavors that are finely woven together. The tannins are firm and rugged and clamp down on the finish. Best after 1997. 8,500 cases made.", 28.0m, "1990", new Guid("3b8466a4-7918-45ff-8489-1304244a782f") },
                    { new Guid("ae644fed-dc52-41d7-83dc-cefb7c97479b"), 3, new DateTime(1996, 3, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Keyneton Estate Eden-Barossa Valleys", "Smooth and spicy, with a nice undercurrent of ripe plum and anise flavors, plus a touch of prune. A harmonious and amazingly elegant Australian red that's appealing now. Shiraz, Cabernet Sauvignon and Malbec. 5,000 cases made.", 22.0m, "1993", new Guid("72c54b93-6b60-437a-833e-c8ace6adcf5e") },
                    { new Guid("694519ac-bd41-4e18-9252-deb493d0cafe"), 3, new DateTime(1995, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Shiraz Keyneton Mount Edelstone", "A full-bodied, concentrated Shiraz that delivers lots of flavor, with ripe cherry, plum and currant, lively spice and cedary oak. Finishes with a complex aftertaste and firm but not overpowering tannins. Drinkable now, but may be better by 1997.  10,000 cases made.", 30.0m, "1992", new Guid("72c54b93-6b60-437a-833e-c8ace6adcf5e") },
                    { new Guid("b1a8b8a8-c9a2-4795-9e4c-15ba072e582a"), 3, new DateTime(1995, 6, 15, 0, 0, 0, 0, DateTimeKind.Unspecified), "Cabernet Sauvignon Keyneton Cyril Henschke", "A smooth and harmonious Cab that shows off a pretty core of black cherry, currant, cedar and spice. Mild, elegant tannins and a lingering aftertaste round it off. Drinkable now, but age-worthy, too. 10,000 cases made.", 35.0m, "1991", new Guid("72c54b93-6b60-437a-833e-c8ace6adcf5e") },
                    { new Guid("6f35378c-b194-4881-8758-78ca588ec2af"), 6, new DateTime(1993, 11, 30, 0, 0, 0, 0, DateTimeKind.Unspecified), "Riesling Spätlese Rheinhessen Nackenheim Rothenberg", "This electrifying wine jolts you with its intense flavors. Starts with citrus, apricot and lemon notes and ends with honey, floral and almond flavors on the clean, focused, complex finish. Drinkable now, but should be better after 1997. 350 cases made.", 17.0m, "1992", new Guid("77c26f2d-ccd3-4932-b16e-88b5636d1c42") },
                    { new Guid("ee5100c0-3a05-4934-a4c4-fa854fed71fe"), 3, new DateTime(2019, 7, 31, 0, 0, 0, 0, DateTimeKind.Unspecified), "Grand Vin Haute-Judée", "Ripe currant, blackberry and plum flavors are nicely displayed with mocha, baking spice and cedar details in this sinewy, focused red. Hints of olive and graphite mark the tannic finish. Drink now through 2025. 3,900 cases made, 1,280 cases imported.", 85.0m, "2016", new Guid("d4228068-9e77-4ef8-ba64-f18f79e99b9e") }
                });

